INSERT INTO `products` (`product_id`, `name`, `author`, `price`, `image`, `description`, `supplier`, `publiser`, `date`) VALUES
('0bnUPlm9yW', 'Phá Vỡ Giới Hạn Để Không Hoài Phí Tuổi Trẻ', 'Trần Vĩnh Quân', 125000, 'https://cdn0.fahasa.com/media/catalog/product/p/h/ph_-v_-gi_i-h_n-_-kh_ng-ho_i-ph_-tu_i-tr_ull.jpg', 'Ph&aacute; Vỡ Giới Hạn Để Kh&ocirc;ng Ho&agrave;i Ph&iacute; Tuổi Trẻ\r\n\r\n&ldquo;Bạn c&oacute; đam m&ecirc; đi&ecirc;n cuồng c&ocirc;ng việc của m&igrave;nh kh&ocirc;ng?&rdquo; Phản ứng đầu ti&ecirc;n của rất nhiều người c&oacute; lẽ l&agrave;: Sao c&oacute; thể? T&ocirc;i c&oacute; thể y&ecirc;u bất cứ điều g&igrave; trừ c&ocirc;ng việc ch&aacute;n ngắt đ&oacute;! Hoặc l&agrave; &ldquo;Y&ecirc;u th&iacute;ch g&igrave; chứ, t&ocirc;i l&agrave;m việc chỉ v&igrave; tiền th&ocirc;i&rdquo;.\r\n\r\nKhoan đ&atilde; n&agrave;o, t&igrave;nh y&ecirc;u với c&ocirc;ng việc kh&ocirc;ng phải l&agrave; một trạng th&aacute;i xa vời chỉ c&oacute; tr&ecirc;n phim ảnh, đấy l&agrave; điều ch&uacute;ng ta c&oacute; thể cảm nhận được mỗi ng&agrave;y. Bạn c&oacute; biết rằng, những người đạt được th&agrave;nh c&ocirc;ng trong sự nghiệp đều giống như những t&ecirc;n đi&ecirc;n y&ecirc;u th&iacute;ch tất cả mọi việc m&igrave;nh l&agrave;m? Nếu kh&ocirc;ng say m&ecirc;, l&agrave;m sao bạn c&oacute; động lực để ho&agrave;n th&agrave;nh c&ocirc;ng việc tốt nhất?\r\n\r\nTh&ocirc;ng qua những c&acirc;u chuyện c&oacute; thật xen lẫn hư cấu, Ph&aacute; vỡ giới hạn để kh&ocirc;ng ho&agrave;i ph&iacute; tuổi trẻ đặt ra c&aacute;c kh&iacute;a cạnh của việc thiết lập tầm nh&igrave;n, mục ti&ecirc;u vững chắc trong c&ocirc;ng việc. Đồng thời, cuốn s&aacute;ch c&ograve;n l&agrave; kim chỉ nam dẫn lối, l&agrave; một sự chuẩn bị cho t&acirc;m l&yacute; v&agrave; phương ph&aacute;p hiệu quả cho những người trẻ tr&ecirc;n con đường ph&aacute; vỡ những r&agrave;o cản của bản th&acirc;n, x&acirc;y  dựng th&agrave;nh lũy t&acirc;m l&yacute; ki&ecirc;n cố để tạo n&ecirc;n gi&aacute; trị cho tuổi trẻ. Khi đ&oacute;, thế giới sẽ mở ra v&ocirc; v&agrave;n c&aacute;nh cửa tiềm năng trước mắt bạn, để bạn thỏa sức tung c&aacute;nh v&agrave;o tương lai.\r\n\r\n&ldquo;Nếu như một người trẻ tuổi kh&ocirc;ng thể ph&aacute;t hiện ra bất kỳ cơ hội n&agrave;o trong c&ocirc;ng việc v&agrave; cuộc sống để cho rằng bản th&acirc;n c&oacute; thể l&agrave;m tốt hơn, vậy th&igrave; thất bại thảm hại với người đ&oacute; chỉ l&agrave; sớm hay muộn m&agrave; th&ocirc;i.&rdquo;- Augson Madden &ndash;\r\n\r\nVề t&aacute;c giả:\r\n\r\nTrần Vĩnh Qu&acirc;n, nam, nh&agrave; văn trẻ mới nổi c&oacute; t&aacute;c phẩm b&aacute;n chạy tr&ecirc;n dangdang. L&agrave; một người trẻ tuổi, nhiệt huyết, Trần Vĩnh Qu&acirc;n đ&atilde; thực hiện nhiều chương tr&igrave;nh thảo luận về chủ đề ph&aacute;t triển v&agrave; th&agrave;nh c&ocirc;ng cho giới trẻ hiện nay. Trần Vĩnh Qu&acirc;n cũng l&agrave; t&aacute;c giả của nhiều b&agrave;i viết về năng lượng t&iacute;ch cực tr&ecirc;n nhiều tờ b&aacute;o nổi tiếng được độc giả hoan ngh&ecirc;nh.', '1980 Books', 'NXB Lao Động', '2023-04-25 01:08:10'),
('0SZaCB4AIT', 'Tôi Quyết Định Sống Cho Chính Tôi ', 'Kim Suhyun', 75000, 'https://cdn0.fahasa.com/media/catalog/product/9/7/9786049781414_2.jpg', 'Cuốn s&aacute;ch đạt giải thưởng &ldquo;Cuốn s&aacute;ch của năm&rdquo; tại H&agrave;n Quốc v&agrave;o năm 2017.\r\n\r\nĐ&atilde; t&aacute;i bản hơn 100 lần kể từ lần xuất bản đầu ti&ecirc;n v&agrave;o năm 2016.\r\n\r\nĐược nhiều nghệ sĩ chọn đọc v&agrave; giới thiệu đến c&aacute;c fan của m&igrave;nh, đơn cử l&agrave; JungKook (BTS).\r\n\r\n Như t&aacute;c giả Kim Suhyun đ&atilde; chia sẻ về qu&aacute; tr&igrave;nh s&aacute;ng t&aacute;c cuốn s&aacute;ch n&agrave;y:\r\n\r\n&ldquo;T&ocirc;i đ&atilde; cố gắng ghi ra những điều quan trọng nhất trong cuộc sống như:\r\n\r\nC&ocirc;ng việc, c&aacute;c mối quan hệ, niềm vui, tinh thần, thể chất, sức khỏe&hellip;\r\n\r\nT&ocirc;i đ&atilde; kh&ocirc;ng lường trước những việc c&oacute; thể xảy ra . M&agrave; t&ocirc;i chỉ l&agrave;m (Do) những việc t&ocirc;i muốn (Want) v&agrave; c&oacute; thể (Can). \r\n\r\nMột c&ocirc;ng thức đơn giản l&agrave; Want + Can = Do\r\n\r\nThay v&agrave;o đ&oacute;, h&atilde;y l&agrave;m việc chăm chỉ.\r\n\r\nT&ocirc;i dần tr&aacute;nh những mối quan hệ chỉ nhấn ch&igrave;m t&ocirc;i hoặc kh&ocirc;ng quan trọng với t&ocirc;i v&agrave; tự nhủ rằng t&ocirc;i sẽ kh&ocirc;ng cho ph&eacute;p bản th&acirc;n l&agrave;m bất kể điều g&igrave; một c&aacute;ch t&ugrave;y tiện nữa.        \r\n\r\nT&ocirc;i d&agrave;nh thời gian t&igrave;m kiếm niềm vui trong cuộc sống.\r\n\r\nC&oacute; những ng&agrave;y, t&ocirc;i ngồi ngắm bầu trời v&agrave;i lần để cảm nhận cuộc sống. T&ocirc;i đối diện với những vấn đề c&ograve;n bỏ ngỏ, giải quyết ch&uacute;ng v&agrave; cố gắng sống l&agrave;nh mạnh.\r\n\r\nV&igrave; những đ&aacute;p &aacute;n do x&atilde; hội quy định kh&aacute;c xa những quan niệm th&ocirc;ng thường của mọi người xung quanh ch&uacute;ng ta n&ecirc;n chỉ c&oacute; một điều m&agrave; ch&uacute;ng ta biết chắc, đ&oacute; l&agrave; cuộc sống rất r&otilde; r&agrave;ng v&agrave; nhẹ tựa l&ocirc;ng hồng.\r\n\r\nVậy &yacute; nghĩa của cuộc sống rốt cuộc l&agrave; g&igrave;?\r\n\r\nSau nhiều lần chất vấn, c&acirc;u trả lời t&ocirc;i t&igrave;m thấy kh&ocirc;ng c&ograve;n l&agrave; đ&aacute;p &aacute;n c&aacute; nh&acirc;n m&agrave; trở th&agrave;nh đ&aacute;p &aacute;n của cả một cộng đồng, đ&oacute; ch&iacute;nh l&agrave; hiện thực h&oacute;a gi&aacute; trị của bản th&acirc;n trong x&atilde; hội.\r\n\r\nT&ocirc;i lu&ocirc;n quan t&acirc;m v&agrave; kh&iacute;ch lệ sự đ&oacute;ng g&oacute;p của c&aacute; nh&acirc;n cho một x&atilde; hội tương lai.\r\n\r\nBởi v&igrave; giải quyết c&aacute;c vấn đề x&atilde; hội ch&iacute;nh l&agrave; một c&aacute;ch trực tiếp để giải quyết c&aacute;c vấn đề c&aacute; nh&acirc;n.\r\n\r\nNhưng đ&oacute; kh&ocirc;ng phải l&iacute; do duy nhất khiến ch&uacute;ng ta đ&oacute;ng g&oacute;p một phần bản th&acirc;n v&agrave;o x&atilde; hội.\r\n\r\nTh&ocirc;ng qua cuốn s&aacute;ch n&agrave;y, t&ocirc;i muốn lan truyền một l&agrave;n s&oacute;ng d&ugrave; nhỏ nhưng c&oacute; &yacute; nghĩa đến mọi người.\r\n\r\nNhững c&acirc;u hỏi kh&ocirc;ng c&oacute; hồi kết như &ldquo;Ch&uacute;ng ta phải sống thế n&agrave;o?&rdquo; lu&ocirc;n nảy sinh trong cuộc sống. Để t&ocirc;i n&oacute;i cho bạn nghe c&acirc;u trả lời của t&ocirc;i nh&eacute;, đ&oacute; ch&iacute;nh l&agrave;: &ldquo;H&atilde;y sống tốt với đời m&igrave;nh.&rdquo;\r\n\r\nBạn kh&ocirc;ng cần phải phức tạp h&oacute;a mọi vấn đề.\r\n\r\nBạn chỉ cần l&agrave;m việc chăm chỉ, t&acirc;m sự với những người th&acirc;n y&ecirc;u, c&ugrave;ng nhau ăn những m&oacute;n ngon, chia sẻ với nhau những b&agrave;i h&aacute;t y&ecirc;u th&iacute;ch, những cuốn s&aacute;ch hay v&agrave;o một ng&agrave;y đẹp trời bạn tận hưởng &aacute;nh mặt trời.\r\n\r\nT&ocirc;i nghĩ rằng một cuộc sống tốt đẹp trọn vẹn ch&iacute;nh l&agrave; chuỗi ng&agrave;y ấm &aacute;p, b&igrave;nh dị đang diễn ra. Nếu c&oacute; thể, h&atilde;y bước th&ecirc;m một nhịp để hướng tới cuộc sống c&oacute; &yacute; nghĩa.\r\n\r\nMặc d&ugrave; trong vũ trụ n&agrave;y c&ograve;n tồn tại những hạt bụi g&acirc;y vẩn đục cuộc sống th&igrave; ch&uacute;ng ta vẫn phải vượt qua v&agrave; giữ lấy phẩm gi&aacute;, t&ocirc;n nghi&ecirc;m của ch&iacute;nh m&igrave;nh. Bất chấp quy luật th&agrave;nh bại của thế giới, t&ocirc;i vẫn muốn tự h&agrave;o về một cuộc sống như vậy.\r\n\r\nC&oacute; một cuốn s&aacute;ch viết rằng: &ldquo;D&ugrave; bạn sống thế n&agrave;o, t&ocirc;i vẫn lu&ocirc;n cổ vũ.&rdquo;\r\n\r\nTuy nhi&ecirc;n, điều cần thiết kh&ocirc;ng phải l&agrave; người kh&aacute;c cổ vũ bạn m&agrave; bạn phải cổ vũ ch&iacute;nh m&igrave;nh.\r\n\r\nGửi đến bản th&acirc;n t&ocirc;i - bạn đồng h&agrave;nh với t&ocirc;i cho đến cuối cuộc đời:\r\n\r\n&ldquo;D&ugrave; t&ocirc;i sống thế n&agrave;o, t&ocirc;i vẫn lu&ocirc;n cổ vũ ch&iacute;nh m&igrave;nh.&rdquo;&rdquo;', 'AZ Việt Nam', 'NXB Thanh Niên', '2023-04-25 01:25:09'),
('146PEDfGuL', 'Sao Chúng Ta Lại Ngủ - Why We SLeep', 'Matthew Walker', 190000, 'https://cdn0.fahasa.com/media/catalog/product/b/i/biaao-01.jpg', 'Sao Ch&uacute;ng Ta Lại Ngủ - Why We SLeep\r\n\r\nL&agrave; cuốn s&aacute;ch về giấc ngủ đầu ti&ecirc;n được viết bởi ch&iacute;nh một chuy&ecirc;n gia khoa học h&agrave;ng đầu, gi&aacute;m đốc Trung t&acirc;m về Khoa học Giấc ngủ Con người của trường Đại học California, Berkeley, Sao ch&uacute;ng ta lại ngủ trở th&agrave;nh một cuộc kh&aacute;m ph&aacute; mang t&iacute;nh đột ph&aacute; về giấc ngủ, giải th&iacute;ch việc ch&uacute;ng ta c&oacute; thể khai th&aacute;c được sức mạnh biến đổi của giấc ngủ nhằm l&agrave;m thay đổi cuộc sống của ch&uacute;ng ta trở n&ecirc;n tốt đẹp hơn như thế n&agrave;o.\r\n\r\nGiấc ngủ từ l&acirc;u đ&atilde; trở th&agrave;nh một trong những kh&iacute;a cạnh quan trọng nhất song lại được hiểu đ&uacute;ng gi&aacute; trị &iacute;t nhất về sự sống, sức khỏe v&agrave; tuổi thọ của con người ch&uacute;ng ta &ndash; cho tới khi xuất hiện sự b&ugrave;ng nổ những cuộc kh&aacute;m ph&aacute; mang t&iacute;nh khoa học trong hai thập kỉ qua đ&atilde; bắt đầu soi rọi &aacute;nh s&aacute;ng mới mẻ về chủ đề n&agrave;y. Giờ đ&acirc;y, chuy&ecirc;n gia giấc ngủ ki&ecirc;m nh&agrave; khoa học thần kinh xuất sắc Matthew Walker sẽ giải th&iacute;ch thật cặn kẽ về tầm quan trọng to lớn của giấc ngủ, điều vốn tồn tại giữa bao chức năng hoạt động kh&aacute;c của cơ thể, gi&uacute;p tăng cường khả năng học tập v&agrave; ra quyết định, hiệu chỉnh lại cảm x&uacute;c, củng cố lại hệ miễn dịch v&agrave; điều tiết sự th&egrave;m ăn của ch&uacute;ng ta. Với lối viết r&otilde; r&agrave;ng, cấu tr&uacute;c tr&igrave;nh b&agrave;y vấn đề l&ocirc;i cuốn, từ ngữ dứt kho&aacute;t v&agrave; dễ hiểu v&ocirc; c&ugrave;ng, Sao ch&uacute;ng ta lại ngủ sẽ ho&agrave;n to&agrave;n l&agrave;m biến đổi sự đ&aacute;nh gi&aacute; v&agrave; hiểu biết của độc giả về giấc ngủ v&agrave; cả những giấc mơ.\r\n\r\nMỘT SỐ Đ&Aacute;NH GI&Aacute; VỀ CUỐN S&Aacute;CH\r\n\r\n&ldquo;Một nh&agrave; khoa học thần kinh chỉ cho ch&uacute;ng ta thấy được giấc ngủ trọn vẹn v&agrave;o ban đ&ecirc;m c&oacute; thể l&agrave;m cho ch&uacute;ng ta trở n&ecirc;n minh mẫn hơn, l&ocirc;i cuốn hơn, thon gọn hơn, hạnh ph&uacute;c hơn, khỏe mạnh hơn v&agrave; tr&aacute;nh khỏi ung thư như thế n&agrave;o.&rdquo; - The Guardian\r\n\r\n&ldquo;Đ&acirc;y l&agrave; cuốn s&aacute;ch khơi gợi sự k&iacute;ch th&iacute;ch v&agrave; c&oacute; tầm quan trọng thực tiễn m&agrave; bạn n&ecirc;n đọc để trang bị sự hiểu biết hơn về lĩnh vực m&agrave; theo như t&aacute;c giả đ&atilde; n&oacute;i l&agrave; &ldquo;c&oacute; giấc ngủ ch&iacute;nh l&agrave; c&oacute; t&igrave;nh y&ecirc;u với vạn vật v&agrave; l&agrave;m được mọi điều&rdquo;. - Financial Times\r\n\r\n&ldquo;Hầu hết ch&uacute;ng ta kh&ocirc;ng suy nghĩ g&igrave; về điều ch&uacute;ng ta thường d&agrave;nh tới một phần ba cuộc đời của m&igrave;nh cho n&oacute;. Nhưng trong cuốn s&aacute;ch s&aacute;ng suốt v&agrave; hấp dẫn n&agrave;y, Matthew Walker giải th&iacute;ch hết sức r&otilde; r&agrave;ng rằng ng&agrave;nh khoa học mới mẻ - khoa học thần kinh - đang nhanh ch&oacute;ng &ldquo;giải m&atilde;&rdquo; b&iacute; mật l&acirc;u đời n&agrave;y một c&aacute;ch thực tiễn ra sao. Sao ch&uacute;ng ta lại ngủ ch&iacute;nh l&agrave; niềm vui tr&iacute; tuệ sẽ khiến cho bạn phải lật giở từng trang s&aacute;ch đầy n&acirc;ng niu trước khi ch&igrave;m v&agrave;o giấc ngủ của m&igrave;nh.&rdquo;- Daniel Gilbert, Giảng vi&ecirc;n về t&acirc;m l&iacute; học tại trường Đại học Harvard ki&ecirc;m t&aacute;c giả cuốn s&aacute;ch Stumbling on Happiness\r\n\r\nGIỚI THIỆU T&Aacute;C GIẢ\r\n\r\nTiến sĩ Matthew Walker l&agrave; Gi&aacute;o sư giảng dạy về Khoa học thần kinh v&agrave; T&acirc;m l&iacute; học tại trường Đại học California, Berkeley, đồng thời l&agrave; gi&aacute;m đốc Trung t&acirc;m về Khoa học Giấc ngủ Con người v&agrave; l&agrave; cựu giảng vi&ecirc;n về T&acirc;m thần học tại trường Đại học Harvard. &Ocirc;ng đ&atilde; xuất bản hơn 100 nghi&ecirc;n cứu khoa học cũng như đ&atilde; xuất hiện trong c&aacute;c chương tr&igrave;nh uy t&iacute;n: 60 Minutes, Nova, BBC News v&agrave; Science Friday của NPR. Sao ch&uacute;ng ta lại ngủ l&agrave; cuốn s&aacute;ch đầu ti&ecirc;n của &ocirc;ng được giới thiệu tới c&ocirc;ng ch&uacute;ng sau hơn 20 năm nghi&ecirc;n cứu về giấc ngủ.', 'Hải Đăng', 'NXB Lao Động', '2023-04-25 01:27:00'),
('1HW4b2fIQH', 'Ám Ảnh Từ Kiếp Trước ', 'Brian L Weiss', 67000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_197918.jpg', 'Cuốn sách hay và khiêu khích suy nghĩ này đã phá vở những rào cản trong trị liệu tâm lý truyền thống và trình bày một biện pháp trị liệu cách tân và hiệu quả cao. Những ai làm việc chuyên về sức khỏe tâm thần cần phải xem xét nó nghiêm túc.” – Edith Fiore, TS., bác sỹ tâm lý học lâm sàng và là tác giả cuốn sách Bạn từng ở đây trước kia (You Have Been Here Before)\r\n\r\nVốn là nhà tâm lý trị liệu truyền thống, TS. Brian Weiss đã kinh ngạc và bi quan khi một trong những bệnh nhân của mình bắt đầu nhớ lại những chấn thương trong kiếp trước mà chúng dường như là chìa khóa để giải quyết những cơn ác mộng và lo lắng lặp đi lặp lại. Tuy nhiên, chủ nghĩa bi quan của ông bị xói mòn khi cô ấy bắt đầu phát những thông điệp từ “những sinh thể ở giữa không gian,” chứa đựng những tiết lộ quan trọng về gia đình của TS. Weiss và cái chết của con trai ông. Bằng biện pháp kiếp trước, ông có khả năng chữa lành cho bệnh nhân và mở ra một giai đoạn mới đầy ý nghĩa trong nghề nghiệp của chính mình.\r\n\r\n“Tường thuật xúc động sâu xa về sự tĩnh thức tâm linh ngoài dự liệu của một ngừoi đàn ông. Cuốn sách rất can đảm này đã mở cánh cửa cho một cuộc hôn phối giữa khoa học và siêu hình học. Cuốn sách phải đọc vì một thế giới khát khao, tìm kiếm linh hồn.” – Jeanne Avery, tác giả của Chiêm tinh học và tiền kiếp (Astrology and Your Past Lives)\r\n\r\n“Một trường hợp xuất thần lịch sử chứng minh tính hiệu quả của liệu pháp kiếp trước. Cuốn sách này sẽ mở ra những cánh cửa cho nhiều người chưa bao giờ nghĩ đến tính hợp lý của việc đầu thai trở lại.” – Richard Sutphen, tác giả của Tiền kiếp, tình yêu tương lai và bạn được sinh trở lại để được bên nhau (Past Lives, Future Loves and You Were Born Again to Be Together)\r\n\r\nTS. Brian L. Weiss, bác sỹ tâm thần học, sống và làm việc ở Miami, Florida. Ông tốt nghiệp trường đại học Columbia và trường y khoa của đại học Yale và là Chủ tịch Danh dự Tâm thần học của Trung tâm Y khoa Núi Sinai ở Miami. TS. Weiss vẫn có phòng trị liệu riêng ở Miami và tổ chức những hội nghị quốc tế, hội thảo trải nghiệm cũng như những chương trình huấn luyện cho những người hành nghề chuyên nghiệp. Ông cũng là tác giả của Đi qua thời gian để chữa lành (Through Time Into Healing) và Linh hồn cũ xác thân mới.\r\n\r\nThông tin về tác giả:\r\n\r\nBrian L. Weiss, M.D\r\n\r\nSau khi tốt nghiệp hạng ưu, trường đại học Columbia và nhận văn bằng y khoa tại trường y khoa của đại học Yale, Brian L. Weiss, TS. Y khoa (MD) tham dự chương trình nội trú tại Trung tâm Y khoa Bellevue, đại học New York, và thăng bậc thành bác sỹ nội trú trưởng, Khoa tâm thần, trường y khoa của đại học Yale. Hiện TS. Weiss là chủ tịch khoa tâm thần tại Trung tâm Y khoa Núi Sinai, vùng duyên hải Miami bang Florida và là trợ lý giáo sư lâm sàng khoa tâm thần, trường y của đại học Miami. Ông chuyên nghiên cứu và trị liệu tình trạng lo lắng và trầm cảm, rối loạn giấc ngủ, rối loạn lạm dụng chất gây nghiện, bệnh Alzheimer và hóa học não.', 'Thái Hà', 'NXB Lao Động', '2023-04-25 00:32:00'),
('2y7ukOCu7P', 'Nick Vujicic - Đừng Bao Giờ Từ Bỏ Khát Vọng', 'Nick vujicic', 86000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_225144.jpg', 'Ch&agrave;o mừng c&aacute;c bạn đến với cuốn s&aacute;ch thứ hai của t&ocirc;i. T&ecirc;n t&ocirc;i l&agrave; Nick Vujicic.\r\n\r\nC&oacute; thể bạn chưa đọc cuốn s&aacute;ch đầu tay của t&ocirc;i, cuốn Cuộc sống kh&ocirc;ng giới hạn, nhưng bạn c&oacute; thể đ&atilde; từng xem những video của t&ocirc;i tr&ecirc;n YouTube hoặc đ&atilde; dự một trong những buổi diễn thuyết v&ograve;ng quanh thế giới của t&ocirc;i.\r\n\r\nNh&igrave;n ảnh của t&ocirc;i tr&ecirc;n b&igrave;a s&aacute;ch, c&aacute;c bạn c&oacute; thể biết t&ocirc;i sinh ra kh&ocirc;ng c&oacute; ch&acirc;n, kh&ocirc;ng c&oacute; tay. Điều m&agrave; c&aacute;c bạn kh&ocirc;ng thể nh&igrave;n thấy nhưng c&oacute; thể cảm nhận được l&agrave; sự khuyết thiếu tứ chi n&agrave;y kh&ocirc;ng thể ngăn cản t&ocirc;i tận hưởng những chuyến phi&ecirc;u lưu th&uacute; vị, kh&ocirc;ng thể ngăn cản t&ocirc;i vươn tới một sự nghiệp đầy &yacute; nghĩa v&agrave; m&atilde;n nguyện, c&agrave;ng kh&ocirc;ng thể cản trở t&ocirc;i c&oacute; được những mối quan hệ đầy t&igrave;nh y&ecirc;u thương.\r\n\r\nT&ocirc;i viết cuốn s&aacute;ch n&agrave;y với mục đ&iacute;ch chia sẻ với c&aacute;c bạn sức mạnh kh&ocirc;ng g&igrave; ngăn cản được của niềm tin trong h&agrave;nh động, nguồn sức mạnh đ&atilde; gi&uacute;p t&ocirc;i tạo lập một cuộc sống tốt đẹp đến mức diệu kỳ, bất chấp những khuyết tật nặng nề. Đặt niềm tin v&agrave;o h&agrave;nh động c&oacute; nghĩa l&agrave; tin tưởng v&agrave; đạt được th&agrave;nh quả. Đ&oacute; l&agrave; niềm tin v&agrave;o bản th&acirc;n bạn, t&agrave;i năng của bạn, mục đ&iacute;ch của bạn, v&agrave; quan trọng hơn hết, l&agrave; niềm tin v&agrave;o t&igrave;nh y&ecirc;u của Đấng S&aacute;ng Tạo v&agrave; những kế hoạch quan trọng m&agrave; Người d&agrave;nh cho cuộc đời bạn.\r\n\r\nNguồn cảm hứng để t&ocirc;i viết cuốn s&aacute;ch n&agrave;y đến từ những người thuộc mọi lứa tuổi tr&ecirc;n khắp thế giới, những người t&ocirc;i đ&atilde; cho lời khuy&ecirc;n v&agrave; hướng dẫn họ đương đầu với những th&aacute;ch thức trong cuộc sống. Từ những buổi diễn thuyết của t&ocirc;i, họ biết rằng tỏi đ&atilde; vượt qua nghịch cảnh, thậm ch&iacute; vượt qua &yacute; định tự vẫn đ&atilde; tho&aacute;ng hiện trong t&ocirc;i v&agrave;i lần khỉ t&ocirc;i c&ograve;n nhỏ, khi t&ocirc;i vật v&atilde; với những c&acirc;u hỏi l&agrave;m thế n&agrave;o t&ocirc;i c&oacute; thể tự nu&ocirc;i sống m&igrave;nh, liệu t&ocirc;i c&oacute; t&igrave;m được một người phụ nữ y&ecirc;u t&ocirc;i hay kh&ocirc;ng, khi t&ocirc;i ch&igrave;m trong cảm gi&aacute;c buồn khổ v&agrave; ch&aacute;n chường vi bị bắt nạt, bị chế giễu, v&agrave; v&igrave; nhiều vấn đề kh&aacute;c, nhiều nỗi bất ổn kh&aacute;c bủa v&acirc;y.\r\n\r\nC&aacute;c chương trong cuốn s&aacute;ch n&agrave;y xoay quanh những c&acirc;u hỏi v&agrave; những th&aacute;ch thức m&agrave; mọi người n&ecirc;u ra khi họ n&oacute;i chuyện v&agrave; viết thư cho t&ocirc;i. Đ&oacute; l&agrave;: Những khủng hoảng c&aacute; nh&acirc;n; Những vấn đề li&ecirc;n quan đến c&aacute;c mối quan hệ; Những th&aacute;ch thức trong c&ocirc;ng việc v&agrave; sự nghiệp; Những mối quan t&acirc;m về sức khỏe v&agrave; khuyết tật; Những &yacute; nghĩ, cảm x&uacute;c ti&ecirc;u cực v&agrave; c&aacute;c chứng nghiện ngập; Sự bắt nạt, khủng bố, sự t&agrave;n nhẫn v&agrave; thiếu khoan dung; Việc đương đầu với những vấn đề vượt ngo&agrave;i tầm kiểm so&aacute;t của ch&uacute;ng ta; C&aacute;ch tiếp cận v&agrave; gi&uacute;p đỡ người kh&aacute;c; C&aacute;ch t&igrave;m được sự thăng bằng về thể chất, tinh thần, tr&aacute;i tim v&agrave; t&acirc;m hồn.\r\n\r\nT&ocirc;i hy vọng rằng việc chia sẻ những c&acirc;u chuyện của t&ocirc;i v&agrave; của những người kh&aacute;c, những người đ&atilde; ki&ecirc;n tr&igrave; vượt qua bao kh&oacute; khăn thử th&aacute;ch trong cuộc sống - nhiều người trong số họ đ&aacute;ng kh&acirc;m phục hơn t&ocirc;i nhiều - sẽ gi&uacute;p &iacute;ch v&agrave; kh&iacute;ch lệ c&aacute;c bạn vượt qua những th&aacute;ch thức m&agrave; c&aacute;c bạn đang phải đối mặt. Dĩ nhi&ecirc;n t&ocirc;i kh&ocirc;ng c&oacute; tất cả mọi c&acirc;u trả lời. Nhưng t&ocirc;i sẽ trao cho c&aacute;c bạn tất cả những g&igrave; m&agrave; t&ocirc;i đ&atilde; c&oacute; được từ những lời khuy&ecirc;n tuyệt vời của những con người th&ocirc;ng th&aacute;i cũng như từ t&igrave;nh y&ecirc;u v&agrave; phước l&agrave;nh của Ch&uacute;a.\r\n\r\nT&ocirc;i nghĩ c&aacute;c bạn sẽ nhận thấy sự chia sẻ của t&ocirc;i trong những trang s&aacute;ch n&agrave;y gi&agrave;u t&iacute;nh thực tiễn v&agrave; đầy t&iacute;nh kh&iacute;ch lệ. Bạn h&atilde;y lu&ocirc;n nhớ một điều rất quan trọng rằng bạn kh&ocirc;ng bao giờ đơn độc. Bạn lu&ocirc;n lu&ocirc;n c&oacute; sự gi&uacute;p đỡ của bạn b&egrave;, gia đ&igrave;nh, thầy c&ocirc; gi&aacute;o, những người cố vấn v&agrave; hỗ trợ tinh thần cho bạn. Đừng bao giờ nghĩ rằng bạn phải một m&igrave;nh đương đầu với những g&aacute;nh nặng của m&igrave;nh. Bạn cũng n&ecirc;n biết rằng tr&ecirc;n đời n&agrave;y vẫn c&ograve;n rất nhiều người đ&atilde; v&agrave; đang phải đương đầu với những th&aacute;ch thức như bạn.\r\n\r\nCuốn s&aacute;ch n&agrave;y sẽ kể với bạn nhiều c&acirc;u chuyện về những con người m&agrave; t&ocirc;i quen biết v&agrave; những người đ&atilde; viết thư cho t&ocirc;i để chia sẻ kinh nghiệm của họ. Trong một số trường hợp t&ocirc;i đ&atilde; thay đổi t&ecirc;n nh&acirc;n vật, nhưng những c&acirc;u chuyện được kể th&igrave; ho&agrave;n to&agrave;n l&agrave; sự thật v&agrave; lu&ocirc;n tr&agrave;n đầy sự kh&iacute;ch lệ bởi đ&oacute; l&agrave; những c&acirc;u chuyện về l&ograve;ng dũng cảm, niềm tin v&agrave; &yacute; ch&iacute; sắt đ&aacute;. Khi c&ograve;n l&agrave; một cậu b&eacute; phải cố gắng th&iacute;ch nghi với những khuyết tật của m&igrave;nh, t&ocirc;i đ&atilde; thật sai lầm khi nghĩ r&agrave;ng kh&ocirc;ng ai tr&ecirc;n đời n&agrave;y phải chịu đựng những nỗi đau như t&ocirc;i v&agrave; rằng những kh&oacute; khăn của t&ocirc;i khủng khiếp đến mức kh&ocirc;ng thể vượt qua nổi. T&ocirc;i cứ nghĩ rằng sự khuyết thiếu tứ chi của t&ocirc;i l&agrave; bằng chứng cho thấy Đấng S&aacute;ng Tạo kh&ocirc;ng y&ecirc;u thương t&ocirc;i v&agrave; rằng cuộc đời t&ocirc;i chẳng c&oacute; mục đ&iacute;ch g&igrave; hết.\r\n\r\nT&ocirc;i cũng cảm thấy rằng t&ocirc;i kh&ocirc;ng thể chia sẻ g&aacute;nh nặng của m&igrave;nh - kh&ocirc;ng thể chia sẻ ngay cả với những người lu&ocirc;n y&ecirc;u thương v&agrave; quan t&acirc;m đến t&ocirc;i. T&ocirc;i đ&atilde; lầm. T&ocirc;i kh&ocirc;ng hề đơn độc trong kh&oacute; khăn v&agrave; đau đớn. Quả thật tr&ecirc;n đời n&agrave;y c&oacute; nhiều người đ&atilde; v&agrave; đang phải đương đầu với những th&aacute;ch thức lớn hơn cả những g&igrave; m&agrave; t&ocirc;i đ&atilde; gặp. V&agrave; Ch&uacute;a kh&ocirc;ng chỉ y&ecirc;u thương t&ocirc;i, Người c&ograve;n cho t&ocirc;i những mục đ&iacute;ch m&agrave; khi c&ograve;n b&eacute; t&ocirc;i kh&ocirc;ng thể h&igrave;nh dung ra. Người đ&atilde; sử dụng t&ocirc;i theo những c&aacute;ch khiến t&ocirc;i kh&ocirc;ng khỏi ngạc nhi&ecirc;n đến kinh ngạc mỗi ng&agrave;y.\r\n\r\nBạn n&ecirc;n biết rằng chừng n&agrave;o bạn c&ograve;n sống tr&ecirc;n đời n&agrave;y, th&igrave; c&ograve;n c&oacute; một mục đ&iacute;ch, một kế hoạch d&agrave;nh cho bạn. Những g&aacute;nh nặng m&agrave; bạn mang c&oacute; thể l&agrave;m bạn nản ch&iacute;, nhưng như bạn sẽ thấy trong những trang s&aacute;ch m&agrave; bạn sắp đọc n&agrave;y, sức mạnh của niềm tin trong h&agrave;nh động thực sự rất kỳ diệu. Để bắt đầu hiểu điều n&agrave;y, bạn chỉ cần nhớ rằng t&ocirc;i, con người kh&ocirc;ng ch&acirc;n kh&ocirc;ng tay n&agrave;y, đ&atilde; đi khắp thế giới, đ&atilde; đến được với h&agrave;ng triệu người v&agrave; được hưởng niềm vui cũng như t&igrave;nh y&ecirc;u kh&ocirc;ng thể đong đếm được. T&ocirc;i khiếm khuyết như bất cứ ai bạn sẽ gặp trong đời. T&ocirc;i cũng c&oacute; những ng&agrave;y vui vẻ, những ng&agrave;y tồi tệ. Những th&aacute;ch thức nảy sinh trong cuộc sống đ&ocirc;i khi khiến t&ocirc;i quỵ ng&atilde;. Tuy nhi&ecirc;n t&ocirc;i biết rằng khi ch&uacute;ng ta đặt niềm tin v&agrave;o h&agrave;nh động th&igrave; kh&ocirc;ng g&igrave; c&oacute; thể ngăn cản được ch&uacute;ng ta vươn tới một cuộc sống tốt đẹp. ', 'FIRST NEWS', 'NXB Tổng Hợp TPHCM', '2023-04-25 01:28:33'),
('3DK59FSKNK', 'Góc Khuất Của Yêu Thương', 'Choi Kwanghuyn', 83000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_196647.jpg', 'Tiếp nối th&agrave;nh c&ocirc;ng của &ldquo;Hai mặt của gia đ&igrave;nh&rdquo;, b&aacute;c sĩ/tiến sĩ t&acirc;m l&yacute; học Choi Kwang Huyn một lần nữa trở lại v&agrave; chinh phục tr&aacute;i tim của độc giả Việt với cuốn s&aacute;ch &ldquo;G&oacute;c khuất của y&ecirc;u thương&rdquo;. Rất nhiều nhận x&eacute;t t&iacute;ch cực đ&atilde; cho rằng, Choi Kwang Huyn l&agrave; t&aacute;c giả thi&ecirc;n thần đ&atilde; đem t&acirc;m l&yacute; học đến gần hơn với đời sống, khiến cho nhiều độc giả cảm thấy được an ủi, xoa dịu v&agrave; rơi những giọt nước mắt trải l&ograve;ng một c&aacute;ch tự nhi&ecirc;n. Sự ch&acirc;n th&agrave;nh v&agrave; giản dị của vị b&aacute;c sĩ đ&aacute;ng k&iacute;nh n&agrave;y c&ograve;n được thể hiện qua những t&acirc;m tư m&agrave; &ocirc;ng d&agrave;nh cho đứa con tinh thần tiếp theo của m&igrave;nh - ấn phẩm &ldquo;&ldquo;G&oacute;c khuất của y&ecirc;u thương&rdquo;.\r\n\r\n&ldquo;Suốt mười năm l&agrave;m c&ocirc;ng việc tham vấn 90% kh&aacute;ch h&agrave;ng của t&ocirc;i l&agrave; nữ giới. Họ kh&aacute;c nhau, ho&agrave;n cảnh kh&aacute;c nhau, c&ocirc;ng việc cũng kh&aacute;c nhau nhưng nỗi trăn trở của họ gần như giống nhau.\r\n\r\n&ldquo;Tại sao người t&ocirc;i y&ecirc;u lại kh&ocirc;ng y&ecirc;u t&ocirc;i?&rdquo;\r\n\r\n&ldquo;Tại sao y&ecirc;u nhau, nhưng lại l&agrave;m tổn thương nhau?&rdquo;\r\n\r\n&ldquo;Tại sao t&ocirc;i kh&ocirc;ng thiếu g&igrave; nhưng lại kh&ocirc;ng hạnh ph&uacute;c?&rdquo;\r\n\r\nV&igrave; ảnh hưởng từ những mối quan hệ m&eacute;o m&oacute;, những tổn thương thuở nhỏ n&ecirc;n họ hiểu sai hoặc kh&ocirc;ng thể hiểu t&igrave;nh y&ecirc;u l&agrave; g&igrave;? L&agrave;m thế n&agrave;o để y&ecirc;u?  L&agrave;m thế n&agrave;o để được y&ecirc;u?\r\n\r\nĐặc biệt, c&agrave;ng l&agrave; người phụ nữ sống ổn định, chưa từng &ldquo;nổi loạn&rdquo;, c&agrave;ng dễ nản l&ograve;ng. Cho đến giờ nếu học h&agrave;nh chăm chỉ, họ c&oacute; thể đạt được th&agrave;nh t&iacute;ch cao, nếu đạt được th&agrave;nh t&iacute;ch cao, họ c&oacute; thể v&agrave;o một trường đại học tốt, nếu cần mẫn trong qu&atilde;ng thời gian đại học, họ c&oacute; thể xin việc v&agrave;o một c&ocirc;ng ty tốt, v&agrave; nếu l&agrave;m việc chăm chỉ, họ sẽ được c&ocirc;ng nhận năng lực. Thế nhưng trong c&aacute;c mối quan hệ kh&ocirc;ng phải cứ cố gắng l&agrave; c&oacute; thể giải quyết theo hướng mong muốn.\r\n\r\nT&ocirc;i kh&ocirc;ng mong g&igrave; hơn, chỉ mong th&ocirc;ng qua cuốn s&aacute;ch n&agrave;y, c&aacute;c độc giả nữ c&oacute; được sự an ủi, đồng cảm, c&ograve;n c&aacute;c độc giả nam nhận ra v&agrave; sẵn s&agrave;ng tr&ograve; chuyện với người con g&aacute;i họ y&ecirc;u, đang b&ecirc;n cạnh họ. Cuốn s&aacute;ch n&agrave;y l&agrave; những c&acirc;u chuyện về c&aacute;ch để mở l&ograve;ng lần nữa v&agrave; c&aacute;ch để l&agrave;m l&agrave;nh với những người chịu tổn thương v&igrave; y&ecirc;u. V&agrave; cuối c&ugrave;ng, ch&uacute;ng ta sẽ tự nhi&ecirc;n biết y&ecirc;u v&agrave; hiểu ch&iacute;nh m&igrave;nh. Cầu ch&uacute;c cho bạn v&agrave; t&ocirc;i, những con người nhỏ b&eacute; thiết tha với cuộc sống n&agrave;y đều sẽ hạnh ph&uacute;c v&agrave; b&igrave;nh an.\r\n\r\n-Choi Kwang Huyn-\r\n\r\nCuốn s&aacute;ch n&agrave;y đặc biệt d&agrave;nh cho:\r\n\r\n- Những ai lu&ocirc;n gặp thất bại trong chuyện t&igrave;nh cảm.\r\n\r\n- Người thiếu tự tin v&agrave; cảm thấy kh&oacute; giao tiếp, nắm bắt t&acirc;m l&yacute; của một nửa c&ograve;n lại.\r\n\r\n- Người từng bị tổn thương, mất niềm tin v&agrave;o t&igrave;nh y&ecirc;u.\r\n\r\n- Người muốn củng cố t&igrave;nh cảm v&agrave; duy tr&igrave; mối quan hệ bền vững.\r\n\r\n- Người muốn tho&aacute;t ra khỏi v&ograve;ng lặp sai lầm trong c&aacute;c mối quan hệ.\r\n\r\n- D&agrave;nh cho những ai chưa y&ecirc;u, đang y&ecirc;u v&agrave; sẽ được y&ecirc;u.\r\n', 'AZ Việt Nam', 'NXB Thanh Niên', '2023-04-25 01:26:10'),
('6eYZStMJul', 'Tủ Sách Học Làm Người – Thinh Lặng Cũng Là Hùng Biện', 'Hoàng Xuân Việt', 101000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_183239.jpg', 'Cuốn sách gồm có 13 chương với nội dung lý giải sức mạnh của sự thinh lặng trong giao tiếp ở nhiều mối quan hệ giữa người với người.\r\n\r\nTác giả Hoàng Xuân Việt chỉ ra nhiều phương pháp cụ thể để người đọc theo đó mà làm theo, rèn đức thinh lặng để hoàn thiện bản thân: Muốn hùng biện thinh lặng phải luyện tự chủ trước hay chính thinh lặng tạo nên điềm tĩnh và điềm tĩnh tạo cho người thinh lặng cái mà người ta gọi họ là “người có tư cách”.\r\n\r\nTrích dẫn:\r\n\r\n- Thinh lặng tạo hào quang cá nhân là thinh lặng do tự chủ, kiêng cữ hoang phí lời nói, cử chỉ, thái độ, hành vi làm cho người ta đánh giá nhẹ mình. Khi thinh lặng bạn tỏ ra trên gương mặt sự hòa hoãn thông cảm chứ không phải biểu lộ nét khổ hạnh. Thái độ này gây nghi kị, xung khắc… Bạn hãm khẩu là bạn theo đúng tiêu chuẩn “Chỉ nói điều cần nói cho kẻ cần nghe vào lúc không thể làm thinh được”. Thứ thinh lặng đó, tự nó làm cho kẻ khác không thể coi thường bạn được. Có một số người nhờ hãm khẩu, mới gặp người ta có thiện cảm. Song đến lúc họ hở môi người ta thất vọng. Họ ăn nói không dè dặt, hấp tấp, luýnh quýnh, cộc cằn, quơ đũa cả nắm… vô tình họ tố cáo một tâm tính không được huấn luyện chu đáo.\r\n\r\n- Người thinh lặng hùng biện bằng phong cách sống giản dị… Trong cuộc giao tiếp hằng ngày ai có cặp mắt tế nhị đều nhận thấy những người ăn nói cầu kỳ, có cử chỉ khách sáo rườm rà thường bị khinh rẻ. Bệnh cầu kỳ, khách sáo một phần lớn do thiếu đạo đức nội tâm. Người ta lo giá trị ngoài mặt, đánh giá con người ở bộ áo, ô tô, nhà lầu mà không nhận thấy chân giá trị của con người là lương tâm thánh đức. Một khi đã xa lạc lý tưởng cao đẹp này thì người ta chỉ còn biết sống với nhau bằng các công thức xã giao rườm rà chừng nào hay chừng ấy. Trong các cung cách giao thiệp ấy người già dặn nhận thấy một tâm hồn trống rỗng, non nớt, nghèo đói về tinh thần. Trái lại ai học hành cao, giàu nhân đức mà ăn nói, hành động giản dị được người sâu sắc nhận là kẻ đáng phục.\r\n\r\n- Bất luận vì lý do nào, bạn phải biết trải qua những khó khăn, chớ nản lòng, song luôn giữ sự tự chủ. Một buổi làm việc cũng như một khoảnh đất để chạy đau mà có nhiều đồ cản. Người ta nhảy qua hàng rào; đằng xa chút là một cái hố, rồi tới cái bờ dốc, và vân vân. Đứng trước trở ngại thứ nhất, người ta không dừng chân, người ta nhảy ngang, và những cái trở ngại cách khoảng nhau bằng những chỗ bình thản, chạy dễ dàng. Thắng được một bức cản sẽ giúp bạn thắng bao nhiêu bức khác; một cố gắng sẽ tránh được bốn lần khác, can đảm một phút đáng giá một ngày trọn và làm việc nặng nhọc sẽ đem lại một việc làm dồi dào và vui tươi.\r\n', 'Alpha Books', 'NXB Thanh Niên', '2023-04-25 00:28:39'),
('85glwWSTrg', 'Bố già', 'Mario Puzo', 212500, 'https://cdn0.fahasa.com/media/catalog/product/z/2/z2611575615164_9f60c133cfed1c7bb3f59b247f-600.jpg', 'Thế giới ngầm được phản ánh trong tiểu thuyết Bố già là sự gặp gỡ giữa một bên là ý chí cương cường và nền tảng gia tộc chặt chẽ theo truyền thống mafia xứ Sicily với một bên là xã hội Mỹ nhập nhằng đen trắng, mảnh đất màu mỡ cho những cơ hội làm ăn bất chính hứa hẹn những món lợi kếch xù. Trong thế giới ấy, hình tượng Bố già được tác giả dày công khắc họa đã trở thành bức chân dung bất hủ trong lòng người đọc. Từ một kẻ nhập cư tay trắng đến ông trùm tột đỉnh quyền uy, Don Vito Corleone là con rắn hổ mang thâm trầm, nguy hiểm khiến kẻ thù phải kiềng nể, e dè, nhưng cũng được bạn bè, thân quyến xem như một đấng toàn năng đầy nghĩa khí. Nhân vật trung tâm ấy đồng thời cũng là hiện thân của một pho triết lí rất “đời” được nhào nặn từ vốn sống của hàng chục năm lăn lộn giữa chốn giang hồ bao phen vào sinh ra tử, vì thế mà có ý kiến cho rằng “Bố già là sự tổng hòa của mọi hiểu biết. Bố già là đáp án cho mọi câu hỏi”.\r\n\r\nVới cấu tứ hoàn hảo, cốt truyện không thiếu những pha hành động gay cấn, tình tiết bất ngờ và không khí kình địch đến nghẹt thở, Bố già xứng đáng là đỉnh cao trong sự nghiệp văn chương của Mario Puzo. Và như một cơ duyên đặc biệt, ngay từ năm 1971-1972, Bố già đã đến với bạn đọc trong nước qua phong cách chuyển ngữ hào sảng, đậm chất giang hồ của dịch giả Ngọc Thứ Lang.\r\n\r\nGiới thiệu tác giả:\r\n\r\nMario Puzo (1920 - 1999) là nhà văn, nhà biên kịch người Mỹ gốc Italy nổi tiếng với nhiều tiểu thuyết về đề tài mafia và tội phạm. Bố già (The Godfather) xuất bản năm 1969 là đỉnh cao của dòng văn chương hư cấu này, đồng thời là tác phẩm đưa Puzo lên tột đỉnh vinh quang. Đây cũng là một trong những tiểu thuyết bán chạy nhất mọi thời đại. Ngoài Bố già, Mario Puzo còn nổi tiếng với các tiểu thuyết khác như Sicilian khúc ca bi tráng, Luật im lặng, Ông trùm quyền lực cuối cùng, Gia đình Giáo hoàng…\r\n\r\nGiới thiệu dịch giả:\r\n\r\nNgọc Thứ Lang tên thật là Nguyễn Ngọc Tú, biệt danh là công tử Bắc Kỳ, vào Sài Gòn lập nghiệp khoảng năm 1950. Ngọc Thứ Lang là dịch giả của thời kì trước năm 1975, đã chuyển ngữ nhiều tác phẩm nhưng có lẽ Bố già là một dấu son trong sự nghiệp của ông.\r\n\r\nNăm 1972, bản dịch Bố già của Ngọc Thứ Lang chuyển ngữ từ nguyên bản tiếng Anh ra mắt và đã thu hút được sự chú ý của rất nhiều độc giả. Nếu như The Godfather của Mario Puzo khi vừa xuất bản đã nằm trong danh sách sách bán chạy nhất suốt 67 tuần thì Bố già của Ngọc Thứ Lang cũng “làm mưa làm gió” trên thị trường văn học dịch của Sài Gòn những năm 70 của thế kỉ trước.\r\n\r\nCái hay, cái khiến người đọc say mê Bố già có lẽ nằm ở chính giọng văn đậm chất giang hồ súng đạn của người dịch. Và bản thân cái tên Bố già cũng là một sáng tạo vô tiền khoáng hậu của Ngọc Thứ Lang. Nhiều độc giả Việt Nam nói rằng nếu đọc The Godfather của Mario Puzo, hãy tìm đúng bản dịch của Ngọc Thứ Lang để thấy chất đàn ông trong đó…\r\n\r\nNhận xét về tác phẩm:\r\n\r\n “Bố già là sự tổng hòa của mọi hiểu biết. Bố già là đáp án cho mọi câu hỏi.” - Diễn viên Tom Hanks\r\n\r\n“Bạn không thể dừng đọc nó và khó lòng ngừng mơ về nó.” - New York Times Magazine\r\n\r\n“Một tác phẩm kinh điển về mafia… Tự bản thân nó đã tạo ra một thứ bùa mê hoặc độc giả.” - The Times.', 'Đông A', 'NXB Văn Học', '2023-04-19 20:59:45'),
('8eROL3R6yU', 'Đường Về', 'Erich Maria Remarque', 46000, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8936071674111.jpg', 'Đường Về\r\n\r\n&ldquo;T&ocirc;i muốn mở cửa, đi v&agrave;o trong ph&ograve;ng, t&ocirc;i muốn nằm cuộn m&igrave;nh tr&ecirc;n chiếc ghế b&agrave;nh, muốn ủ đ&ocirc;i b&agrave;n tay trong hơi ấm của gian ph&ograve;ng để hơi ấm ấy lan tỏa khắp người, t&ocirc;i muốn n&oacute;i chuyện, muốn mọi bạo t&agrave;n, khốc liệt v&agrave; qu&aacute; khứ sẽ tan chảy dưới đ&ocirc;i mắt y&ecirc;n lặng của người phụ nữ kia, v&agrave; muốn bỏ ch&uacute;ng lại ph&iacute;a sau, muốn cởi bỏ ch&uacute;ng như cởi bỏ một bộ quần &aacute;o bẩn.&rdquo;\r\n\r\n***\r\n\r\nTrở về - l&agrave; khải ho&agrave;n sau những năm chiến chinh, l&agrave; đo&agrave;n tụ sau thời gian xa c&aacute;ch, l&agrave; khoảnh khắc ấm &aacute;p của những con người đ&atilde; t&igrave;m lại được nhau&hellip; Nhưng cuộc trở về của những người l&iacute;nh Đức sau Thế chiến I lại l&agrave; h&agrave;nh tr&igrave;nh khắc nghiệt của những c&aacute;nh chim lạc bầy vật lộn giữa b&atilde;o gi&ocirc;ng t&igrave;m đường quay lại cố hương, l&agrave; g&aacute;nh nặng của lửa đạn chiến tranh chết ch&oacute;c, l&agrave; hố thẳm ngăn c&aacute;ch giữa hai phần của cuộc đời. V&agrave; cả khi vượt qua tất cả để sang tới bến bờ b&ecirc;n kia, họ cũng chưa chắc sẽ t&igrave;m được những g&igrave; dấu y&ecirc;u m&igrave;nh từng để lại sau lưng v&agrave;o c&aacute;i năm ly biệt.\r\n\r\nĐ&acirc;y l&agrave; lần đầu ti&ecirc;n Đường về, t&aacute;c phẩm được xem như phần hậu của Ph&iacute;a T&acirc;y kh&ocirc;ng c&oacute; g&igrave; lạ bởi sự t&aacute;i xuất của một số nh&acirc;n vật, ra mắt tại Việt Nam. Qua bản dịch đầy hồn l&iacute;nh m&agrave; vẫn đậm chất thơ của dịch giả Vũ Hương Giang, độc giả sẽ lại được gặp một Remarque rất kh&aacute;c, vừa khắc nghiệt lại mơ m&agrave;ng, trong tuyệt vọng lại hồi sinh hy vọng.\r\n\r\nTh&ocirc;ng tin t&aacute;c giả:\r\n\r\nRemarque (1898 &ndash; 1970) l&agrave; nh&agrave; văn lừng danh người Đức. &Ocirc;ng nổi tiếng với Ph&iacute;a T&acirc;y kh&ocirc;ng c&oacute; g&igrave; lạ, một trong những t&aacute;c phẩm hay nhất về Thế chiến I.C&aacute;c t&aacute;c phẩm ti&ecirc;u biểu kh&aacute;c của &ocirc;ng như Ba người bạn, Khải ho&agrave;n m&ocirc;n, Đ&ecirc;m Lisbon&hellip;cũng g&oacute;p phần kh&ocirc;ng nhỏ trong việc khẳng định t&agrave;i năng v&agrave; danh tiếng của &ocirc;ng khắp năm ch&acirc;u. Năm 1931, &ocirc;ng được đề cử cả giải Nobel Văn chương v&agrave; H&ograve;a b&igrave;nh.\r\n\r\nCuốn Đường về (Der Weg zur&uuml;ck &ndash; 1931) của Remarque tập trung v&agrave;o đề t&agrave;i người l&iacute;nh chịu chấn thương hậu chiến. C&acirc;y b&uacute;t Christopher Morley từng nhận x&eacute;t về t&aacute;c phẩm n&agrave;y: &ldquo;đ&atilde; ph&ocirc; b&agrave;y những mất m&aacute;t trong nội t&acirc;m người l&iacute;nh với một nội lực phi thường ẩn dưới nỗi sầu bi c&ocirc; tịch&hellip; ch&acirc;n thật, tinh tế, khơi gợi&hellip; một cuốn s&aacute;ch tuyệt diệu&rdquo;.', 'Đông A', 'NXB Văn Học', '2023-04-25 01:10:45'),
('9BvTuNOgbu', '[Phiên chợ sách cũ] Thành Long Chưa Lớn Đã Già', 'Chu Mặc', 100000, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935095620906_1_thanh_ly.jpg', 'Th&agrave;nh Long Chưa Lớn Đ&atilde; Gi&agrave;\r\nTh&agrave;nh Long kh&ocirc;ng phải l&agrave; c&aacute;i t&ecirc;n xa lạ với tất cả ch&uacute;ng ta. Tuổi thơ của cả một thế hệ đ&atilde; lớn l&ecirc;n c&ugrave;ng với những thước phim m&agrave; Th&agrave;nh Long thủ vai ch&iacute;nh. Đến b&acirc;y giờ, khi nhắc lại, những c&aacute;i t&ecirc;n như Mười hai con gi&aacute;p, Kế hoạch BB, C&acirc;u chuyện cảnh s&aacute;t hay Giờ cao điểm vẫn l&agrave; những bộ phim m&agrave; ch&uacute;ng ta kh&ocirc;ng thể qu&ecirc;n khi nhắc đến Th&agrave;nh Long.\r\n\r\n \r\n\r\nNgười ta nghĩ Th&agrave;nh Long - Jackie Chan l&agrave; một người &ldquo;m&igrave;nh đồng da sắt&rdquo; v&agrave; cho đến khi Th&agrave;nh Long gi&agrave; th&igrave; chắc l&agrave; họ vẫn suy nghĩ như vậy. Kh&aacute;n giả đang nh&igrave;n v&agrave;o những việc &ldquo;phi thường&rdquo; m&agrave; Th&agrave;nh Long đ&atilde; l&agrave;m suốt những thời gian qua m&agrave; qu&ecirc;n mất rằng, suy cho c&ugrave;ng Th&agrave;nh Long cũng chỉ l&agrave; một con người &ldquo;b&igrave;nh thường&rdquo; như bao người b&igrave;nh thường kh&aacute;c. Đến ngay Th&agrave;nh Long cũng đ&atilde; từng n&oacute;i rằng: &ldquo;T&ocirc;i l&agrave; một người b&igrave;nh thường, chỉ c&oacute; điều d&aacute;m l&agrave;m những việc phi thường.&rdquo;\r\n\r\n \r\n\r\nCh&uacute;ng ta đa phần đều biết đến Th&agrave;nh Long qua c&aacute;c bộ phim, biết đến anh với tư c&aacute;ch l&agrave; một diễn vi&ecirc;n v&otilde; thuật t&agrave;i ba nhưng rất &iacute;t người biết đến cuộc sống ri&ecirc;ng tư của Th&agrave;nh Long. V&agrave; lần đầu ti&ecirc;n th&ocirc;ng qua một t&aacute;c phẩm, Th&agrave;nh Long đ&atilde; cho ch&uacute;ng ta biết nhiều hơn về cuộc đời anh v&agrave; những trải nghiệm của anh khi quay c&aacute;c cảnh v&otilde; thuật tr&ecirc;n m&agrave;n ảnh m&agrave; ch&uacute;ng ta đ&atilde; xem trong nhiều năm qua.\r\n\r\n \r\n\r\nC&ugrave;ng với người bạn th&acirc;n Chu Mặc, Th&agrave;nh Long đ&atilde; cho ra mắt độc giả v&agrave; những fan h&acirc;m mộ anh cuốn tự truyện Th&agrave;nh Long - Chưa lớn đ&atilde; gi&agrave;. Khi đọc cuốn s&aacute;ch n&agrave;y ra, ch&uacute;ng ta sẽ thấy cuộc đời của Th&agrave;nh Long mở ra trước mắt, với những c&acirc;u chuyện chưa bao giờ kể. Tuổi thơ, những qu&atilde;ng thời gian vật lộn để c&oacute; th&agrave;nh c&ocirc;ng như b&acirc;y giờ, những &ldquo;b&oacute;ng hồng&rdquo; trong cuộc đời Th&agrave;nh Long, những vết thương v&agrave; cả những nụ cười đều được g&oacute;i gh&eacute;m trong gần 500 trang s&aacute;ch. Ch&uacute;ng ta sẽ thấy một Th&agrave;nh Long rất kh&aacute;c, rất b&igrave;nh dị v&agrave; đời thường. \r\n\r\n ', 'Huy Hoang Bookstore', 'NXB Hội Nhà Văn', '2023-04-25 01:34:38'),
('9jexwQ7Nor', 'Buông Bỏ Buồn Buông', 'Ajahn Brahm', 88000, 'https://cdn0.fahasa.com/media/catalog/product/6/0/600bia-buongbobuonbuong_1.jpg', 'Buông Bỏ Buồn Buông\r\n\r\nThế giới vận động và phát triển không ngừng, mang lại cho con người năng suất lao động cao hơn, cuộc sống nhiều tiện nghi hơn. Tuy nhiên, cuộc sống hiện đại như dòng thác cuồn cuộn chảy đã buộc con người phải cuốn theo với biết bao áp lực, đè nặng về cả thể xác lẫn tinh thần. Thời gian và năng lượng sống của con người phải chia sẻ cho công việc cơ quan, công sở đến gia đình, công việc của cộng đồng xã hội đến chuyện riêng tư… Điều đó khiến cho sự mệt mỏi ngày càng xâm lấn sâu vào từng đường gân thớ thịt; sự lo lắng xâm lấn vào trí não làm cho con người có cảm giác trĩu nặng, dồn nén, và có lúc bạn cảm thấy sợ hãi, chùn bước với những gì đang ở phía trước.\r\n\r\nBạn sẽ yên tâm hơn, tự tin hơn như được ở bên cạnh một nhà tâm lý học thấu hiểu và luôn chia sẻ cùng bạn khi có trong tay cuốn sách “Buông bỏ buồn buông”. Cuốn sách sẽ giúp bạn bóc tách những lo âu, phiền não ra khỏi tâm trí; giúp bạn có một cái nhìn về cuộc sống lạc quan, vui vẻ cách ứng xử thông và nhẹ nhàng hơn.\r\n\r\nKhi đọc cuốn sách, bạn sẽ cảm nhận như mình đang đi dạo trên một con đường với nhiều cung bậc cảm xúc. Cảm xúc của sự chiêm nghiệm về cuộc sống, về đời người và cách nhìn về những nghịch cảnh trong cuộc sống. Mỗi bước chúng ta bước đi là một con đường, con đường của sự bao dung, tha thứ, cảm thông, chia sẻ. Sau tất cả, chúng ta sẽ học được cách nhận biết, buông bỏ những rắc rối, tiêu cực để cuộc sống của bạn luôn tươi trẻ và tạo được sự an nhiên thư thái trong tâm hồn.\r\n\r\nVới niềm tin của vị chân tu Ajahn Brahm đầy lòng tin vào một điểm tựa đó là triết lý sống của \"Đức Phật\" và những trải nghiệm thú vị thể hiện trong từng trang viết, thầy mong rằng tất cả những khó khăn, gian nan, vất vả trong mọi guồng quay của cuộc sống đều có thể vượt qua.\r\n\r\nBí mật của của cuốn sách này chất chứa rất nhiều tâm tình về những câu chuyện của cuộc sống, sau mỗi một câu chuyện đều gửi gắm một thông điệp, bài học ý nghĩa, sâu lắng đáng để chúng ta học hỏi và lấy làm kinh nghiệm sống cho chính mình. Bởi lẽ con người chúng ta thường đến rồi tiếp xúc với thế giới và cuộc sống xung quanh ta với một \"tâm hồn tham lam\", một tâm hồn tìm kiếm sự hiểu biết chỉ qua kiến thức, một tâm hồn tìm kiếm sự che chở và làm hài lòng cho chính nó trước tiên. Cái bản ngã ấy nó không đáng tin cậy, không chân thật và không có tình thương yêu. Hãy đọc và tập cách Buông bỏ - Buồn buông những thứ không nên giữ lại, biết giũ bỏ mọi lo lắng, ưu phiền để có một cuộc sống thư thái trong tâm hồn.\r\n\r\nHãy học cách chấp nhận để biết thế nào là đủ và tập buông. Rồi bạn sẽ thấy: Buông bỏ - Buồn buông chính là cách nắm giữ những điều tốt đẹp. Nếu con người biết buông bỏ trong đời sống hiện tại, buông đi những danh lợi, những hận thù chấp nhặt, những mưu cầu tính toán cho bản thân, bỏ đi những \"tham - sân - si\" trong cuộc sống thường ngày sẽ tự tìm thấy cho chính bản thân niềm an vui và thanh thản trong tâm hồn.\r\n\r\nKhi tâm hồn trong sáng, vui vẻ là bạn đang tiến dần đến mục tiêu, thành công của cuộc sống mà chúng ta mong đợi. Chúng ta sẽ làm việc tốt hơn, sống đam mê hơn, nhân hậu, tha thứ và rộng mở, học thêm được nhiều điều…Hạnh phúc, bình an,yêu thương sẽ đến với bạn nhiều hơn khi biết buông bỏ. Hãy sống thật với bản thân mình để mang lại bình an cho mọi người.\r\n', 'FIRST NEWS', 'Tổng Hợp Thành Phố Hồ Chí Minh', '2023-04-25 00:52:27'),
('a6cqDBoUNh', 'Vào Trong Dòng Nước', 'Paula Hawkins', 118000, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935235214705.jpg', 'Vào Trong Dòng Nước\r\n\r\nĐừng vội an tâm trước làn nước lặng - Ta chẳng thể nào biết bên dưới giấu những gì\r\n\r\nNgười ta tìm thấy xác một người mẹ đơn thân dưới con sông chảy qua thị trấn. Đầu mùa hè năm đó, một cô bé tuổi teen cũng gặp số phận tương tự. Họ không phải là những phụ nữ đầu tiên chết trông dòng nước hắc ám ấy, nhưng cái chết của họ đã khuấy động bao bí ẩn từng chôn vùi nơi đây.\r\n\r\nVẫn bằng cách viết đưa đẩy và những hiểu biết chính xác về tâm lý con người từng khiến hàng triệu độc giả hâm mộ từ tác phẩm bestseller quốc tế Cô gái trên tàu, Vào trong dòng nước đã một lần nữa thành công vang dội trên trường quốc tế. Ly kỳ một cách tao nhã, chậm rãi mà không buồn tẻ, Vào trong dòng nước là một câu chuyện trinh thám đậm chất văn chương không chỉ về những vụ án mạng cần điều tra mà còn về những lắt léo trong tâm trí con người, tính dối lừa của xúc cảm và ký ức; và, trên hết thảy, sự hủy diệt của quá khứ đen tối khi nó trườn tới quấn lấy hiện tại.\r\n\r\n“Hawkins, bị ảnh hưởng bởi Hitchcock, có con mắt điện ảnh và đôi tai cho một thứ ngôn ngữ lạ lùng gợi nhiều liên tưởng… Vậy nên hãy đắm chìm vào đây. Phần thưởng sẽ là một cái kết ấn tượng. Và một trải nghiệm đọc có thể sẽ khiến bạn phải suy nghĩ kỹ trước khi nhúng dù chỉ một ngón chân xuống những dòng nước đen tối và lạnh giá ấy.”\r\n\r\n—USA Today\r\n\r\n“Khi nói đến một sự căng thẳng đến mức ta có thể dùng dao mà cắt, không ai có thể tạo ra nó giỏi hơn Hawkins.”\r\n\r\n—New York Post\r\n\r\n “Hawkins thực sự có tài trong việc khai thác cái cách chúng ta liên tục lật lại các câu chuyện trong đầu mình, trong việc tạo ra những độc thoại nội tâm vừa phong phú vừa gần gũi… Một tiểu thuyết hấp dẫn, sống động, thuyết phục, giàu tính nhân văn và lòng trắc ẩn đến độ đáng ngạc nhiên.” \r\n\r\n—The A.V. Club\r\n', 'Nhã Nam', 'NXB Hội Nhà Văn', '2023-04-25 00:35:15'),
('BhECZ94yCh', 'Thời Khắc Tươi Đẹp', 'Nina Riggs', 160000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_195509_1_37347.jpg', 'Hồi ức về sự sống và cái chết\r\n\r\nMột cuốn hồi ký tuyệt vời về sự sống và cái chết, từ nhà thơ Nina Riggs, mẹ của hai đứa con trai và hậu duệ trực hệ của tác gia Ralph Waldo Emerson. \r\nNina Riggs mới ba mươi bảy tuổi khi bị chẩn đoán mắc bệnh ung thư vú. Người mẹ của hai đứa con trai bảy và chín tuổi, cùng với người chồng mười sáu năm, sẽ ra sao khi biết mình mắc bệnh hiểm nghèo, sắp chết?\r\nNhiều người suy sụp khi biết không còn sống được bao lâu. Nhiều người gắng gượng lạc quan để động viên người thân yêu bên cạnh. Nhưng Nina Riggs đối mặt với cái chết bằng những thời khắc tươi đẹp nhất của cuộc đời. “Cái chết phải đâu ngày tận thế. Có nhiều điều còn tồi tệ hơn là chết: những mối hận thù, không có khiếu hài hước hay chứng táo bón.” Và Riggs – người phụ nữ sắp chết này sẽ chỉ cho bạn hay thế nào mới là sống. \r\nNina Riggs (1977-2017) lấy bằng thạc sĩ nghệ thuật về thơ vào năm 2004 và từng xuất bản một tập thơ mang tên Lucky, Lucky vào năm 2009. Trên blog cá nhân, cô đăng những bài viết về hành trình đương đầu với căn bệnh ung thư, và cuốn sách này chính là tập hợp những bài viết ấy. \r\n\r\nRực sáng, hài hước và cảm động sâu sắc, Thời khắc tươi đẹp viết về cách yêu tất cả mọi ngày, ngay cả những ngày tồi tệ. Cuốn sách viết về việc đối diện với cái chết – việc ai cũng phải làm trong đời dù sớm hay muộn, dù bệnh tật, tai nạn hay già nua. Nhưng quan trọng hơn cả là bạn nghĩ gì và đối mặt như thế nào. Ngòi bút của Riggs thúc giục chúng ta sống tốt và không đánh mất những gì làm nên con người: tình yêu, nghệ thuật, âm nhạc, ngôn từ… Ngoài những triết lý và ưu tư, còn là những hài hước, thú vị từ giọng văn của một nữ thi sĩ.\r\nKhám phá tình mẫu tử, tình yêu, tình bạn và ký ức, cuốn hồi ký chân thực và ngoạn mục của Nina Riggs tiếp tục cuộc trò chuyện mà Paul Kalanithi đã bắt đầu trong cuốn Khi hơi thở hóa thinh không tuyệt đẹp của anh. Khi bạn đang cầm trên tay cuốn sách này, Riggs đã ra đi được khá lâu, trước cả khi cuốn sách của cô lên kệ. Nhưng như Riggs đã nói: “Tôi chưa bao giờ ngừng kinh ngạc khi thế giới này có thể tàn khốc đồng thời lại đẹp đẽ tới nhường nào”. \r\nĐiều đẹp đẽ đầu tiên, cuốn sách này đã trở thành tác phẩm bán chạy và được bình chọn là một trong những cuốn sách hay nhất năm 2017 theo Washington Post.\r\nVà điều đẹp đẽ phi thường hơn cả, chồng của Riggs đã tiến một bước nữa cùng Lucy – vợ của bác sĩ Paul Kalanithi đã mất. Họ cùng nhau đi tiếp hành trình yêu thương mà những người quá cố hi vọng. Có lẽ ở một thế giới khác, Riggs và Paul đã gặp nhau và mỉm cười. \r\n\r\n+ĐÁNH GIÁ/NHẬN XÉT CHUYÊN GIA:\r\n\r\n“Thời khắc tươi đẹp là một tác phẩm gây choáng váng, một suy tư xé lòng về cuộc sống – phải biết trân trọng nó khi bạn còn sống, cũng như biết nắm lấy hồi kết của nó. Đây chính là Khi hơi thở hóa thinh không của năm nay.” – Nora Krug, Washington Post\r\n\r\n“Đẹp và ám ảnh [...] một chuyến thám hiểm đầy suy tư và xúc cảm về ý nghĩa cuộc sống trong những ngày còn lại của đời người. [...] Đây là cuốn sách mà mỗi bác sĩ và bệnh nhân nên đọc. [...] Thật khó để không so sánh Thời khắc tươi đẹp với Khi hơi thở hóa thinh không. [...] Tuy nhiên, cuốn sách của Riggs khác biệt hẳn về giọng văn và nội dung, hài hước hơn và ít triết lý hơn, song cũng không kém phần cảm động.” – Matt McCatty, MD, USA Today\r\n ', 'Alpha Books', 'NXB Dân Trí', '2023-04-25 00:38:29');
INSERT INTO `products` (`product_id`, `name`, `author`, `price`, `image`, `description`, `supplier`, `publiser`, `date`) VALUES
('cce9UovhwF', 'Lò Mổ Linh Hồn', 'Hannah Maguire, Suruthi Bala', 165000, 'https://cdn0.fahasa.com/media/flashmagazine/images/page_images/lo_mo_linh_hon/2023_04_13_11_35_15_1-390x510.jpg', 'Cuốn sách cũng xoáy sâu vào câu hỏi “Điều gì khiến chúng ta cứ vấn vương về những kẻ giết người, các thành viên giáo phái và những kẻ ăn thịt đồng loại ngay cả khi chúng khiến chúng ta khiếp sợ”? Chúng ta thấy những câu chuyện về chúng vô cùng hấp dẫn và đáng sợ, bởi chúng là tấm gương phản chiếu những thất bại của xã hội và cả những nỗi kinh hoàng mà con người chúng ta có thể gieo rắc? RedHanded thẳng thắn bác bỏ câu chuyện lỗi thời về những kẻ giết người bị coi quái vật và rằng một nạn nhân trở thành nạn nhân chỉ vì họ đã “đến sai nơi, vào sai thời điểm”. Thay vào đó, nó mổ xẻ câu chuyện về những kẻ giết người theo hướng thách thức nhận thức của chúng ta, từ đó đặt ra những câu hỏi hóc búa về xã hội, giới tính, nghèo đói, văn hóa và thậm chí cả nền chính trị của chúng ta.\r\n\r\nVới sự hài hước mang thương hiệu cá nhân, những nghiên cứu tỉ mỉ về tội phạm có thật và cả các phân tích dễ hiểu về điều gì tạo nên một tên tội phạm, Bala và Maguire sẽ giúp chúng ta hiểu rõ được những động lực thôi thúc các hành vi cực đoan nhất của con người để tìm hiểu nguồn cơn cho câu hỏi: Động lực nào thôi thúc một kẻ giết người xuống tay tàn độc với đồng loại?\r\n\r\nVề tác giả:\r\n\r\nDựa trên Podcast đoạt giải Khán giả bình chọn năm 2021 tại Anh\r\n\r\nCuốn sách dựa trên podcast nổi tiếng cùng tên của Hannah Maguire và Suruthi Bala (podcast của năm 2021, giải thưởng do người nghe bình chọn), đi sâu vào phân tích các vụ án có thật nhằm giúp trả lời một lần và mãi mãi câu hỏi rằng một kẻ giết người có sẵn “máu” sát nhân từ khi sinh ra hay do quá trình nuôi dưỡng mà thành.\r\n\r\nSau khi gặp nhau tại một bữa tiệc tại nhà ở London, nơi họ phát hiện ra cả hai có chung nỗi ám về tội phạm có thật, sau khi ngà ngà say, Suruthi Bala và Hannah Maguire đã hứa hẹn một ngày nào đó sẽ cùng bắt đầu thực hiện podcast về tội phạm giết người. Sáu tuần sau, họ đã đặt hàng những chiếc micro đầu tiên và cho ra đời đứa con tinh thần của cả hai.', '1980 Books', 'Thanh Niên', '2023-04-25 01:04:20'),
('ceaNNwIYgq', 'Tâm Lý Học Tội Phạm - Phác Họa Chân Dung Kẻ Phạm Tội', 'Diệp Hồng Vũ', 108000, 'https://cdn0.fahasa.com/media/catalog/product/p/h/ph_c-h_a-ch_n-dung-k_-ph_m-t_i.jpg', 'TÂM LÝ HỌC TỘI PHẠM - PHÁC HỌA CHÂN DUNG KẺ PHẠM TỘI\r\n\r\nTội phạm, nhất là những vụ án mạng, luôn là một chủ đề thu hút sự quan tâm của công chúng, khơi gợi sự hiếu kỳ của bất cứ ai. Một khi đã bắt đầu theo dõi vụ án, hẳn bạn không thể không quan tâm tới kết cục, đặc biệt là cách thức và động cơ của kẻ sát nhân, từ những vụ án phạm vi hẹp cho đến những vụ án làm rúng động cả thế giới.\r\n\r\nLấy 36 vụ án CÓ THẬT kinh điển nhất trong hồ sơ tội phạm của FBI, “Tâm lý học tội phạm - phác họa chân dung kẻ phạm tội” mang đến cái nhìn toàn cảnh của các chuyên gia về chân dung tâm lý tội phạm. Trả lời cho câu hỏi: Làm thế nào phân tích được tâm lý và hành vi tội phạm, từ đó khôi phục sự thật thông qua các manh mối, từ hiện trường vụ án, thời gian, dấu tích,… để tìm ra kẻ sát nhân thực sự. \r\n\r\nĐằng sau máu và nước mắt là các câu chuyện rợn tóc gáy về tội ác, góc khuất xã hội và những màn đấu trí đầy gay cấn giữa điều tra viên và kẻ phạm tội. Trong số đó có những con quỷ ăn thịt người; những cô gái xinh đẹp nhưng xảo quyệt; và cả cách trả thù đầy man rợ của các nhà khoa học,… Một số đã sa vào lưới pháp luật ngay khi chúng vừa ra tay, nhưng cũng có những kẻ cứ vậy ngủ yên hơn hai mươi năm. \r\n\r\nBằng giọng văn sắc bén, “Tâm lý học tội phạm - phác họa chân dung kẻ phạm tội” hứa hẹn dẫn dắt người đọc đi qua các cung bậc cảm xúc từ tò mò, ngạc nhiên đến sợ hãi, hoang mang tận cùng. Chúng ta sẽ lần tìm về quá khứ để từng bước gỡ những nút thắt chưa được giải, khiến ta \"ngạt thở\" đọc tới tận trang cuối cùng. \r\n\r\nHy vọng cuốn sách sẽ giúp bạn có cái nhìn sâu sắc, rõ ràng hơn về bộ môn tâm lý học tội phạm và có thể rèn luyện thêm sự tư duy, nhạy bén.', 'AZ Việt Nam', 'NXB Thanh Niên', '2023-04-25 00:59:14'),
('CZivKMecYY', 'Những Nguyên Tắc Để Giàu Có', 'Richard Templar', 109000, 'https://cdn0.fahasa.com/media/catalog/product/f/0/f08d1271-8ed4-4a8b-8425-aec79f957930.jpg', 'Những Nguy&ecirc;n Tắc Để Gi&agrave;u C&oacute; (T&aacute;i Bản 2018)\r\n\r\nTiền bạc v&agrave; sự gi&agrave;u c&oacute; dường như c&oacute; ma lực v&ocirc; h&igrave;nh hấp dẫn ch&uacute;ng ta. C&oacute; lẽ ai cũng từng một lần mơ ước th&agrave;nh tỷ ph&uacute;. Trở th&agrave;nh &quot;kiến tr&uacute;c sư trưởng&quot; của Microsoft như Bill Gates, sở hữu những bất động sản trị gi&aacute; h&agrave;ng tỷ đ&ocirc; la như Donald Trump hay thu được khoản lợi nhuận khổng lồ từ đầu tư chứng kho&aacute;n như Warren Buffett&hellip; l&agrave; giấc mơ của hầu hết mọi người. Nhưng l&agrave;m thế n&agrave;o để biến kh&aacute;t khao th&agrave;nh h&agrave;nh động, biến ước mơ th&agrave;nh hiện thực? Đ&atilde; c&oacute; h&agrave;ng trăm cuốn s&aacute;ch dạy l&agrave;m gi&agrave;u, những chương tr&igrave;nh hướng dẫn l&agrave;m kinh tế, song, &aacute;p dụng ch&uacute;ng thế n&agrave;o v&agrave; c&oacute; th&agrave;nh c&ocirc;ng hay kh&ocirc;ng lại phụ thuộc v&agrave;o ch&iacute;nh bạn. Bởi tiền bạc kh&ocirc;ng tự t&igrave;m đến bạn v&agrave; con đường l&agrave;m gi&agrave;u cũng đầy ch&ocirc;ng gai.\r\n\r\nGiống như khi đi t&igrave;m kho b&aacute;u, để khởi đầu h&agrave;nh tr&igrave;nh đến đ&iacute;ch gi&agrave;u c&oacute;, bạn cũng cần một tấm bản đồ chi tiết, ch&iacute;nh x&aacute;c. Những quy tắc để gi&agrave;u c&oacute; của t&aacute;c giả Richard Templar ch&iacute;nh l&agrave; tấm bản đồ như thế. Trong cuốn s&aacute;ch n&agrave;y, Richard Templar sẽ dẫn dắt c&aacute;c bạn bắt đầu với suy nghĩ Gi&agrave;u c&oacute; để trở n&ecirc;n gi&agrave;u c&oacute; v&agrave; gi&agrave;u c&oacute; hơn nữa rồi chia sẻ gi&agrave;u c&oacute;. Những quy tắc thiết thực, hữu &iacute;ch, được đ&uacute;c kết từ kinh nghiệm của rất nhiều người m&agrave; t&aacute;c giả đưa ra sẽ gi&uacute;p bạn ng&agrave;y c&agrave;ng tiến gần đến đ&iacute;ch. H&atilde;y đọc, nghiền ngẫm kỹ từng quy tắc, v&agrave; quan trọng nhất l&agrave; h&atilde;y &aacute;p dụng ch&uacute;ng, bạn sẽ thấy điều k&igrave; diệu xảy ra.', 'Alpha Books', 'NXB Lao Động - Xã Hội', '2023-04-25 01:13:38'),
('CZMjvAkVzD', '8 Vụ Án Hoàn Hảo - Vạch Trần Tâm Lý Kẻ Phạm Tội', 'Peter Swanson', 108000, 'https://cdn0.fahasa.com/media/catalog/product/3/9/393073.jpg', '8 Vụ &Aacute;n Ho&agrave;n Hảo - Vạch Trần T&acirc;m L&yacute; Kẻ Phạm Tội\r\n\r\n&ldquo;Khi một vụ g.iết người cụ thể được ẩn giấu trong một chuỗi những vụ &aacute;n kh&aacute;c để mọi thứ tr&ocirc;ng như t&aacute;c phẩm của một kẻ cuồng s.&aacute;t t&acirc;m thần&hellip;&rdquo;\r\n\r\nV&agrave;i năm về trước, Malcolm Kershaw l&agrave; một nh&acirc;n vi&ecirc;n b&aacute;n s&aacute;ch, nhưng đồng thời cũng l&agrave; quản l&yacute; nội dung trang blog đang nổi như cồn của cửa h&agrave;ng - một nơi d&agrave;nh cho những người y&ecirc;u trinh th&aacute;m tham gia bản luận. Tại đ&acirc;y anh đ&atilde; viết ra danh s&aacute;ch &ldquo;8 vụ g.iết người ho&agrave;n hảo&rdquo; được chọn lọc từ c&aacute;c cuốn s&aacute;ch trinh th&aacute;m thuộc dạng &ldquo;kinh điển&rdquo; v&agrave; cả những &ldquo;vi&ecirc;n ngọc &iacute;t người biết tới&rdquo; như: The Red House Mystery của A. A. Milne; The A. B.C Murders của Agatha Christie; Strangers on a train của Patricia Highsmith;....\r\n\r\nTuy nhi&ecirc;n, điều kh&ocirc;ng tưởng nhất đ&atilde; xảy ra với Malcolm. Khi một đặc vụ FBI bất ngờ gặp anh để t&igrave;m kiếm th&ocirc;ng tin cho h&agrave;ng loạt &aacute;n mạng b&iacute; ẩn, nhưng đều do một hung thủ g&acirc;y n&ecirc;n. V&agrave; kẻ đ&oacute; đang bắt chước c&aacute;ch g.iết người giống hệt bản danh s&aacute;ch tưởng chừng như bị bỏ qu&ecirc;n kia.\r\n\r\nKh&ocirc;ng dừng lại ở đ&oacute;, t&ecirc;n s.&aacute;t nh&acirc;n xảo quyệt c&ograve;n l&agrave; một mối đe dọa nguy hiểm với Malcolm. Bởi hắn biết qu&aacute; nhiều th&ocirc;ng tin c&aacute; nh&acirc;n về anh. Thậm ch&iacute; l&agrave; cả những b&iacute; mật, những g&oacute;c tối kh&ocirc;ng ngờ tới m&agrave; Mal chưa bao giờ n&oacute;i với ai, ngay cả người vợ mới qua đời của m&igrave;nh.\r\n\r\nĐể bảo vệ bản th&acirc;n, Malcolm bắt đầu h&agrave;nh tr&igrave;nh t&igrave;m ra ch&acirc;n dung t&ecirc;n s.&aacute;t nh&acirc;n thực sự&hellip;. Vậy cuối c&ugrave;ng kẻ đ&oacute; l&agrave; ai? L&agrave;m c&aacute;ch n&agrave;o m&agrave; một kẻ g.iết tinh ranh c&oacute; thể thực hiện những h&agrave;nh vi giết người theo một bản danh s&aacute;ch?\r\n\r\nTất cả sẽ được giải m&atilde; trong  &ldquo;8 vụ &aacute;n ho&agrave;n hảo - vạch trần t&acirc;m l&yacute; kẻ phạm tội&rdquo;.\r\n\r\nCuốn s&aacute;ch n&agrave;y l&agrave; sự kết hợp nhuần nhuyễn giữa thể loại t&acirc;m l&yacute; v&agrave; kinh dị giật g&acirc;n. Dưới ng&ograve;i b&uacute;t sắc b&eacute;n của Peter Swanson, từng trang s&aacute;ch sẽ dẫn dắt người đọc đi qua c&aacute;c cung bậc cảm x&uacute;c từ t&ograve; m&ograve;, ngạc nhi&ecirc;n đến sợ h&atilde;i, hoang mang tận c&ugrave;ng. Ch&uacute;ng ta sẽ lần t&igrave;m về qu&aacute; khứ, khai ph&aacute; t&acirc;m l&yacute; t&ecirc;n s.&aacute;t nh&acirc;n đi&ecirc;n cuồng để từng bước gỡ những n&uacute;t thắt chưa được giải. \r\n', 'AZ Việt Nam', 'Thanh Niên', '2023-04-25 01:07:09'),
('Dv2eXzNcZY', 'Trái Tim Của Brutus', 'Higashino Keigo', 117000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_195509_1_50119.jpg', 'Trái tim của Brutus được nhà xuất bản Kobunsha xuất bản lần đầu năm 1989, và năm 2011 được chuyển thể thành phim truyền hình do diễn viên Fujiwara Tatsuya thủ vai chính.\r\n\r\nGIỚI THIỆU SÁCH:\r\n\r\nSuenaga Takuya đảm trách phát triển robot sử dụng trí thông minh nhân tạo ở công ty Công nghiệp nặng MM. Là một gã trai sáng giá, đầy hứa hẹn tương lai, y trở thành “ứng cử viên” chức hôn phu của Hoshiko – con gái út ông chủ. Vì thế, khi người tình Yasuko bắt đầu thai nghén đứa con của gã, Takuya hoang mang. Cùng với Naoki – anh trai cùng cha khác mẹ của Hoshiko, và Hashimoto – đồng nghiệp ở công ty, y quyết định vạch ra một kế hoạch giết người…\r\n\r\nMột cuộc “chạy tiếp sức” cho án mạng được bắt đầu hoàn hảo, nhưng cái kết lại nằm ngoài sức tưởng tượng của bất cứ ai.\r\n\r\nVề tác giả:\r\nHigashino Keigo sinh năm 1958 tại Osaka, là nhà văn trinh thám hàng đầu hiện nay tại Nhật Bản. Năm 1985, Higashino Keigo giành được giải Edogawa Rampo lần thứ 31 cho tác phẩm trinh thám hay nhất với tiểu thuyết Giờ tan học.\r\n\r\nCác năm sau đó, Higashino Keigo liên tục được đề cử vô số giải thưởng văn học lớn. Năm 1999, ông đoạt giải Mystery Writers of Japan Inc với tiểu thuyết Bí mật của Naoko, và năm 2006, là giải Naoki lần thứ 134 cho Phía sau nghi can X.\r\n\r\nCác tác phẩm khác của Higashino Keigo đã được Nhã Nam phát hành:\r\n- Phía sau nghi can X\r\n- Bí mật của Naoko\r\n- Bạch dạ hành\r\n- Điều kỳ diệu ở tiệm tạp hóa Namiya\r\n- Hoa mộng ảo\r\n- Ảo dạ\r\n- Ma nữ của Laplace\r\n- Phương trình hạ chí\r\n- Sự cứu rỗi của Thánh nữ\r\n- Ma thuật bị cấm\r\n- Án mạng mười một chữ\r\n- Vụ án mạng ở lữ quán Kairotei', 'Nhã Nam', 'NXB Hà Nội', '2023-04-25 00:46:55'),
('e8M13U7eOs', 'Tuổi Trẻ Đáng Giá Bao Nhiêu', 'Rosie Nguyễn', 67500, 'https://encrypted-tbn0.gstatic.com/shopping?q=tbn:ANd9GcSrv-mgwTc98hztAPGj63HQpZFq4FQVv_ztegcsf5HZe1w8eZyZnHHFP2hvyrcGQ614f5HuoMtJlt3z12r3Y13vnAEXtNlIwtwX07lOb2gY&usqp=CAE', '“Bạn hối tiếc vì không nắm bắt lấy một cơ hội nào đó, chẳng có ai phải mất ngủ...\r\n\r\nBạn trải qua những ngày tháng nhạt nhẽo với công việc bạn căm ghét, người ta chẳng hề bận lòng.\r\n\r\nBạn có chết mòn nơi xó tường với những ước mơ dang dở, đó không phải là việc của họ.\r\n\r\nSuy cho cùng, quyết định là ở bạn. Muốn có điều gì hay không là tùy bạn.\r\n\r\nNên hãy làm những điều bạn thích. Hãy đi theo tiếng nói trái tim. Hãy sống theo cách bạn cho là mình nên sống.\r\n\r\nVì sau tất cả, chẳng ai quan tâm.”\r\n\r\n“Tôi đã đọc quyển sách này một cách thích thú. Có nhiều kiến thức và kinh nghiệm hữu ích, những điều mới mẻ ngay cả với người gần trung niên như tôi.\r\n\r\nTuổi trẻ đáng giá bao nhiêu? được tác giả chia làm 3 phần: HỌC, LÀM, ĐI.\r\n\r\nNhưng tôi thấy cuốn sách còn thể hiện một phần thứ tư nữa, đó là ĐỌC.\r\n\r\nHãy đọc sách, nếu bạn đọc sách một cách bền bỉ, sẽ đến lúc bạn bị thôi thúc không ngừng bởi ý muốn viết nên cuốn sách của riêng mình.\r\n\r\nNếu tôi còn ở tuổi đôi mươi, hẳn là tôi sẽ đọc Tuổi trẻ đáng giá bao nhiêu? nhiều hơn một lần.”\r\n\r\n- Đặng Nguyễn Đông Vy, tác giả, nhà báo.', 'Nhã Nam', 'NXB Hội Nhà Văn', '2023-04-19 21:00:42'),
('ELUAEbeE5I', 'Chết Bởi Giả Tạo - Thức Tỉnh Trước Khi Đánh Mất Chính Mình', 'Phạm Sỹ Thanh', 45000, 'https://cdn0.fahasa.com/media/catalog/product/c/h/chetboigiatao.jpg', 'CHẾT BỞI GIẢ TẠO - H&Atilde;Y THỨC TỈNH TRƯỚC KHI Đ&Aacute;NH MẤT CH&Iacute;NH M&Igrave;NH!\r\n\r\nThử ngẫm lại xem, cuộc sống của ch&uacute;ng ta c&oacute; xoay quanh v&ograve;ng luẩn quẩn n&agrave;y kh&ocirc;ng?\r\n\r\n- Gặp ai đ&oacute; v&ocirc; c&ugrave;ng kh&oacute; ưa, th&iacute;ch khoe mẽ, nhưng b&ecirc;n ngo&agrave;i lại vẫn tỏ ra th&acirc;n thiết, thậm ch&iacute; t&acirc;ng bốc người ta.\r\n\r\n- Cực kỳ ch&aacute;n gh&eacute;t những người &quot;chỉ tay năm ng&oacute;n&quot;, nhưng kh&ocirc;ng phản kh&aacute;ng, ngo&agrave;i mặt vẫn xởi lởi n&oacute;i cười, qua loa cho xong việc.\r\n\r\n- Đ&ocirc;i khi chăm chỉ check-in tăng ca tại c&ocirc;ng ty, ngụy tạo một cuộc sống bận rộn, th&agrave;nh c&ocirc;ng, trong khi hiệu quả l&agrave;m việc chẳng ra g&igrave; v&agrave; c&ograve;n c&oacute; nguy cơ bị sa thải.\r\n\r\n...\r\n\r\nR&otilde; r&agrave;ng theo bản năng, ai cũng muốn được c&ocirc;ng nhận, được nổi bật, được thoải m&aacute;i. Tuy nhi&ecirc;n, thay v&igrave; cố gắng l&agrave;m việc, phấn đấu để đạt được những điều đ&oacute;, kh&ocirc;ng &iacute;t người lại tự t&ocirc; vẽ những h&igrave;nh ảnh đẹp đẽ để l&agrave;m thỏa m&atilde;n nhất thời c&aacute;i gọi l&agrave; &quot;hữu danh v&ocirc; thực&quot;.\r\n\r\nCh&uacute;ng ta - kh&ocirc;ng c&oacute; ngoại lệ - ai cũng rất dễ trở th&agrave;nh &quot;số &iacute;t&quot; mang c&aacute;i mặt nạ giả dối ấy. Bởi sự thật, giống như &aacute;nh s&aacute;ng, l&agrave;m người ta ch&oacute;i mắt. Sự giả dối lại giống như &aacute;nh chiều tươi đẹp, l&agrave; c&aacute;i m&agrave; số đ&ocirc;ng muốn nh&igrave;n, l&agrave; những điều người kh&aacute;c muốn nghe v&agrave; muốn tin.\r\n\r\nNhưng r&otilde; r&agrave;ng, d&ugrave; giả vờ cố gắng để gạt người tốt đến đ&acirc;u, lời n&oacute;i dối c&oacute; ngọt ng&agrave;o cỡ n&agrave;o, ch&uacute;ng ta vẫn rất kh&oacute; để phỉnh lừa được ch&iacute;nh m&igrave;nh.\r\n\r\nVới lời văn đanh th&eacute;p, phơi b&agrave;y một phần thực trạng x&atilde; hội - &ldquo;Chết bởi giả tạo&rdquo; của Phạm Sỹ Thanh (t&aacute;c giả cuốn &ldquo;Đến cỏ dại c&ograve;n đ&agrave;ng ho&agrave;ng m&agrave; sống&rdquo; từng l&agrave; s&aacute;ch b&aacute;n chạy được rất nhiều bạn trẻ t&acirc;m đắc) sẽ gi&uacute;p ch&uacute;ng ta:\r\n\r\n- Hiểu được căn nguy&ecirc;n của sự giả tạo trong x&atilde; hội.\r\n\r\n- &ldquo;Gỡ bỏ&rdquo; chiếc mặt nạ giả dối để sống đ&uacute;ng với bản th&acirc;n.\r\n\r\n- Tr&acirc;n trọng gi&aacute; trị của ch&iacute;nh m&igrave;nh nhưng kh&ocirc;ng ngạo mạn, bởi c&agrave;ng hiểu biết th&igrave; c&agrave;ng khi&ecirc;m nhường.\r\n\r\n&quot;Bạn c&oacute; biết điều g&igrave; khiến m&igrave;nh thua k&eacute;m người kh&aacute;c kh&ocirc;ng? Chắc chắn kh&ocirc;ng phải v&igrave; bạn kh&ocirc;ng c&oacute; mấy tấm h&igrave;nh check-in ở nước n&agrave;y nước nọ, cũng kh&ocirc;ng phải bạn kh&ocirc;ng c&oacute; nh&agrave; đẹp xe sang. Bạn chỉ thua k&eacute;m người kh&aacute;c v&igrave; kh&ocirc;ng biết m&igrave;nh đ&aacute;ng gi&aacute; bao nhi&ecirc;u. V&igrave; kh&ocirc;ng định gi&aacute; được, bạn mới sẵn s&agrave;ng b&aacute;n rẻ bản th&acirc;n cho những chuẩn mực m&agrave; ai cũng hướng tới.&quot; (Tr&iacute;ch từ &ldquo;Chết bởi giả tạo&rdquo; )\r\n\r\nChết bởi giả tạo - h&atilde;y đọc để ngưng &quot;sống m&ograve;n&quot;. Đ&atilde; đến l&uacute;c bạn cần &quot;thức tỉnh&quot; v&agrave; sống sao để đến khi về gi&agrave;, &iacute;t đi những lần hối hận, tự tin chứng minh bạn chưa từng đ&aacute;nh mất ch&iacute;nh bản th&acirc;n m&igrave;nh!', 'Skybooks', 'NXB Lao Động', '2023-04-25 01:20:52'),
('EPHBjxBoy1', 'Muôn Kiếp Nhân Sinh - Many Times, Many Lives', 'Nguyên Phong', 117600, 'https://cdn0.fahasa.com/media/catalog/product/m/u/muonkiepnhansinh.jpg', 'Muôn Kiếp Nhân Sinh - Many Times, Many Lives\r\n\r\nGiáo sư John Vũ – Nguyên Phong và những câu chuyện chưa từng có về tiền kiếp, khám phá luật Nhân quả, Luân hồi.\r\n\r\n“Muôn kiếp nhân sinh” là tác phẩm do Giáo sư John Vũ - Nguyên Phong viết từ năm 2017 và hoàn tất đầu năm 2020 ghi lại những câu chuyện, trải nghiệm tiền kiếp kỳ lạ từ nhiều kiếp sống của người bạn tâm giao lâu năm, ông Thomas – một nhà kinh doanh tài chính nổi tiếng ở New York. Những câu chuyện chưa từng tiết lộ này sẽ giúp mọi người trên thế giới chiêm nghiệm, khám phá các quy luật về luật Nhân quả và Luân hồi của vũ trụ giữa lúc trái đất đang gặp nhiều tai ương, biến động, khủng hoảng từng ngày.\r\n\r\n“Muôn kiếp nhân sinh” là một bức tranh lớn với vô vàn mảnh ghép cuộc đời, là một cuốn phim đồ sộ, sống động về những kiếp sống huyền bí, trải dài từ nền văn minh Atlantis hùng mạnh đến vương quốc Ai Cập cổ đại của các Pharaoh quyền uy, đến Hợp Chủng Quốc Hoa Kỳ ngày nay.\r\n\r\n“Muôn kiếp nhân sinh”cung cấp cho bạn đọc kiến thức mới mẻ, vô tận của nhân loại lần đầu được hé mở, cùng những phân tích uyên bác, tiên đoán bất ngờ về hiện tại và tương lai thế giới của những bậc hiền triết thông thái. Đời người tưởng chừng rất dài nhưng lại trôi qua rất nhanh, sinh vượng suy tử, mong manh như sóng nước. Luật nhân quả cực kỳ chính xác, chi tiết, phức tạp được thu thập qua nhiều đời, nhiều kiếp, liên hệ tương hỗ đan xen chặt chữ lẫn nhau, không ai có thể tính được tích đức này có thể trừ được nghiệp kia không, không ai có thể biết được khi nào nhân sẽ trổ quả. Nhưng, một khi đã gây ra nhân thì chắc chắn sẽ gặt quả - luật Nhân quả của vũ trụ trước giờ không bao giờ sai.\r\n\r\nLuật Luân hồi và Nhân quả đã tạo nhân duyên để người này gặp người kia. Gặp nhau có khi là duyên, có khi là nợ; gặp nhau có lúc để trả nợ, có lúc để nối lại duyên xưa. Có biết bao việc diễn ra trong đời, tưởng chừng như là ngẫu nhiên nhưng thật ra đã được sắp đặt từ trước. Luân hồi là một ngôi trường rộng lớn, nơi tất cả con người, tất cả sinh vật đều phải học bài học của riêng mình cho đến khi thật hoàn thiện mới thôi. Nếu không chịu học hay chưa học được trọn vẹn thì buộc phải học lại, chính xác theo quy luật của Nhân quả.\r\n\r\nThomas đã chia sẻ vì sao đã kể những câu chuyện riêng tư huyền bí này với Giáo sư John Vũ để thực hiện tác phẩm “Muôn kiếp nhân sinh”:\r\n\r\n “Hiện nay thế giới đang trải qua giai đoạn hỗn loạn, xáo trộn, mà thật ra thì mọi quốc gia đều đang gánh chịu những nghiệp quả mà họ đã gây ra trong quá khứ. Mỗi quốc gia, cũng như mọi cá nhân, đều có những nghiệp quả riêng do những nhân mà họ đã gây ra. Cá nhân thì có ‘biệt nghiệp‘ riêng của từng người, nhưng quốc gia thì có ‘cộng nghiệp‘ mà tất cả những người sống trong đó đều phải trả.\r\n\r\nThường thì con người, khi hành động, ít ai nghĩ đến hậu quả, nhưng một khi hậu quả xảy đến thì họ nghĩ gì, làm gì? Họ oán hận, trách trời, trách đất, trách những người chung quanh đã gây ra những hậu quả đó? Có mấy ai biết chiêm nghiệm, tự trách mình và thay đổi không?\r\n\r\nTôi mong chúng ta - những cánh bướm bé nhỏ rung động mong manh cũng có thể tạo nên những trận cuồng phong mãnh liệt để thức tỉnh mọi người.\r\n\r\nTương lai của mỗi con người, mỗi tổ chức, mỗi quốc gia và cả hành tinh này sẽ ra sao trong giai đoạn sắp tới là tùy thuộc vào thái độ ứng xử, nhìn nhận và thức tỉnh của từng cá nhân, từng tổ chức, từng quốc gia đó tạo nên. Nếu muốn thay đổi, cần khởi đầu bằng việc nhận thức, chuyển đổi tâm thức, lan tỏa yêu thương và chia sẻ sự hiểu biết từ mỗi người chúng ta trước.\r\n\r\nNhân quả đừng đợi thấy mới tin.\r\n\r\nNhân quả là bảng chỉ đường, giúp con người tìm về thiện lương“\r\n\r\nCuốn sách được xuất bản bằng tiếng Việt trước khi được chuyển nhượng bản quyền cho các quốc gia khác trên thế giới.\r\n\r\nVề tác giả\r\n\r\nTác giả Nguyên Phong (Vũ Văn Du) du học ở Mỹ từ năm 1968, tốt nghiệp cao học Sinh vật học, Điện toán. Ông từng là Kỹ sư trưởng, CIO của Tập đoàn Boeing của Mỹ, Viện trưởng Viện Công nghệ Sinh học Đại học Carnegie Mellon. Ông được mọi người biết tới là Giáo sư John Vu – Nhà khoa học uy tín về công nghệ thông tin. , CMMI và từng giảng dạy ở nhiều trường đại học trên thế giới.\r\n\r\n Nguyên Phong là bút danh của bộ sách văn hóa tâm linh được dịch, viết phóng tác từ trải nghiệm, tiềm thức và quá trình nghiên cứu, khám phá các giá trị tinh thần Đông phương. Ông đã viết phóng tác tác phẩm bất hủ Hành trình về Phương Đông năm 24 tuổi (1974). Các tác phẩm khác của Nguyên Phong được bạn đọc nhiều thế hệ yêu thích: Ngọc sáng trong hoa sen, Bên rặng tuyết sơn, Hoa sen trên tuyết, Hoa trôi trên sóng nước, Huyền thuật và các đạo sĩ Tây Tạng, Trở về từ xứ tuyết, Trở về từ cõi sáng, Minh triết trong đời sống, Đường mây qua xứ tuyết, Dấu chân trên cát, Đường mây trong cõi mộng, Đường mây trên đất hoa… và bộ sách dành cho sinh viên, thầy cô: Khởi hành, Kết nối, Bước ra thế giới, Kiến tạo thế hệ Việt Nam ưu việt, GS John Vu và lời khuyên dành cho thầy cô, GS John Vu và lời khuyên dành cho các bậc cha mẹ.', 'FIRST NEWS', 'NXB Tổng Hợp TPHCM', '2023-04-19 21:21:10'),
('EqQyz0lW6g', 'Nick Vujicic - Cuộc Sống Không Giới Hạn', 'Nick Vujicic', 150000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_195509_1_49329_1.jpg', 'Cuộc sống l&agrave; do mỗi người đặt ra cho m&igrave;nh, giới hạn ấy thể hiện r&otilde; khả năng v&agrave; sự nỗ lực của bạn trong việc ước mơ v&agrave; chinh phục ước mơ. Giới hạn ấy, tất nhi&ecirc;n, l&agrave; ho&agrave;n cảnh sống, l&agrave; t&iacute;nh c&aacute;ch, l&agrave; con người, l&agrave; khả năng t&agrave;i ch&iacute;nh, l&agrave; điều kiện gi&aacute;o dục,... V&agrave; đ&ocirc;i khi, ch&uacute;ng ta đổ lỗi cho ch&uacute;ng v&igrave; sự thất bại của m&igrave;nh.\r\n\r\nNhưng vốn dĩ cuộc sống thực sự kh&ocirc;ng c&oacute; giới hạn n&agrave;o cả, chỉ cần bạn c&oacute; đủ sức mạnh, &yacute; ch&iacute; v&agrave; khả năng để chinh phục th&igrave; giới hạn cuộc sống vĩnh viễn nằm ở điểm v&ocirc; cực.\r\n\r\n&ldquo;Bạn đẹp đẽ v&agrave; qu&yacute; gi&aacute; hơn tất cả những vi&ecirc;n kim cương tr&ecirc;n thế gian n&agrave;y. Dẫu vậy, ch&uacute;ng ta n&ecirc;n lu&ocirc;n lu&ocirc;n đặt ra cho m&igrave;nh mục ti&ecirc;u trở th&agrave;nh những con người tốt hơn, to&agrave;n thiện hơn, đẩy l&ugrave;i v&agrave; loại bỏ những giới hạn bằng c&aacute;ch mơ những giấc mơ lớn lao. Trong h&agrave;nh tr&igrave;nh đ&oacute;, ch&uacute;ng ta lu&ocirc;n cần c&oacute; những điều chỉnh (bởi v&igrave; cuộc đời n&agrave;y kh&ocirc;ng phải l&uacute;c n&agrave;o cũng to&agrave;n l&agrave; m&agrave;u hồng), nhưng cuộc đời n&agrave;y lu&ocirc;n đ&aacute;ng sống. T&ocirc;i đến đ&acirc;y để n&oacute;i với bạn rằng cho d&ugrave; bạn đang ở trong ho&agrave;n cảnh n&agrave;o, miễn l&agrave; bạn c&ograve;n thở, th&igrave; bạn vẫn c&oacute; thể đ&oacute;ng g&oacute;p cho cuộc đời n&agrave;y&hellip;&rdquo;\r\n\r\nCuộc Sống Kh&ocirc;ng Giới Hạn kh&ocirc;ng chỉ l&agrave; một cuộc đời, cũng kh&ocirc;ng chỉ rao giảng những b&agrave;i học trống rỗng v&agrave; v&ocirc; hồn về sức mạnh của con người. C&acirc;u chuyện của Nick l&agrave; c&acirc;u chuyện đ&atilde; l&agrave;m rung động tr&aacute;i tim h&agrave;ng triệu người về nghị lực sống, kh&aacute;t vọng chinh phục, sức mạnh vượt qua những kh&oacute; khăn bất tận của cuộc sống để l&agrave;m chủ cuộc đời m&igrave;nh.\r\n\r\nCuộc Sống Kh&ocirc;ng Giới Hạn - Nick Vujicic\r\nC&acirc;u Chuyện Diệu Kỳ Của Ch&agrave;ng Trai Đặc Biệt Nhất H&agrave;nh Tinh\r\n\r\nNick sinh ra mắc hội chứng Tetra-amelia bẩm sinh, một rối loạn gene hiếm gặp g&acirc;y ra sự thiếu hụt ch&acirc;n, tay. Điều đ&oacute; đồng nghĩa với việc anh c&oacute; rất &iacute;t hy vọng để sống một cuộc đời b&igrave;nh thường. Người mẹ v&agrave; người cha th&acirc;n y&ecirc;u của anh lần đầu nh&igrave;n thấy con trai đ&atilde; sốc kinh khủng. Sự ra đời của Nick đ&atilde; l&agrave;m chao đảo cả cuộc sống của một gia đ&igrave;nh trẻ. Họ kh&oacute; c&oacute; thể chấp nhận được sự thật đau l&ograve;ng về đứa con b&eacute; bỏng; kh&ocirc;ng chỉ v&ocirc; c&ugrave;ng đau khổ, họ c&ograve;n hết sức lo lắng cho tương lai của con trai.\r\n\r\nLớn l&ecirc;n, bắt đầu &yacute; thức về th&acirc;n phận của m&igrave;nh cũng l&agrave; l&uacute;c Nick chỉ muốn biến mất khỏi cuộc sống. Như anh từng t&acirc;m sự: &ldquo;Ho&agrave;n cảnh nghiệt ng&atilde; tưởng đ&atilde; c&oacute; l&uacute;c nhấn ch&igrave;m t&ocirc;i. Hơn ai hết, t&ocirc;i từng muốn tự tử, v&agrave; đ&atilde; từng rất nhiều lần định bỏ cuộc. Nhưng cuối c&ugrave;ng, t&ocirc;i đ&atilde; can đảm đứng dậy sau h&agrave;ng ng&agrave;n lần ng&atilde;&hellip;&rdquo;\r\nĐiều g&igrave; đ&atilde; khiến Nick đứng dậy v&agrave; đi qua tất cả? Đ&oacute; thật sự l&agrave; một điều kỳ diệu lớn lao &ndash; Kh&aacute;t vọng sống m&atilde;nh liệt v&agrave; &yacute; ch&iacute; quật cường chiến thắng số phận.\r\n\r\n&ldquo;Thường th&igrave; ch&uacute;ng ta cứ tự nhủ rằng m&igrave;nh kh&ocirc;ng đủ th&ocirc;ng minh hoặc kh&ocirc;ng đủ hấp dẫn hoặc kh&ocirc;ng đủ t&agrave;i năng để theo đuổi những ước mơ. Ch&uacute;ng ta tin những g&igrave; người kh&aacute;c n&oacute;i về ch&uacute;ng ta,hoặc tự đặt ra những giới hạn cho bản th&acirc;n m&igrave;nh. Tồi tệ hơn, khi bạn tự coi m&igrave;nh l&agrave; một người v&ocirc; gi&aacute; trị c&oacute; nghĩa l&agrave; bạn đang đặt ra giới hạn cho những điều kỳ diệu m&agrave; Ch&uacute;a c&oacute; thể trao gửi cho bạn!&rdquo;-(Nick Vujicic)\r\n\r\n&ldquo;Từ s&acirc;u thẳm tr&aacute;i tim m&igrave;nh, t&ocirc;i tin rằng cuộc đời kh&ocirc;ng c&oacute; bất cứ giới hạn n&agrave;o hết. Cho d&ugrave; những th&aacute;ch thức m&agrave; bạn đang phải đối mặt l&agrave; g&igrave; đi nữa, cho d&ugrave; những th&aacute;ch thức ấy c&oacute; khốc liệt, nghiệt ng&atilde; đến mức n&agrave;o chăng nữa, t&ocirc;i cũng mong bạn h&atilde;y tin tưởng v&agrave; cảm thấy như vậy về cuộc sống của ch&iacute;nh m&igrave;nh&hellip;&rdquo; - (Nick Vujicic)\r\n\r\nNick đ&atilde; v&agrave; đang sống để chứng minh ch&acirc;n l&yacute; lớn lao: Kh&ocirc;ng c&oacute; giới hạn n&agrave;o lớn hơn sự tự giới hạn ch&iacute;nh m&igrave;nh. Chỉ cần nhắc tới c&aacute;i t&ecirc;n Nick Vujicic, h&agrave;ng triệu người tr&ecirc;n to&agrave;n cầu đ&atilde; c&oacute; thể bật kh&oacute;c v&igrave; x&uacute;c động v&agrave; cảm phục. Nick đ&atilde; trở th&agrave;nh một &acirc;n ph&uacute;c thật sự cho những ai được tiếp x&uacute;c với anh, hoặc từng biết đến anh qua s&aacute;ch, b&aacute;o, băng đĩa, internet&hellip;\r\n\r\nKh&ocirc;ng c&oacute; tay, Nick Vujicic vẫn chạm tới tr&aacute;i tim của h&agrave;ng triệu người mỗi khi hiện diện. Kh&ocirc;ng c&oacute; ch&acirc;n, Nick vẫn đi tới khắp mọi nơi tr&ecirc;n to&agrave;n thế giới. Hiện tại, Nick l&agrave; Chủ tịch v&agrave; l&agrave; CEO của tổ chức quốc tế Life Without Limbs, l&agrave; gi&aacute;m đốc c&ocirc;ng ty Attitude Is Altitude, đồng thời l&agrave; một diễn giả c&oacute; sức truyền cảm lớn nhất v&agrave; đặc biệt nhất h&agrave;nh tinh.\r\n\r\n&ldquo;L&agrave; đứa con của Ch&uacute;a, bạn chắc chắn đẹp đẽ v&agrave; qu&yacute; gi&aacute; hơn tất cả những vi&ecirc;n kim cương tr&ecirc;n thế gian n&agrave;y. Bạn v&agrave; t&ocirc;i cực kỳ th&iacute;ch hợp để trở th&agrave;nh những con người m&agrave; đấng s&aacute;ng tạo ra ch&uacute;ng ta muốn ch&uacute;ng ta trở th&agrave;nh! Dẫu vậy, ch&uacute;ng ta n&ecirc;n lu&ocirc;n lu&ocirc;n đặt ra cho m&igrave;nh mục ti&ecirc;u trở th&agrave;nh những con người tốt hơn, to&agrave;n thiện hơn, đẩy l&ugrave;i v&agrave; loại bỏ những giới hạn bằng c&aacute;ch mơ những giấc mơ lớn lao. Trong h&agrave;nh tr&igrave;nh đ&oacute;, ch&uacute;ng ta lu&ocirc;n cần c&oacute; những điều chỉnh (bởi v&igrave; cuộc đời n&agrave;y kh&ocirc;ng phải l&uacute;c n&agrave;o cũng to&agrave;n l&agrave; m&agrave;u hồng), nhưng cuộc đời n&agrave;y lu&ocirc;n đ&aacute;ng sống. T&ocirc;i đến đ&acirc;y để n&oacute;i với bạn rằng cho d&ugrave; bạn đang ở trong ho&agrave;n cảnh n&agrave;o, miễn l&agrave; bạn c&ograve;n thở, th&igrave; bạn vẫn c&oacute; thể đ&oacute;ng g&oacute;p cho cuộc đời n&agrave;y&hellip;&rdquo;\r\n\r\n\r\n&ldquo;T&ocirc;i kh&ocirc;ng thể đặt b&agrave;n tay l&ecirc;n vai bạn để động vi&ecirc;n, nhưng t&ocirc;i c&oacute; thể n&oacute;i lời n&oacute;i ch&acirc;n th&agrave;nh nhất từ tận đ&aacute;y l&ograve;ng m&igrave;nh. D&ugrave; cuộc đời của bạn c&oacute; đ&aacute;ng thất vọng đến mức n&agrave;o, th&igrave; niềm hy vọng vẫn lu&ocirc;n ở ph&iacute;a trước. Trong khốn c&ugrave;ng, vẫn c&oacute; ng&agrave;y mai tươi s&aacute;ng đang chờ bạn&hellip;&rdquo; &ndash; (Nick Vujicic)\r\n\r\nCuộc Sống Kh&ocirc;ng Giới Hạn kh&ocirc;ng chỉ đơn giản kể lại c&acirc;u chuyện của cậu b&eacute; Nick kh&ocirc;ng tay, kh&ocirc;ng ch&acirc;n vượt qua kh&oacute; khăn trong cuộc sống như thế n&agrave;o, để c&oacute; được cuộc sống tr&agrave;n ngập tiếng cười ng&agrave;y h&ocirc;m nay, Nick đ&atilde; c&oacute; những ng&agrave;y th&aacute;ng đầy nước mắt như thế n&agrave;o. Vượt l&ecirc;n tr&ecirc;n hết l&agrave; nghị lực phi thường, sự mạnh mẽ kh&ocirc;ng bi&ecirc;n giới, &yacute; ch&iacute; vượt tho&aacute;t khỏi ho&agrave;n cảnh nghiệt ng&atilde; của số phận.\r\n\r\nChuyện cổ t&iacute;ch hiện hữu trong đời thực\r\n\r\nNick hiện tại đang v&ocirc; c&ugrave;ng hạnh ph&uacute;c b&ecirc;n người vợ t&ecirc;n l&agrave; Kanae Miyahara, một c&ocirc; g&aacute;i xinh đẹp,ho&agrave;n to&agrave;n b&igrave;nh thường. Đ&aacute;m cưới của họ diễn ra v&agrave;o ng&agrave;y 10 th&aacute;ng 2 năm 2012 ở California, Mỹ. H&agrave;ng trăm tờ b&aacute;o tr&ecirc;n thế giới đ&atilde; đưa tin về đ&aacute;m cưới của Nick. Người ta gọi đ&aacute;m cưới của Nick l&agrave; đ&aacute;m cưới của thế kỷ, l&agrave; sự kiện tuyệt vời nhất, th&ocirc;ng điệp hy vọng c&oacute; sức thuyết phục nhất của năm2012.\r\n\r\nV&agrave; hơn thế, Nick đang hồi hộp chờ đ&oacute;n đứa con đầu l&ograve;ng. Đ&oacute; l&agrave; đứa b&eacute; được sinh ra từ l&ograve;ng can trường của người bố v&agrave; t&igrave;nh y&ecirc;u tuyệt vời của người mẹ. Nhưng đ&oacute; kh&ocirc;ng phải l&agrave; điều kỳ diệu duy nhất của cuộc sống v&agrave; của Nick, ch&iacute;nh ch&uacute;ng ta cũng ho&agrave;n to&agrave;n c&oacute; thể biến cuộc sống của ch&iacute;nh ch&uacute;ng ta trở th&agrave;nh kỳ diệu khi biết tho&aacute;t ra những điều giới hạn, biết đối mặt với thử th&aacute;ch, v&agrave; hơn hết đừng bao giờ ho&agrave;i nghi về khả năng của bản th&acirc;n m&igrave;nh, như Nick mong muốn th&ocirc;ng qua cuốn tự truyện &ldquo;Cuộc Sống Kh&ocirc;ng Giới Hạn&rdquo;.', 'FIRST NEWS', 'NXB Tổng Hợp TPHCM', '2023-04-25 01:29:18'),
('EVH2qH2iOB', 'Tâm Lý Học Tội Phạm - Tập 2', 'Stanton E Samenow', 112000, 'https://cdn0.fahasa.com/media/catalog/product/t/_/t_m_l_h_c_t_i_ph_m_t_p_2_-_b_a_1.jpg', '\"Tâm lý học tội phạm\" là bộ sách gồm 2 tập đề cập đến quyền lựa chọn, ý chí tự do, cái thiện và cái ác, phản ứng trước cám dỗ và sự thể hiện lòng dũng cảm hay hèn nhát khi đối mặt với nghịch cảnh của con người. Những cuốn sách thiêng liêng của các tôn giáo đều khuyên loài người không nên lừa dối, giận dữ và kiêu ngạo. Chúng ta nghĩ bản thân thế nào thì sẽ là như thế. Chúng ta không thể giúp một người từ bỏ tội ác và sống có trách nhiệm nếu không thể khiến anh ta thay đổi nhân tính, đó chính là \"suy nghĩ\".\r\n\r\nẤn bản đầu tiên của cuốn sách “Tâm lý học tội phạm” được xuất bản năm 1984, và ấn bản thứ hai xuất bản năm 2004. Sau một thập kỷ, và đã đến lúc bổ sung thêm các thông tin cho cuốn sách này. Trong ấn bản này, bạn sẽ hiểu chi tiết về các quá trình tư duy và chiến thuật phổ biến đối với những kẻ phạm tội, bất kể lý lịch hay tội ác của chúng.\r\n\r\nGiờ đây, với cái nhìn sâu sắc hơn tác giả Stanton E.Samenow đã cung cấp cho độc giả một ấn bản cập nhật hoàn toàn về tác phẩm kinh điển của mình, bao gồm những sự nhận thức mới mẻ về tội ác đang được chú ý ngày nay, từ sự rình rập và bạo lực gia đình đến tội phạm cổ cồn và chính trị khủng bố. Ông đã từng có ba thập kỷ làm việc với tội phạm khẳng định lại lập luận của mình rằng các yếu tố như nghèo đói, ly hôn và bạo lực trên phương tiện truyền thông không gây ra tội phạm. Đúng hơn, như các tài liệu của Samenow ở đây, tất cả tội phạm đều có chung một suy nghĩ đặc biệt - thường thấy rõ trong thời thơ ấu - khác hẳn với suy nghĩ của một công dân có trách nhiệm.\r\n\r\nTrong khi các loại tội phạm mới ngày càng phổ biến hơn, hoặc ít nhất là dễ nhìn thấy hơn với công chúng - từ lạm dụng vợ chồng đến các vụ xả súng ở trường học - có rất ít thay đổi về cách tiếp cận của chúng ta đối với tội phạm. Các chương trình phục hồi dựa trên giả định rằng xã hội đổ lỗi cho tội phạm nhiều hơn là tội phạm, một giả định mà mối liên hệ nhân quả vẫn chưa được thiết lập, đã được chứng minh là không đầy đủ. Tội phạm tiếp tục xâm chiếm mọi khía cạnh của cuộc sống chúng ta, các tòa án hình sự và nhà tù luôn quá tải, và tỷ lệ tái phạm tiếp tục leo thang.\r\n\r\nTiến sĩ Samenow, một nhà tâm lý học lâm sàng đã bác bỏ một cách hợp pháp những lời giải thích về hành vi tội phạm đổ lỗi cho hoàn cảnh, môi trường (xã hội, gia đình, truyền hình bạo lực,...). Ông thường xuyên đưa ra những lời khái quát sâu rộng và không cung cấp gì khác ngoài những trường hợp có thật ủng hộ quan điểm của ông rằng tất cả tội phạm đều vi phạm pháp luật một cách có ý thức và cố ý.\r\n\r\nHiểu biết thông thường về nguyên nhân phạm tội hiện vẫn là kim chỉ nam cho nhiều người vạch ra các chính sách phòng chống tội phạm. Các nhà hoạch định chính sách lãng phí hàng tỷ đô la khi họ ngây thơ tìm cách chống lại hành vi tội phạm bằng cách loại bỏ cái gọi là “nguyên nhân gốc rễ” về môi trường xung quanh.\r\n\r\nĐiều quan trọng là chúng ta phải biết kẻ phạm tội là ai và làm thế nào và tại sao hắn lại hành động khác với những công dân có trách nhiệm. Từ sự hiểu biết đó có thể đưa ra những giải pháp hợp lý, nhân ái và hiệu quả.\r\n', '1980 Books', 'NXB Đại học Kinh Tế Quốc Dân', '2023-04-25 01:00:43'),
('f3qROhoact', 'Làm Gì Có Ai Thực Lòng Muốn Chết', 'Lim Sewon', 78000, 'https://cdn0.fahasa.com/media/catalog/product/b/i/bia-lam-gi-co-ai-thuc-long-muon-chet-gui-in-1600.jpg', 'Làm Gì Có Ai Thực Lòng Muốn Chết\r\n\r\nCố giáo sư Lim Sewon là giáo sư tại Đại học Y khoa Sungkyunkwan và là bác sĩ tâm thần tại bệnh viện Kang Buk Samsung. Công việc của giáo sư Lim Sewon là tìm lại hạnh phúc cho những người đang đánh mất chúng, người ta gọi đó là công việc \"điều trị bệnh trầm cảm\".\r\n\r\nTrầm cảm - căn bệnh quái ác ăn mòn nụ cười, làm mất đi năng lượng và hơn tất cả đánh mất đi hy vọng. Căn bệnh này chẳng bỏ qua bất cứ ai, kể cả khi đó là một bác sĩ hơn mười năm tích lũy kinh nghiệm lâm sàng, lắng nghe những câu chuyện của bệnh nhân và giúp họ \"tìm lại hy vọng\" để nguôi ngoai nỗi đau trong tâm hồn.\r\n\r\nNgôn từ giản dị nhưng sâu sắc, ẩn chứa nhiều thông điệp tích cực và chữa lành cho bất cứ ai đọc cuốn sách này, hy vọng vào tương lai để có thể được hạnh phúc. Khi đặt bút viết cuốn sách này, tác giả muốn giúp nhiều người hơn, kể cả chính ông, những người đang phải vật lộn với nỗi đau khổ, để học có thể tìm thấy dẫu chỉ là một chút của niềm hy vọng mong manh, để có thể nắm trong tay một manh mối nhỏ của hạnh phúc.\r\n\r\n Cuốn sách này có thể giúp đỡ dù chỉ là một phần nhỏ bé cho những ai đang rơi vào tuyệt vọng, hay những người thân trong gia đình họ, trên hết là tất cả những người muốn lấp đầy từng khoảnh khắc của cuộc đời mình bằng niềm hạnh phúc.\r\n—---\r\nMỘT TRÍCH ĐOẠN TRONG SÁCH:\r\n\r\n“Bác sĩ không hiểu bệnh này đâu...”\r\n\r\nCác bệnh nhân thường nói với tôi như vậy. Và tôi rất ghét phải nghe câu nói ấy.\r\n\r\nĐặc biệt là trong những năm đầu khi vừa nhận được bằng cấp về chuyên môn, tôi đã gần như nổi giận khi nghe thấy điều đó. Tôi, học đại học Y sáu năm, thực tập một năm, nội trú bốn năm, và có cả bằng cấp chuyên môn được Nhà nước công nhận là thành thạo trong lĩnh vực này, mà lại còn không hiểu về chuyên ngành của mình - bệnh trầm cảm - vậy thì rốt cuộc ai mới là người hiểu rõ về căn bệnh này đây?\r\n\r\nTuy nhiên, một năm, hai năm, khi kinh nghiệm lâm sàng dần dần tích lũy lại, tôi nhận ra rằng những lời nói này của các bệnh nhân chỉ là cách họ thể hiện sự bất mãn của mình đối với phương pháp điều trị hiện tại. Và rồi tôi đã nói với các bệnh nhân như thế này:\r\n\r\n“Bác sĩ điều trị ung thư không nhất thiết phải mắc bệnh ung thư mới có thể hiểu rõ về nó và điều trị tốt cho bệnh nhân ung thư. Bệnh trầm cảm cũng vậy. Tuyệt đối không phải chỉ những người mắc bệnh này mới hiểu và có thể chữa trị được nó.”\r\n\r\nNhưng phải hơn mười năm sau khi trở thành bác sĩ chuyên khoa, tôi mới biết mình đã sai và các bệnh nhân đã đúng. Không phải vì tôi đã gặp nhiều bệnh nhân và tích lũy được những kinh nghiệm lâm sàng, mà chỉ khi chính bản thân tôi tự trải qua những tháng ngày dài đau đớn vì bệnh trầm cảm, tôi mới hiểu.\r\n\r\nLà một bác sĩ, hơn ai hết, tôi hiểu rõ trầm cảm là bệnh gì, phải điều trị ra sao. Nhưng tôi lại không biết các bệnh nhân đã thực sự trải qua căn bệnh này như thế nào.', 'AZ Việt Nam', 'Văn Học', '2023-04-25 00:24:22'),
('FA9xuq83Fy', 'Những Quy Tắc Trong Quản Lý', 'Richard Templar', 118000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_180753_1.jpg', 'Những doanh nh&acirc;n th&agrave;nh c&ocirc;ng nhất trong lịch sử như Henry Ford - Chủ tịch v&agrave; Người s&aacute;ng lập h&atilde;ng &ocirc;t&ocirc; Ford, hay Akio Morita - Chủ tịch v&agrave; Người s&aacute;ng lập Tập đo&agrave;n Sony của Nhật Bản đều nhấn mạnh đến nghệ thuật quản l&yacute;. C&oacute; thể n&oacute;i, quản l&yacute; l&agrave; một m&ocirc;n nghệ thuật nhưng cũng l&agrave; một m&ocirc;n khoa học.\r\n\r\nKhi bạn ngồi trong một văn ph&ograve;ng gi&aacute;m đốc, bạn phải c&oacute; một &yacute; niệm r&otilde; r&agrave;ng về khoa tổ chức. L&yacute; tưởng trong việc tổ chức một c&ocirc;ng cuộc kinh doanh l&agrave; tạo n&ecirc;n một nh&oacute;m người biết l&agrave;m việc chung để đạt đến mục ti&ecirc;u chung. L&agrave;m người quản l&yacute;, nhiệm vụ của bạn l&agrave; phải th&uacute;c đẩy h&agrave;nh động chứ kh&ocirc;ng phải ngăn cản n&oacute;.\r\n\r\nH&agrave;ng ng&agrave;y, mỗi nh&agrave; l&atilde;nh đạo, mỗi nh&agrave; quản l&yacute; đều phải xử l&yacute; v&agrave; quyết định rất nhiều vấn đề trải rộng trong mọi lĩnh vực của cuộc sống. Vậy cần phải c&oacute; những nguy&ecirc;n tắc n&agrave;o để điều h&agrave;nh tổ chức v&agrave; đạt được mục ti&ecirc;u của m&igrave;nh? Đ&acirc;u l&agrave; những b&iacute; quyết v&agrave; kỹ năng m&agrave; c&aacute;c nh&agrave; quản l&yacute; cần phải c&oacute;?\r\n\r\nBất kỳ ai cũng c&oacute; thể học hỏi từ những chỉ dẫn rất hữu &iacute;ch n&agrave;y. V&iacute; dụ như c&aacute;ch khuyến kh&iacute;ch nh&acirc;n vi&ecirc;n h&agrave;o hứng v&agrave; t&iacute;ch cực l&agrave;m việc, c&aacute;ch l&agrave;m cho cuộc họp trở n&ecirc;n vui vẻ, hay biết c&aacute;ch tự quản l&yacute; ch&iacute;nh m&igrave;nh: Tiến l&ecirc;n ph&iacute;a trước chứ kh&ocirc;ng thụt l&ugrave;i, hay đặt ra mục ti&ecirc;u thực tiễn cho m&igrave;nh, nhưng phải l&agrave; những mục ti&ecirc;u c&oacute; t&iacute;nh thực tiễn cao, h&atilde;y quyết đo&aacute;n, ngay cả đ&ocirc;i khi điều n&agrave;y khiến bạn mắc sai lầm&hellip;\r\n\r\nĐ&acirc;y cũng c&oacute; thể xem l&agrave; một t&uacute;i b&iacute; k&iacute;p để nh&agrave; quản l&yacute; giở ra khi muốn t&igrave;m sự tham vấn, hoặc muốn t&igrave;m sự đồng cảm trong nghệ thuật quản trị nguồn nh&acirc;n lực bằng ch&iacute;nh n&eacute;t văn ho&aacute; ri&ecirc;ng của doanh nghiệp m&igrave;nh.', 'Alpha Books', 'NXB Lao Động', '2023-04-25 01:14:25'),
('gHxD9d4Dz1', 'Càng Bình Tĩnh Càng Hạnh Phúc', 'Vãn Tình', 105000, 'https://cdn0.fahasa.com/media/catalog/product/b/_/b_a-1_c_ng-b_nh-t_nh-c_ng-h_nh-ph_c600.jpg', 'C&agrave;ng B&igrave;nh Tĩnh C&agrave;ng Hạnh Ph&uacute;c\r\n\r\nTiếp nối th&agrave;nh c&ocirc;ng rực rỡ của Bạn đắt gi&aacute; bao nhi&ecirc;u?, Kh&iacute; chất bao nhi&ecirc;u hạnh ph&uacute;c bấy nhi&ecirc;u, Kh&ocirc;ng sợ chậm chỉ sợ dừng,&hellip; V&atilde;n T&igrave;nh đ&atilde; quay trở lại c&ugrave;ng cuốn s&aacute;ch C&agrave;ng b&igrave;nh tĩnh C&agrave;ng hạnh ph&uacute;c. Đ&acirc;y l&agrave; cuốn s&aacute;ch thứ bảy của c&ocirc; được xuất bản tại Việt Nam bởi thương hiệu s&aacute;ch Bloom Books, đ&aacute;nh dấu cho h&agrave;nh tr&igrave;nh trưởng th&agrave;nh đầy rực rỡ của V&atilde;n T&igrave;nh &ndash; từ một c&ocirc; g&aacute;i trẻ trung, mạnh mẽ trở n&ecirc;n ch&iacute;n chắn, điềm tĩnh v&agrave; bao dung hơn với cuộc đời.\r\n\r\nGần bảy mươi c&acirc;u chuyện trong s&aacute;ch xoay quanh c&aacute;c chủ đề t&igrave;nh y&ecirc;u, h&ocirc;n nh&acirc;n, gia đ&igrave;nh, sự nghiệp&hellip; đến từ ch&iacute;nh cuộc sống của t&aacute;c giả v&agrave; những người xung quanh, vừa thực tế lại vừa gợi mở, dễ d&agrave;ng gi&uacute;p ch&uacute;ng ta li&ecirc;n hệ với t&igrave;nh huống của ch&iacute;nh m&igrave;nh. Với những c&acirc;u chuyện đ&oacute;, V&atilde;n T&igrave;nh hy vọng c&oacute; thể gi&uacute;p c&aacute;c c&ocirc; g&aacute;i trưởng th&agrave;nh, độc lập v&agrave; tự tin hơn, t&igrave;m lại bản ng&atilde; v&agrave; sống cuộc đời theo c&aacute;ch m&agrave; m&igrave;nh mong muốn.\r\n\r\nTh&ocirc;ng điệp xuy&ecirc;n suốt trong &ldquo;C&agrave;ng b&igrave;nh tĩnh C&agrave;ng hạnh ph&uacute;c&rdquo; m&agrave; V&atilde;n T&igrave;nh muốn gửi gắm tới độc giả l&agrave;:\r\n\r\n&ldquo;Bảy năm trước t&ocirc;i sẽ c&atilde;i v&atilde; với người ta khi phật &yacute;, bảy năm sau t&ocirc;i tu&acirc;n thủ c&acirc;u n&oacute;i &lsquo;Bận rộn l&agrave; liều thuốc trị mọi chứng bệnh t&acirc;m l&yacute;.&rsquo;\r\n\r\nBảy năm trước t&ocirc;i lu&ocirc;n chuẩn bị cẩn thận cho mọi ng&agrave;y lễ, bảy năm sau t&ocirc;i bận tới mức qu&ecirc;n cả bản th&acirc;n m&igrave;nh chứ đừng n&oacute;i tới ng&agrave;y lễ g&igrave;.\r\n\r\nBảy năm trước, ch&uacute; c&uacute;n nh&agrave; t&ocirc;i qua đời, bảy năm sau, t&ocirc;i kh&ocirc;ng c&ograve;n nu&ocirc;i c&uacute;n nữa.\r\n\r\nBảy năm đủ để một phụ nữ ng&acirc;y ng&ocirc; trở n&ecirc;n ch&iacute;n chắn, ng&acirc;y thơ trở n&ecirc;n l&yacute; tr&iacute;, v&agrave; xốc nổi trở n&ecirc;n b&igrave;nh tĩnh ung dung, dần dần t&igrave;m thấy &yacute; nghĩa của cuộc đời m&igrave;nh.\r\n\r\nKh&ocirc;ng biết bảy năm sau t&ocirc;i sẽ như thế n&agrave;o.&rdquo;\r\n\r\nC&ograve;n bạn, bảy năm trước bạn l&agrave; ai?\r\n\r\nV&agrave; bảy năm sau, bạn muốn trở th&agrave;nh một người như thế n&agrave;o?\r\n\r\nTr&iacute;ch dẫn hay của V&atilde;n T&igrave;nh trong C&agrave;ng b&igrave;nh tĩnh C&agrave;ng hạnh ph&uacute;c:\r\n\r\n&ldquo;Phồn hoa tựa gấm, h&agrave;o quang bốn phương đều c&oacute; thể từ bỏ, nhưng ch&acirc;n t&igrave;nh đ&acirc;u dễ g&igrave; c&oacute; được.\r\n\r\nT&ocirc;i muốn n&oacute;i với mọi người rằng: &lsquo;C&oacute; người y&ecirc;u thương ch&uacute;ng ta l&agrave; một điều tuyệt vời, nhưng nếu kh&ocirc;ng c&oacute; ai y&ecirc;u thương ch&uacute;ng ta, ch&uacute;ng ta phải y&ecirc;u thương bản th&acirc;n m&igrave;nh gấp bội. Cuộc đời n&agrave;y &iacute;t người &lsquo;trong tuyết đưa than&rsquo;, m&agrave; đa phần l&agrave; &lsquo;dệt hoa tr&ecirc;n gấm&rsquo;, bởi vậy h&atilde;y biến bản th&acirc;n th&agrave;nh &lsquo;gấm&rsquo; trước đ&atilde;!&rdquo;\r\n\r\n&ldquo;Người ta sống tr&ecirc;n đời kh&oacute; tr&aacute;nh nhất l&agrave; một chữ &lsquo;T&igrave;nh&rsquo;, kh&ocirc;ng ai c&oacute; thể l&agrave;m lơ t&igrave;nh y&ecirc;u, nhưng đời người đ&atilde; c&oacute; qu&aacute; nhiều tiếc nuối v&agrave; bất lực, khiến tr&aacute;i tim yếu mềm của ch&uacute;ng ta chịu nhiều gi&agrave;y v&ograve; khi phải đối mặt với v&ocirc; v&agrave;n đau khổ v&agrave; mất m&aacute;t.\r\n\r\nNếu để cho phụ nữ trong chốn hồng trần n&agrave;y lựa chọn giữa quyền lực, tiền bạc v&agrave; t&igrave;nh y&ecirc;u ch&acirc;n th&agrave;nh, c&oacute; lẽ mọi người đều muốn c&oacute; được một tấm ch&acirc;n t&igrave;nh, chỉ ước sao được l&agrave;m c&ocirc; g&aacute;i yếu mềm của người ấy.&rdquo;\r\n\r\n&ldquo;Ch&uacute;ng ta thường n&oacute;i, sự khẳng định lớn nhất của một người đ&agrave;n &ocirc;ng d&agrave;nh cho một người phụ nữ l&agrave; cưới c&ocirc; ấy về nh&agrave;. Nhưng t&ocirc;i muốn n&oacute;i rằng: &lsquo;Sự khẳng định lớn nhất của một người phụ nữ d&agrave;nh cho bản th&acirc;n m&igrave;nh ch&iacute;nh l&agrave; nắm bắt cuộc sống m&igrave;nh mong muốn một c&aacute;ch ph&oacute;ng kho&aacute;ng ung dung.&rsquo;&rdquo;\r\n\r\nVề t&aacute;c giả:\r\n\r\nV&atilde;n T&igrave;nh l&agrave; nh&agrave; bi&ecirc;n kịch - t&aacute;c giả của những đầu s&aacute;ch b&aacute;n chạy tại Trung Quốc. C&aacute;c t&aacute;c phẩm của c&ocirc; đều thẳng thắn, trực diện, đ&aacute;nh tr&uacute;ng t&acirc;m l&yacute; c&aacute;c c&ocirc; g&aacute;i. \r\n\r\nỞ Việt Nam, V&atilde;n T&igrave;nh được coi như &ldquo;nữ ho&agrave;ng&rdquo; của d&ograve;ng s&aacute;ch cảm hứng sống d&agrave;nh cho ph&aacute;i nữ. Cuốn s&aacute;ch Bạn đắt gi&aacute; bao nhi&ecirc;u? của c&ocirc; trở th&agrave;nh cuốn s&aacute;ch B&aacute;n chạy nhất tr&ecirc;n nền tảng Tiki.vn (2019), tạo n&ecirc;n một l&agrave;n s&oacute;ng mạnh mẽ nhằm cổ vũ tinh thần, thay đổi quan điểm hạnh ph&uacute;c của bất kỳ ai từng đọc cuốn s&aacute;ch.\r\n\r\nTrong lần trở lại n&agrave;y, V&atilde;n T&igrave;nh mang đến cho bạn đọc một t&aacute;c phẩm với văn phong nhẹ nh&agrave;ng nhưng đầy trải nghiệm về h&agrave;nh tr&igrave;nh trưởng th&agrave;nh của một người phụ nữ:\r\n\r\n&ldquo;Khi bạn c&agrave;ng B&igrave;nh tĩnh,\r\n\r\nbạn sẽ c&agrave;ng tới gần Hạnh ph&uacute;c.&rdquo;\r\n\r\nS&aacute;ch đ&atilde; xuất bản:\r\n\r\n- Bạn đắt gi&aacute; bao nhi&ecirc;u? (2018)\r\n\r\n- Kh&iacute; chất bao nhi&ecirc;u &ndash; Hạnh ph&uacute;c bấy nhi&ecirc;u (2018)\r\n\r\n- Kh&ocirc;ng tự khinh bỉ - Kh&ocirc;ng tự ph&iacute; ho&agrave;i (2019)\r\n\r\n- Lấy t&igrave;nh th&acirc;m m&agrave; đổi đầu bạc (2019)\r\n\r\n- Kh&ocirc;ng sợ chậm Chỉ sợ dừng (2020)\r\n\r\n- Rất th&iacute;ch rất th&iacute;ch em (2021)', 'AZ Việt Nam', 'Thế Giới', '2023-04-25 01:40:12'),
('gL50A65yxI', 'Mãi Mãi Là Yêu Thương', 'First News', 58000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_216228.jpg', 'M&Atilde;I M&Atilde;I L&Agrave; Y&Ecirc;U THƯƠNG\r\n\r\nY&ecirc;u thương l&agrave; cảm x&uacute;c thi&ecirc;ng li&ecirc;ng nhất trong cuộc sống của ch&uacute;ng ta. Kh&ocirc;ng một ai c&oacute; thể sống tr&ecirc;n đời m&agrave; thiếu t&igrave;nh y&ecirc;u thương. Cuộc sống của bạn sẽ &yacute; nghĩa hơn nhiều khi bạn biết d&agrave;nh t&igrave;nh thương y&ecirc;u để l&agrave;m cho cuộc sống của những người xung quanh v&agrave; của m&igrave;nh ấm &aacute;p, hạnh ph&uacute;c hơn.\r\n\r\nFirst News h&acirc;n hạnh giới thiệu tập s&aacute;ch M&atilde;i M&atilde;i L&agrave; Y&ecirc;u Thương như một sự chia sẻ với c&aacute;c bạn về gi&aacute; trị của y&ecirc;u thương. C&oacute; thể bạn đang được sống trong t&igrave;nh y&ecirc;u thương của gia đ&igrave;nh, của người th&acirc;n, bạn b&egrave;. Cuộc sống trong mắt bạn chỉ to&agrave;n một m&agrave;u hồng tươi s&aacute;ng, đ&aacute;ng y&ecirc;u. Thậm ch&iacute; c&oacute; l&uacute;c bạn c&ograve;n kh&ocirc;ng nhận ra hay qu&yacute; trọng đ&uacute;ng mức những t&igrave;nh cảm ấy. Chỉ đến khi gặp phải những thất bại hay bất hạnh trong cuộc sống, ch&uacute;ng ta mới cảm nhận được hết chiều s&acirc;u của t&igrave;nh cảm y&ecirc;u thương. Khi đ&oacute;, ch&uacute;ng ta mới thấu hiểu v&agrave; tr&acirc;n trọng &yacute; nghĩa của t&igrave;nh y&ecirc;u thương &ndash; t&igrave;nh cảm ấm &aacute;p c&oacute; sức mạnh an ủi, n&acirc;ng đỡ nhiệm m&agrave;u đối với t&acirc;m hồn của con người.\r\n\r\nHy vọng tập s&aacute;ch n&agrave;y sẽ l&agrave;m bạn mở rộng tr&aacute;i tim v&agrave; &aacute;nh mắt cho những cảm nhận y&ecirc;u thương sưởi ấm t&acirc;m hồn. Bởi hơn tất cả mọi thứ tr&ecirc;n đời, t&igrave;nh y&ecirc;u thương l&agrave; sức mạnh v&ocirc; bi&ecirc;n, l&agrave; điều chia sẻ qu&yacute; gi&aacute; kh&ocirc;ng thể thiếu trong cuộc sống con người.\r\n', 'FIRST NEWS', 'NXB Tổng Hợp TPHCM', '2023-04-25 01:16:50');
INSERT INTO `products` (`product_id`, `name`, `author`, `price`, `image`, `description`, `supplier`, `publiser`, `date`) VALUES
('H6HLY5FImj', '[Phiên chợ sách cũ] Chạy Bộ Để Vượt Qua', 'Meb Keflezighi, Dick Patrick', 199000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_195509_1_41283_thanh_ly.jpg', '&ldquo;Kể cả khi mọi chuyện đều trở n&ecirc;n tồi tệ, t&ocirc;i vẫn cố gắng hết m&igrave;nh.&rdquo; - Meb Keflezighi.\r\n\r\nĐ&acirc;y l&agrave; c&acirc;u chuyện phi thường c&oacute; thật của Meb Keflezighi, người gi&agrave;nh chiến thắng trong giải Boston Marathon năm 2014!\r\n\r\nKhi Meb Keflezighi đăng k&yacute; chạy trong cuộc thi Boston Marathon v&agrave;o năm 2014, kh&ocirc;ng ai c&oacute; thể nghĩ rằng anh ấy sẽ l&agrave; người c&aacute;n đ&iacute;ch đầu ti&ecirc;n. Nhưng việc Meb giỏi nhất l&agrave; lu&ocirc;n vươn l&ecirc;n. Tuy nhi&ecirc;n, Meb lại l&agrave; hiện th&acirc;n sống của giấc mơ Mỹ. Theo gia đ&igrave;nh đến Mỹ để trốn khỏi sự ngh&egrave;o đ&oacute;i v&agrave; chiến tranh, cậu b&eacute; Meb l&uacute;c đ&oacute; mới chỉ 12 tuổi, một chữ tiếng Anh bẻ đ&ocirc;i cũng kh&ocirc;ng biết v&agrave; cũng chưa từng chạy đua một lần n&agrave;o.\r\n\r\nBằng ch&iacute;nh nỗ lực v&agrave; sự ki&ecirc;n định của m&igrave;nh, cậu đ&atilde; v&ocirc; c&ugrave;ng xuất sắc trong học tập v&agrave; v&agrave; gi&agrave;nh được một huy chương Bạc ở Thế vận hội Olympic. Thế nhưng mọi thứ đ&atilde; sụp đổ khi Meb, từ vị tr&iacute; ứng cử vi&ecirc;n xuất sắc cho Olympic Bắc Kinh, sau khi bị g&atilde;y xương chậu v&agrave; xương h&ocirc;ng trong khi tập luyện, đ&atilde; bị bỏ lại đ&uacute;ng nghĩa đen. C&ugrave;ng ng&agrave;y đ&oacute;, anh cũng mất người bạn th&acirc;n - người đồng h&agrave;nh tr&ecirc;n dường chạy marathon do căn bệnh rối loạn nhịp tim. Trong t&igrave;nh cảnh suy sụp ấy, Meb kiểm chứng được liệu niềm tin của anh v&agrave;o Ch&uacute;a, những gi&aacute; trị m&agrave; cha mẹ đ&atilde; dạy anh v&agrave; niềm tin của bản th&acirc;n rằng m&igrave;nh sinh ra để chạy liệu c&oacute; đủ để anh tiếp tục con đường m&agrave; anh đ&atilde; chọn. &ldquo;Chạy bộ để vượt qua&rdquo; l&agrave; một c&acirc;u chuyện về một nh&agrave; v&ocirc; địch thực thụ của nước Mỹ, người đ&atilde; t&igrave;m thấy &yacute; nghĩa đ&iacute;ch thực của chiến thắng bất chấp kh&oacute; khăn.', 'Panda Books', 'NXB Thể Thao Và Du Lịch', '2023-04-25 01:36:58'),
('h9VqqAyj9A', 'Tủ Sách Học Làm Người - Nên Thân Với Đời', 'Hoàng Xuân Việt', 67000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_182657.jpg', 'Cuốn sách viết về những kiến thức thực tiễn của cuộc sống mà chúng ta cần học ngoài những kiến thức nhận được trên ghế nhà trường, để hoàn thiện bản thân trở thành người thành đạt. Học giả Hoàng Xuân Việt chỉ ra cho bạn đọc những việc phá hoại tâm lực là gì? Rèn nghị lực ra sao? Luyện trí tưởng tượng thế nào thì tốt? Những phiền muộn nào khiến bạn rủn chí rồi dễ dàng đầu hàng sầu thảm? Được chia thành 2 phần: Nên thân và Yên tâm, cuốn sách nhỏ này sẽ vô cùng hữu ích cho người đọc trên hành trình hoàn thiện bản thân.\r\n\r\nNên thân với đời nằm trong Tủ sách Học làm người của học giả Hoàng Xuân Việt, với những bài học vô cùng thiết thực, dù đã trải qua một thế kỷ nhưng vẫn còn vẹn nguyên giá trị cho người trẻ thời nay.\r\n\r\nTrích dẫn:\r\n\r\n- Người yên tâm là người hạnh phúc. Phải không bạn? Giữa thời đại mà trên nhiều lĩnh vực dễ rối lương tâm mà bạn lập trong lương tâm mình thế quân bình, tống khứ các ưu tư cho niềm vui tự đáy lòng nở qua nụ cười, nét mặt rồi nhìn cuộc sống như mùa hoa nở. Ai mà không cho là vạn hạnh. Mình có yên tâm đi đã, tư tưởng mới thâm trầm, lời nói mới cương quyết, hành động mới đắc lực và sống như vậy mới thật sự hữu ích cho mình và cho xã hội.\r\n\r\n- Trong phút tĩnh tâm nào đó, bạn dựng lại toàn bộ lịch sử của đời tư bạn. Coi trong các tháng năm qua đại khái bạn đã làm gì, thành bại ra sao, tại sao? Bạn coi vốn học của mình tới đâu? Nghề nghiệp thế nào? Giao tế đắc nhân tâm hay thường gây thù chuốc oán? Làm việc có nghị lực không? Bi quan hay lạc quan với cuộc sống hiện tại? Hành động suy nghĩ hay nông nổi? Can đảm hay nhát gan? Ký ức thế nào? Tưởng tượng phong phú không? -Sau khi đã xét lại con người của mình, bạn tự hỏi coi bây giờ bạn có định cải thiện hoàn cảnh của mình không. Đức tính nào bạn muốn phát triển. Mẫu người bạn nhắm đào luyện ra sao? Bạn tin mình sẽ thành công bằng những luật thành công hay phó mặc may rủi. Nhiều khi ta mắc tật lo dòm ngó “cái tôi” của kẻ khác, thèm thuồng giá trị của người này, người nọ mà quên nhận diện “cái tôi” của mình, và đánh giá sai cái khả năng của chính mình. Phải thành thực với mình và sáng suốt nhận thức nguyên nhân của các thất bại. Tự vấn tại sao trong lãnh vực nào đó ta bị kẻ khác hại, ta lỡ dịp may, ta nói kẻ khác không nghe, ta làm hỏng trách vụ người trên giao phó.\r\n\r\n- Một lời nói chua cay của kẻ khác làm ta muốn nhảy chồm lên. Một hung tín làm ta tối tăm mày mặt. Một đồ vật hay món hàng nào đó làm ta say mê. Một người ta thích, ta yêu và luôn muốn gặp, muốn gần gũi. Trong các trường hợp này cảm xúc tính nổi sóng gió trong ta. Tình dục bao vây lý trí, kình chống với ý chí. Tinh thần mà non tay ấn là ta hành động nô lệ thú tính. Hầu hết những hối hận của ta về tình cảm, về xử thế là do ta thiếu tự chủ. Ta bị đứa thất phu chọc tức, ta nhào tới “mắt thế mắt, răng thế răng”. Được một tin buồn ta luýnh quýnh, mất ăn mất ngủ. Khi lòng ham muốn hay thiện cảm hoặc tình yêu phát lên cuồng bạo, ta lý luận chủ quan, cho mình cái gì cũng phải, hành động bất cần hậu quả ra sao. Lúc cảm xúc lắng xuống, ta mới giật mình thấy tại sao khi mất quân bình, nội tâm nông nổi đến thế. Muốn tiết kiệm tâm lực, trong các trường hợp náo động đó, phải thinh lặng. Bạn không nói gì hết, không viết gì hết, không hành động gì hết khi nội tâm nổi phong ba. Lúc thinh lặng bạn tự kỷ ám thị bằng những câu: “Tôi tự chủ. Tôi bình tĩnh. Tôi suy nghĩ trước khi hành động”. Đếm từ 1 đến 100 rồi hễ nói khi cảm xúc ồ ạt. Làm ngược lại ý mình khi cảm xúc nổi lên. Nhìn việc gì trên đời cũng bằng cặp mắt khách quan, lạnh lạt. Đừng quan trọng hóa việc cỏn con. Cứ hành động, nói năng ra vẻ như mình vốn là người tự chủ.', 'Alpha Books', 'NXB Thanh Niên', '2023-04-25 00:27:12'),
('Hkf3YUxYC6', 'Những Quy Tắc Để Sống Khỏe', 'Alpha Books biên soạn', 101000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_176067.jpg', 'Trong ch&uacute;ng ta ai cũng mơ ước c&oacute; một cơ thể khoẻ mạnh, tr&agrave;n đầy năng lượng. Ai cũng mong muốn c&oacute; một cuộc sống hạnh ph&uacute;c th&agrave;nh đạt. Theo tổ chức Y tế thế giới: &ldquo;Sức khoẻ l&agrave; trạng th&aacute;i thoải m&aacute;i ho&agrave;n to&agrave;n về thể chất, tinh thần v&agrave; x&atilde; hội chứ kh&ocirc;ng phải l&agrave; một t&igrave;nh trạng kh&ocirc;ng c&oacute; bệnh hay t&agrave;n tật.&rdquo; Tuy nhi&ecirc;n trong cuộc sống, ch&uacute;ng ta c&oacute; thể mắc một căn bệnh n&agrave;o đ&oacute; hoặc gặp rắc rối trong c&ocirc;ng việc, trong c&aacute;c mối quan hệ x&atilde; hội&hellip; điều đ&oacute; khiến bạn căng thẳng v&agrave; mệt mỏi. Vậy l&agrave;m thế n&agrave;o để c&oacute; một sức khoẻ tốt?\r\n\r\nNhững chỉ dẫn trong cuốn Những quy tắc để sống khoẻ sẽ gi&uacute;p bạn mở rộng tầm nh&igrave;n về sức khoẻ to&agrave;n diện, hiểu biết hơn về c&aacute;c yếu tố nguy cơ ảnh hưởng đến sức khỏe của bạn v&agrave; người th&acirc;n, từ đ&oacute; bạn c&oacute; thể ph&ograve;ng tr&aacute;nh c&aacute;c yếu tố c&oacute; hại cho sức khỏe của bạn v&agrave; n&acirc;ng cao sức khỏe cho ch&iacute;nh bản th&acirc;n v&agrave; gia đ&igrave;nh bạn.\r\n\r\nSức khỏe cũng giống như một cơ hội. Những quy tắc được đề cập trong cuốn s&aacute;ch n&agrave;y sẽ gi&uacute;p bạn kh&ocirc;ng để tuột mất c&aacute;c cơ hội đ&oacute;, nếu như bạn biết &aacute;p dụng ch&uacute;ng trong cuộc sống h&agrave;ng ng&agrave;y. Một ch&uacute;t kiến thức về ăn uống, tập luyện, ch&uacute;t kiến thức về loại bỏ stress, đạt được sự thăng bằng trong cuộc sống tinh thần, ch&uacute;t kiến thức về giao tiếp với mọi người sẽ trở th&agrave;nh cẩm nang bỗ trợ cho sức khỏe của mỗi người.\r\n\r\nC&oacute; thể c&oacute; những quy tắc bạn đ&atilde; biết nhưng qu&ecirc;n kh&ocirc;ng thực h&agrave;nh n&oacute;, cũng c&oacute; thể c&oacute; những quy tắc bạn chưa từng biết đến, nhưng tất cả những quy tắc đ&oacute; đều gi&uacute;p bạn c&oacute; một sức khỏe tốt.', 'Alpha Books', 'NXB Lao Động', '2023-04-25 01:12:49'),
('I1VXO8uwI8', 'Không Phải Sói Nhưng Cũng Đừng Là Cừu', 'Lê Bảo Ngọc', 96000, 'https://cdn0.fahasa.com/media/catalog/product/_/k/_khong-phai-soi-nhung-cung-dung-la-cuu.jpg', 'Không Phải Sói Nhưng Cũng Đừng Là Cừu\r\n\r\nSÓI VÀ CỪU - BẠN KHÔNG TỐT NHƯ BẠN NGHĨ ĐÂU!\r\n\r\nLàn ranh của việc ngây thơ hay xấu xa đôi khi mỏng manh hơn bạn nghĩ.\r\n\r\nBạn làm một việc mà mình cho là đúng, kết quả lại bị mọi người khiển trách.\r\n\r\nBạn ủng hộ một quan điểm của ai đó, và số đông khác lại ủng hộ một quan điểm trái chiều.\r\n\r\nRốt cuộc thì bạn sai hay họ sai?\r\n\r\nCuốn sách “Không phải sói nhưng cũng đừng là cừu” đến từ tác giả Lê Bảo Ngọc sẽ giúp bạn hiểu rõ hơn những khía cạnh sắc sảo phía sau những nhận định đúng, sai đơn thuần của mỗi người.\r\n\r\nNhững câu hỏi đầy tính tranh cãi như “Cứu người hay cứu chó?”, “Một kẻ thô lỗ trong lớp vỏ “thẳng tính” khác với người EQ thấp như thế nào?”, “Vì sao một bộ phận nữ giới lại victim blaming đối với nạn nhân bị xâm hại?”,... được tác giả đưa ra trong “Không phải sói nhưng cũng đừng là cừu”. Bằng từng chương sách của mình, tác giả vạch rõ cho bạn rằng “thật sự thế nào mới là người tốt”, ngây thơ và xấu xa có ranh giới rõ ràng như thế không, rốt cuộc bạn có là người tốt như mình vẫn luôn nghĩ?\r\n\r\nTrong thời đại mà mọi thứ đều rất chóng vánh này, ranh giới giữa tốt và xấu, đúng và sai đôi lúc là không tồn tại.\r\n\r\nCái tốt mà chúng ta nghĩ, hóa ra lại là xấu trong mắt kẻ khác.\r\n\r\nCái đúng ở thời điểm này, đến một thời điểm khác lại trở thành sai.\r\n\r\nTốt đẹp hay xấu xa, thật khó phân định.\r\n\r\nCuốn sách “Không phải sói nhưng cũng đừng là cừu” của tác giả Lê Bảo Ngọc - admin Tâm Lý Học Tổ Kén đồng thời là Giám đốc Trung tâm Pháp Luật và Văn hóa sẽ là câu trả lời thấu suốt và khiến bạn phải đặt ra câu hỏi cho lối tư duy bấy lâu bạn luôn nghĩ là đúng. Bạn sẽ là người giải phóng chính mình, khỏi gông xiềng của định kiến, quy chuẩn cũ kĩ vốn được thiết lập lên để mang lại lợi ích cho kẻ khác. Và bạn sẽ không còn phải lăn tăn giữa tốt và xấu, sói hay cừu, vì điều đó là không quan trọng. Bạn sẽ tìm được chính mình và muốn là chính mình sau từng trang sách của “Không phải sói mà cũng đừng là cừu\"\r\n\r\nKhông phải sói, cũng đừng là cừu - Cuốn sách đập tan những định kiến cũ kỹ, kiến tạo tư duy và giúp bạn xây dựng lại chính mình!', 'Skybooks', 'Thế Giới', '2023-04-19 20:59:45'),
('ie0cPWxESK', '[Phiên chợ sách cũ] Ông Giáo Làng Trên Tầng Gác Mái', 'Nguyễn Thế Vinh', 75000, 'https://cdn0.fahasa.com/media/catalog/product/9/7/9786047732968_thanh_ly.jpg', '&Ocirc;ng Gi&aacute;o L&agrave;ng Tr&ecirc;n Tầng G&aacute;c M&aacute;i\r\n\r\n(&hellip;) Trong những trang s&aacute;ch tiếp theo, c&oacute; thể bạn đọc sẽ cảm thấy chuyện đời t&ocirc;i kh&ocirc;ng hề bằng phẳng v&agrave; c&aacute;ch t&ocirc;i sống c&oacute; đ&ocirc;i ba phần lạ lẫm, kh&aacute;c người. Những điều nhiều người thường l&agrave;m, t&ocirc;i lại kh&ocirc;ng l&agrave;m theo. Con đường phẳng phiu ngay trước mắt, t&ocirc;i lại chẳng cất bước m&agrave; quyết rẽ v&agrave;o con đường kh&uacute;c khuỷu. \r\n\r\nT&ocirc;i nghĩ cuộc đời m&agrave; bằng phẳng qu&aacute; c&oacute; khi lại vơi bớt đ&ocirc;i ba phần &yacute; nghĩa. Những người c&oacute; đời sống nhiều ch&ocirc;ng gai c&oacute; khi l&agrave; do ch&iacute;nh &ocirc;ng trời ưu &aacute;i thế để họ được trải qua nhiều ho&agrave;n cảnh kh&aacute;c nhau, từ đ&oacute; t&iacute;ch lũy vốn sống, cảm x&uacute;c, kinh nghiệm v&agrave; ng&agrave;y c&agrave;ng trưởng th&agrave;nh. Thế n&ecirc;n, khi gặp nghịch cảnh, trước ti&ecirc;n h&atilde;y cứ chấp nhận ch&uacute;ng rồi sau đ&oacute; tập th&iacute;ch nghi v&agrave; vượt qua.\r\n\r\nC&oacute; l&uacute;c t&ocirc;i buồn lắm. Buồn nhiều tới mức kh&ocirc;ng c&aacute;ch n&agrave;o lướt qua hoặc kh&ocirc;ng t&igrave;m được nơi n&agrave;o chất chứa cho hết. Nhưng người ta buồn m&atilde;i rồi cũng sẽ c&oacute; l&uacute;c vui th&ocirc;i. Đời m&igrave;nh kh&ocirc;ng c&oacute; g&igrave; để vui th&igrave; phải kiếm ra chuyện để vui. \r\nNhững năm th&aacute;ng t&ocirc;i sống giống một d&ograve;ng suối miệt m&agrave;i chảy qua mọi địa h&igrave;nh, tận lực giữ cho m&igrave;nh kh&ocirc;ng bị cạn kiệt trước khi h&ograve;a v&agrave;o đại dương m&ecirc;nh m&ocirc;ng. T&ocirc;i đ&atilde; sống đơn giản như thế. \r\n\r\nChuyện đời t&ocirc;i sẽ kh&ocirc;ng c&oacute; yếu tố n&agrave;o giật g&acirc;n để cuốn h&uacute;t bạn đọc ngay từ đầu. Thế n&ecirc;n, mong bạn đọc h&atilde;y thong thả bước l&ecirc;n chuyến t&agrave;u k&yacute; ức của t&ocirc;i, thong thả ngắm những khung cảnh xưa cũ lướt qua &ocirc; cửa sổ, v&agrave; tất nhi&ecirc;n, bạn lu&ocirc;n c&oacute; thể dừng ở bất cứ nh&agrave; ga n&agrave;o bạn muốn. \r\n\r\n- Nguyễn Thế Vinh -', 'Saigon Books', 'NXB Hội Nhà Văn', '2023-04-25 01:35:22'),
('kyfLna4MRt', 'Mọi Nỗ Lực Và Chờ Đợi Của Bạn Đều Có Ý Nghĩa', 'Uyển Tử Văn, Uyển Từ Hào', 86000, 'https://cdn0.fahasa.com/media/catalog/product/c/a/capture_3.jpg', 'Bạn đ&atilde; bao giờ cảm thấy lạc lối chưa?\r\n\r\nQu&aacute; tr&igrave;nh lớn l&ecirc;n - trưởng th&agrave;nh - ph&aacute;t triển hay ch&iacute;nh l&agrave; qu&aacute; tr&igrave;nh ch&uacute;ng ta bắt đầu rời xa b&agrave;n tay bảo bọc của bố mẹ l&agrave; qu&atilde;ng thời gian m&agrave; bản th&acirc;n mối ch&uacute;ng ta dễ trở n&ecirc;n m&ocirc;ng lung, lạc lối nhất. Bạn sống đơn giản, thế n&agrave;o cũng được th&igrave; qu&aacute; tr&igrave;nh đ&oacute; sẽ xuất hiện chậm hơn một ch&uacute;t. Bạn lớn nhanh hơn một ch&uacute;t, biết suy nghĩ nhiều hơn th&igrave; qu&aacute; tr&igrave;nh đ&oacute; cũng xuất hiện sớm hơn.\r\n\r\nKhoảng thời gian từ 18 tuổi trở đi bạn sẽ cảm thấy rất m&ocirc;ng lung trong chọn lựa của bản th&acirc;n m&igrave;nh. Chẳng biết m&igrave;nh muốn g&igrave;, sẽ v&agrave;o trường đại học n&agrave;o để cố gắng thi cử. Thay v&igrave; xem m&igrave;nh muốn g&igrave;, ch&uacute;ng ta thường c&oacute; xu hướng t&ugrave;y theo bố mẹ muốn m&igrave;nh học c&aacute;i g&igrave;, nghề n&agrave;o đang HOT.\r\n\r\nKhoảng thời gian 20 tuổi cho đến 22 tuổi sau khi chơi ch&aacute;n những ng&agrave;y th&aacute;ng đại học tự do tự tại, nh&igrave;n c&aacute;c bạn dần thay đổi như ch&uacute; s&acirc;u bướm tho&aacute;t x&aacute;c trở n&ecirc;n rực rỡ hơn bạn lại lăn tăn kh&ocirc;ng biết n&ecirc;n đi phương n&agrave;o, tin v&agrave;o đ&acirc;u v&agrave; sống như n&agrave;o mới đ&uacute;ng. C&oacute; những người b&ecirc;n cạnh ban đang dần dần cố gắng, khẳng định vị thế của họ trong trường v&agrave; ngo&agrave;i x&atilde; hội, c&ograve;n bạn vẫn đứng đ&oacute; dậm ch&acirc;n tại chỗ kh&ocirc;ng biết tiến l&ecirc;n, rẽ tr&aacute;i hay ngoặt phải. Tư tưởng đấu tranh mạnh mẽ nhưng ch&acirc;n cuối c&ugrave;ng cũng chẳng thể bước đi được.\r\n\r\nSau khi ra trường, bước ra ngo&agrave;i cuộc sống phải tự cung tự cấp, bị hiện thực đ&aacute;nh cho nằm &uacute;p bạn mới nhận ra rằng bằng ấy thời gian qua bạn đ&atilde; sống v&ocirc; nghĩa như thế n&agrave;o. Ch&uacute;ng ta đ&atilde; qu&aacute; tự do đến mức bu&ocirc;ng thả bản th&acirc;n m&igrave;nh chẳng c&ograve;n biết m&igrave;nh n&ecirc;n l&agrave;m g&igrave;, đi đ&acirc;u, sống như thế n&agrave;o.\r\n\r\nĐ&acirc;y ch&iacute;nh l&agrave; những l&yacute; do m&agrave; bạn phải đọc cuốn s&aacute;ch &ldquo;Mọi nỗ lực v&agrave; chờ đợi của bạn đều c&oacute; &yacute; nghĩa&rdquo; của Uyển Tử Văn v&agrave; Uyển Từ H&agrave;o - Cặp anh em sinh đ&ocirc;i 9x nổi tiếng của Trung Quốc, đều l&agrave; sinh vi&ecirc;n trường top 1 cả nước: Đại học Bắc Kinh.\r\n\r\n&ldquo;Mọi Nỗ Lực V&agrave; Chờ Đợi Của Bạn Đều C&oacute; &Yacute; Nghĩa&rdquo; l&agrave; cuốn tản văn mới nhất của họ, nội dung chủ yếu viết về cuộc sống của hai anh em, từ những kh&oacute; khăn vất vả tr&ecirc;n con đường học h&agrave;nh cho tới những sẻ chia ấm &aacute;p của gia đ&igrave;nh. Từ khi thi đỗ Đại học Bắc Kinh cho đến khi tốt nghiệp ra trường, trở th&agrave;nh người lớn bước ch&acirc;n v&agrave;o x&atilde; hội. Điều tuyệt với nhất của tuổi trẻ ch&iacute;nh l&agrave; kh&ocirc;ng nản l&ograve;ng, tho&aacute;i ch&iacute;, những thứ ta bỏ ra, những sự thử nghiệm v&agrave; cả những đợi chờ, tất cả đều c&oacute; &yacute; nghĩa.\r\n\r\nMỗi một ng&agrave;y nỗ lực phấn đầu đều xứng đ&aacute;ng được ghi nhận, h&atilde;y dũng cảm sống cuộc đời như m&igrave;nh mong muốn. Bạn c&oacute; thể ngưỡng mộ &aacute;nh s&aacute;ng từ người kh&aacute;c, nhưng c&agrave;ng phải tin tưởng v&agrave;o sức mạnh của bản th&acirc;n. Nh&acirc;n l&uacute;c trong người c&ograve;n đang căng đầy tự tin v&agrave; nhiệt huyết, h&atilde;y cố gắng hết m&igrave;nh nh&eacute;.\r\n\r\n&ldquo;Mọi Nỗ Lực V&agrave; Chờ Đợi Của Bạn Đều C&oacute; &Yacute; Nghĩa&rdquo; - Cuốn s&aacute;ch chỉ đường, dẫn lối, tiếp th&ecirc;m sức mạnh cho bạn tr&ecirc;n bước đường trưởng th&agrave;nh v&agrave; th&agrave;nh c&ocirc;ng. L&agrave;m điều m&igrave;nh muốn, sống cuộc đời m&igrave;nh mong, hạnh ph&uacute;c với những g&igrave; m&igrave;nh nỗ lực đạt đc.', 'AZ Việt Nam', 'NXB Hà Nội', '2023-04-25 01:22:29'),
('LiA1eAoDIV', 'Nóng Giận Là Bản Năng, Tĩnh Lặng Là Bản Lĩnh ', 'Tống Mặc', 66000, 'https://cdn0.fahasa.com/media/catalog/product/n/o/nong-gian-la-ban-nang-1_1.jpg', 'Nóng Giận Là Bản Năng, Tĩnh Lặng Là Bản Lĩnh (Tái Bản 2020)\r\n\r\nSai lầm lớn nhất của chúng ta là đem những tật xấu, những cảm xúc tiêu cực trút bỏ lên những người xung quanh, càng là người thân càng dễ gây thương tổn.\r\n\r\nCái gì cũng nói toạc ra, cái gì cũng bộc lộ hết không phải là thẳng tính, mà là thiếu bản lĩnh. Suy cho cùng, tất cả những cảm xúc tiêu cực của con người đều là sự phẫn nộ dành cho bất lực của bản thân. Nếu bạn đúng, bạn không cần phải nổi giận. Nếu bạn sai, bạn không có tư cách nổi giận.\r\n\r\nĐem một nắm muối bỏ vào cốc nước, cốc nước trở nên mặn chát. Đem một nắm muối bỏ vào hồ nước, hồ nước vẫn ngọt lành. Lòng người cũng vậy, càng nông cạn càng dễ biến chất, càng sâu sắc càng khó lung lay. Ý nghĩa của đời người không ngoài việc tu tâm dưỡng tính, để mở lòng ra bao la như biển hồ, trước những nắm muối thị phi của cuộc đời vẫn thản nhiên không xao động.\r\n\r\n“Nóng giận là bản năng, tĩnh lặng là bản lĩnh” là từ bỏ “tam độc”, tu dưỡng một trái tim trong sáng:\r\n\r\n- Từ bỏ “tham” – bớt một phần ham muốn, thêm một phần tự do.\r\n\r\n- Từ bỏ “sân” – bớt một phần tranh chấp, thêm một phần ung dung.\r\n\r\n- Từ bỏ “si” – bớt một phần mê muội, thêm một phần tĩnh tâm.\r\n\r\nCuốn sách là tập hợp những bài học, lời tâm sự về nhân sinh, luận về cuộc đời của đại sư Hoằng Nhất – vị tài tử buông mọi trần tục để quy y cửa Phật, người được mệnh danh tinh thông kim cổ và cũng có tầm ảnh hưởng lớn trong Phật giáo.\r\n\r\nTrưởng thành, hãy để lòng rộng mở, tiến gần đến chữ “Người”, học được cách bao dung, học được cách khống chế cảm xúc. Đừng để những xúc động nhất thời như ngọn lửa, tưởng thiêu rụi được kẻ thù mà thực ra lại làm bỏng tay ta trước.', 'Skybooks', 'Thế Giới', '2023-04-25 00:53:59'),
('LMneqdUPdG', 'Chuộc Tội', 'Minato Kanae', 91000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_195509_1_11932.jpg', 'Emily, Sae, Maki, Akiko, Yuka – năm cô bé cùng nhau đến trường chơi bóng chuyền song chỉ có bốn về được nhà, với tư cách là bốn nhân chứng của một vụ giết người tàn nhẫn gây chấn động.\r\n\r\nNhưng trở về an toàn không có nghĩa là bình an sống tiếp.\r\n\r\nTrước bốn lời khai như một: “không nhớ rõ mặt hung thủ”, người mẹ mất con gái đã phẫn nộ gieo vào đầu bốn cô bé lời nguyền rủa độc địa – bước ngoặt lái bốn số phận mới chớm thành hình vào bốn ngã rẽ bất hạnh khác nhau:\r\n\r\n“Hãy tìm ra hung thủ trước khi quá thời hạn khởi tố. Nếu không làm được điều đó, hãy tìm ra cách chuộc tội khiến tao có thể chấp nhận. Còn nếu không làm được một trong hai điều trên tao sẽ trả thù chúng mày.”\r\n\r\n“Căng thẳng từng chữ, đồng thời cũng lay động từng dòng… Minato đã khéo léo viết nên một câu chuyện ớn lạnh của bi kịch, tội lỗi và sự chuộc tội.” - Publishers Weekly', 'Nhã Nam', 'NXB Hà Nội', '2023-04-25 00:42:58'),
('lqPEDOhEbC', 'Giải Nghiệm Cuộc Đời', 'Scott Galloway', 127000, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935278606321.jpg', 'Giải Nghiệm Cuộc Đời\r\n\r\nLàm sao để đạt được thành công? Làm cách nào để dung hòa giữa tham vọng và việc phát triển bản thân phù hợp với năng lực và đam mê?\r\n\r\nBạn có bao giờ hỏi bản thân nên làm gì ngay bây giờ để không phải hối tiếc khi bước qua tuổi 40, 50 hoặc 80?\r\n\r\nTrên hành trình trưởng thành, nếu bạn gặp căng thẳng hay thậm chí u sầu, hãy ý thức rằng đây là một giai đoạn rất bình thường và tiếp tục tiến về phía trước. Hạnh phúc đang chờ bạn.\r\n\r\nTác giả Scott Galloway trong cuốn sách “Giải nghiệm cuộc đời” đã chia sẻ quan sát của mình với tư cách một doanh nhân thành công với nhiều công ty khởi nghiệp, một nhà nghiên cứu, một người chồng, người cha cũng như một đứa con, và còn là một người đàn ông Mỹ. Ông đã tổ chức buổi học cuối khóa kéo dài ba giờ với tên gọi “Phương trình Hạnh phúc”. Xuyên suốt buổi học, ông đã bóc tách các khái niệm thành công, tình yêu và một cuộc đời đáng sống.\r\n\r\nGiải nghiệm cuộc đời gồm 4 phần:\r\n\r\nPhần đầu tiên phác thảo những phương trình cơ bản mà tác giả và sinh viên của ông cùng nhau ôn lại vào mỗi mùa xuân: Nếu bí quyết tạo ra hạnh phúc được quy về một số lượng phương trình hữu hạn, chúng sẽ là gì?\r\n\r\nPhần thứ hai khai thác sâu vào những gì Scot Galloway đã học được về thành công, hoài bão, sự nghiệp và tiền bạc từ kinh nghiệm bản thân với tư cách là nhân viên ngân hàng đầu tư, doanh nhân, giáo sư trường kinh doanh, đồng thời là tiếng nói chỉ ra tác động của các ông trùm công nghệ lên nền kinh tế và xã hội.\r\n\r\nPhần ba – tình yêu và các mối quan hệ – lại sâu sắc hơn cả. Những người trẻ, đặc biệt là cánh đàn ông, đang phải vật lộn chắp vá những thông điệp hỗn loạn về cách gìn giữ các mối quan hệ và chinh phục thành công, để từ đó giành cho mình một chỗ đứng trong xã hội tư bản.\r\n\r\nPhần thứ tư, cũng là phần cuối cùng, thách thức người đọc đối diện và giải quyết những vấn đề của bản thân, bao gồm việc nuôi dưỡng và chăm sóc sức khỏe thể chất, mặt tối của tâm hồn, cũng như những ngày cuối cùng nơi trần thế.', 'Saigon Books', 'NXB Thanh Niên', '2023-04-25 00:50:27'),
('mb76HK12nb', 'Muôn Kiếp Nhân Sinh - Many Times, Many Lives - Tập 2', 'Nguyên Phong', 201000, 'https://cdn0.fahasa.com/media/catalog/product/m/u/muonkiepnhansinh2_bia-01.jpg', 'Hiếm có cuốn sách nào ngay từ khi ra mắt đã tạo nên hiện tượng văn hóa đọc và sau nửa năm đã trở thành cuốn sách bán chạy nhất năm 2020 tại Việt Nam như Muôn Kiếp Nhân Sinh. Cơn sốt của cuốn sách này tiếp tục được dấy lên vào dịp Tết Nguyên Đán 2021 khi công ty First News Trí Việt hé lộ đang “ngày đêm thực hiện Muôn Kiếp Nhân Sinh tập 2”. Đáp lại sự mong đợi của độc giả suốt hơn ba tháng, Muôn Kiếp Nhân Sinh tập 2 đã chính thức phát hành trên cả nước.\r\n\r\nMuôn Kiếp Nhân Sinh tập 2 của tác giả Nguyên Phong tiếp tục là những câu chuyện tiền kiếp, nhân quả luân hồi hấp dẫn gắn liền với những kiến giải uyên bác về quá khứ, hiện tại, tương lai của nhân loại và thế giới thông qua góc nhìn của cả khoa học và tâm linh. Chúng ta là ai, chúng ta đến từ đâu và sẽ đi về đâu? Làm cách nào để chữa lành thế giới này, hành tinh này trước những biến cố lớn đang và sẽ diễn ra trong tương lai gần?\r\n\r\nTrong tập 2, hành trình thức tỉnh tâm linh của nhân vật Thomas sẽ dẫn dắt người đọc đến với những tầng cõi năng lượng gắn chặt với thế giới hữu hình của con người thông qua sự vận động của nghiệp lực, nhân quả, để giải thích về thứ năng lượng huyền bí luôn biến chuyển trong chu kỳ bất tận của vũ trụ. Không chỉ là trải nghiệm ly kỳ tại tiền kiếp cổ đại ở Assiria, Hy Lạp, độc giả còn được khám phá hành trình của linh hồn đến những tầng cõi – trạm trung chuyển khác nhau, được xâu chuỗi lại bằng cả triết học, tín ngưỡng, lịch sử và khoa học thế giới từ Đông sang Tây, từ cổ đại đến hiện đại. Năng lượng không tự nhiên sinh ra và cũng không tự nhiên mất đi, sự sống này cũng vậy, khởi đầu và kết thúc - tuy hai mà một, và chỉ có nhân quả sẽ luôn tuần hoàn vận hành bất tận.\r\n\r\nMuôn Kiếp Nhân Sinh tập 2 lý giải rằng vạn vật trên thế giới như cỏ cây, hoa lá, côn trùng, thú vật và con người tuy bề ngoài có vẻ khác biệt nhưng tinh hoa bên trong vẫn là một, đó là sự sống hay năng lượng khởi thủy uyên nguyên. Tinh hoa đó như là “Nước”, còn vạn vật chỉ là “Sóng”. Sóng thì muôn trùng, sóng lớn, sóng nhỏ… nhưng chúng vẫn chỉ là nước. Nếu chân ngã là đại dương thì bản ngã chỉ là nước chứa đựng trong cái chén. Vấn đề là làm sao phá vỡ cái chén để nước trở về với đại dương và đó cũng chính là hành trình của Thomas từ một doanh nhân với niềm tin duy vật hoàn toàn đã từng bước giác ngộ, thức tỉnh để khám phá về luân hồi, nhân quả, chuyển hóa nghiệp quả, chuyển đổi tâm thức và gửi gắm những thông điệp nhân sinh tốt đẹp đến độc giả.\r\n\r\nTác giả Nguyên Phong chia sẻ trong cuốn sách rằng, mọi việc dù lớn hay nhỏ cũng sẽ góp phần ảnh hưởng trực tiếp đến cuộc sống hiện tại hoặc trong tương lai, thậm chí đến tận kiếp sau. Đó là những nghiệp báo ở kiếp trước cộng nghiệp với hành vi của chúng ta trong kiếp này để hình thành nên bánh xe nghiệp lực khổng lồ phức tạp nhưng lại không bỏ sót bất cứ ai, bất cứ quốc gia nào và không bỏ sót bất cứ hành vi hay tư tưởng nào. Bởi lẽ, chúng ta đều là phần tử của một sự sống vĩ đại thuộc về một năng lượng khởi thuỷ, được sắp đặt khéo léo bởi những định luật trong vũ trụ.\r\n\r\nBằng những câu chuyện thú vị, sâu sắc, liên kết với nhau một cách chặt chẽ, Muôn Kiếp Nhân Sinh tập 2 sẽ đưa độc giả vào cuộc hành trình trên chuyến tàu thời gian từ phố Wall nước Mỹ đến những cuộc chinh phạt của Alexander Đại Đế, bí ẩn lăng mộ Tần Thủy Hoàng, những cõi giới linh hồn, rồi quay ngược lại cuộc sống đời thường mà bất cứ ai cũng thấy có mình ở trong đó. Để cùng suy ngẫm về những giá trị đẹp đẽ của đời sống. Để nắm lấy những thời khắc còn lại của cuộc đời mình và xoay chuyển số phận, nghiệp lực theo chiều hướng tỉnh thức và nhân ái. Và trên hết, đó là cùng chung tay xây dựng một thế giới tốt đẹp trên nền tảng của yêu thương và chữa lành.\r\n\r\n“Nếu chúng ta có thể cùng nhauchuyển hoá những tư tưởng thù hận, tham lam, íchkỷ bằng những tư tưởng cao thượng, tốt lành; chuyểnhóa các hành động hận thù, giết chóc bằng tình thươngyêu rộng lớn thì mọi việc có thể thay đổi” – trích Muôn Kiếp Nhân Sinh tập 2.\r\n\r\nVỀ TÁC GIẢ:\r\n\r\n \r\n\r\nTác giả Nguyên Phong (Vũ Văn Du) du học ở Mỹ từ năm 1968, tốt nghiệp cao học Sinh vật học, Điện toán. Ông từng là Kỹ sư trưởng của Tập đoàn Boeing của Mỹ, Viện trưởng Viện Công nghệ Sinh học Đại học Carnegie Mellon. Ông được mọi người biết tới là Giáo sư John Vu – Nhà khoa học uy tín về công nghệ thông tin. , CMMI và từng giảng dạy ở nhiều trường đại học trên thế giới.\r\n\r\nNguyên Phong là bút danh của bộ sách văn hóa tâm linh được dịch, viết phóng tác từ trải nghiệm, tiềm thức và quá trình nghiên cứu, khám phá các giá trị tinh thần Đông phương. Ông đã viết phóng tác tác phẩm bất hủ Hành trình về Phương Đông năm 24 tuổi (1974). Các tác phẩm khác của Nguyên Phong được bạn đọc nhiều thế hệ yêu thích: Ngọc sáng trong hoa sen, Bên rặng tuyết sơn, Hoa sen trên tuyết, Hoa trôi trên sóng nước, Huyền thuật và các đạo sĩ Tây Tạng, Trở về từ xứ tuyết, Trở về từ cõi sáng, Minh triết trong đời sống, Đường mây qua xứ tuyết, Dấu chân trên cát, Đường mây trong cõi mộng, Đường mây trên đất hoa… và bộ sách dành cho sinh viên, thầy cô: Khởi hành, Kết nối, Bước ra thế giới, Kiến tạo thế hệ Việt Nam ưu việt, GS John Vu và lời khuyên dành cho thầy cô, GS John Vu và lời khuyên dành cho các bậc cha mẹ.', 'FIRST NEWS', 'NXB Tổng Hợp TPHCM', '2023-04-25 00:15:16'),
('mLGiYbf6Ar', 'Vòng Đu Quay Đêm', 'Minato Kanae', 109000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_195509_1_37548.jpg', '“Sự thật chỉ có một. Đối tượng để khóc thương, đối tượng để chỉ trích, đối tượng để an ủi, tất cả đều là gia đình, chỉ vậy mà thôi.” Khu dân cư cao cấp Hibarigaoka rúng động bởi vụ án mạng xảy ra trong một gia đình hạnh phúc như tranh vẽ. Cha là nạn nhân, mẹ là nghi phạm, ba đứa trẻ nhà Takahashi đột ngột đối diện với một tương lai tàn khốc mà chúng chưa từng tưởng tượng ra. Trong quá trình bóc tách từng lớp bóng tối hòng tìm ra sự thật, chúng nhận ra những uẩn khúc của vụ án dần được phơi bày từ câu chuyện của chính những người sống ở nhà đối diện. Cuốn tiểu thuyết gây sốc lấy đề tài “gia đình” đến từ Minato Kanae – tác giả của kiệt tác “Thú tội”.\r\n\r\nGIỚI THIỆU TÁC GIẢ:\r\n\r\nMinato Kanae là tiểu thuyết gia Nhật Bản, sinh năm 1973 tại Hiroshima. Năm 2007, với truyện ngắn Kẻ truyền đạo bà đã giành được giải Tác giả tiểu thuyết trinh thám mới lần thứ 29. Năm 2008 bà viết tác phẩm đầu tay Thú tội với chương đầu chính là truyện ngắn Kẻ truyền đạo, đứng vị trí thứ nhất trong Bảng xếp hạng top 10 truyện trinh thám năm 2008 của tuần báo Bunshun, sau đó giành được giải Booksellers’ Award lần thứ 6. Đến năm 2014, Thú tội lại được tờ Wall Street Journal của Mỹ chọn làm một trong 10 tác phẩm trinh thám xuất sắc nhất. Năm 2012, truyện ngắn Sao trên mặt biển trong tập truyện Vọng hương của bà đã nhận giải Hiệp hội tác giả truyện trinh thám Nhật Bản lần thứ 65 trong hạng mục truyện ngắn. Năm 2016, tiểu thuyết Utopia của bà nhận giải Yamamoto Shugoro lần thứ 29. Những tác phẩm khác của Minato Kanae đã được Nhã Nam xuất bản: - Thú tội - Chuộc tội - Tất cả vì N', 'Nhã Nam', 'NXB Hà Nội', '2023-04-25 00:40:54'),
('mo3qoGTYSh', 'Tâm Trí Tội Phạm', 'Same Kaean', 216000, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8936066694346_-_copy.jpg', '&ldquo;Nếu ch&uacute;ng ta đ&aacute;nh đổi đạo đức để lấy những tiến bộ khoa học, những kết quả đạt chỉ l&agrave; tội &aacute;c.&rdquo;\r\n\r\nTrong cuốn s&aacute;ch n&agrave;y, t&aacute;c giả Sam Kean sẽ dẫn lối người đọc một c&aacute;ch th&agrave;nh thạo hệt như một hướng dẫn vi&ecirc;n du lịch l&acirc;u năm trải qua chuyến h&agrave;nh tr&igrave;nh suốt hai ngh&igrave;n năm lịch sử, bắt đầu từ những h&agrave;nh động đen tối của Cleopatra ở Ai Cập cổ đại.\r\n\r\nCuốn s&aacute;ch tiết lộ nguồn gốc của phần lớn khoa học hiện đại về hoạt động bu&ocirc;n b&aacute;n n&ocirc; lệ xuy&ecirc;n Đại T&acirc;y Dương v&agrave;o những năm 1700, cũng như vai tr&ograve; của Thomas Edison với sự ra đời của ghế điện v&agrave; logic sai lệch của những điệp vi&ecirc;n đ&atilde; th&acirc;m nhập v&agrave;o Dự &aacute;n Manhattan. Nhưng những tội lỗi của khoa học kh&ocirc;ng phải tất cả đều được ch&ocirc;n v&ugrave;i an to&agrave;n trong qu&aacute; khứ. Kean nhắc nhở ch&uacute;ng ta rằng nhiều trong số đ&oacute; vẫn c&ograve;n ảnh hưởng đến ch&uacute;ng ta ng&agrave;y nay.\r\n\r\nCh&uacute;ng ta c&oacute; thể nối một đường thẳng từ sự lạm dụng y tế của Tuskegee v&agrave; Đức Quốc x&atilde; đến vắc-xin hiện tại, kết nối bệnh u &aacute;c t&iacute;nh từ những năm 1950 với những thất bại đương thời của việc chăm s&oacute;c sức khỏe t&acirc;m thần. Kean thậm ch&iacute; c&ograve;n đưa ch&uacute;ng ta v&agrave;o tương lai, khi m&aacute;y t&iacute;nh ti&ecirc;n tiến v&agrave; kỹ thuật di truyền c&oacute; thể mở ra những c&aacute;ch ho&agrave;n to&agrave;n mới trong sai phạm.\r\n\r\nKh&ocirc;ng nao n&uacute;ng v&agrave; phấn kh&iacute;ch đến trang cuối c&ugrave;ng, &ldquo;T&acirc;m tr&iacute; tội phạm&rdquo; l&agrave; sự kết hợp bộ phim kh&aacute;m ph&aacute; khoa học với sự hồi hộp bất hợp ph&aacute;p của những c&acirc;u chuyện tội phạm c&oacute; thật. Với sự th&ocirc;ng minh v&agrave; ch&iacute;nh x&aacute;c đặc trưng của m&igrave;nh, Kean cho thấy rằng, trong khi khoa học c&oacute; &iacute;ch hơn l&agrave; hại tr&ecirc;n thế giới, th&igrave; c&aacute;c nh&agrave; khoa học giả mạo vẫn tồn tại, v&agrave; khi hy sinh đạo đức v&igrave; sự tiến bộ, ch&uacute;ng ta thường sẽ kh&ocirc;ng bao giờ đạt được kết quả.\r\n\r\nTh&ocirc;ng tin t&aacute;c giả\r\n\r\nSam Kean l&agrave; t&aacute;c giả b&aacute;n chạy nhất theo b&igrave;nh chọn của tờ New York Times với rất nhiều t&aacute;c phẩm nổi bật. Hai cuốn s&aacute;ch đầu ti&ecirc;n của anh, The Disappearing Spoon v&agrave; The Violinist&#039;s Thumb được vinh danh l&agrave; &ldquo;Top 5&rdquo; s&aacute;ch khoa học của năm. Trong đ&oacute; The Disappearing Spoon được Hiệp hội Ho&agrave;ng gia đề cử l&agrave; một trong những cuốn s&aacute;ch khoa học h&agrave;ng đầu của năm 2010, c&ograve;n The Violinist&#039;s Thumb lọt v&agrave;o v&ograve;ng chung kết cho giải thưởng văn học khoa học của PEN.\r\n\r\nLời khen tặng\r\n\r\n &ldquo;Th&uacute; vị v&agrave; nghẹt thở&hellip; Sam Kean đ&atilde; dẫn lối người đọc v&agrave;o một chuyến tham quan lịch sử hấp dẫn - v&agrave; c&oacute; phần kinh ho&agrave;ng - về những tội &aacute;c đội lốt khoa học&hellip; Dưới ng&ograve;i b&uacute;t tinh tế c&ugrave;ng kiến thức uy&ecirc;n th&acirc;m, c&aacute;c trang s&aacute;ch như b&ugrave;ng nổ với v&ocirc; số th&ocirc;ng tin v&agrave; chi tiết hấp dẫn, được kết hợp h&agrave;i h&ograve;a với lối kể chuyện đi&ecirc;u luyện.&rdquo;\r\n\r\n- Lucinda Robb, Washington Post.', '1980 Books', 'Dân Trí', '2023-04-25 01:06:23'),
('moSFzGzTYX', 'Tuổi Trẻ Sống An Nhiên Nhưng Đừng An Phận', 'Sten Morgan', 84000, 'https://cdn0.fahasa.com/media/catalog/product/t/u/tuoi_tre_song_an_nhien_nhung_dung_an_phan_1_2020_10_24_16_15_44.jpg', 'Trong những gi&acirc;y ph&uacute;t th&agrave;nh thật nhất với bản th&acirc;n, c&oacute; bao giờ bạn cảm thấy lo lắng, sợ rằng nếu cuộc đời m&igrave;nh cứ tiếp diễn như hiện tại, th&igrave; năm sau, rồi năm sau nữa, bạn sẽ vẫn chỉ giậm ch&acirc;n tại chỗ? Trước khi bạn kịp nhận ra, quỹ thời gian của bạn đ&atilde; cạn kiệt m&agrave; bạn vẫn chỉ ở nguy&ecirc;n vị tr&iacute; cũ, bị mắc kẹt v&agrave; tr&agrave;n đầy hối hận v&igrave; đ&atilde; kh&ocirc;ng chịu h&agrave;nh động khi c&ograve;n c&oacute; thể.\r\n\r\nTuy vậy, kh&ocirc;ng mấy người tr&ecirc;n đời lại quyết t&acirc;m thay đổi để đạt được những th&agrave;nh tựu trọn vẹn trong c&ocirc;ng việc cũng như cuộc sống c&aacute; nh&acirc;n. Sợ h&atilde;i những xung đột, c&aacute;c rủi ro v&agrave; cảm gi&aacute;c phiền muộn, ch&uacute;ng ta thường c&oacute; xu hướng ở y&ecirc;n trong v&ugrave;ng an to&agrave;n, cố hết sức bảo vệ ch&iacute;nh m&igrave;nh v&agrave; tự thỏa m&atilde;n với nhịp sống đều đặn kh&ocirc;ng biến cố.\r\n\r\nĐể ph&aacute; vỡ t&igrave;nh trạng đ&igrave;nh trệ, th&ocirc;i th&uacute;c c&aacute;c c&aacute; nh&acirc;n khai ph&aacute; tiềm năng của bản th&acirc;n v&agrave; nắm lấy những cơ hội vươn tới th&agrave;nh c&ocirc;ng đ&iacute;ch thực v&agrave; &yacute; nghĩa, cuốn s&aacute;ch &ldquo;Tuổi trẻ - Sống an nhi&ecirc;n nhưng đừng an phận&rdquo; của Sten Morgan đ&atilde; cung cấp một hướng đi r&otilde; r&agrave;ng v&agrave; c&oacute; t&iacute;nh ứng dụng cao cho mọi người đọc. Bất kể bạn bao nhi&ecirc;u tuổi, đến từ đ&acirc;u, v&agrave; đang ở giai đoạn n&agrave;o trong sự nghiệp, h&atilde;y nhớ rằng bạn lu&ocirc;n c&oacute; thể thay đổi - v&agrave; sự thay đổi bắt đầu từ ch&iacute;nh tư duy của bạn, cũng như những quan niệm m&agrave; bạn sở hữu về thế giới xung quanh m&igrave;nh.\r\n\r\n&ldquo;Tuổi trẻ - Sống an nhi&ecirc;n nhưng đừng an phận&rdquo; tr&igrave;nh b&agrave;y bảy quan niệm t&acirc;m tr&iacute; dẫn lối tới th&agrave;nh c&ocirc;ng. Kết hợp lại, c&aacute;c quan niệm tạo th&agrave;nh c&ocirc;ng cụ định hướng đ&aacute;ng tin cậy nhất, để bạn c&oacute; thể ngồi l&ecirc;n chiếc xe ph&aacute;t triển bản th&acirc;n v&agrave; chủ động nhấn ga, chinh phục h&agrave;nh tr&igrave;nh tiến tới mục ti&ecirc;u đỉnh cao. Trong cuốn s&aacute;ch n&agrave;y, người đọc kh&ocirc;ng chỉ kh&aacute;m ph&aacute; những c&acirc;u chuyện về c&aacute;ch tận dụng xung đột, tiếp thu quan điểm mới, gỡ bỏ lớp ph&ograve;ng hộ, hướng tới bản thể tương lai l&yacute; tưởng&hellip;, m&agrave; c&ograve;n thấu hiểu được động lực ẩn s&acirc;u để ki&ecirc;n tr&igrave; theo đuổi đến c&ugrave;ng, biến &yacute; tưởng gian kh&oacute; trở th&agrave;nh hiện thực.\r\n\r\nTrước khi đọc s&aacute;ch, h&atilde;y trả lời c&acirc;u hỏi: Liệu bạn c&oacute; sẵn s&agrave;ng l&agrave;m mọi điều c&oacute; thể để biến những tiềm năng th&agrave;nh kết quả thực tiễn hay kh&ocirc;ng? Nếu c&acirc;u trả lời l&agrave; c&oacute;, th&igrave; cuốn s&aacute;ch n&agrave;y chắc chắn được viết để d&agrave;nh cho bạn!', '1980 Books', 'NXB Lao Động', '2023-04-25 01:08:50'),
('mp2Rjm8h6s', 'Cây Cam Ngọt Của Tôi', 'José Mauro de Vasconcelos', 81000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_217480.jpg', 'Vị chua chát của cái nghèo hòa trộn với vị ngọt ngào khi khám phá ra những điều khiến cuộc đời này đáng sống... một tác phẩm kinh điển của Brazil.” - Booklist\r\n\r\n“Một cách nhìn cuộc sống gần như hoàn chỉnh từ con mắt trẻ thơ… có sức mạnh sưởi ấm và làm tan nát cõi lòng, dù người đọc ở lứa tuổi nào.” - The National\r\n\r\nHãy làm quen với Zezé, cậu bé tinh nghịch siêu hạng đồng thời cũng đáng yêu bậc nhất, với ước mơ lớn lên trở thành nhà thơ cổ thắt nơ bướm. Chẳng phải ai cũng công nhận khoản “đáng yêu” kia đâu nhé. Bởi vì, ở cái xóm ngoại ô nghèo ấy, nỗi khắc khổ bủa vây đã che mờ mắt người ta trước trái tim thiện lương cùng trí tưởng tượng tuyệt vời của cậu bé con năm tuổi.\r\n\r\nCó hề gì đâu bao nhiêu là hắt hủi, đánh mắng, vì Zezé đã có một người bạn đặc biệt để trút nỗi lòng: cây cam ngọt nơi vườn sau. Và cả một người bạn nữa, bằng xương bằng thịt, một ngày kia xuất hiện, cho cậu bé nhạy cảm khôn sớm biết thế nào là trìu mến, thế nào là nỗi đau, và mãi mãi thay đổi cuộc đời cậu.\r\n\r\nMở đầu bằng những thanh âm trong sáng và kết thúc lắng lại trong những nốt trầm hoài niệm, Cây cam ngọt của tôi khiến ta nhận ra vẻ đẹp thực sự của cuộc sống đến từ những điều giản dị như bông hoa trắng của cái cây sau nhà, và rằng cuộc đời thật khốn khổ nếu thiếu đi lòng yêu thương và niềm trắc ẩn. Cuốn sách kinh điển này bởi thế không ngừng khiến trái tim người đọc khắp thế giới thổn thức, kể từ khi ra mắt lần đầu năm 1968 tại Brazil.\r\n\r\nTÁC GIẢ:\r\n\r\nJOSÉ MAURO DE VASCONCELOS (1920-1984) là nhà văn người Brazil. Sinh ra trong một gia đình nghèo ở ngoại ô Rio de Janeiro, lớn lên ông phải làm đủ nghề để kiếm sống. Nhưng với tài kể chuyện thiên bẩm, trí nhớ phi thường, trí tưởng tượng tuyệt vời cùng vốn sống phong phú, José cảm thấy trong mình thôi thúc phải trở thành nhà văn nên đã bắt đầu sáng tác năm 22 tuổi. Tác phẩm nổi tiếng nhất của ông là tiểu thuyết mang màu sắc tự truyện Cây cam ngọt của tôi. Cuốn sách được đưa vào chương trình tiểu học của Brazil, được bán bản quyền cho hai mươi quốc gia và chuyển thể thành phim điện ảnh. Ngoài ra, José còn rất thành công trong vai trò diễn viên điện ảnh và biên kịch.', 'Nhã Nam', 'NXB Hội Nhà Văn', '2023-04-25 00:47:50'),
('mpWPkh0oP9', 'Từ Giận Dữ Đến Bình An', 'Mike George', 83000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_220495.jpg', 'Cuộc sống mang đến cho con người nhiều trạng thái cảm xúc khác nhau, có cả vui buồn, hạnh phúc, bình an và tức giậ Tất cả những điều này không những đang diễn ra hết sức đa dạng mà còn rất nhanh chóng. Thế giới nội tâm của con người như một bánh xe không ngừng luân chuyển, và ai cũng mong tìm kiếm cho mình một chốn bình an, thanh thản, được sống trong hạnh phúc giữa sự xô bồ của cuộc đời.\r\n\r\nVà có lẽ từ trước đến nay, chưa bao giờ sự tức giận, phẫn uất và bất an lại chất chứa trong trái tim và tâm trí con người nhiều đến như vậy. Cúng ta đã bị quá tải trước những tin tức, câu chuyện, hình ảnh về khủng bố, về những cơn thịnh nộ và cả về sự trả thù từ các phương tiện thông tin đại chúng. Ngành công nghiệp giải trí cũng mang đến cho chúng ta vô số những câu chuyện, bộ phim đề cập đến hàng động trả thù, căm phẫn và oán giận. Gần như giận dữ, thù địch đã trở thành một đề tài để kinh doanh. Những xung đột trong cuộc sống hàng ngày và những hành vi ứng xử thô lỗ đang dần làm suy yếu đi nền tảng của mối quan hệ cộng đồng tốt đẹp.\r\n\r\nNhưng đó chỉ mới là cái nhìn tổng thể trên bề mặt vấn đề và như thế thì chưa thể nói lên được tất cả cho đến khi những chi tiết nhỏ trở nên sáng tỏ. Đó chính là Bạn và cảm xúc của bạn. Bạn có bao giờ tức giận không? Bạn tức giận như thế nào? Và đã khi nào trong lòng bạn trở nên \"sôi sục\" vì một sự oán giận nào đó chưa? Hẳn bạn sẽ trả lời ngay rằng: \"Hàng ngày, ai mà chẳng có lúc bực tức!\", nhưng thực ra đó cũng chỉ là một câu trả lời có tính chất chung chung, không làm cho bạn hiểu được nguồn gốc cơn giận của mình.\r\n\r\nQuyển sách này dựa trên quan niệm về tâm linh như là một lời giải đáp cả ý thức và vô thức để hiểu nguyên nhân và con đường giải thoát khỏi sự tức giận, hướng chúng ta đến cuộc sống bình an và vị tha.\r\n', 'FIRST NEWS', 'NXB Thanh Niên', '2023-04-25 00:54:41'),
('MSsz05DldR', 'Tủ Sách Học Làm Người – Thuật Gây Ảnh Hưởng Hay', 'Hoàng Xuân Việt', 75000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_182655.jpg', 'Cuốn sách gồm 2 phần:\r\n\r\n- Đặt vấn đề truyền bá tư tưởng, tuyên truyền.\r\n\r\n- Truyền bá tư tưởng hay là tuyên truyền như thế nào?\r\n\r\nMỗi phần tác giả lý giải vai trò, nguyên nhân tại sao và cách làm như thế nào để có thể truyền bá được cái mình muốn truyền đạt một cách hiệu quả. Cuốn sách này gửi đến bạn những nghiên cứu tâm lý cùng kỹ thuật tuyên truyền để giúp giải đáp các hỏi trên.\r\n\r\nCuốn sách nằm trong Tủ sách Học làm người của học giả Hoàng Xuân Việt. Ông từng đảm nhiệm vị trí hiệu trưởng của Trường Nhân Xã Học Làm người (1966-1975), Trường Hán Nôm Học Làm Người Nguyễn Trãi (1993-2001), và là tác giả của hơn 300 đầu sách trong nhiều lĩnh vực, nổi bật là sách học làm người.\r\n\r\nTrích dẫn:\r\n\r\n- Trước hết xin bạn hãy là tín đồ của thần chân lý. Phải phục vụ chân lý thì cuộc đời mới thực có chân giá trị. Tuyên truyền dù tinh xảo đến đâu mà bạn phổ biến tà thuyết thì công lao của bạn rút cục là bia cho đời nguyền rủa. Nói chân lý là nói những lý ít ra không bị lung lay dưới sự kiểm soát của triết học và khoa học. Không có gì khốn đốn cho một đời người bằng hy sinh phục vụ trọn kiếp cho một tà thuyết. Cũng có gì đáng nguyền rủa bằng dùng tà thuyết mà mê hoặc hạng ngu dốt, xô đẩy thế hệ này qua thế hệ kia vào vòng mê hoặc. Đời này đã lỗ đời sau không có gì bảo đảm vốn lời.\r\n\r\n- Con đường của những danh nhân đi là con đường chân lý hay ít ra họ cho là chân lý. Các ý tưởng của họ được nối kết lại thành hệ thống mà người ta gọi là ý thức hệ. Con đường của họ chắc cũng là con đường bạn nhắm vì có thể bạn không nuôi cao vọng làm vĩ nhân nhưng bạn vẫn ước mong truyền bá những tư tưởng của mình. Ai trong chúng ta mà từ lúc trên ghế trung đại học không có lúc ngồi nghĩ khi ra trường sẽ hoạt động văn hóa, xã hội, kinh tế, chính trị hay cứu nhân độ thế cách nào đó. Mộng đó là một trong muôn ngàn mộng đẹp của tuổi xuân. Còn nói cho khi ra trường mà ta gặp ngay một cơ hội nào đó để hoạt động. Nói hoạt động là nói truyền bá ý tưởng bằng ngôn từ, cử chỉ, hành vi.\r\n\r\n- Cuộc đời của bạn sẽ là một cuộc đời có thực giá nếu bạn theo một chính thuyết, phục vụ một chân giáo, trọn đời theo đuổi khoa học, phụng sự nhân loại. Vũ trụ và con người cho đến bây giờ còn là cái rừng u minh. Các chân lý chưa được khám phá bao nhiêu. Bạn hãy tiếp tay với các danh nhân tiền bối vào công việc khai trí, khai tâm nhân loại. Đời còn thiếu những thế hệ trẻ giàu nhiệt huyết phục vụ lý tưởng ấy. Khi nắm được chân lý, bạn đừng để chân lý khô khan. Nếu vàng người ta làm thành dây tay, kính, lắc để hấp dẫn sự trang sức thì chân lý cũng cần được chuyển thành những ý lực trong một hệ thống.', 'Alpha Books', 'NXB Thanh Niên', '2023-04-25 00:30:16'),
('mXs3s1MmMp', 'Hạnh Phúc Đến Từ Sự Biến Mất', 'Ajahn Brahm', 84000, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935086855676.jpg', 'L&agrave; sư trụ tr&igrave; của tu viện Bodhiyana (Gi&aacute;c Thừa) v&agrave; l&agrave; Gi&aacute;m đốc t&acirc;m linh của Hiệp hội Phật gi&aacute;o T&acirc;y &Uacute;c, Ajahn Bram kh&ocirc;ng chỉ l&agrave; một vị cao tăng, người thầy dẫn dắt t&acirc;m linh của h&agrave;ng ng&agrave;n m&ocirc;n đệ, &ocirc;ng c&ograve;n l&agrave; t&aacute;c giả của rất nhiều đầu s&aacute;ch về Phật gi&aacute;o v&agrave; thiền định c&oacute; ảnh hưởng đến Phật tử khắp nơi tr&ecirc;n thế giới.\r\n\r\nTrong &ldquo;Hạnh ph&uacute;c đến từ sự biến mất&rdquo;, tu sỹ Ajahn Brahm chia sẻ những trải nghiệm v&agrave; suy nghĩ của m&igrave;nh về con đường đi t&igrave;m hạnh ph&uacute;c vĩnh hằng. Đ&oacute; ch&iacute;nh l&agrave; con đường ch&aacute;nh niệm, diệt trừ c&aacute;i t&ocirc;i, trở n&ecirc;n &ldquo;v&ocirc; ng&atilde;&rdquo; của Đức Phật từ ng&agrave;n xưa.\r\n\r\nĐối diện với những biến cố trong cuộc sống, với v&ograve;ng sinh tử lu&acirc;n hồi, ta trở n&ecirc;n lo lắng, sợ h&atilde;i, bất an, đau khổ trước những sự thay đổi.  Nhưng ta c&oacute; tự hỏi, v&igrave; cớ n&agrave;o m&agrave; m&igrave;nh trở n&ecirc;n đau khổ, sợ h&atilde;i?\r\n\r\nĐ&oacute; l&agrave; v&igrave; ta thường nghĩ đến m&igrave;nh v&agrave; sợ mất đi những thứ &ldquo;của m&igrave;nh&rdquo;. Đối với Ajahn Brahm, ch&igrave;a kh&oacute;a để mở c&aacute;nh cửa hạnh ph&uacute;c vĩnh hằng v&agrave; b&igrave;nh an ch&iacute;nh l&agrave; bu&ocirc;ng bỏ. Biến mất l&agrave; dứt bỏ tất cả mọi thứ, kh&ocirc;ng chuốc th&ecirc;m vật chất hay gi&agrave;nh giật địa vị. Khi ta dứt bỏ c&aacute;i g&igrave; đ&oacute;, dứt bỏ thật sự th&igrave; n&oacute; biến mất. Đ&oacute; l&agrave; sự biến mất từ trong ra ngo&agrave;i, sự biến mất của c&aacute;i t&ocirc;i &ndash; bản ng&atilde;. Dứt bỏ c&aacute;i t&ocirc;i th&igrave; ta dứt bỏ được tất cả ph&ugrave; phiếm, ảo ảnh b&ecirc;n ngo&agrave;i; dứt bỏ được những nỗi đau, lo lắng thua thiệt đi k&egrave;m n&oacute;. Từ đ&oacute;, ta t&igrave;m được b&igrave;nh y&ecirc;n thực sự, t&igrave;m được niềm hạnh ph&uacute;c bất diệt.\r\n\r\nĐiểm đặc biệt của cuốn s&aacute;ch &ldquo;Hạnh ph&uacute;c đến từ sự biến mất&rdquo; l&agrave; Ajahn Brahm kh&ocirc;ng chủ t&acirc;m đem đến một quyển cẩm nang hay s&aacute;ch hướng dẫn h&agrave;nh thiền theo từng bước. Thay v&agrave;o đ&oacute;, Ajahn Brahm chỉ đơn giản đưa ra những lời chia sẻ, những m&ocirc; tả về con đường quay trở về bản chất &ldquo;v&ocirc; ng&atilde;&rdquo; của một người. Đ&acirc;y l&agrave; những lời chia sẻ minh triết, đi thẳng v&agrave;o trọng t&acirc;m, th&aacute;o gỡ những thắc mắc, băn khoăn của c&aacute;c thiền sinh, tu sỹ. \r\n\r\nS&aacute;ch bao gồm 11 chương, mỗi chương chia sẻ về c&aacute;c vấn đề, trải nghiệm m&agrave; thiền sinh đối mặt trong h&agrave;nh tr&igrave;nh đạt được gi&aacute;c ngộ. Những chia sẻ n&agrave;y kh&ocirc;ng chỉ gi&uacute;p c&aacute;c thiền sinh vượt qua những trở ngại trong qu&aacute; tr&igrave;nh tu thiền, m&agrave; c&ograve;n l&agrave; những chỉ dẫn gi&uacute;p họ soi x&eacute;t lộ tr&igrave;nh gi&aacute;c ngộ, đ&uacute;ng với con đường m&agrave; Đức Phật đ&atilde; đi qua. \r\n\r\nTrong &ldquo;Hạnh ph&uacute;c đến từ sự biến mất&rdquo;, Ajahn Brahm tr&iacute;ch tại nhiều điển t&iacute;ch, lời dạy của Đức Phật. &Ocirc;ng giảng giải, chia sẻ những c&acirc;u chuyện về thiền định v&agrave; h&agrave;nh tr&igrave;nh gi&aacute;c ngộ qua giọng văn ch&acirc;n thực, đ&ocirc;i khi c&oacute; ch&uacute;t h&agrave;i hước. Cuốn s&aacute;ch l&agrave; một sự lựa chọn s&aacute;ng suốt cho những ai mong muốn t&igrave;m hiểu s&acirc;u sắc về Phật gi&aacute;o, v&agrave; đang nghi&ecirc;m t&uacute;c thực h&agrave;nh thiền định. S&aacute;ch do Trần Thị Hương Lan dịch, First News thực hiện, NXB Tổng hợp ấn h&agrave;nh. \r\n\r\nAjahn Brahm sinh năm 1951 ở Lu&acirc;n Đ&ocirc;n, nước Anh, tốt nghiệp vật l&yacute; l&yacute; thuyết đại học Cambridge. Năm 1974, dưới sự dẫn dắt của tu sĩ nổi tiếng Ajahn Chah, &ocirc;ng trở th&agrave;nh tu sĩ Phật gi&aacute;o ở Th&aacute;i Lan,. Ng&agrave;y nay, Ajahn Brahm l&agrave; người hướng dẫn t&acirc;m linh được t&ocirc;n k&iacute;nh v&agrave; l&agrave; tu viện trưởng của tu viện Bodhiyana (Gi&aacute;c Thừa) thu h&uacute;t h&agrave;ng ng&agrave;n người tới c&aacute;c buổi thuyế giảng s&aacute;ng tạo v&agrave; s&acirc;u sắc của m&igrave;nh. &Ocirc;ng cũng l&agrave; t&aacute;c giả của nhiều cuốn s&aacute;ch về Phật Gi&aacute;o với h&agrave;ng chục ng&agrave;n bản in.\r\n', 'FIRST NEWS', 'NXB Tổng Hợp TPHCM', '2023-04-25 01:39:17');
INSERT INTO `products` (`product_id`, `name`, `author`, `price`, `image`, `description`, `supplier`, `publiser`, `date`) VALUES
('n9W3CpArB2', 'Tủ Sách Học Làm Người – Đầu Tư Tương Lai', 'Hoàng Xuân Việt', 84000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_183238.jpg', 'Cuốn sách tập hợp nhiều bài viết, mỗi bài viết là một quan điểm, một tính tình, những việc mà chúng ta cần làm để có một tương lai xán lạn và thành công. Đó là bạn nên lên kế hoạch đầu tư vốn học cho bản thân như thế nào? Bạn đang làm việc có chương trình không? Những bước chọn lựa nào là định mệnh, là bước ngoặt của đời bạn?\r\n\r\nĐầu tư tương lai nằm trong Tủ sách Học làm người của học giả Hoàng Xuân Việt, với những bài học vô cùng thiết thực, dù đã trải qua một thế kỷ nhưng vẫn còn vẹn nguyên giá trị cho người trẻ thời nay.\r\n\r\nTrích dẫn:\r\n\r\n- Trong mỗi cá nhân, tạo hóa ban cho một số khả năng nào đó hoặc tiềm tàng hoặc hiện lộ nhờ những cơ hội thuận tiện thúc đẩy. Tôi có dịp nói trước công chúng tuy tôi nói dở mà bắt buộc phải nói mãi sau cùng bớt ấp úng. Còn trong bạn biết đâu bạn đã có sẵn mầm mống hùng biện, sáng tác, chỉ huy, hội họa, điêu khắc… Chúng như con diều, bạn hãy làm gió cho chúng cất cánh. Chúng là hòn ngọc đang bị bụi cát che lấp đấy, bạn hãy khai quật chúng lên. Muốn vậy bạn hãy tin rằng giá trị đời bạn, bạn có thể xây dựng được và phải xây dựng nữa. Bạn phải cả tin rằng bạn không tự xây dựng thì không ai xây dựng cho bạn cả.\r\n\r\n- Làm việc có chương trình. Thưa bạn! Mấy tiếng ấy là con đẻ của óc tổ chức công việc theo khoa học. Làm việc có chương trình là suy nghĩ trước việc mình sắp làm, thấy trước mục tiêu, đo lường trước các trở lực, đề phòng trước các thất bại. Tinh thần khoa học áp dụng vào công việc là tinh thần tổng hợp và phân tích. Nhờ tổng hợp người ta nhìn thống quán toàn bộ công việc. Nhờ phân tích người ta mổ xẻ công việc trong chi tiết. Hai tinh thần đó là kim chỉ nam để lập các chương trình hành động.\r\n\r\n- Trong việc chụp cơ hội tôi muốn nhắn riêng với bạn điều này. Là phần đông chúng ta ưa dễ ngại khó. Một cơ hội tốt bao giờ cũng đòi hỏi một số điều kiện. Chụp một cơ hội tốt là tự dấn thân vào công việc không phải đông người làm. Bạn quá biết con đường mòn dễ đi, con đường rừng mới vạch thì gập ghềnh, chông gai, song vì đường mòn dễ đi nên đông người và ta có thể bị nghẽn lối. Chọn nghề mà lựa đường mòn thì đụng phải ý nghĩa của câu: “Một con cá lội mấy người buông câu.”\r\n\r\n- Thưa bạn, người bạn trẻ đã quen biết hoặc chưa quen biết, đang đặt chân lên thềm đời hay còn nuôi mộng vàng trên ghế học đường hoặc đã lăn lóc nhiều với cuộc đời nhiều gió bụi! Tôi kể vài trường hợp điển hình trên chỉ muốn để nói với bạn điều này: Ta hãy đối đầu với nghịch cảnh. Ai trên đời cũng có thứ nghịch cảnh riêng cả. Chính mình, mình thấy rõ nghịch cảnh của mình nhất. Hễ vào đời thì sớm muộn gì cũng phải chấp nhận định luật này là: Đối đầu với trở lực. Bao giờ cũng có trở lực, nếu không hành động như vậy thì đầu hàng nó. Ta không tiêu diệt nghịch cảnh thì nghịch cảnh tiêu diệt ta. Hễ muốn ngoi đầu lên thì bằng bất cứ giá nào cũng phải lo chuyển bại thành thắng. Tất cả giá trị của một người đều nằm ở chỗ lật lại thế cờ một tình trạng mà họ cho là bi đát”.', 'Alpha Books', 'NXB Thanh Niên', '2023-04-25 00:27:58'),
('nltuMZflML', 'Ba Người Bạn', 'Erich Maria Remarque', 93000, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8936071673770_1.jpg', 'Ba Người Bạn\r\n\r\nVới Ba người bạn, nước Đức của Remarque kh&ocirc;ng c&ograve;n l&agrave; những t&ograve;a nh&agrave; m&agrave;u x&aacute;m m&agrave; l&agrave; một th&agrave;nh phố nhỏ mờ sương của những bụi tử đinh hương, một g&oacute;c gi&aacute;o đường, những qu&aacute;n rượu nhỏ n&eacute;p m&igrave;nh trong l&agrave;n mưa lất phất. Đ&oacute; cũng kh&ocirc;ng phải l&agrave; đất nước của những bộ &oacute;c lạnh l&ugrave;ng l&yacute; tr&iacute; m&agrave; l&agrave; của những th&acirc;n phận nhỏ b&eacute; d&ugrave; bị tr&ocirc;i giạt đến bước đường c&ugrave;ng vẫn khao kh&aacute;t v&agrave; chan chứa y&ecirc;u thương. Đ&oacute; l&agrave; một nước Đức của t&igrave;nh cảm v&agrave; mộng mơ, một nước Đức của nỗi buồn v&agrave; c&aacute;i đẹp.\r\n\r\nTh&ocirc;ng tin t&aacute;c giả:\r\n\r\nErich Maria Remarque (1898 &ndash; 1970) l&agrave; nh&agrave; văn nổi tiếng người Đức. Ngo&agrave;i kiệt t&aacute;c Ph&iacute;a T&acirc;y kh&ocirc;ng c&oacute; g&igrave; lạ đầy x&uacute;c động, &ocirc;ng c&ograve;n được y&ecirc;u mến qua h&agrave;ng loạt c&aacute;c t&aacute;c phẩm chan chứa t&igrave;nh y&ecirc;u d&agrave;nh cho con người v&agrave; cuộc sống kh&aacute;c như Ba người bạn, Khải Ho&agrave;n M&ocirc;n, Đ&ecirc;m Lisbon&hellip;. Năm 1931, &ocirc;ng được đề cử cả giải Nobel Văn chương lẫn Nobel H&ograve;a b&igrave;nh.\r\n\r\nBa người bạn l&agrave; t&aacute;c phẩm Remarque cho ra mắt v&agrave;o năm 1936 kể về cuộc sống nước Đức thời kỳ hậu chiến. Mặc d&ugrave; kh&ocirc;ng nổi tiếng như Ph&iacute;a T&acirc;y kh&ocirc;ng c&oacute; g&igrave; lạ nhưng với nhiều độc giả Việt Nam, đ&acirc;y lại l&agrave; t&aacute;c phẩm để lại ấn tượng s&acirc;u sắc nhất, thay đổi ho&agrave;n to&agrave;n c&aacute;i nh&igrave;n của họ về đất nước v&agrave; con người Đức, thậm ch&iacute; l&agrave; cả c&aacute;ch nh&igrave;n về cuộc sống.', 'Đông A', 'Văn Học', '2023-04-25 01:12:04'),
('nQhTvc08Ob', 'Những Quy Tắc Tư Duy', 'Richard Templar', 109000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_219974.jpg', 'Một v&agrave;i c&aacute;ch tư duy th&ocirc;ng minh sẽ gi&uacute;p bạn cảm thấy hạnh ph&uacute;c hơn, bền bỉ hơn, trong khi những c&aacute;ch kh&aacute;c sẽ gi&uacute;p bạn lập kế hoạch hiệu quả hơn v&agrave; đưa ra những quyết định sang suốt hơn.\r\n\r\nTư duy dựa tr&ecirc;n quy tắc sẽ cải thiện sự sang tạo, kỹ năng giải quyết vấn đề, khả năng ph&acirc;n t&iacute;ch, đ&aacute;nh gi&aacute; v&agrave; ph&ecirc; ph&aacute;n của bạn. Tư duy th&ocirc;ng minh hơn sẽ c&oacute; ảnh hưởng t&iacute;ch cực l&ecirc;n mọi mặt đời sống của bạn, d&ugrave; l&agrave; tại nh&agrave;, ở chỗ l&agrave;m hay trong c&aacute;c mối quan hệ.\r\n\r\nX&eacute;t từ g&oacute;c độ n&agrave;o đ&oacute;, cuốn s&aacute;ch n&agrave;y kh&ocirc;ng đề cập tới c&aacute;ch suy nghĩ. Nhiều quy tắc chỉ đơn thuần l&agrave; chỉ dẫn để loại bỏ những r&agrave;o cản đ&atilde; v&agrave; đang ngăn cản khả năng tư duy thấu đ&aacute;o của bạn.', 'Alpha Books', 'NXB Lao Động', '2023-04-25 01:15:02'),
('o2hSQrxrrh', 'Án Mạng Mười Một Chữ', 'Higashino Keigo', 93500, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_195509_1_32586.jpg', 'Tình cờ phát hiện những điều bất thường sau cái chết thảm khốc của người yêu, nhân vật “tôi”, một nữ nhà văn viết tiểu thuyết trinh thám đã cùng bạn mình, Hagio Fuyuko, cũng là biên tập viên phụ trách sách của “tôi” quyết định điều tra về cái chết này. Trong quá trình điều tra hai người phát hiện người yêu của “tôi” đã từng gặp tai nạn lật thuyền trong chuyến du lịch đảo một năm trước. Và khi họ tìm tới những người cũng tham gia chuyến đi đó để tìm hiểu thì những người này cũng lần lượt bị sát hại. Cuối cùng “tôi” buộc phải tự mình phán đoán, điều tra để tìm ra chân tướng sự việc.\r\n\r\nÁn mạng mười một chữ cũng đã được chuyển thể thành phim truyền hình vào năm 2011.\r\n\r\nGIỚI THIỆU TÁC GIẢ:\r\nHigashino Keigo là tiểu thuyết gia trinh thám hàng đầu Nhật Bản với nhiều tác phẩm hàng triệu bản bán ra trong và ngoài nước, gặt hái vô vàn giải thưởng. Ông từng là Chủ tịch thứ 13 của Hội nhà văn Trinh thám Nhật Bản từ năm 2009 tới năm 2013.\r\n\r\nMỗi tác phẩm của ông đều có phong cách khác nhau, nhưng nhìn chung đều có diễn biến bất ngờ, khắc họa tâm lý nhân vật sâu sắc, làm nên nét riêng biệt trong chất văn của Higashino Keigo.', 'Nhã Nam', 'NXB Hội Nhà Văn', '2023-04-25 00:45:37'),
('oHn7WxJa3g', 'Yêu Thương - Ai Cũng Như Ai', 'Bob Goff', 137000, 'https://cdn0.fahasa.com/media/catalog/product/b/i/bia_sau_2.png', 'Y&ecirc;u Thương - Ai Cũng Như Ai\r\n\r\nH&atilde;y t&igrave;m c&aacute;ch y&ecirc;u thương những người kh&oacute; chịu hơn v&agrave; c&aacute;c bạn sẽ sống một cuộc sống đ&aacute;ng mơ ước. H&atilde;y đi t&igrave;m ai đ&oacute; bạn vẫn lu&ocirc;n n&eacute; tr&aacute;nh v&agrave; d&agrave;nh t&igrave;nh y&ecirc;u thương thật lớn cho họ. Bạn sẽ học được th&ecirc;m nhiều về h&agrave;ng x&oacute;m của bạn, kẻ th&ugrave; của bạn v&agrave; niềm tin của bạn. H&atilde;y t&igrave;m ai đ&oacute; m&agrave; bạn nghĩ l&agrave; sai tr&aacute;i, ai đ&oacute; m&agrave; bạn kh&ocirc;ng t&aacute;n đồng, ai đ&oacute; kh&aacute;c biệt so với bạn,v&agrave; h&atilde;y quyết định y&ecirc;u thương người đ&oacute; như c&aacute;ch bạn được tạo ra từ y&ecirc;u thương.\r\nCh&uacute;ng ta cần y&ecirc;u thương mọi người, ai cũng như ai!\r\n\r\nHọc c&aacute;ch y&ecirc;u thương trong một thế giới đầy rẫy đổ vỡ v&agrave; những con người kh&oacute; nhọc\r\n\r\nY&ecirc;u thương l&agrave; t&igrave;nh cảm cao qu&yacute; m&agrave; mọi người d&agrave;nh cho nhau, đ&oacute; l&agrave; t&igrave;nh cảm gia đ&igrave;nh, bạn b&egrave;, đồng nghiệp&hellip; Cuốn s&aacute;ch l&agrave; những mẩu truyện nhỏ, những chi&ecirc;m nghiệm của t&aacute;c giả về t&igrave;nh y&ecirc;u thương xung quanh &ocirc;ng. Tất cả đều được vun đắp, x&acirc;y dựng trong một thời gian d&agrave;i, nhưng đ&oacute; cũng c&oacute; thể l&agrave; t&igrave;nh cảm ph&aacute;t sinh bất chợt. Nhưng cho d&ugrave; như thế n&agrave;o th&igrave; t&igrave;nh y&ecirc;u thương cũng lu&ocirc;n đem đến những điều k&igrave; diệu cho cả người cho đi v&agrave; người nhận lại. Hạnh ph&uacute;c m&agrave; t&igrave;nh y&ecirc;u thương đem lại cho cả hai l&agrave; cảm gi&aacute;c b&igrave;nh y&ecirc;n v&agrave; thật s&acirc;u lắng.\r\n\r\nĐiều đặc biệt ở cuốn s&aacute;ch l&agrave; t&aacute;c giả sẽ kh&ocirc;ng viết về những chốn xa lạ, h&agrave;o nho&aacute;ng n&agrave;o đ&oacute; m&agrave; n&oacute; ch&iacute;nh l&agrave; b&agrave; h&agrave;ng x&oacute;m gi&agrave;, l&agrave; chặng đường lớn l&ecirc;n của đứa con nhỏ, l&agrave; những m&acirc;u thuẫn trong đời sống vợ chồng. Tất cả sẽ nhẹ nh&agrave;ng len lỏi v&agrave;o t&acirc;m tr&iacute; bạn, gi&uacute;p bạn nh&igrave;n nhận, thấu hiểu v&agrave; trở n&ecirc;n biết y&ecirc;u thương.\r\n\r\nCh&uacute;a ở b&ecirc;n ta cho n&ecirc;n ta cần ở b&ecirc;n nhau.\r\n\r\nMọi người thường nghĩ phải mất cả đời để trở th&agrave;nh bạn của ai đ&oacute;, nhưng thực tế, ch&uacute;ng ta c&oacute; thể l&agrave;m việc đ&oacute; chỉ cần ba ph&uacute;t mỗi lần. H&atilde;y cho mọi người thấy bạn l&agrave; ai qua những g&igrave; bạn l&agrave;m, chứ kh&ocirc;ng chỉ n&oacute;i cho họ biết những g&igrave; bạn nghĩ. Đừng để ng&ocirc;n từ của ch&uacute;ng ta n&oacute;i ch&uacute;ng ta l&agrave; một người n&agrave;y, nhưng cuộc sống của ch&uacute;ng ta lại n&oacute;i ch&uacute;ng ta l&agrave; ai đ&oacute; kh&aacute;c. H&atilde;y t&igrave;m những người xa lạ v&agrave; ch&agrave;o đ&oacute;n họ đến với cuộc đời bạn. Đ&oacute; l&agrave; c&aacute;ch m&agrave; bạn trở n&ecirc;n biết y&ecirc;u thương.\r\n\r\nBởi ch&uacute;ng ta cần y&ecirc;u thương mọi người, ai cũng như ai!\r\n\r\n&ldquo;C&oacute; một chuyện vẫn nguy&ecirc;n như vậy. Mỗi lần t&ocirc;i thắc mắc xem t&ocirc;i n&ecirc;n y&ecirc;u thương ai v&agrave; n&ecirc;n y&ecirc;u thương họ bao l&acirc;u th&igrave; Ch&uacute;a lại tiếp tục th&igrave; thầm với t&ocirc;i: Ai cũng như ai.&rdquo; &ndash; T&aacute;c giả ', 'MCBooks', 'NXB Hồng Đức', '2023-04-25 01:17:35'),
('p79z2AU4vG', 'Tôi Ước Mình Chưa Từng Được Sinh Ra', 'Yim Minkyung', 77000, 'https://cdn0.fahasa.com/media/catalog/product/b/i/bia_toi-uoc-minh-chua-tung-duoc-sinh-ra_600.jpg', 'Tôi Ước Mình Chưa Từng Được Sinh Ra\r\n\r\n\"T.ự T.ử\" gần đây đã trở thành một cùm từ được nhắc đến thường xuyên hơn bao giờ hết. Thật đau lòng khi nghe một ai đó lựa chọn rời bỏ thế gian này, nhưng chính chúng ta không biết tại sao họ lại đưa ra quyết định như vậy, cũng không biết họ đã trải qua những đấu tranh tâm lý đến nhường nào.\r\n\r\nLiệu chúng ta có thực sự hiểu về t.ự t.ử?\r\n\r\nTác giả của cuốn sách này, với vai trò là một chuyên gia về tâm lý học, sẽ giải đáp những câu hỏi xoay quanh tâm lý của những người có ý định t.ự s.á.t thông qua những \"trải nghiệm\" văn học. Một phương thức để \"hiểu đầy đủ về t.ự t.ử\" thông qua những nhân vật trong các cuốn tiểu thuyết kinh điển mà chúng ta có thể đã từng đọc qua. Những nhân vật được phân tích trong cuốn sách này là những người muốn hoặc đang cố gắng t.ự t.ử, thường xuyên nghĩ đến việc t.ự t.ử hoặc gây t.ổ.n h.ạ.i tới chính mình. Họ cũng là những người mắc chứng t.r.ầ.m c.ả.m, ảo tưởng thính giác hoặc nghiện ch.ấ.t k.í.c.h, liên tục tiến gần hơn tới việc t.ự s.á.t.\r\n\r\nVăn học, đối với tác giả, đơn giản cho thấy một hiện tượng nào đó mà không cố giải thích từng lý do và nguyên tắc cho mọi người, đôi khi làm chứng, những cũng có lúc từ chối bênh vực. Mỗi quyển sách cũng là một cuộc đời và nhân vật đang sống cuộc đời của họ. Bạn đã từng bao giờ nghĩ mình sẽ tiếp cận và hiểu về tâm lý học nói chung và hiện tượng t.ự s.á.t bằng việc tìm hiểu tâm lý của những nhân vật trong những quyển sách tiểu thuyết chưa?\r\n\r\nCuốn sách không phải là một cuốn sách khuyến khích t.ự t.ử mà là quyển sách mang đến sự an ủi. Những kiến thức tâm lý học nói chung và tâm lý học về t.ự s.á.t dễ dàng tiếp cận một cách nhẹ nhàng, hy vọng sẽ giúp những bạn đang đọc cuốn sách này, hiểu về t.ự t.ử theo một cách nào đó và lựa chọn sống một cuộc sống hạnh phúc hơn.\r\n\r\n Cuốn sách này dành tặng cho:\r\n\r\n- Những người đã từng thoáng qua một suy nghĩ rời bỏ thế gian này, vì cuốn sách này sẽ giải đáp những câu hỏi xoay quanh tâm lý của bạn.\r\n\r\n- Những ai muốn hiểu những người lựa chọn rời bỏ thế gian này đã phải trải qua những đấu tranh tâm lý như thế nào, để cảm thông và yêu thương nhiều hơn trong cuộc đời này.\r\n\r\nHơn hết, “Tôi ước mình chưa từng được sinh ra” là lời động viên, cùng bạn đồng hành qua những khoảnh khắc mơ hồ, hoang mang nhất. Nỗi đau rồi sẽ qua đi, bạn sẽ cảm ơn bạn vì đã dũng cảm bước qua những vết thương trong tâm hồn!\r\n\r\nHãy hạnh phúc khi còn sống trên đời này nhé!\r\n', 'AZ Việt Nam', 'Dân Trí', '2023-04-25 00:25:12'),
('pHER638SIy', 'Thái Độ Sống Tạo Nên Tất Cả', 'J.P. Vaswani', 57000, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935086854129.jpg', 'Thái Độ Sống Tạo Nên Tất Cả\r\n\r\nCuộc sống ngày càng trở nên hối hả, và theo đó, con người chúng ta ngày càng có nhiều nhiệm vụ và mục tiêu phải hoàn thành. Cuộc sống tất bật này đã khiến nhiều người cảm thấy mệt mỏi và đôi lúc gần như không thể kiểm soát được đời sống của chính mình. Những lúc ấy, không ít người đã tự hỏi: Cuộc sống là gì và làm thế nào để có được một cuộc sống như mong muốn?\r\n\r\nTừ kinh nghiệm và hiểu biết của mình, J. P. Vaswani đã mang đến một phương pháp hữu hiệu giúp độc giả biết cách thay đổi thái độ để từ đó làm chủ cuộc đời mình. Những câu chuyện sâu sắc trong cuốn sách này có thể giúp độc giả hình thành nên những suy nghĩ tích cực và có được thái độ sống đúng đắn.\r\n\r\n\"Không ai có thể đem đến sự yên bình thanh thản cho bạn ngoài chính bản thân bạn\"\r\n(Ralph Waldo Emerson)\r\n\r\n\"Ý nghĩa cuộc sống không phải nằm ở chỗ nó đem đến cho ta điều gì, mà ở chỗ ta có thái độ với nó ra sao, không phải ở chỗ điều gì xảy ra với ta, mà ở chỗ ta phản ứng với những điều đó như thế nào.\"\r\n(Lewis L.Dunnington)', 'FIRST NEWS', 'NXB Tổng Hợp TPHCM', '2023-04-25 00:51:03'),
('PMadGPTWqa', 'Hiểu Về Trái Tim', 'Minh Niệm', 135000, 'https://cdn0.fahasa.com/media/catalog/product/z/4/z4118763446785_cf4bc22d353b065bbb37e686de1f9207.jpg', 'HIỂU VỀ TR&Aacute;I TIM &ndash; CUỐN S&Aacute;CH MỞ CỬA THẾ GIỚI CẢM X&Uacute;C CỦA MỖI NGƯỜI  \r\n\r\n&ldquo;Hiểu về tr&aacute;i tim&rdquo; l&agrave; một cuốn s&aacute;ch đặc biệt được viết bởi thiền sư Minh Niệm. Với phong th&aacute;i v&agrave; lối h&agrave;nh văn gần gũi với những sinh hoạt của người Việt, thầy Minh Niệm đ&atilde; thật sự thổi hồn Việt v&agrave;o cuốn s&aacute;ch nhỏ n&agrave;y.\r\n\r\nXuất bản lần đầu ti&ecirc;n v&agrave;o năm 2011, Hiểu Về Tr&aacute;i Tim tr&igrave;nh l&agrave;ng cũng l&uacute;c với kỷ lục: cuốn s&aacute;ch c&oacute; số lượng in lần đầu lớn nhất: 100.000 bản. Trung t&acirc;m s&aacute;ch kỷ lục Việt Nam c&ocirc;ng nhận kỳ t&iacute;ch ấy nhưng đến nay, con số ph&aacute;t h&agrave;nh của Hiểu về tr&aacute;i tim vẫn chưa c&oacute; dấu hiệu chậm lại.\r\n\r\nL&agrave; t&aacute;c phẩm đầu tay của nh&agrave; sư Minh Niệm, người s&aacute;ng lập d&ograve;ng thiền hiểu biết (Understanding Meditation), kết hợp giữa tư tưởng Phật gi&aacute;o Đại thừa v&agrave; Thiền nguy&ecirc;n thủy Vipassana, nhưng Hiểu Về Tr&aacute;i Tim kh&ocirc;ng phải t&aacute;c phẩm thuyết gi&aacute;o về Phật ph&aacute;p. Cuốn s&aacute;ch rất &ldquo;đời&rdquo; với những ưu tư của một người tu nh&igrave;n về c&otilde;i thế. Ở đ&oacute;, c&oacute; hạnh ph&uacute;c, c&oacute; đau khổ, c&oacute; t&igrave;nh y&ecirc;u, c&oacute; c&ocirc; đơn, c&oacute; tuyệt vọng, c&oacute; lười biếng, c&oacute; yếu đuối, c&oacute; bu&ocirc;ng xả&hellip; Nhưng, tất cả những hỉ nộ &aacute;i ố ấy đều được kho&aacute;c l&ecirc;n tấm &aacute;o mới, một tấm &aacute;o tinh khiết v&agrave; xuy&ecirc;n suốt, khiến người đọc khi nh&igrave;n v&agrave;o, đều thấy mọi sự như nhẹ nh&agrave;ng hơn&hellip;\r\n\r\nTrong d&ograve;ng chảy tất bật của cuộc sống, c&oacute; bao giờ ch&uacute;ng ta dừng lại v&agrave; tự hỏi: Tại sao ta giận? Tại sao ta buồn? Tại sao ta hạnh ph&uacute;c? Tại sao ta c&ocirc; đơn?... Tất cả những hiện tượng t&acirc;m l&yacute; ấy kh&ocirc;ng ngừng biến h&oacute;a trong ta v&agrave; t&aacute;c động l&ecirc;n đời sống của ta, nhưng ta lại biết rất &iacute;t về nguồn gốc v&agrave; sự vận h&agrave;nh của n&oacute;. Chỉ cần một cơn giận, hay một &yacute; niệm nghi ngờ, cũng c&oacute; thể qu&eacute;t sạch năng lượng b&igrave;nh y&ecirc;n trong ta v&agrave; khiến ta nh&igrave;n mọi thứ đều sai lệch. Từ thất bại n&agrave;y đến đổ vỡ kh&aacute;c m&agrave; ta kh&ocirc;ng l&yacute; giải nổi, chỉ biết d&ugrave;ng &yacute; ch&iacute; để tự nhắc nhở m&igrave;nh cố gắng tiến bộ hơn. Cho n&ecirc;n, hiểu về tr&aacute;i tim ch&iacute;nh l&agrave; nhu cầu căn bản nhất của con người.\r\n\r\nHiểu về tr&aacute;i tim l&agrave; th&aacute;i độ trở về tiếp nhận v&agrave; l&agrave;m mới lại t&acirc;m hồn m&igrave;nh. Bởi hiểu được ch&iacute;nh m&igrave;nh, ta sẽ dễ d&agrave;ng hiểu được người kh&aacute;c, để ta c&oacute; thể thương nhau ch&acirc;n thật.\r\n\r\nXuy&ecirc;n suốt cuốn s&aacute;ch, t&aacute;c giả đ&atilde; đưa ra 50 kh&aacute;i niệm trong cuộc sống, 50 b&agrave;i viết t&acirc;m l&yacute; trị liệu, được tr&igrave;nh b&agrave;y rất ch&acirc;n phương, dễ hiểu, thực tế,  vốn dĩ rất đời thường nhưng nếu suy ngẫm một ch&uacute;t ch&uacute;ng ta sẽ thấy thật s&acirc;u sắc như Khổ đau, Hạnh ph&uacute;c, T&igrave;nh y&ecirc;u, Tức giận, Ghen tu&ocirc;ng, &Iacute;ch kỷ, Tham vọng, Th&agrave;nh thật, Nghi ngờ, Lo lắng, Do dự, Bu&ocirc;ng xả, Thảnh thơi, B&igrave;nh y&ecirc;n, C&ocirc; đơn, &Aacute;i ngữ, Lắng nghe&hellip; Đ&uacute;ng như tựa đề s&aacute;ch, s&aacute;ch sẽ gi&uacute;p ta hiểu về tr&aacute;i tim, hiểu về những t&acirc;m trạng, t&iacute;nh c&aacute;ch s&acirc;u thẳm trong tr&aacute;i tim ta.\r\n\r\nL&uacute;c sinh thời cố Gi&aacute;o sư, Tiến sĩ Trần Văn Khu&ecirc;, c&oacute; dịp tiếp cận với Hiểu Về Tr&aacute;i Tim. &Ocirc;ng nhận x&eacute;t, như một cuốn s&aacute;ch đầu ti&ecirc;n thuộc chủ đề Hạt Giống T&acirc;m Hồn do một t&aacute;c giả Việt Nam viết, cuốn s&aacute;ch sẽ gi&uacute;p người đọc hiểu được cảm x&uacute;c của t&acirc;m hồn, tr&aacute;i tim của ch&iacute;nh m&igrave;nh v&agrave; của người kh&aacute;c. Để, tận c&ugrave;ng l&agrave; loại bỏ nỗi buồn, tổn thương v&agrave; t&igrave;m được hạnh ph&uacute;c trong cuộc sống. C&oacute; lẽ, v&igrave; điều n&agrave;y m&agrave; hơn 10 năm qua, Hiểu về tr&aacute;i tim vẫn l&agrave; cuốn s&aacute;ch li&ecirc;n tục được t&aacute;i bản v&agrave; chưa c&oacute; dấu hiệu &ldquo;hạ nhiệt&rdquo;, nhiều năm trời li&ecirc;n tục nằm trong top s&aacute;ch b&aacute;n chạy nhất tại Việt Nam.\r\n\r\nĐ&aacute;ng qu&yacute; hơn, t&ograve;an bộ lợi nhuận thu được từ việc ph&aacute;t h&agrave;nh cuốn s&aacute;ch n&agrave;y đều được chuyển về quỹ từ thiện c&ugrave;ng t&ecirc;n &ldquo;Hiểu về tr&aacute;i tim&rdquo; để gi&uacute;p đỡ trẻ em c&oacute; ho&agrave;n cảnh kh&oacute; khăn, bất hạnh tại Việt Nam. V&agrave; đ&acirc;y cũng ch&iacute;nh l&agrave; niềm hạnh ph&uacute;c cũng như &yacute; nghĩa nh&acirc;n &aacute;i lớn nhất m&agrave; cuốn s&aacute;ch đ&atilde; mang lại, đặc biệt l&agrave; khi tất cả h&agrave;nh tr&igrave;nh n&agrave;y c&ograve;n c&oacute; sự đồng h&agrave;nh v&agrave; g&oacute;p sức của h&agrave;ng trăm ngh&igrave;n bản đọc tr&ecirc;n khắp cả nước Việt Nam.\r\n\r\nNgười nổi tiếng n&oacute;i về cuốn s&aacute;ch:\r\n\r\n&ldquo;Để chữa l&agrave;nh những tổn thương v&agrave; nổi đau, c&aacute;ch tốt nhứt v&agrave; hữu hiệu nhất l&agrave; cần hiểu r&otilde; được tr&aacute;i tim, t&acirc;m hồn của m&igrave;nh, v&agrave; của người kh&aacute;c, cuốn s&aacute;ch Hiểu về Tr&aacute;i Tim ch&iacute;nh l&agrave; cuốn s&aacute;ch gi&uacute;p bạn đọc l&agrave;m được điều đ&oacute;: Hiểu r&otilde; v&agrave; chữa l&agrave;nh tr&aacute;i tim, t&acirc;m hồn của m&igrave;nh v&agrave; của những người xung quanh, để mọi người c&ugrave;ng được sống trong hạnh ph&uacute;c v&agrave; y&ecirc;u thương. Với cuốn s&aacute;ch n&agrave;y, ch&uacute;c bạn đọc sẽ lu&ocirc;n hạnh ph&uacute;c v&agrave; kh&ocirc;ng bao giờ phải sống với một tr&aacute;i tim tan vỡ hay một t&acirc;m hồn tổn thương&rdquo;  - Gi&aacute;o sư &ndash; Tiến sĩ Trần Văn Kh&ecirc;\r\n\r\n&quot;Cuốn s&aacute;ch Hiểu về tr&aacute;i tim được viết ra với những trải nghiệm s&acirc;u sắc, nhằm gi&uacute;p con người hiểu r&otilde; v&agrave; l&yacute; giải những cảm x&uacute;c của ch&iacute;nh m&igrave;nh để t&igrave;m được sự b&igrave;nh an v&agrave; hạnh ph&uacute;c thật sự&rdquo;. - Anh h&ugrave;ng Lao động, Thầy thuốc nh&acirc;n d&acirc;n, GS B&aacute;c sĩ Nguyễn Thị Ngọc Phượng\r\n\r\n&quot;Đ&acirc;y ch&iacute;nh l&agrave; con đường của đạo T&acirc;m, với những nguy&ecirc;n tắc sống hạnh ph&uacute;c &ndash; một thứ &ldquo;an lạc hạnh&rdquo; &ndash; từ những sẻ chia ch&acirc;n th&agrave;nh của t&aacute;c giả. Con đường hạnh ph&uacute;c đ&oacute; đ&ograve;i hỏi sự khổ luyện, chứng nghiệm qua qu&aacute;n chiếu bản th&acirc;n, từ đ&oacute; thấy biết bản chất của khổ đau, phiền n&atilde;o, v&agrave;, vượt tho&aacute;t&hellip;&rdquo; - B&aacute;c sĩ Đỗ Hồng Ngọc. Nguy&ecirc;n Gi&aacute;m Đốc Trung T&acirc;m Truyền Th&ocirc;ng &ndash; Gi&aacute;o Dục Sức Khoẻ TP.HCM\r\n\r\n&quot;Một cuốn s&aacute;ch hay, thực tế v&agrave; rất hữu &iacute;ch cho mọi người, đặc biệt đối với thanh thiếu ni&ecirc;n v&agrave; c&aacute;c bạn trẻ. Nếu r&egrave;n luyện được theo những điều hay như thế th&igrave; cuộc sống sẽ tốt đẹp hơn rất nhiều&quot;. - Tạ B&iacute;ch Loan, Trưởng Ban Thanh thiếu ni&ecirc;n Đ&agrave;i truyền h&igrave;nh Việt Nam\r\n\r\n&quot;Đ&acirc;y l&agrave; một cuốn s&aacute;ch đặc biệt, c&oacute; t&iacute;nh gi&aacute;o dục, tự nhận thức cao, được viết từ tr&aacute;i tim để chữa l&agrave;nh những tr&aacute;i tim. Một cuốn s&aacute;ch rất &yacute; nghĩa!&rdquo;. - Nh&agrave; b&aacute;o Trần Tử Văn, Ph&oacute; Tổng bi&ecirc;n tập B&aacute;o C&ocirc;ng an TP.HCM\r\n\r\n&quot;Hiểu về tr&aacute;i tim l&agrave; cuốn s&aacute;ch thứ 180 của Tủ s&aacute;ch Hạt giống t&acirc;m hồn m&agrave; First News đ&atilde; xuất bản, nhưng đ&acirc;y l&agrave; cuốn s&aacute;ch của một t&aacute;c giả Việt Nam đ&atilde; để lại trong t&ocirc;i những cảm x&uacute;c đặc biệt nhất. Với những trải nghiệm s&acirc;u sắc v&agrave; t&acirc;m huyết m&agrave; t&aacute;c giả đ&atilde; viết trong 8 năm chắc chắn sẽ mang đến cho bạn đọc những kh&aacute;m ph&aacute; mới mẻ v&agrave; th&uacute; vị. Một c&aacute;nh cửa rộng mở đang chờ đ&oacute;n bạn&rdquo;. - Nguyễn Văn Phước, Gi&aacute;m đốc First News - Tr&iacute; Việt\r\n\r\nB&aacute;o ch&iacute; n&oacute;i g&igrave; về &ldquo;Hiểu về tr&aacute;i tim&rdquo;:\r\n\r\n&ldquo;&#039;Hiểu về tr&aacute;i tim&#039; l&agrave; một cuốn s&aacute;ch đặc biệt, được viết n&ecirc;n từ t&acirc;m huyết của một nh&agrave; thiền sư mang t&ecirc;n Minh Niệm. Đ&atilde; bao giờ giữa cuộc đời hối hả, bạn chợt dừng lại v&agrave; tự hỏi m&igrave;nh rằng &#039; hạnh ph&uacute;c l&agrave; g&igrave;?&#039; , &#039;&#039; khổ đau l&agrave; g&igrave;?&#039; hay chưa? V&acirc;ng, cuốn s&aacute;ch n&agrave;y sẽ giải đ&aacute;p cho bạn tất cả những băn khoăn đ&oacute;.&rdquo; &ndash; baomoi.vn\r\n\r\nVề t&aacute;c giả:\r\n\r\nSinh tại Ch&acirc;u Th&agrave;nh, Tiền Giang, xuất gia tại Phật Học Viện Huệ Nghi&ecirc;m &ndash; S&agrave;i G&ograve;n, Minh Niệm từng thọ gi&aacute;o thiền sư Th&iacute;ch Nhất Hạnh tại Ph&aacute;p v&agrave; thiền sư Tejaniya tại Mỹ. Kết quả sau qu&aacute; tr&igrave;nh tu tập, lĩnh hội kiến thức&hellip; &Ocirc;ng quyết định chọn con đường hướng dẫn thiền v&agrave; khai triển t&acirc;m l&yacute; trị liệu cho giới trẻ l&agrave;m Phật sự của m&igrave;nh. Tiếp cận với nhiều người trẻ, lắng nghe thế giới quan của họ v&agrave; quan s&aacute;t những đổi thay trong đời sống hiện đại, &ocirc;ng ph&aacute;t hiện c&oacute; rất nhiều vấn đề của cuộc sống. Nhưng, tất cả, chỉ xuất ph&aacute;t từ một nguy&ecirc;n nh&acirc;n: Ch&uacute;ng ta chưa hiểu, v&agrave; chưa hiểu đ&uacute;ng về tr&aacute;i tim m&igrave;nh l&agrave; chưa cơ chế vận động của những hỉ, nộ, &aacute;i, ố trong mỗi con người.\r\n\r\n&ldquo;T&ocirc;i đ&atilde; từng quyết l&ograve;ng ra đi t&igrave;m hạnh ph&uacute;c ch&acirc;n thật. D&ugrave; thời điểm ấy, &yacute; niệm về h', 'FIRST NEWS', 'NXB Tổng Hợp TPHCM', '2023-04-25 01:38:37'),
('PyQBcVSwQP', 'Thú Tội', 'Minato Kanae', 94000, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935235237247.jpg', 'Thú Tội\r\n\r\nMoriguchi là giáo viên cấp hai đồng thời là bà mẹ đơn thân của một cô gái 4 tuổi. Một hôm, xác cô bé được phát hiện trong bể bơi trường học. Cảnh sát nhận định đây là vụ tai nạn nhưng Moriguchi biết ai đã sát hại con gái mình - kẻ ở ngay trong lớp học do mình chủ nhiệm. Moriguchi bắt đầu kế hoạch báo thù của riêng cô...\r\n\r\n\"Một câu chuyện tâm lý kinh dị, ớn lạnh, thuyết phục, gây bất ngờ không chỉ một lần...Day dứt, tàn nhẫn và choáng váng.\"\r\n\r\n- WALL STREET JOURNAL\r\n\r\n\"Cuốn sách đáng đọc này vừa tăm tối, gây xáo động và biết đánh lừa độc giả, với giọng văn sắc bén, dồn dập, mỗi chương đều đưa người đọc tới tận cùng căng thẳng.\"\r\n\r\n- TORONTO STAR', 'Nhã Nam', 'NXB Hội Nhà Văn', '2023-04-25 00:42:06'),
('QelgC4YnOe', 'Những Quy Tắc Trong Tình Yêu ', 'Richard Templar', 110000, 'https://cdn0.fahasa.com/media/catalog/product/n/h/nhung-quy-tac-trong-tinh-yeu_outline_28.3.2019-01-_1_.jpg', 'Những Quy Tắc Trong T&igrave;nh Y&ecirc;u (T&aacute;i Bản 2019)\r\n\r\nC&aacute;i ng&agrave;y m&agrave; một nửa kia &ndash; Mr hay Ms Ho&agrave;n hảo của bạn &ndash; bước v&agrave;o cuộc sống của bạn, giăng lưới đ&aacute;nh cắp tr&aacute;i tim bạn, th&igrave; bạn &quot;khốn đốn&quot; rồi: bạn đ&atilde; rơi v&agrave;o m&ecirc; hồn trận của t&igrave;nh y&ecirc;u. H&igrave;nh ảnh người ấy sẽ dẫn lối bạn v&agrave;o khu vườn rực rỡ b&iacute; ẩn của t&acirc;m hồn bạn, nơi kh&ocirc;ng c&oacute; g&igrave; phải cũng chẳng c&oacute; g&igrave; tr&aacute;i, nơi ngự trị của v&ocirc; v&agrave;n những ph&uacute;t gi&acirc;y đam m&ecirc;, hạnh ph&uacute;c, khắc khoải đợi chờ...\r\n\r\nNhưng đ&oacute; chỉ mới l&agrave; bước khởi đầu!\r\n\r\nBạn sẽ kh&ocirc;ng ngừng quay cuồng v&agrave; rối l&ecirc;n v&igrave; một thứ t&igrave;nh cảm thi&ecirc;ng li&ecirc;ng với rất nhhiều cung bậc được gọi l&agrave; t&igrave;nh y&ecirc;u. Hẹn h&ograve;, giận dỗi, ho&agrave;i nghi, đổ vỡ, quan hệ bạn b&egrave; nhạt phai, gia đ&igrave;nh kh&ocirc;ng ở b&ecirc;n khi ta cần...\r\n\r\nGiữa một thế giới vi diệu nhưng cũng đầy phức tạp đ&oacute;, l&agrave;m sao biết được m&igrave;nh đ&atilde; t&igrave;m được t&igrave;nh y&ecirc;u đ&iacute;ch thực? L&agrave;m sao để người ấy lu&ocirc;n hạnh ph&uacute;c? L&agrave;m sao thắp l&ecirc;n v&agrave; giữ lửa t&igrave;nh y&ecirc;u theo c&ugrave;ng năm th&aacute;ng?\r\n\r\nNhưng đừng qu&aacute; lo lắng, Những quy tắc trong t&igrave;nh y&ecirc;u sẽ l&agrave; người đồng h&agrave;nh của bạn trong suốt h&agrave;nh tr&igrave;nh n&agrave;y. Với 100 quy tắc r&uacute;t ra từ ch&iacute;nh những trải nghiệm của c&aacute; nh&acirc;n t&aacute;c giả v&agrave; từ thực tế quan s&aacute;t được, cuốn s&aacute;ch sẽ gi&uacute;p bạn tận hưởng đến c&ugrave;ng những khoảnh khắc kỳ diệu khi đang y&ecirc;u, v&agrave; cũng để vững v&agrave;ng khi s&oacute;ng gi&oacute; xảy ra... Để bạn y&ecirc;u v&agrave; được y&ecirc;u theo đ&uacute;ng nghĩa trọn vẹn nhất của từ n&agrave;y!\r\n', 'Alpha Books', 'NXB Lao Động', '2023-04-25 01:15:53'),
('Qg9kfupYAu', 'Nhà Giả Kim', 'Paulo Coelho', 65500, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_195509_1_36793.jpg', 'Tất cả những trải nghiệm trong chuyến phiêu du theo đuổi vận mệnh của mình đã giúp Santiago thấu hiểu được ý nghĩa sâu xa nhất của hạnh phúc, hòa hợp với vũ trụ và con người. \r\n\r\nTiểu thuyết Nhà giả kim của Paulo Coelho như một câu chuyện cổ tích giản dị, nhân ái, giàu chất thơ, thấm đẫm những minh triết huyền bí của phương Đông. Trong lần xuất bản đầu tiên tại Brazil vào năm 1988, sách chỉ bán được 900 bản. Nhưng, với số phận đặc biệt của cuốn sách dành cho toàn nhân loại, vượt ra ngoài biên giới quốc gia, Nhà giả kim đã làm rung động hàng triệu tâm hồn, trở thành một trong những cuốn sách bán chạy nhất mọi thời đại, và có thể làm thay đổi cuộc đời người đọc.\r\n\r\n“Nhưng nhà luyện kim đan không quan tâm mấy đến những điều ấy. Ông đã từng thấy nhiều người đến rồi đi, trong khi ốc đảo và sa mạc vẫn là ốc đảo và sa mạc. Ông đã thấy vua chúa và kẻ ăn xin đi qua biển cát này, cái biển cát thường xuyên thay hình đổi dạng vì gió thổi nhưng vẫn mãi mãi là biển cát mà ông đã biết từ thuở nhỏ. Tuy vậy, tự đáy lòng mình, ông không thể không cảm thấy vui trước hạnh phúc của mỗi người lữ khách, sau bao ngày chỉ có cát vàng với trời xanh nay được thấy chà là xanh tươi hiện ra trước mắt. ‘Có thể Thượng đế tạo ra sa mạc chỉ để cho con người biết quý trọng cây chà là,’ ông nghĩ.”\r\n\r\n- Trích Nhà giả kim\r\n\r\nNhận định\r\n\r\n“Sau Garcia Márquez, đây là nhà văn Mỹ Latinh được đọc nhiều nhất thế giới.” - The Economist, London, Anh\r\n\r\n \r\n\r\n“Santiago có khả năng cảm nhận bằng trái tim như Hoàng tử bé của Saint-Exupéry.” - Frankfurter Allgemeine Zeitung, Đức.', 'Nhã Nam', 'NXB Hội Nhà Văn', '2023-04-19 20:59:45'),
('qM3VHN5STe', 'Bạn Chính Là Kho Báu Của Bản Thân', 'Kính Tịnh', 125900, 'https://cdn0.fahasa.com/media/flashmagazine/images/page_images/ban_chinh_la_kho_bau_cua_ban_than/2022_09_20_15_53_54_1-390x510.jpg', 'Bạn Chính Là Kho Báu Của Bản Thân\r\n\r\nCuộc sống là một chiếc áo choàng bình thường, nhưng bạn hãy khảm ngọc lên nó bằng sự chăm chỉ và nỗ lực của chính mình.\r\n\r\nNhững sắp đặt của cuộc đời khiến đôi khi chúng ta cảm thấy thật thất vọng và bất lực trước hiện thực. Đối mặt với bộn bề cuộc sống chỉ ước bản thân có thể mạnh mẽ, trở nên xinh đẹp và giỏi giang hơn để những thiệt thòi đừng làm tủi hổ.\r\n\r\nSự trưởng thành theo năm tháng, sự tỉnh ngộ sau mỗi lần bị tổn thương đến tuyệt vọng khiến chúng ta dần trở nên chai sạn, gặp những vấn đề chỉ cười trừ cho qua, sống như những hiệp sĩ mình đồng da sắt, bảo vệ bản thân trong tấm áo giáp do chính mình tạo ra. Trái tim chúng ta đã dần dần “trưởng thành” như thế, nhưng đó cũng là món quà mà thời gian dành tặng cho những người phụ nữ kiên cường.\r\n\r\nCuộc đời này, điều duy nhất xứng đáng để chúng ta nỗ lực chính là trở thành người mình muốn trở thành. Chặng đường còn dài, chúng ta hãy cùng nhau nỗ lực.\r\n\r\nĐọc cuốn sách Bạn Chính Là Kho Báu Của Bản Thân này mới thấy rằng, mỗi sinh mệnh chúng ta giống như một quả cầu đàn hồi và không ngừng thay đổi vậy - Cốt lõi của nó chính là bản thân bạn. Quả cầu này còn được tạo thành bởi nhiều khía cạnh như: Sức khoẻ, ngoại hình, cảm xúc, sở thích, sự theo đuổi giá trị tinh thần, vợ/chồng, bạn bè, công việc, con cái hay bố mẹ… Tất cả những điều này tạo nên sinh mệnh. Chúng vừa gắn kết với nhau vừa có khả năng tự mình đảm đương công việc của riêng chúng, mỗi khía cạnh đều không thể bị rò rỉ, cũng không thể mất đi, nếu không, quả cầu sinh mệnh sẽ dần xẹp xuống, thậm chí không thể bật lên được nữa. Cuộc sống là sự viên mãn mà bạn tự theo đuổi, là quả cầu hoạt động luôn biến hoá không ngừng.\r\n\r\nThời gian trong tương lai có thể bình lặng, cũng có thể bạn sẽ gặp những khó khăn chưa từng trải qua. Hy vọng trên con đường trưởng thành, bạn mất đi điều gì, có được điều gì, đều có thể thông qua đó mà rút ra cho mình những bài học, tự cho bản thân một cuộc đời thật rực rỡ. Sau cùng, mong bạn sẽ luôn được là chính mình!\r\n\r\nMột số điều mà tác giả Bạn Chính Là Kho Báu Của Bản Thân muốn gửi gắm tới các cô gái:\r\n\r\n- Lòng đố kỵ sẽ làm con người mất đi lý trí. Đôi khi, sự tồn tại của bạn cũng đã khiến người khác đố kỵ, nhưng điều này không phải là lỗi của bạn.\r\n\r\n- Đôi khi, chúng ta cũng không thể hiểu được sự phức tạp của bản tính con người, Chúng ta không thể yêu cầu người khác, nhưng ít nhất chúng ta phải bảo vệ chính mình.\r\n\r\n- Không đáng để dành thời gian và sức lực quý báu của bản thân đi báo thù những người làm tổn thương mình, nhưng tuyệt đối không được nhân nhượng. Đồng thời, phải nhanh chóng tránh xa, không được để họ có cơ hội làm tổn thương bạn thêm một lần nào nữa.\r\n', 'MCBooks', 'Hồng Đức', '2023-04-25 00:56:15'),
('qMwf9z5LvP', 'Thiên Tài Bên Trái, Kẻ Điên Bên Phải', 'Cao Minh', 124000, 'https://cdn0.fahasa.com/media/catalog/product/b/_/b_a-thi_n-t_i-b_n-tr_i-k_-_i_n-b_n-ph_i_1.jpg', 'Thiên Tài Bên Trái, Kẻ Điên Bên Phải\r\n\r\nNẾU MỘT NGÀY ANH THẤY TÔI ĐIÊN, THỰC RA CHÍNH LÀ ANH ĐIÊN ĐẤY!\r\n\r\nHỡi những con người đang oằn mình trong cuộc sống, bạn biết gì về thế giới của mình? Là vô vàn thứ lý thuyết được các bậc vĩ nhân kiểm chứng, là luật lệ, là cả nghìn thứ sự thật bọc trong cái lốt hiển nhiên, hay những triết lý cứng nhắc của cuộc đời?\r\n\r\nLại đây, vượt qua thứ nhận thức tẻ nhạt bị đóng kín bằng con mắt trần gian, khai mở toàn bộ suy nghĩ, để dòng máu trong bạn sục sôi trước những điều kỳ vĩ, phá vỡ mọi quy tắc. Thế giới sẽ gọi bạn là kẻ điên, nhưng vậy thì có sao? Ranh giới duy nhất giữa kẻ điên và thiên tài chẳng qua là một sợi chỉ mỏng manh: Thiên tài chứng minh được thế giới của mình, còn kẻ điên chưa kịp làm điều đó. Chọn trở thành một kẻ điên để vẫy vùng giữa nhân gian loạn thế hay khóa hết chúng lại, sống mãi một cuộc đời bình thường khiến bạn cảm thấy hạnh phúc hơn?\r\n\r\nThiên tài bên trái, kẻ điên bên phải là cuốn sách dành cho những người điên rồ, những kẻ gây rối, những người chống đối, những mảnh ghép hình tròn trong những ô vuông không vừa vặn… những người nhìn mọi thứ khác biệt, không quan tâm đến quy tắc. Bạn có thể đồng ý, có thể phản đối, có thể vinh danh hay lăng mạ họ, nhưng điều duy nhất bạn không thể làm là phủ nhận sự tồn tại của họ. Đó là những người luôn tạo ra sự thay đổi trong khi hầu hết con người chỉ sống rập khuôn như một cái máy. Đa số đều nghĩ họ thật điên rồ nhưng nếu nhìn ở góc khác, ta lại thấy họ thiên tài. Bởi chỉ những người đủ điên nghĩ rằng họ có thể thay đổi thế giới mới là những người làm được điều đó. \r\n\r\nChào mừng đến với thế giới của những kẻ điên.\r\n', 'AZ Việt Nam', 'NXB Thế Giới', '2023-04-25 00:21:52'),
('reDmkqiSH1', 'Lời từ chối hoàn hảo', 'Willam Ury', 103500, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935251418477.jpg', '', 'Alpha Books', 'NXB Công Thương', '2023-04-19 20:59:45'),
('STr7BmWAm2', 'Ghi Chép Pháp Y - Những Cái Chết Bí Ẩn', 'Lưu Hiểu Huy', 112300, 'https://cdn0.fahasa.com/media/catalog/product/b/_/b_a-tr_c---ph_p-y-600.jpg', 'GHI CHÉP PHÁP Y - NHỮNG CÁI CHẾT BÍ ẨN –\r\n\r\nLàm cách nào để một “xác chết lên tiếng”? - đó là công việc của bác sĩ pháp y. \r\n\r\n“Ghi chép pháp y - Những cái chết bí ẩn” là cuốn sách nằm trong hệ liệt với “Pháp y Tần Minh” - bộ tiểu thuyết nổi đình đám của xứ Trung đã được chuyển thể thành series phim. Cuốn sách tổng hợp những vụ án có thật, được viết bởi bác sĩ pháp y Lưu Hiểu Huy - người có 15 năm kinh nghiệm và từng mổ xẻ hơn 800 tử thi. \r\n\r\nTrải qua 15 câu chuyện kinh hoàng, cuốn sách sẽ đưa bạn bước vào hiện trường của những vụ án man rợ như: xác chết phi tang dưới cánh đồng ngô, thi thể thiếu nữ không lành lặn, xác chết nhầy nhụa đang bị giòi bọ đục khoét hay một thi thể co cứng trong màng bọc nilon…lần theo những dấu vết, ghép lại sự thật từ những mảnh vụn thi thể, nguyên nhân của cái chết sẽ dần được hé mở.\r\n\r\nMỗi vụ án đều là một ẩn số, hiện trường vụ án bao gồm cả xác chết chính là chiếc chìa khóa để truy tìm hung thủ ngay cả khi nó không còn nguyên vẹn. Vậy làm cách để các bác sĩ pháp y có thể xác định thương tật, các dấu hiệu cơ thể bị xâm hại? Cuốn sách này sẽ trình bày những kiến thức chuyên môn và quy trình xử lý vụ án, từ hiện trường vụ án đến đài giải phẫu, xét nghiệm nội tạng, phân tích chất độc… từng bước tìm ra bí mật ẩn giấu sau mỗi tử thi, phơi bày những manh mối liên quan đến tội ác con người và lỗ hổng của xã hội.\r\n\r\nKhông chỉ thuật lại những vụ án và các phương pháp pháp y, cuốn sách còn chứa đựng cảm xúc của tác giả đối với nghề nghiệp và sự méo mó của bản chất con người. Chẳng hạn, một đứa trẻ bệnh nặng phải đau đớn đến thế nào khi bị người nhà đem ra làm công cụ để đòi bồi thường? Cuốn sách sẽ phơi bày những góc khuất của xã hội, những hành vi thủ ác, sự biến chất và mất nhân tính của một bộ phận con người trong xã hội.\r\n\r\nNếu bạn tò mò muốn biết cách đánh giá tuổi của người chết dựa trên mức độ mòn răng? Hay cách dùng một con giòi từ người chết để suy ra thời điểm chính xác của vụ án giết người?  Hy vọng cuốn sách này sẽ cho bạn trải nghiệm khó quên về nghề pháp y.', 'AZ Việt Nam', 'Thanh Niên', '2023-04-25 00:58:41'),
('tbu427TZ89', 'Khi Hơi Thở Hóa Thinh Không', 'Paul Kalanithi', 124000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_220964.jpg', 'Khi hơi thở hóa thinh không là tự truyện của một bác sĩ bị mắc bệnh ung thư phổi. Trong cuốn sách này, tác giả đã chia sẻ những trải nghiệm từ khi mới bắt đầu học ngành y, tiếp xúc với bệnh nhân cho tới khi phát hiện ra mình bị ung thư và phải điều trị lâu dài.\r\n\r\nKalanithi rất yêu thích văn chương nên câu chuyện của anh đã được thuật lại theo một phong cách mượt mà, dung dị và đầy cảm xúc. Độc giả cũng được hiểu thêm về triết lý sống, triết lý nghề y của Kalanithi, thông qua ký ức về những ngày anh còn là sinh viên, rồi thực tập, cho đến khi chính thức hành nghề phẫu thuật thần kinh. “Đối với bệnh nhân và gia đình, phẫu thuật não là sự kiện bi thảm nhất mà họ từng phải đối mặt và nó có tác động như bất kỳ một biến cố lớn lao trong đời. Trong những thời điểm nguy cấp đó, câu hỏi không chỉ đơn thuần là sống hay chết mà còn là cuộc sống nào đáng sống.” – Kalanithi luôn biết cách đưa vào câu chuyện những suy nghĩ sâu sắc và đầy sự đồng cảm như thế.\r\n\r\nBạn bè và gia đình đã dành tặng những lời trìu mến nhất cho con người đáng kính trọng cả về tài năng lẫn nhân cách này. Dù không thể vượt qua cơn bệnh nan y, nhưng thông điệp của tác giả sẽ còn khiến người đọc nhớ mãi.', 'Alpha Books', 'NXB Lao Động', '2023-04-25 00:36:29'),
('tg086V4ivg', 'Chúng Ta Sống, Là Vì…?', 'Nguyễn Phong Việt', 87000, 'https://cdn0.fahasa.com/media/catalog/product/t/t/ttph---11_11---ch_ng-ta-s_ng_-l_-v_..._---wavebooksb_a-1_1.jpg', 'Chúng Ta Sống, Là Vì…?\r\n\r\nSau cột mốc khép lại hành trình mười năm sáng tác thơ qua tập thơ Đã đi qua thương nhớ, ra mắt vào mùa Giáng sinh năm 2021, Nguyễn Phong Việt chính thức trở lại, nối tiếp sứ mệnh ngòi bút của mình dưới một hình thức khác, được thể hiện qua cuốn tản văn mới nhất Chúng ta sống, là vì…?\r\n\r\nChúng ta sống, là vì…? tập hơn 43 mẩu chuyện dài có, ngắn có, mang đậm dấu ấn và phong cách rất riêng mà Nguyễn Phong Việt đã ghi vào lòng độc giả của mình trong suốt những năm qua. Đó là những câu từ được viết ra bởi những chiêm nghiệm thực tế cộng hưởng với cảm xúc chân thành, mộc mạc của tác giả trước dòng chảy cuộc sống. Đó là những ký ức giản dị như món ăn tuổi thơ, những vấp váp trái tim đã trải qua trước khi cứng cáp hơn, và đó cũng có thể là tâm tư sau một năm đại dịch với nhiều mất mát, đổi thay. Cuốn sách này không quá lãng mạn như thơ, mà chứa nhiều góc cạnh hiện thực hơn.\r\n\r\nĐộc giả sẽ bắt gặp hình ảnh bản thân ở đâu đó trong những trang sách bởi câu “Chúng ta sống, là vì…?” vốn là một câu tự vấn mà trong chuỗi ngày sống, lắm lúc chúng ta cũng tự đặt ra cho chính mình. Đáp án cho câu hỏi trên của mỗi người sẽ khác nhau, bởi chúng ta luôn có sự khác biệt. Những dẫu cuộc đời có thế nào, thì “quỹ thời gian của con người cũng chỉ có chừng đó, sao không chọn vui - cho chính mình - mà phải đi chọn những thứ khác…?”.\r\n\r\nChúng ta sống, là vì…? sẽ là một món quà để độc giả gói ghém những gì đã xảy trong một năm, xoa dịu những góc còn gồ ghề và chuẩn bị tâm thế tích cực cho một năm mới.\r\n\r\nSách được phát hành bởi thương hiệu sách Wavebooks với sứ mệnhtrở thành người bạn đồng hành tin cậy cho quá trình phát triển kỹ năng và tâm hồn người Việt trẻ.', 'AZ Việt Nam', 'Thế Giới', '2023-04-25 00:57:04'),
('tRCgaj9hRs', 'Bóng Tối Thiên Đường', 'Erich Maria Remarque', 72000, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8936071673817.jpg', 'B&oacute;ng Tối Thi&ecirc;n Đường\r\n\r\nSau những năm th&aacute;ng bị h&agrave;nh hạ trong trại tập trung t&agrave;n khốc tr&ecirc;n ch&iacute;nh qu&ecirc; hương m&igrave;nh, nh&acirc;n vật ch&iacute;nh của B&oacute;ng tối thi&ecirc;n đường, Robert Ross cuối c&ugrave;ng cũng được an to&agrave;n ở Hoa Kỳ. Ch&iacute;nh tr&ecirc;n miền đất mơ ước của những người tị nạn chiến tranh n&agrave;y, anh đ&atilde; t&igrave;m thấy những g&igrave; m&agrave; m&igrave;nh hằng mơ ước: một t&igrave;nh y&ecirc;u cuồng nhiệt với n&agrave;ng Natasha xinh đẹp v&agrave; quyến rũ, một c&ocirc;ng việc với đồng lương hậu hĩnh, một cuộc sống tự do kh&ocirc;ng c&ograve;n nguy hiểm r&igrave;nh rập&hellip; Những tưởng tất cả điều đ&oacute; sẽ mang lại cho Ross hạnh ph&uacute;c vĩnh cửu, nhưng b&oacute;ng đen qu&aacute; khứ vẫn lẩn khuất trong những giấc mơ hằng đ&ecirc;m; ch&uacute;ng b&aacute;m lấy anh, th&ocirc;i th&uacute;c anh đi đến quyết định trọng đại nhất cho cuộc đời m&igrave;nh.\r\n\r\nL&agrave; tiểu thuyết sau c&ugrave;ng được viết trong những năm th&aacute;ng cuối đời ở Thụy Sĩ, B&oacute;ng tối thi&ecirc;n đường ch&iacute;nh l&agrave; niềm nhớ thương da diết hướng về qu&ecirc; hương của nh&agrave; văn Remarque. Th&ocirc;ng qua t&aacute;c phẩm, &ocirc;ng gửi gắm t&igrave;nh y&ecirc;u bất tận d&agrave;nh cho đất nước của m&igrave;nh, d&ugrave; ch&iacute;nh mảnh đất ấy đ&atilde; từng chối bỏ &ocirc;ng.\r\n\r\nTh&ocirc;ng tin t&aacute;c giả:\r\n\r\nErich Maria Remarque (1898 &ndash; 1970) l&agrave; nh&agrave; văn lừng danh nước Đức. &Ocirc;ng nổi tiếng với Ph&iacute;a T&acirc;y kh&ocirc;ng c&oacute; g&igrave; lạ, một trong những t&aacute;c phẩm hay nhất về Thế chiến I. C&aacute;c t&aacute;c phẩm ti&ecirc;u biểu kh&aacute;c như Ba người bạn, Khải Ho&agrave;n M&ocirc;n, Thời gian để sống v&agrave; thời gian để chết, B&oacute;ng tối thi&ecirc;n đường&hellip; cũng g&oacute;p phần kh&ocirc;ng nhỏ trong việc khẳng định t&agrave;i năng v&agrave; danh tiếng của &ocirc;ng khắp năm ch&acirc;u. Năm 1931, &ocirc;ng được đề cử giải Nobel Văn chương v&agrave; H&ograve;a b&igrave;nh.\r\n\r\nB&oacute;ng tối thi&ecirc;n đường (Schatten im Paradies &ndash; 1971) l&agrave; nỗi l&ograve;ng quay quắt của t&aacute;c giả: mong mỏi trở về cố hương để t&igrave;m lại mảnh đất dưới ch&acirc;n, cũng l&agrave; t&igrave;m lại sự b&igrave;nh an trong t&acirc;m hồn m&agrave; &ocirc;ng kh&ocirc;ng thể t&igrave;m thấy ở bất cứ đ&acirc;u, ngay cả ở những th&agrave;nh phố mệnh danh l&agrave; thi&ecirc;n đường.', 'Đông A', 'NXB Thanh Niên', '2023-04-25 01:11:28'),
('tttfTtHORQ', 'Thao Túng Tâm Lý', 'Shannon Thomas, LCSW', 126750, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8936066692298-1.jpg', 'Thao Túng Tâm Lý - Nhận Diện, Thức Tỉnh Và Chữa Lành Những Tổn Thương Tiềm Ẩn\r\n\r\nTrong cuốn “Thao túng tâm lý”, tác giả Shannon Thomas giới thiệu đến độc giả những hiểu biết liên quan đến thao túng tâm lý và lạm dụng tiềm ẩn.\r\n\r\n“Thao túng tâm lý” là một dạng của lạm dụng tâm lý. Cũng giống như lạm dụng tâm lý, thao túng tâm lý có thể xuất hiện ở bất kỳ môi trường nào, từ bất cứ đối tượng độc hại nào. Đó có thể là bố mẹ ruột, anh chị em ruột, người yêu, vợ hoặc chồng, sếp hay đồng nghiệp… của bạn. Với tính chất bí hiểm, âm thầm gây hại, thao túng, lạm dụng tâm lý làm tổn thương cảm xúc, lòng tự trọng, cuộc sống của mỗi nạn nhân. Những người từng bị lạm dụng tâm lý thường không thể miêu tả rõ ràng điều gì đã xảy ra với mình. Trong nhiều trường hợp, nạn nhân bị thao túng đến mức tự hỏi có phải họ là người có lỗi, thậm chí họ có phải là người độc hại trong mối quan hệ đó.\r\n\r\nHành vi của (những) kẻ lạm dụng giống như một trò chơi bí ẩn, tệ hại và lặp đi lặp lại, do một cá nhân hoặc một nhóm người thực hiện với nạn nhân. Những hành vi này được ngụy trang tài tình đến mức hành vi độc ác của họ diễn ra thường xuyên, nhưng không bị phát hiện.\r\nShannon Thomas giới thiệu những kiến thức cơ bản về đặc điểm, các dạng của lạm dụng tâm lý nói chung, thao túng tâm lý nói riêng, và cung cấp cho người đọc hành trình chữa lành gồm 6 giai đoạn:\r\n\r\n- Giai đoạn 1: Tuyệt vọng\r\n\r\n- Giai đoạn 2: Nhận diện\r\n\r\n- Giai đoạn 3: Thức tỉnh\r\n\r\n- Giai đoạn 4: Những ranh giới\r\n\r\n- Giai đoạn 5: Phục hồi\r\n\r\n- Giai đoạn 6: Duy trì\r\n\r\nBằng những kiến thức chuyên sâu và sự thấu hiểu, tác giả sẽ giúp bạn từng bước vượt qua những rắc rối của thao túng tâm lý, lạm dụng tiểm ẩn để có cuộc sống ý nghĩa và lành mạnh hơn.\r\n\r\nThông tin tác giả:\r\n\r\nShannon Thomas, là một nhà giám sát công tác xã hội y tế được cấp phép hành nghề, và là chủ phòng tư vấn/chuyên gia tư vấn tâm lý chính của phòng tư vấn Southlake Christian (SCC) ở Southlake, bang Texas.\r\n\r\nSCC từng nhận giải thưởng “Phòng thực hành tư vấn tâm lý tốt nhất” năm 2016 của Living Magazine khu vực Hạt Northeast Tarrant tại Dallas-Fort Worth.\r\n\r\nThomas là Trợ giảng chuyên ngành và là thành viên Ủy ban tư vấn của Khoa Công tác xã hội – Trường Đại học Texas Christian.\r\n\r\nCách tiếp cận khi tư vấn tâm lý của cô ấy xuất phát từ góc nhìn của một nhà tư vấn tâm lý được cấp phép hành nghề đồng thời từ góc nhìn của một người đi trước, một người sống sót sau khi bị lạm dụng tâm lý.', '1980 Books', 'NXB Dân Trí', '2023-04-19 20:59:45'),
('TtVwJGbE0W', '[Phiên chợ sách cũ] Đứa Con Lưu Lạc Của Singapore', 'Kaiwen Leong', 43000, 'https://cdn0.fahasa.com/media/catalog/product/9/7/9789822288551_thanh_ly.jpg', 'Đứa Con Lưu Lạc Của Singapore Đứa Con Lưu Lạc Của Singapore l&agrave; một hồi ức đầy kinh ngạc với những t&igrave;nh tiết kh&oacute; qu&ecirc;n, được kể bằng giọng văn s&uacute;c t&iacute;ch, l&ocirc;i cuốn. Đ&acirc;y l&agrave; một c&acirc;u chuyện ấn tượng về l&ograve;ng can đảm v&agrave; ki&ecirc;n tr&igrave;. Quyển s&aacute;ch n&agrave;y, tất nhi&ecirc;n, đ&aacute;ng đọc với bất cứ ai mong muốn th&agrave;nh c&ocirc;ng trong cuộc sống. Đ&acirc;y cũng l&agrave; lời nhắc nhở cho những ai nản l&ograve;ng tho&aacute;i ch&iacute;, đ&oacute; l&agrave; đừng đ&aacute;nh mất hy vọng m&agrave; h&atilde;y nghĩ ra những chiến lược c&oacute; thể vực m&igrave;nh dậy. Nhận định Nửa hồi k&yacute;, nửa kể chuyện, quyển s&aacute;ch n&agrave;y b&agrave;n về tầm quan trọng của khả năng tự lập v&agrave; quyết t&acirc;m cao độ, cũng như l&ograve;ng trắc ẩn v&agrave; t&igrave;nh bạn. Tr&ecirc;n hết, quyển s&aacute;ch n&agrave;y c&oacute; &yacute; n&oacute;i rằng &lsquo;kh&ocirc;ng g&igrave; l&agrave; kh&ocirc;ng thể&rsquo; khi ta c&oacute; một mục ti&ecirc;u d&agrave;i hạn v&agrave; ki&ecirc;n tr&igrave; theo đuổi n&oacute;. (Gi&aacute;o sư Shirley M. Tilghman, chủ tịch Đại học Princeton) Một v&iacute; dụ minh họa về th&agrave;nh c&ocirc;ng đến từ một mục ti&ecirc;u r&otilde; r&agrave;ng v&agrave; sức chịu đựng bền bỉ. Nhiều b&agrave;i học cho thế hệ trẻ của ch&uacute;ng ta. (Inderjit Singh - Đại biểu quốc hội)', 'Cty TGMTác', 'NXB Phụ Nữ', '2023-04-25 01:36:17');
INSERT INTO `products` (`product_id`, `name`, `author`, `price`, `image`, `description`, `supplier`, `publiser`, `date`) VALUES
('u6V0ebvajw', 'Kiếp Nào Ta Cũng Tìm Thấy Nhau', 'Brian L Weiss', 74000, 'https://cdn0.fahasa.com/media/catalog/product/6/7/67db9bf2590d75f978e68f9dcfe0db9a.jpg', 'Kiếp Nào Ta Cũng Tìm Thấy Nhau (Tái Bản 2022)\r\n\r\nKiếp nào ta cũng tìm thấy nhau là cuốn sách thứ ba của Brain L. Weiss – một nhà tâm thần học có tiếng. Trước đó ông đã viết hai cuốn sách: cuốn đầu tiên là Ám ảnh từ kiếp trước, cuốn sách mô tả câu chuyện có thật về một bệnh nhân trẻ tuổi cùng với những liệu pháp thôi miên về kiếp trước đã làm thay đổi cả cuộc đời tác giả lẫn cô ấy. Cuốn sách đã bán chạy trên toàn thế giới với hơn 2 triệu bản in và được dịch sang hơn 20 ngôn ngữ. Cuốn sách thứ hai Through Time into Healing (Đi qua thời gian để chữa lành), mô tả những gì tác giả đã học được về tiềm năng chữa bệnh của liệu pháp hồi quy tiền kiếp. Trong cuốn sách đều là những câu chuyện người thật việc thật. Nhưng câu chuyện hấp dẫn nhất lại nằm trong cuốn sách thứ ba.\r\n\r\nKiếp nào ta cũng tìm thấy nhau nói về những linh hồn tri kỷ, những người có mối liên kết vĩnh viễn với nhau bằng tình yêu thương, luôn gặp lại nhau hết lần này đến lần khác, qua hết kiếp này tới kiếp khác. Chúng ta sẽ tìm thấy và nhận ra tri kỷ của mình như thế nào, rồi đưa ra những quyết định làm thay đổi cuộc sống của chính mình ra sao là những khoảnh khắc quan trọng và xúc động nhất trong cuộc đời mỗi người.\r\n\r\nĐịnh mệnh sẽ dẫn lối cho những linh hồn tri kỷ hội ngộ. Chúng ta sẽ gặp họ. Nhưng quyết định làm gì sau đó lại là quyền tự do lựa chọn của mỗi người. Một lựa chọn sai lầm hoặc một cơ hội bị bỏ lỡ có thể dẫn đến nỗi cô đơn và thống khổ tột cùng. Và một lựa chọn đúng đắn, một cơ hội được nắm bắt có thể mang lại niềm hạnh phúc sâu sắc.\r\n\r\nNhững gì tác giả viết trong sách được ghi lại từ hồ sơ bệnh án, băng thu âm và trí nhớ. Chỉ có tên và một vài chi tiết nhỏ được thay đổi để giữ tính bảo mật. Đây là câu chuyện về vận mệnh và hy vọng. Đây là câu chuyện xảy ra âm thầm mỗi ngày.\r\n\r\nNgày này năm đó, đã có người lắng nghe.\r\n\r\nLinh hồn tựa như nước\r\n\r\nRơi xuống từ thiên đường\r\n\r\nLên trời như khói sương\r\n\r\nRồi trở về với đất\r\n\r\nChuỗi tuần hoàn bất tận.\r\n\r\nGOETHE', 'Thái Hà', 'NXB Lao Động', '2023-04-25 00:32:45'),
('uomxNwEzUI', 'Hồ Sơ Tâm Lý Học Tâm Thần Hay Kẻ Điên', 'Mục Qua', 141000, 'https://cdn0.fahasa.com/media/catalog/product/t/b/tbph--h_-s_-t_m-l_-h_c---t_m-th_n-hay-k_-_i_nb_a-tr_c600.jpg', 'HỒ SƠ TÂM LÝ HỌC - TÂM THẦN HAY KẺ ĐIÊN  - Điều gì đang xảy ra trong thế giới của những kẻ tâm thần\r\n\r\n“Có lúc, chúng ta gọi bệnh tâm thần là bệnh cố chấp, các bệnh nhân đều cố chấp sống trong thế giới kì thú của mình, không chịu hòa nhập với người xung quanh, không thể giống với đám đông, họ cố chấp dằn vặt chính mình.”\r\n\r\nNếu không có chuyên môn chúng ta sẽ chẳng thể hiểu nổi điều gì đang thống trị trong thế giới của những kẻ tâm thần. Tuy nhiên, nếu bạn đã sẵn sàng tiến thêm một bước vào mảng tối trong thế giới của họ, hãy cầm cuốn sách này lên.\r\n\r\nCuốn sách “Hồ sơ tâm lý học - Tâm thần hay kẻ điên” của tác giả Mục Qua sẽ tái hiện chân thực những khốn cảnh tâm lý của những bệnh nhân tâm thần dưới góc nhìn của bác sĩ, cũng như quá trình đấu tranh và hội phục tâm lý của họ. Từ đó phơi bày những góc khuất của xã hội, môi trường gia đình, bạo lực, tội phạm,... mối liên hệ giữa bệnh nhân và xã hội. \r\n\r\nCuốn sách bao gồm 13 câu chuyện khác nhau diễn ra trong cùng một bệnh viện tâm thần, nó lột tả chi tiết từng khía cạnh về những căn bệnh tâm thần như chứng cuồng phóng hỏa, rối loạn nhân dạng phân ly, trầm cảm cười, chứng hoang tưởng,....Xuyên suốt cuốn sách bạn sẽ bắt gặp một nhóm “người điên” đang sống trong vực thẳm. Họ có thể là những thiên tài kỳ quái với những ý tưởng kỳ lạ không thể hiểu nổi. Là những kẻ lừa đảo có học, giấu đi nhân cách phản xã hội và phản nhân loại một cách đầy hoàn hảo, càng là những kẻ cô độc đến đáng thương,... Họ mang trong mình những tâm hồn tan nát song đầy nóng bỏng ẩn sau hai từ “kẻ điên”. \r\n\r\nĐặc biệt, những bối cảnh tâm lý trong cuốn sách đều dựa trên những câu chuyện thực tế của chính bản thân tác giả khi làm việc tại Trung Tâm sức khỏe Tâm thần từ khi là thực tập sinh. Dưới ngòi bút của mình, bổ sung thêm kiến thức tâm lý học và trích dẫn lý thuyết một cách rõ ràng cuốn sách sẽ dẫn dắt bạn đi qua tất cả những cung bậc cảm xúc từ hoài nghi, phẫn nỗ đến bi thương và đồng cảm, phá vỡ những định kiến về căn bệnh này.\r\n\r\nBiến mình trở thành một đứa trẻ ngoan, giết chết nhân cách vốn có, một đứa trẻ đã phải trải qua những gì để có thể làm hài lòng cha mẹ? Cuốn sách sẽ đưa bạn xuyên qua những khu rừng tăm tối của trái tim con người và chạm vào những mảnh đời đang chờ đợi được sửa chữa. Bởi suy cho cùng những bệnh nhân tâm thần chính là những con người đáng thương nhất khi họ luôn phải chạy trốn trong chính vỏ bọc mà mình tạo ra. ', 'AZ Việt Nam', 'Thanh Niên', '2023-04-25 00:23:28'),
('USrDdAPokQ', 'Nghệ Thuật Thuyết Trình Bằng Câu Chuyện', 'Bùi Thị Ngọc Thu', 169000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_196449.jpg', 'Storytelling – Nghệ thuật thuyết trình bằng câu chuyện\r\n\r\nHãy thuyết phục thế giới bằng cách riêng của bạn\r\n\r\nCuốn sách này sẽ giúp các bạn chứng kiến cuộc gặp gỡ lịch sử giữa khoa học thuyết trình và nghệ thuật kể chuyện. Một sự kết hợp đầy tương phản nhưng lại bổ trợ mãnh mẽ cho nhau giữa tư duy lý trí và khoa học xúc cảm. Cuốn sách đã mở ra những quan điểm mới mẻ về thuyết trình bằng câu chuyện, một phương pháp giúp ý tưởng tuyệt vời của bạn lan tỏa rộng khắp. Tác giả tin rằng mỗi người là một câu chuyện rất đặc biệt và xứng đáng lan tỏa. Và không có một phương pháp thuyết trình nào là tối ưu nhất. Bởi vì thế giới rộng lớn biết chừng nào, và phong cách của các diễn giả, cũng như nhu cầu của khán giả là rất đa dạng! Do đó, hãy làm chủ bài thuyết trình và THUYẾT PHỤC THẾ GIỚI THEO CÁCH RIÊNG CỦA BẠN.\r\n\r\nVới những nghiên cứu, học hỏi và ứng dụng vào trong thực tiễn nội dung cuốn sách đã phân tích tập trung vào hai từ khóa chính: Phương pháp thuyết phục khi thuyết trình và nghệ thuật kể chuyện đầy cảm hứng. Các kiến thức và nội dung trong cuốn sách này không dành cho một đối tượng cụ thể hay lĩnh vực cụ thể nào, mà nó dành cho tất cả các độc giả khi muốn thuyết phục một điều gì đó với người nghe.\r\n\r\nCác đoạn hay trong sách:\r\n\r\nKhi tôi làm việc với khách hàng cũng như khi chia sẻ trong các chương trình đào tạo về thuyết trình, những câu hỏi tôi thường xuyên nhận được:\r\n\r\nLàm thế nào đề không quên bài khi thuyết trình?\r\n\r\nLàm sao để nói chuyện có đầu có đuôi?\r\n\r\nLàm sao để nói chuyện thuyết phục?\r\n\r\nLàm sao để nói mà người nghe hiểu ý của mình?\r\n\r\nLàm sao để khi thuyết trình, cấp trên cắt ngang vẫn còn nhớ cần phải nói gì tiếp?\r\n\r\n…\r\n\r\nTôi nhận thấy những câu hỏi này đều liên quan đến một khía cạnh rất quan trọng, đó là cách cấu trúc bài thuyết trình sao cho dễ nhớ và thuyết phục. Chúng ta có thể hình dung về cấu trúc bài thuyết trình cũng giống như cấu trúc một ngôi nhà vậy. Trước khi chúng ta quyết định xây một ngôi nhà đẹp thì nền móng của ngôi nhà cần vững chắc dựa trên cấu trúc bản vẽ cụ thể. Dù nhiều người thợ đang cùng nhau xây nhà, nhưng họ đều hiểu rõ mình cần làm gì. Người thuyết trình cũng vậy, dù khán giả có chen ngang hay có chuyện gì xảy ra, người thuyết trình cũng phải nắm rất chắc mình đang nói ở phần nào, sắp tới là phần nào. Tôi nhận thấy điều này không khó và chúng ta có thể bắt đầu bằng việc sắp xếp bài nói theo một trật tự có chủ đích.\r\n\r\nKhi ánh đèn sân khấu bật lên, hàng nghìn ánh mắt bên dưới đều tập trung vào bạn. Tim bạn đập nhanh hơn, tay vã mồ hôi, mắt mờ đi và bạn không biết nên bắt đầu từ đâu. Vì lúc này, trong đầu bạn đang suy nghĩ là mọi người đang nhìn mình và đánh giá về mình: “Hôm nay mình có chỉn chu không? Quần áo có phù hợp không? Trang điểm có đẹp không? Đầu tóc có gọn gàng không? Giọng mình có dễ nghe không?”… những suy nghĩ ngập tràn tâm trí bạn và làm bạn cảm thấy khó khăn trong việc thuyết trình.\r\n\r\nĐó cũng là những khó khăn ban đầu tôi đã gặp phải, nhưng khi đi qua con đường này một thời gian đủ dài, thì điều tôi muốn chia sẻ với bạn là “Hãy bỏ cái tôi của mình đi”. Chúng ta không phải là người hùng trên sân khấu. Khán giá tới đây không phải để rảnh rỗi đánh giá bạn, họ tới để mong đợi những điều hữu ích hơn. Vì vậy, thay vì tập trung nghĩ về bản thân mình, hãy suy nghĩ làm cách nào để mang lại những điều hữu ích nhất cho khán giả của mình. Khán giả mới chính là nhân vật chính trong buổi thuyết trình. Bạn không phải là người hùng trên sân khấu với trang phục lộng lẫy, thao thao những điều thú vị về bạn hay những điều bạn rất tâm đắc và tự hào về sản phẩm hay dịch vụ của công ty bạn. Khán giả đến đó để coi bạn có thể làm gì cho họ, bạn có thể giúp gì cho họ hay sản phẩm/ dịch vụ của bạn có giải quyết được vấn đề của họ không?\r\n\r\nTôi cho rằng, mỗi ngày trôi qua, chúng ta đều thực hiện hàng trăm buổi hội thoại, giao tiếp và trong ta đều mong muốn mình giao tiếp hiệu quả. Khi một người lãnh đạo nói chuyện trong buổi họp với đội nhóm của mình, đó cũng là một buổi thuyết trình giao tiếp. Dù 5 phút hay 50 phút, vị lãnh đạo cũng mong muốn có thể thuyết phục đội ngũ về một định hướng mới, hay truyền cảm hứng về sự thay đổi của tổ chức. Khi bạn bước lên sân khấu, hay nói chuyện trong căn phòng với bất kỳ ai, thậm chí là trong giao tiếp hàng ngày với con cái, … bạn đều có thể áp dụng các kỹ thuật này để đạt mục tiêu giao tiếp và thuyết trình. Và dù bạn đang làm bất kỳ công việc gì thì giao tiếp truyền cảm hứng và thuyết trình thuyết phục sẽ luôn là công cụ hiệu quả đưa bạn đến những đỉnh cao hơn trong sự nghiệp và cuộc sống. Đối với những người làm công việc thuyết trình chuyên nghiệp như các chuyên gia bán hàng, nhà huấn luyện, người đào tạo hay các diễn giả thì không thể phủ nhận rằng: họ sẽ tìm mọi cách để làm chủ năng lực giao tiếp truyền cảm hứng và thuyết trình thuyết phục. Thuyết trình bằng câu chuyện sẽ là một phương pháp đắc lúc họ trở thành những nhà truyền thông đầy cảm hứng và thuyết phục như trên sân khấu Ted Talk. Việc làm chủ phương pháp thuyết trình bằng câu chuyện sẽ giúp họ lan tỏa ý tưởng giá trị rộng khắp.\r\n\r\nTuy nhiên, tất cả những gì được đề cập ở đây mới chỉ là sự bắt đầu.\r\n\r\nHiện nay việc giao tiếp và thuyết trình là công việc hàng ngày của mọi người, nhưng nhu cầu của mọi người về việc làm chủ khả năng giao tiếp và thuyết trình thuyết phục lại mới ở giai đoạn đầu. Nhiều người vẫn cho rằng: giao tiếp và thuyết trình là một kỹ năng có thể học hoặc không cần học, học cũng tốt mà không học cũng không sao. Và với muôn vàn lý do khác nhau, đại đa số mọi người vẫn cho rằng: mình đã giao tiếp mấy chục năm qua, từ nhỏ cho đến lớn, cớ gì phải đi học thêm một chương trình và giao tiếp truyền cảm hứng và trình bày thuyết phục.\r\n\r\nThế giới rộng khắp và ngày càng dịch chuyển không ngừng. Nhu cầu của con người sẽ còn thay đổi liên tục. Không thể nghi ngờ việc mỗi cá nhân ngày càng có mong muốn được khẳng định giá trị của chính mình, thiết kế cuộc sống của riêng mình và chia sẻ ý tưởng của mình cho nhiều người khác. Trên hành trình đó, việc làm chủ khả năng giao tiếp truyền cảm hứng và thuyết trình thuyết phục là điều tất yếu. Khi bạn chưa sẵn sàng, cơ hội sẽ dành về người khác.\r\n\r\nVậy nên, hãy trao cho chính bản thân mình cơ hội làm chủ khả năng giao tiếp truyền cảm hứng và thuyết trình thuyết phục.\r\n\r\nVà tôi hi vọng rằng bằng việc viết cuốn sách này – Nghệ thuật thuyết trình bằng câu chuyện sẽ là điều hữu ích mà tôi mang đến cho bạn.\r\n\r\nNếu bạn vẫn còn muốn tiếp tục cuộc hành trình làm chủ khả năng giao tiếp truyền cảm hứng và thuyết trình thuyết phục, hãy truy cập vào nhóm kín này. Bạn sẽ có cơ hội học tập suốt đời.\r\n\r\nVề tác giả:\r\n\r\nBùi Thị Ngọc Thu\r\n\r\nCEO - Founder Công ty đào tạo và huấn luyện ICTS Training & Coaching.\r\n\r\nChuyên gia huấn luyện cấp cao về giao tiếp truyền cảm hứng và thuyết trình thuyết phục\r\n\r\nHuấn luyện viên thuyết trình trước công chúng của tổ chức quốc tế ICF\r\n\r\nGiảng viên của nhiều khóa học về nghệ thuật thuyết trình bằng câu chuyện.', 'Alpha Books', 'NXB Thế Giới', '2023-04-25 00:39:55'),
('ut291WASYw', ' Bạn Sẽ Sống Mấy Lần?', 'Triết Học Đường Phố 2.0', 75000, 'https://cdn0.fahasa.com/media/catalog/product/b/_/b_n-s_-s_ng-m_y-l_nb_a-t_p-1.jpg', 'Bạn Sẽ Sống Mấy Lần?\r\n\r\nBạn có đang thật sự kiểm soát được tâm trí của chính mình không?\r\n\r\nHay tâm trí của bạn đã bị lập trình bởi văn hóa, tập thể, truyền thông, giải trí, tiêu thụ?\r\n\r\nCuốn sách “Bạn sẽ sống mấy lần?” nằm trong bộ sách gồm 2 tập (“Bạn sẽ sống mấy lần?” và “Bạn không chỉ sống một lần”) của Triết Học Đường Phố 2.0 sẽ giải đáp những khía cạnh mà có thể bạn đã từng trải qua trong cuộc sống nhưng không thể định nghĩa được nó.\r\n\r\n“Bạn sẽ sống mấy lần?” vẽ ra một vòng tròn hợp lý hóa mọi thứ bạn làm, bạn nghĩ và cho bạn cái nhìn chân thật về hành vi của bản thân trước những vấn đề xã hội. Bạn luôn bị trói buộc vào sự tồn tại của chính mình. Đôi khi bạn tự tạo cho mình những gánh nặng để chạy đua theo một chuẩn mực xa vời, ngụy biện hay tự lừa dối bản thân để vừa lòng thiên hạ.\r\n\r\nKhi gấp lại những trang sách, bạn biết rằng đã đến lúc phải làm chủ con thuyền của đời mình, biết chọn lọc những gì mình sẽ mang theo. \r\n\r\nCuốn sách còn được lồng ghép quan điểm của các nhà nghiên cứu cổ xưa, nhà hiền triết và tu sĩ nổi tiếng,… nhằm đưa ra góc nhìn đa chiều về các mâu thuẫn trong tâm trí con người và xã hội. Từ đó bạn chúng ta có thể buông bỏ lòng tham, lòng đố kỵ đối với kẻ khác, sự hằn học khi bị cuộc đời trêu đùa. Giữ lại trong tim một chút tình yêu không bi lụy, một chút thản nhiên không vô cảm, một chút thứ tha, bao dung… cuộc sống nhẹ nhàng hơn bao giờ hết.\r\n\r\nThông qua góc nhìn của Triết Học Đường Phố 2.0, bạn cảm nhận được tất cả những gì bạn có thể đọc tên, nhìn thấy, sử dụng như cơ thể, xúc cảm, tâm trí, nghề nghiệp... để hiểu rõ hơn về Cội Nguồn hay tìm hiểu về bản chất của chính mình, đi qua cột mốc cơ thể một cách gần gũi. Từ đó, bạn sẽ tìm được nguồn năng lượng thực sự để phát triển bản thân tốt nhất là đến từ ý thức và bình an nội tâm.\r\n\r\n triết và tu sĩ nổi tiếng,… nhằm đưa ra góc nhìn đa chiều về các mâu thuẫn trong tâm trí con người và xã hội. Từ đó bạn chúng ta có thể buông bỏ lòng tham, lòng đố kỵ đối với kẻ khác, sự hằn học khi bị cuộc đời trêu đùa. Giữ lại trong tim một chút tình yêu không bi lụy, một chút thản nhiên không vô cảm, một chút thứ tha, bao dung… cuộc sống nhẹ nhàng hơn bao giờ hết.\r\n\r\nThông qua góc nhìn của Triết Học Đường Phố 2.0, bạn cảm nhận được tất cả những gì bạn có thể đọc tên, nhìn thấy, sử dụng như cơ thể, xúc cảm, tâm trí, nghề nghiệp... để hiểu rõ hơn về Cội Nguồn hay tìm hiểu về bản chất của chính mình, đi qua cột mốc cơ thể một cách gần gũi. Từ đó, bạn sẽ tìm được nguồn năng lượng thực sự để phát triển bản thân tốt nhất là đến từ ý thức và bình an nội tâm.', 'AZ Việt Nam', 'Phụ Nữ Việt Nam', '2023-04-25 00:55:23'),
('uy4RhEQpVC', 'Tâm Lý Học Tội Phạm - Tập 1', 'Stanton E Samenow', 126000, 'https://cdn0.fahasa.com/media/catalog/product/t/_/t_m_l_h_c_t_i_ph_m_1_-_b_a_1.png', '\"Tâm lý học tội phạm\" là bộ sách gồm 2 tập đề cập đến quyền lựa chọn, ý chí tự do, cái thiện và cái ác, phản ứng trước cám dỗ và sự thể hiện lòng dũng cảm hay hèn nhát khi đối mặt với nghịch cảnh của con người. Những cuốn sách thiêng liêng của các tôn giáo đều khuyên loài người không nên lừa dối, giận dữ và kiêu ngạo. Chúng ta nghĩ bản thân thế nào thì sẽ là như thế. Chúng ta không thể giúp một người từ bỏ tội ác và sống có trách nhiệm nếu không thể khiến anh ta thay đổi nhân tính, đó chính là \"suy nghĩ\".\r\n\r\nẤn bản đầu tiên của cuốn sách “Tâm lý học tội phạm” được xuất bản năm 1984, và ấn bản thứ hai xuất bản năm 2004. Sau một thập kỷ, và đã đến lúc bổ sung thêm các thông tin cho cuốn sách này. Trong ấn bản này, bạn sẽ hiểu chi tiết về các quá trình tư duy và chiến thuật phổ biến đối với những kẻ phạm tội, bất kể lý lịch hay tội ác của chúng.\r\n\r\nGiờ đây, với cái nhìn sâu sắc hơn tác giả Stanton E.Samenow đã cung cấp cho độc giả một ấn bản cập nhật hoàn toàn về tác phẩm kinh điển của mình, bao gồm những sự nhận thức mới mẻ về tội ác đang được chú ý ngày nay, từ sự rình rập và bạo lực gia đình đến tội phạm cổ cồn và chính trị khủng bố. Ông đã từng có ba thập kỷ làm việc với tội phạm khẳng định lại lập luận của mình rằng các yếu tố như nghèo đói, ly hôn và bạo lực trên phương tiện truyền thông không gây ra tội phạm. Đúng hơn, như các tài liệu của Samenow ở đây, tất cả tội phạm đều có chung một suy nghĩ đặc biệt - thường thấy rõ trong thời thơ ấu - khác hẳn với suy nghĩ của một công dân có trách nhiệm.\r\n\r\nTrong khi các loại tội phạm mới ngày càng phổ biến hơn, hoặc ít nhất là dễ nhìn thấy hơn với công chúng - từ lạm dụng vợ chồng đến các vụ xả súng ở trường học - có rất ít thay đổi về cách tiếp cận của chúng ta đối với tội phạm. Các chương trình phục hồi dựa trên giả định rằng xã hội đổ lỗi cho tội phạm nhiều hơn là tội phạm, một giả định mà mối liên hệ nhân quả vẫn chưa được thiết lập, đã được chứng minh là không đầy đủ. Tội phạm tiếp tục xâm chiếm mọi khía cạnh của cuộc sống chúng ta, các tòa án hình sự và nhà tù luôn quá tải, và tỷ lệ tái phạm tiếp tục leo thang.\r\n\r\nTiến sĩ Samenow, một nhà tâm lý học lâm sàng đã bác bỏ một cách hợp pháp những lời giải thích về hành vi tội phạm đổ lỗi cho hoàn cảnh, môi trường (xã hội, gia đình, truyền hình bạo lực,...). Ông thường xuyên đưa ra những lời khái quát sâu rộng và không cung cấp gì khác ngoài những trường hợp có thật ủng hộ quan điểm của ông rằng tất cả tội phạm đều vi phạm pháp luật một cách có ý thức và cố ý.\r\n\r\nHiểu biết thông thường về nguyên nhân phạm tội hiện vẫn là kim chỉ nam cho nhiều người vạch ra các chính sách phòng chống tội phạm. Các nhà hoạch định chính sách lãng phí hàng tỷ đô la khi họ ngây thơ tìm cách chống lại hành vi tội phạm bằng cách loại bỏ cái gọi là “nguyên nhân gốc rễ” về môi trường xung quanh.\r\n\r\nĐiều quan trọng là chúng ta phải biết kẻ phạm tội là ai và làm thế nào và tại sao hắn lại hành động khác với những công dân có trách nhiệm. Từ sự hiểu biết đó có thể đưa ra những giải pháp hợp lý, nhân ái và hiệu quả.', '1980 Books', 'NXB Đại học Kinh Tế Quốc Dân', '2023-04-25 01:00:04'),
('V8srMJfVlT', 'Hành Tinh Của Một Kẻ Nghĩ Nhiều', 'Amateur Psychology Nguyễn Đoàn Minh Thư', 645000, 'https://cdn0.fahasa.com/media/catalog/product/b/_/b_a_h_nh-tinh-c_a-m_t-k_-ngh_-nhi_u-tr_c-1-1.jpg', 'Hành Tinh Của Một Kẻ Nghĩ Nhiều!!!!!!!\r\n\r\n“Đó là mùa hè năm 2020, mùa hè của COVID-19 và một ngàn vạn khủng hoảng tuổi đôi mươi. Đó là mùa hè mình bắt chuyến bay sớm nhất có thể vào ngày 20 tháng 3 để chạy khỏi nước Anh đang bùng dịch, bị kẹt lại sân bay Bangkok trong 24 giờ đồng hồ vì chuyến bay quá cảnh về Sài Gòn bị huỷ..\r\n\r\nĐó là mỗi đêm mùa hè nằm trên giường stress chuyện deadline luận văn, stress chuyện thất nghiệp không kiếm ra tiền, stress chuyện bỏ lỡ cơ hội thực tập giúp ích cho sự nghiệp của mình, stress chuyện học hành dở dang - không biết bao giờ mới có thể quay lại Anh và hoàn thành tấm bằng đại học, stress chuyện tồn tại một cách mơ hồ, không biết mình là ai.”\r\n\r\nHành tinh của một kẻ nghĩ nhiều là hành trình khám phá thế giới nội tâm của một người trẻ. Đó là một hành tinh đầy hỗn loạn của những suy nghĩ trăn trở, những dằn vặt, những cuộc chiến nội tâm, những cảm xúc vừa phức tạp cũng vừa rất đỗi con người. Một thế giới quen thuộc với tất cả chúng ta. \r\n\r\nChứa đựng những lời chia sẻ và kiến thức từ podcast của Amateur Psychology - Tay mơ học đời bằng Tâm lý học, tác giả sẽ dẫn lối và đưa bạn tới từng ngóc ngách thầm kín nhất của hành tinh ấy dưới một góc nhìn chuyên sâu hơn.  \r\n\r\nVà nếu bạn cũng là một kẻ nghĩ nhiều, chào mừng bạn đến với hành tinh này.\r\n\r\n“Sự cô đơn không nằm ở việc không có bạn bè hay người thân mà ngay cả trong sự sum vầy ta vẫn cảm thấy không có ai có thể hiểu mình tường tận từ sâu tâm hồn. Là sự cô đơn như thể chỉ có một mình mình trên một hành tinh đơn độc trong đầu, một hành tinh không bao giờ có khách viếng thăm”.', 'AZ Việt Nam', 'Thế Giới', '2023-04-19 21:01:16'),
('VI4tt7URCb', 'Cuộc Chơi Khởi Nghiệp 1', 'Brad Feld, Jason Medelson', 143000, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935251406290.jpg', 'Cuộc Chơi Khởi Nghiệp 1\r\n\r\n&ldquo;Một cuốn s&aacute;ch cần thiết cho c&aacute;c chủ doanh nghiệp. Brad v&agrave; Jason đ&atilde; l&agrave;m s&aacute;ng tỏ mớ lộn xộn những hợp đồng, điều khoản của một thương vụ mua b&aacute;n v&agrave; s&aacute;p nhập, đồng thời cắt bỏ c&aacute;c vấn đề ph&aacute;p l&yacute; rườm r&agrave; v&agrave; tập trung v&agrave;o những vấn đề mấu chốt nhất. Thế n&ecirc;n kh&ocirc;ng chỉ c&aacute;c doanh nh&acirc;n, m&agrave; c&agrave; c&aacute;c nh&agrave; đầu tư v&agrave; luật sư cũng sẽ thấy cuốn s&aacute;ch n&agrave;y rất cần thiết. Bởi một doanh nh&acirc;n th&ocirc;ng hiểu về lĩnh vực đầu tư sẽ khiến việc đ&agrave;m ph&aacute;n trở n&ecirc;n dễ d&agrave;ng hơn rất nhiều.&quot;\r\n\r\n- Greg Gottesman,\r\n\r\nCEO của Madrona Venture Group\r\n\r\n&quot;Freld v&agrave; Medelson đ&atilde; đưa cả một kh&oacute;a học v&agrave;o cuốn s&aacute;ch sinh động v&agrave; dễ hiểu n&agrave;y. Với phong c&aacute;ch thẳng thắn v&agrave; c&aacute;i nh&igrave;n sắc b&eacute;n, t&aacute;c giả đ&atilde; mang lại những kiến thức v&ocirc; c&ugrave;ng hữu &iacute;ch cho c&aacute;c chủ doanh nghiệp, c&aacute;c nh&agrave; đầu tư khởi nghiệp v&agrave; cả những sinh vi&ecirc;n mới tốt nghiệp.  H&atilde;y đọc cuốn s&aacute;ch n&agrave;y nếu bạn muốn hiểu bản chất v&agrave; c&aacute;c chiến lược cho một thương vụ đầu tư mạo hiểm.&quot;\r\n\r\n- Brad Bernthal,\r\n\r\nGi&aacute;o sư Luật, chuy&ecirc;n Luật doanh nghiệp v&agrave; ch&iacute;nh s&aacute;ch c&ocirc;ng nghệ\r\n', 'Alpha Books', 'NXB Công Thương', '2023-04-25 02:09:45'),
('VpaNNNcKEQ', 'Lý Tiểu Long - Một Cuộc Đời Phi Thường', 'Matthew Polly', 340000, 'https://cdn0.fahasa.com/media/catalog/product/l/_/l_-ti_u-long---m_t-cu_c-_i-phi-th_ng.jpg', 'L&yacute; Tiểu Long - Một Cuộc Đời Phi Thường\r\n\r\nBruce Lee (L&yacute; Tiểu Long) l&agrave; diễn vi&ecirc;n đầu ti&ecirc;n của Trung Hoa v&agrave; ch&acirc;u &Aacute; khuynh đảo kinh đ&ocirc; điện ảnh Hollywood, tạo n&ecirc;n tr&agrave;o lưu phim v&otilde; thuật tr&ecirc;n khắp thế giới v&agrave; g&oacute;p phần nối liền văn h&oacute;a Đ&ocirc;ng &ndash; T&acirc;y. Trong cuộc đời ngắn ngủi 32 năm của m&igrave;nh, anh đ&atilde; đạt được nhiều th&agrave;nh tựu vĩ đại m&agrave; gần nửa thế kỷ sau khi anh qua đời vẫn chưa c&oacute; ai ph&aacute; vỡ được.\r\n\r\nBruce Lee trở th&agrave;nh th&agrave;nh ho&agrave;ng của m&ocirc;n v&otilde; thuật kung-fu, được t&ocirc;n thờ như một vị b&aacute;n thần. Thanh thiếu ni&ecirc;n Nhật Bản cắt t&oacute;c giống anh. Người Đ&agrave;i Loan ca tụng anh l&agrave; &ldquo;người c&oacute; đ&ocirc;i ch&acirc;n v&agrave;ng biết h&aacute;t&rdquo;, người Anh th&igrave; lại xem anh như &ldquo;Vua Kung Fu&rdquo;, người &Uacute;c gọi anh &ldquo;người c&oacute; c&uacute; đấm nhanh nhất tại phương Đ&ocirc;ng&rdquo;. Với l&ograve;ng nhiệt huyết cho c&ocirc;ng cuộc truyền b&aacute; kung-fu, Bruce đ&atilde; nỗ lực sử dụng phương tiện điện ảnh để quảng b&aacute; tinh hoa v&otilde; thuật tới c&ocirc;ng ch&uacute;ng tr&ecirc;n to&agrave;n thế giới.\r\n\r\nAnh đ&atilde; th&agrave;nh c&ocirc;ng vượt cả sự tưởng tượng hoang đường nhất. Ở nước Mỹ, nơi những diễn vi&ecirc;n điện ảnh người Hoa hầu hết bị giao cho những vai diễn thấp k&eacute;m, h&egrave;n mọn, Bruce Lee đ&atilde; vượt qua mọi trở ngại tr&ecirc;n con đường đầy tham vọng nhưng cũng lắm ch&ocirc;ng gai, để ph&aacute; vỡ định kiến về m&agrave;u da v&agrave;ng ch&acirc;u &Aacute; của Hollywood. Anh trở th&agrave;nh nam diễn vi&ecirc;n người Mỹ gốc Hoa đầu ti&ecirc;n thủ vai ch&iacute;nh trong một bộ phim Hollywood v&agrave; l&agrave; người ch&acirc;u &Aacute; đầu ti&ecirc;n xuất hiện trong một bộ phim c&oacute; &acirc;m thanh kể từ khi nh&acirc;n loại ph&aacute;t minh ra n&oacute;. Trước khi Bruce Lee qua đời, chỉ c&oacute; hơn năm trăm trường dạy v&otilde; tr&ecirc;n to&agrave;n thế giới; v&agrave;o cuối những năm 1990, dưới sự ảnh hưởng của anh, đ&atilde; c&oacute; hơn 20 triệu v&otilde; sinh chỉ t&iacute;nh ri&ecirc;ng ở nước Mỹ. Tại nước Anh, nhu cầu tăng cao đến mức đ&aacute;m đ&ocirc;ng chen lấn x&ocirc; đẩy nhau xếp h&agrave;ng ph&iacute;a ngo&agrave;i c&aacute;c v&otilde; đường thương mại v&agrave;, đ&uacute;ng theo nghĩa đen, họ n&eacute;m tiền v&agrave;o v&otilde; sư chỉ để giữ một suất học sắp tới. &ldquo;Bruce Lee đ&atilde; từng v&agrave; sẽ m&atilde;i m&atilde;i l&agrave; l&yacute; do ch&iacute;nh để ch&aacute;u nỗ lực đạt đến sự tinh th&ocirc;ng v&otilde; nghệ trong những năm sắp tới&rdquo;, một cậu b&eacute; từ tiểu bang South Carolina viết trong l&aacute; thư gửi đến tạp ch&iacute; Black Belt.\r\n\r\n----------\r\n\r\n&ldquo;L&yacute; Tiểu Long &ndash; Một cuộc đời phi thường&rdquo; được nghi&ecirc;n cứu trong v&ograve;ng một thập kỷ bởi t&aacute;c giả Matthew Polly. Đ&acirc;y l&agrave; quyển s&aacute;ch đầu ti&ecirc;n mang đến cho người đọc một bức ch&acirc;n dung ho&agrave;n chỉnh, dung dị m&agrave; tuyệt vời về L&yacute; Tiểu Long, người đ&atilde; đưa v&otilde; thuật ch&acirc;u &Aacute; đến với thế giới: từ thời thơ ấu l&agrave;m diễn vi&ecirc;n nh&iacute; b&ecirc;n c&aacute;i b&oacute;ng lớn của cha, đến thời ni&ecirc;n thiếu hổ b&aacute;o đ&aacute;nh đấm triền mi&ecirc;n đến mức bị &ldquo;đ&agrave;y ải&rdquo; sang đất Mỹ; buổi đầu l&agrave;m thầy dạy v&otilde;; những năm th&aacute;ng chật vật t&igrave;m kiếm vị tr&iacute; tại kinh đ&ocirc; điện ảnh Hollywood; cuộc sống khi chạm tay đến vinh quang c&ugrave;ng tr&aacute;ch nhiệm l&agrave;m chồng l&agrave;m cha; v&agrave; cả c&aacute;i chết l&agrave;m r&uacute;ng động to&agrave;n cầu đến tận ng&agrave;y nay.', 'Saigon Books', 'NXB Văn Hóa - Văn Nghệ', '2023-04-25 01:30:55'),
('VvXTC7aR9y', 'Mỗi Lần Vấp Ngã Là Một Lần Trưởng Thành', 'Liêu Trí Phong', 73000, 'https://sachtrading.com/wp-content/uploads/2022/06/moi-lan-la-1-vap-nga.jpg', 'Mỗi Lần Vấp Ngã Là Một Lần Trưởng Thành\r\n\r\n Cuộc đời là quá trình không ngừng trưởng thành.\r\n\r\nTrong quá trình này, bạn sẽ khó tránh khỏi vấp ngã, sẽ trải qua nhiều lần đau thương, và sẽ có những lúc bạn cảm thấy vô cùng mỏi mệt, nhưng hãy tin rằng, chỉ có những người đã từng đau thương thì mới trở nên vững vàng hơn.\r\n\r\nMỗi chúng ta từ nhỏ đến lớn, dù ít dù nhiều đều đã từng trải qua những lúc cảm thấy đau khổ, đây chính là trở ngại mà chúng ta thường nói tới. Tôi tin rằng sẽ không một người nào dám khẳng định cuộc đời của họ chẳng bao giờ gặp trở ngại. Đó chính là cuộc sống.\r\n\r\nNhưng mong bạn suy nghĩ kĩ, mỗi khi bạn gặp phải khó khăn hoặc vấp ngã, ngoài việc cảm thấy đau khổ về mặt tinh thần, bạn còn học được điều gì? Tôi dám khẳng định rằng bạn đã có được kinh nghiệm, và khi đã lĩnh hội, bạn sẽ không vấp ngã lại nơi bạn đã từng ngã. Đây chính là sự trưởng thành.\r\n\r\nĐặc biệt, khi bạn rời xa vòng tay che chở, bao bọc của cha mẹ và nhà trường, bước chân vào xã hội, bạn sẽ gặp phải rất nhiều trở ngại và nhận ra xã hội này vốn không hề đơn giản như bạn tưởng tượng! Bạn cũng sẽ nhận ra khi bạn đau buồn hay gặp phải khó khăn, sẽ không có ai để ý đến sự tủi thân của bạn và cũng chẳng quan tâm đến sự bất lực của bạn. Thậm chí bạn cũng không muốn kể cho cha mẹ nghe vì sợ họ phải lo lắng cho mình, bạn chỉ có thể tự mình giải quyết, tự mình gánh chịu.\r\n\r\nMột nữ sinh vừa tốt nghiệp đại học kể với tôi với vẻ rất tủi thân, người chưa hề biết cạnh tranh với ai như cô đã phải chịu sự đối xử bất công nơi làm việc, cấp trên không trọng dụng cô, còn luôn giao cho cô những công việc nặng nề và ngoài chuyên môn của cô, thậm chí đồng nghiệp cũng bài xích cô. Tôi nói với cô gái: “Đây là chuyện rất bình thường, bạn phải dừng ngay việc than vãn trách móc và học cách chấp nhận. Bạn cũng đừng chờ đợi một lúc nào đó hoàn cảnh thay đổi, cũng đừng hi vọng người khác sẽ thay đổi, mà hãy thay đổi suy nghĩ của chính bạn. Bạn nghĩ xem, tại sao bạn không được trọng dụng, và liệu bạn đã thể hiện hết năng lực của bản thân trong môi trường ‘khốc liệt’ này chưa?”\r\n\r\nCô gái đã nghe lời tôi, không than vãn nữa, cô tìm đọc những cuốn sách mà tôi gợi ý, học cách đón nhận mọi sự việc nơi cô làm việc, dần dần cô biết cách ứng xử mềm mỏng trước mặt cấp trên, đồng thời tích cực đưa ra những ý kiến đóng góp của mình. Cấp trên bắt đầu đánh giá cao cô. Ngoài ra, cô cũng hòa đồng với đồng nghiệp hơn, thỉnh thoảng giúp đỡ mọi người, dần dần mọi chuyện đều trở nên tốt đẹp hơn.\r\n\r\nMột năm sau, khi gặp lại nữ sinh ấy, cô đã không còn phiền muộn như trước kia nữa, con người cô giờ đây tươi mới như hoa cỏ mùa xuân. Điều quan trọng nhất là cô ấy đã biết lựa theo thời thế để thăng tiến. Bây giờ cô ấy đang tiến lên với những mục tiêu cao hơn.\r\n\r\nVì thế, điều tôi muốn nói là, khi bạn vấp ngã hay thất bại thì cũng đừng nên lo lắng than trách, mà phải cảm ơn sự không công bằng của thế giới này, chúng luôn luôn tồn tại khách quan, bạn than vãn cũng không thể thay đổi được gì. Điều thực sự có thể giúp ích cho bạn chính là chấp nhận hiện thực, thay đổi bản thân mình.\r\n\r\nKhi phát hiện có người dị nghị sau lưng mình, bạn cảm thấy đau khổ và tổn thương, nhưng vì thế mà bạn sẽ phủ nhận tất cả mọi người trên thế giới này sao? Đương nhiên không thể. Bất luận bạn muốn giành thành công theo cách nào cũng không thể thiếu sự hợp tác với mọi người. Bạn nên chấp nhận hiện thực, sau này cần đánh giá rõ ràng hơn về mối quan hệ của bản thân với người khác, có ý thức đề phòng.\r\n\r\nKhi bạn lao đao vất vả trên con đường lập nghiệp, bạn sẽ cảm thấy mệt mỏi bởi sự vô tình của chốn thương trường, tự trách mình đã gây ra sai lầm, nhưng những điều này cũng không thể thay đổi được gì. Thời gian trôi đi không bao giờ quay trở lại, điều bạn cần làm là đúc kết ra những kinh nghiệm và giá trị từ trong thất bại, sau đó phủi tay, đứng lên từ nơi bạn đã từng vấp ngã. Chí ít, sau khi bạn nghiêm túc tổng kết, bạn sẽ không mắc lại sai lầm cũ.\r\n\r\nCuộc đời là vậy, chỉ có vấp ngã bạn mới rút ra kinh nghiệm quý báu cho bản thân và trưởng thành hơn. Đau khổ, thất bại, bị lừa dối, bị tổn thương, mất mát, đều chỉ là những đoạn đường mấp mô mà bạn phải trải qua. Chỉ có vượt qua chúng thì bạn mới có thể bước tiếp đến nơi xa hơn, trải nghiệm cuộc sống tốt đẹp hơn.\r\n\r\nChính vì thế, hãy dừng việc than thân trách phận, dẫu biết cuộc sống này quả thật không dễ dàng gì, nhưng nó luôn dành cho bạn những bài học quý giá nhất, đó là bài học về sự trưởng thành mà trường học không dạy bạn. Vẫn là câu nói ban đầu, chỉ có vấp ngã mới khiến bạn trưởng thành. Điều bạn cần làm là chấp nhận hiện thực, quý trọng từng ngày, hướng tới ngày mai tươi đẹp, nỗ lực phấn đấu, bạn sẽ nhìn thấy ánh sáng hi vọng.\r\n', 'Minh Long', 'NXB Thanh Niên', '2023-04-25 01:23:32'),
('WRbXZjhpy9', 'Thuật Thao Túng - Góc Tối Ẩn Sau Mỗi Câu Nói', 'Wladislaw Jachtchenko', 104000, 'https://cdn0.fahasa.com/media/catalog/product/u/n/untitledthaotungtamly.jpg', 'Thuật Thao Túng\r\n\r\nBạn có muốn giành phần thắng cuối cùng trong các cuộc tranh luận?\r\n\r\nBạn có muốn dẹp đi bộ mặt kiêu ngạo của các đồng nghiệp xung quanh mình?\r\n\r\nBạn có muốn chứng minh rằng bạn đã đúng về mọi thứ?\r\n\r\nĐây là quyển sách chứa đựng đáp án mà bạn mong muốn. Thuật thao túng sẽ giúp bạn thuần thục các kỹ năng thuộc bộ môn “nghệ thuật” làm chủ cảm xúc, làm chủ vận mệnh, điều chỉnh tâm lý và đạt được thứ bạn muốn một cách tinh vi: thao túng tâm lý người đối diện, khiến họ hành động theo hướng ta mong đợi. Không những vậy, quyển sách còn giúp bạn nhìn nhận lại về định nghĩa thao túng, những tốt-xấu ẩn giấu đằng sau và giải đáp vấn đề đạo đức con người mà bạn luôn trăn trở khi thực hiện những hành vi này. Bật mí, con người khi vừa sinh ra đã làm một thao tác thao túng tâm lý người khác rồi đấy!\r\n\r\nCó thể bạn chưa biết, bạn đã và đang thao túng người khác hoặc bị người khác thao túng thông qua cử chỉ ngôn hành mỗi ngày, như-một-trò-đùa.\r\n\r\nCó thể bạn chưa biết, nạn nhân bị thao túng chưa chắc đã rơi vào tình thế bất lợi, nhưng rơi vào tình thế bất lợi chắc chắn đã bị thao túng.\r\n\r\nCó thể bạn chưa biết, người có đạo đức chắc chắn không thao túng người khác, nhưng kẻ thao túng người khác chưa chắc đã vô đạo đức.\r\n\r\nVới 10 kỹ năng và 37 thủ thuật, Thuật thao túng sẽ giúp bạn nhận ra và thoát khỏi những suy nghĩ xấu xa nơi tiềm thức của chính mình, đồng thời vạch trần góc tối ẩn giấu sau mỗi câu nói của đối phương, đưa những chiêu trò thao túng ấy ra ánh sáng để mọi người không lần nữa rơi vào cạm bẫy. Hơn cả, quyển sách này sẽ dẫn lối bạn trở thành một “nghệ nhân” thao túng có đạo đức.\r\n\r\nVề tác giả\r\n\r\nTác giả người Đức Wladislaw Jachtchenko - diễn giả hàng đầu châu Âu, người sáng lập Học viện Argumentorik giảng dạy về giao tiếp - dạy bạn cách giao tiếp phù hợp để đạt được điều bạn muốn.', 'AZ Việt Nam', 'Thế Giới', '2023-04-19 21:08:36'),
('x3u0HqygtD', 'Sự Im Lặng Của Bầy Cừu', 'Thomas Harris', 86000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_180812.jpg', 'Những cuộc phỏng vấn ở xà lim với kẻ ăn thịt người ham thích trò đùa trí tuệ, những tiết lộ nửa chừng hắn chỉ dành cho kẻ nào thông minh, những cái nhìn xuyên thấu thân phận và suy tư của cô mà đôi khi cô muốn lảng tránh... Clarice Starling đã dấn thân vào cuộc điều tra án giết người lột da hàng loạt như thế, để rồi trong tiếng bức bối của chiếc đồng hồ đếm ngược về cái chết, cô phải vật lộn để chấm dứt tiếng kêu bao lâu nay vẫn đeo đẳng giấc mơ mình: tiếng kêu của bầy cừu sắp bị đem đi giết thịt.\r\n\r\nSự im lặng của bầy cừu hội tụ đầy đủ những yếu tố làm nên một cuốn tiểu thuyết trinh thám kinh dị xuất sắc nhất: không một dấu vết lúng túng trong những chi tiết thuộc lĩnh vực chuyên môn, với các tình tiết giật gân, cái chết luôn lơ lửng, với cuộc so găng của những bộ óc lớn mà không có chỗ cho kẻ ngu ngốc để cuộc chơi trí tuệ trở nên dễ dàng. Bồi đắp vào cốt truyện lôi cuốn đó là cơ hội được trải nghiệm trong trí não của cả kẻ gây tội lẫn kẻ thi hành công lý, khi mỗi bên phải vật vã trong ngục tù của đau đớn để tìm kiếm, khẩn thiết và liên tục, một sự lắng dịu cho tâm hồn.\r\n\r\nNhận định\r\n\r\n“...xây dựng tình tiết đẹp với lối viết thông tuệ. Không tác phẩm kinh dị nào vượt được cuốn này.”\r\n\r\n- Clive Barker\r\n\r\n“Một cuốn sách giáo khoa đúng nghĩa về nghệ thuật viết truyện kinh dị, một kiệt tác chứa xung lực đưa nó lao vụt lên đỉnh cao không một khiếm khuyết... Harris đơn giản chính là tiểu thuyết gia kinh dị xuất sắc nhất thời nay.”\r\n\r\n- The Washington Post\r\n\r\n“Tiết điệu dồn dập... đánh thức sự tò mò... lôi cuốn.”\r\n\r\n- Chicago Tribune\r\n', 'Nhã Nam', 'NXB Hội Nhà Văn', '2023-04-25 00:33:59'),
('xaEgvUnCVr', '1% Mỗi Ngày - Không Ngừng Chinh Phục Bản Thân', 'Ngô Di Lân', 102000, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8934974179566.jpg', '“Đây không phải là một cuốn sách dạy làm giàu hay một cuốn sách kể về cuộc đời và những chiến công hiển hách của bậc vĩ nhân. Thực lòng tôi không nghĩ người trẻ cần những cuốn sách đó lắm. Người trẻ chúng ta dễ cảm thấy mông lung trên hành trình đi tìm bản thân. Có chút tự ti, có phần sợ hãi nhưng có lẽ đa phần là háo hức chờ đợi những gì đang chờ đón ta ở phía trước. Thiết nghĩ sẽ thật tuyệt vời nếu có một cuốn sách thấu hiểu được nỗi lòng của người trẻ, để các bạn cảm thấy dù mình đang ở đâu gặp khó khăn gì thì vẫn luôn có một ai đó bên cạnh và nói được tiếng lòng của các bạn. Mong rằng những dòng chia sẻ trong cuốn sách này sẽ giúp các bạn cảm thấy vững tâm hơn trên hành trình đi tìm bản thân và được tiếp sức mỗi khi bị cuộc đời làm cho gục ngã. Hơn hết, tôi mong rằng các bạn sẽ được truyền thêm cả động lực lẫn ý tưởng để có thể hoàn thiện bản thân hơn, dù chỉ là 1% mỗi ngày.” - Ngô Di Lân\r\n\r\nChinh phục bản thân về bản chất là quá trình một người rèn luyện và phát triển để trở nên toàn diện và hoàn thiện nhất có thể xét trên những khía cạnh quan trọng nhất trong cuộc sống. Những ai đã học được cách chinh phục bản thân vừa có thể sống tốt một cách độc lập, vừa có thể hòa nhập được với xã hội một cách dễ dàng, đồng thời có nhận thức đủ tốt để sửa được những thói xấu và khiếm khuyết lớn nhất mà một người thường có. Trong cuốn sách, tác giả sẽ bàn luận kỹ hơn về bốn mục tiêu cốt lõi của hành trình chinh phục bản thân: kiểm soát cảm xúc tiêu cực, khai mở tâm trí, rèn kỹ năng và giữ vững tinh thần “thắng không kiêu, bại không nản”.\r\n\r\nTác giả Ngô Di Lân \r\n- Chủ nhân Facebook fanpage Self Conquest \r\n- Học bổng Tiến sĩ toàn phần Đại học Brandeis 2015-2020\r\n- Sáng lập - Chủ tịch của Tổ chức Hợp tác Thanh niên Việt Nam (VYCO).', 'NXB Trẻ', 'NXB Trẻ', '2023-04-19 20:59:45'),
('XD5Frlk5lY', 'Trí Tuệ Do Thái', 'Eran Katz', 141750, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935251419184.jpg', 'Trí tuệ Do Tháilà một cuốnsách tư duyđầy tham vọng trong việc nâng cao khả năng tự học tập, ghi nhớ và phân tích - những điều đã khiến người Do Thái vượt trội lên, chiếm lĩnh những vị trí quan trọng trong ngành truyền thông, ngân hàng và những giải thưởng sáng tạo trên thế giới.\r\n\r\nTuy là một cuốn sách nhỏ nhưngTrí Tuệ Do Tháilại mang trong mình tri thức về một dân tộc có thể nhỏ về số lượng nhưng vĩ đại về trí tuệ và tài năng. Cuốn sách không chỉ lý giải lý do vì sao những người Do Thái trên thế giới lại thông minh và giàu có, mà còn đặc tả con đường thành công của một người Do Thái - Jerome cùng những triết lý được đúc kết đầy giá trị.\r\n\r\nTrí Tuệ Do Tháikhông dừng lại ở giới hạn của một cuốnsách triết lýhay sáchkỹ năng. Thông qua Jerome, một kẻ lông bông thích la cà, tác giả đưa người đọc vào một chuyến khám phá về trí tuệ của người Do Thái, từ đó khơi ra những giới hạn để người đọc có thể tự khai phá trí tuệ bản thân với \"Năm nguyên tắc\" và \"Mười lăm gợi ý\". Đây sẽ là những bài học quý giá dành cho những ai muốn tồn tại và phát triển mạnh mẽ, không chỉ với con đường thành công của riêng mình.\r\n\r\nKhông được viết như một cuốnsách kỹ năngkhô khan,Trí Tuệ Do Tháiđược dựng lên bằng một câu chuyện và rồi cũng khép lại với một cái kết mở, nơi những người Do Thái đang không ngừng đối mặt với cuộc sống và chinh phục nó.', 'Alpha Books', 'Công Thương', '2023-04-19 20:59:45'),
('xHTigq5GLI', 'Điềm Tĩnh Và Nóng Giận', 'Tạ Quốc Kế', 72000, 'https://cdn0.fahasa.com/media/catalog/product/-/t/-tin-xu_t-b_n-_i_m-t_nh-n_ng-gi_nb_a-1.jpg', 'Điềm Tĩnh Và Nóng Giận\r\n\r\nTrong cuộc sống thường ngày, chúng ta thường nổi giận vì nhiều nguyên do: công việc không suôn sẻ, chúng ta tức giận; bị người khác hiểu nhầm, chúng ta tức giận; thấy việc chướng tai gai mắt, chúng ta tức giận; không thể chấp nhận được dư luận xã hội, chúng ta tức giận. Thậm chí, chúng ta bực tức, cáu gắt, hờn dỗi, nhỏ nhen, uất ức vì thời tiết xấu, vì tiền lương thấp, vì nhà cửa bừa bộn, vì thái độ của người khác, vì những chuyện không may mà mình gặp phải. Dường như cuộc đời chúng ta là một chuỗi tức giận không hồi kết. Hãy thử tự hỏi bản thân: sau khi tức giận thì phiền não sẽ tan biến ư?\r\n\r\nDân gian vốn có câu “cả giận mất khôn”, Đức Phật cũng đã dạy “một ngọn lửa sân đốt cháy rừng công đức”. Cơn nóng giận có thể hủy diệt sức khỏe, sự nghiệp, tình cảm và hạnh phúc của chúng ta. Cơn giận khiến đầu óc ta bốc hỏa, kích động và lỗ mãng để rồi phạm phải những sai lầm không thể bù đắp. Nóng giận là trừng phạt bản thân bằng lỗi lầm của người khác, đánh cược hạnh phúc cả đời bằng một phút thiếu kiềm chế. Hầu hết những bất hạnh trong đời đều từ đó mà ra. Bởi thế, mỗi người chúng ta sống trong xã hội này đều cần rèn giũa cho mình một thái độ sống điềm tĩnh trước mọi điều.\r\n\r\nTiếp nối sự thành công sau cuốn sách “Thuận cho người lợi cho mình”, tác giả Tạ Quốc Kế tái ngộ với chúng ta qua cuốn sách Điềm tĩnh & nóng giận, tiếp tục truyền cảm hứng sống đẹp, sống có ích cho mọi người. Thông qua những câu chuyện cụ thể và thực tế, lời lẽ chân thành và đầy sức thuyết phục, độc giả sẽ có những phút giây đủ tĩnh lặng để nhìn nhận lại cảm xúc của bản thân, hiểu hơn về tác hại của nóng giận và học được cách làm sao để kiểm soát tâm trạng.', 'AZ Việt Nam', 'NXB Thanh Niên', '2023-04-25 00:51:37'),
('XKSyj9mYUn', 'Nick Vujicic - Sống Cho Điều Ý Nghĩa Hơn', 'Nick vujicic', 75000, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935086855379.jpg', 'Nick Vujicic - Sống Cho Điều &Yacute; Nghĩa Hơn\r\n\r\nCUỐN S&Aacute;CH MỚI NHẤT CỦA CH&Agrave;NG TRAIKH&Ocirc;NG TAY KH&Ocirc;NG CH&Acirc;N NICK VUJICIC - &ldquo;LIMITLESS&rdquo;\r\n\r\n&ldquo;Sống cho điều &yacute; nghĩa hơn&rdquo; Sau hai cuốn tự truyện g&acirc;y sốt cho độc giả: Cuộc Sống Kh&ocirc;ng Giới Hạn v&agrave; Đừng Bao Giờ Từ Bỏ Kh&aacute;t Vọng, một lần nữa độc giả Việt Nam lại được hội ngộ c&ugrave;ng Nick Vujicic th&ocirc;ng qua t&aacute;c phẩm Limitless &ndash; Sống Cho Điều &Yacute; Nghĩa Hơn. Tựa s&aacute;ch được đặt từ &yacute; nghĩa của b&agrave;i h&aacute;t nổi tiếng nhất của Nick Something More, do C&ocirc;ng ty Văn h&oacute;a S&aacute;ng tạo Tr&iacute; Việt &ndash; First News ấn h&agrave;nh v&agrave;o ng&agrave;y 16/05/2013 nh&acirc;n sự kiện ch&agrave;ng trai đặc biệt nhất h&agrave;nh tinh Nick Vujicic sang thăm Việt Nam v&agrave; gặp gỡ, tr&ograve; chuyện c&ugrave;ng mọi người.\r\n\r\nSinh ra kh&ocirc;ng c&oacute; một cơ thể l&agrave;nh lặn, Nick ch&agrave;o đời trong t&igrave;nh trạng kh&ocirc;ng tay kh&ocirc;ng ch&acirc;n. Từng c&oacute; &yacute; định tự tử, tuyệt vọng đến mức tin rằng cuộc sống của m&igrave;nh sẽ chẳng c&oacute; gi&aacute; trị g&igrave; hết v&agrave; anh sẽ m&atilde;i m&atilde;i l&agrave; g&aacute;nh nặng cho những người m&agrave; anh y&ecirc;u thương. Nhưng qua thời gian, Nick đ&atilde; tự nhận thức được về &yacute; nghĩa đặc biệt của sự khuyết thiếu tay ch&acirc;n khi anh được sinh ra, đ&oacute; l&agrave; sứ mạng trở th&agrave;nh người truyền cảm hứng v&agrave; kh&iacute;ch lệ tinh thần tất cả những người kh&aacute;c tr&ecirc;n tr&aacute;i đất n&agrave;y. Nick nhận ra: &ldquo;Cuộc sống kh&ocirc;ng c&oacute; giới hạn n&agrave;o ngo&agrave;i những giới hạn ta tự đặt ra cho ch&iacute;nh bản th&acirc;n m&igrave;nh&rdquo;. CH&Igrave;A KH&Oacute;A MỞ CỬA T&Acirc;M HỒNTrong cuốn s&aacute;ch lần n&agrave;y, khi đ&atilde; nhận diện được bản th&acirc;n v&agrave; vượt qua giới hạn m&agrave; bản th&acirc;n m&igrave;nh đặt ra, Nick đề cao sức mạnh của sự hy vọng. Hy vọng giống như một phương thuốc tinh thần hữu hiệu, kh&ocirc;ng chỉ xoa l&agrave;nh những buồn đau, tuyệt vọng m&agrave; c&ograve;n hướng con người tới những điều tốt đẹp trong tương lai. Nick cho rằng &ldquo;Mất hy vọng c&ograve;n tồi tệ hơn mất ch&acirc;n tay&rdquo; v&agrave; khuy&ecirc;n bạn &ldquo;H&atilde;y hy vọng về những điều tưởng chừng như kh&ocirc;ng thể&rdquo;. Bởi v&igrave; &ldquo;Hy vọng l&agrave; nơi những ước mơ bắt đầu.\r\n\r\nHy vọng l&agrave; m&oacute;n qu&agrave; qu&yacute; b&aacute;u m&agrave; tạo h&oacute;a d&agrave;nh cho ch&uacute;ng ta, một c&aacute;nh cửa hướng tới tương lai&rdquo;. Nhờ đ&oacute;, cậu b&eacute; thất vọng ng&agrave;y ấy giờ đ&acirc;y đ&atilde; trở th&agrave;nh một người đ&agrave;n &ocirc;ng c&oacute; cuộc sống tr&agrave;n ngập h&agrave;nh ph&uacute;c v&agrave; sự b&igrave;nh y&ecirc;n. Nick chia sẻ: &ldquo;Khi c&ograve;n l&agrave; một cậu b&eacute; mười tuổi t&ocirc;i kh&ocirc;ng thể tin rằng mười năm sau, t&ocirc;i đi khắp thế giới diễn thuyết trước h&agrave;ng triệu người kh&iacute;ch lệ tinh thần họ v&agrave; dẫn dắt họ bước tới một cuộc sống tốt đẹp hơn. Khi đ&oacute; t&ocirc;i cũng kh&ocirc;ng thể biết được t&ocirc;i sẽ được đ&oacute;n nhận t&igrave;nh y&ecirc;u của một người con g&aacute;i xinh đẹp, th&ocirc;ng minh, tr&agrave;n đầy đức tin v&agrave; l&ograve;ng can đảm, một t&igrave;nh y&ecirc;u s&aacute;nh ngang thậm ch&iacute; vượt tr&ecirc;n t&igrave;nh y&ecirc;u của gia đ&igrave;nh t&ocirc;i v&agrave; người con g&aacute;i đ&oacute; giờ đ&acirc;y đ&atilde; trở th&agrave;nh vợ của t&ocirc;i&rdquo;.\r\n\r\nTrong cuốn s&aacute;ch Sống Cho Điều &Yacute; Nghĩa Hơn, bạn đọc kh&ocirc;ng chỉ bị chinh phục bởi Nick Vujicic m&agrave; c&ograve;n bởi những người m&agrave; anh đ&atilde; gặp gỡ v&agrave; tiếp x&uacute;c. Đ&oacute; l&agrave; Helen Keller bị m&ugrave; v&agrave; bị điếc do mắc bệnh từ khi b&agrave; chưa tr&ograve;n hai tuổi nhưng người phụ nữ n&agrave;y vẫn sống trọn vẹn với hạnh ph&uacute;c của m&igrave;nh v&igrave; &ldquo;trung th&agrave;nh với mục đ&iacute;ch xứng đ&aacute;ng&rdquo;. L&agrave; những em nhỏ bị mồ c&ocirc;i trong trận động đất ở Tứ Xuy&ecirc;n nhưng vẻ mặt vẫn h&acirc;n hoan, rạng ngời, niềm vui vẫn lấp l&aacute;nh trong &aacute;nh mắt khi biểu diễn trong tiết mục nghệ thuật thuộc chương tr&igrave;nh Olympic. Đ&oacute; c&ograve;n l&agrave; Bethany Hamilton, một vận động vi&ecirc;n lướt s&oacute;ng đẳng cấp quốc tế đ&atilde; bị c&aacute; mập tấn c&ocirc;ng v&agrave; cắn đứt một c&aacute;nh tay khi c&ocirc; mười ba tuổi đang lướt s&oacute;ng ở Hawaii. V&agrave; mặc d&ugrave; chỉ c&ograve;n một c&aacute;nh tay nhưng trong cuộc thi d&agrave;nh cho c&aacute;c vận động vi&ecirc;n lướt s&oacute;ng h&agrave;ng đầu thế giới c&ocirc; gi&agrave;nh vị tr&iacute; thứ ba&hellip;\r\n\r\nC&ograve;n nhiều, rất nhiều những tấm gương như vậy m&agrave; Nick đ&atilde; gặp hoặc đ&atilde; khắc ghi v&agrave;o t&acirc;m tr&iacute; m&igrave;nh qua những c&acirc;u chuyện được nghe, được đọc. Giống như Nick, những tấm gương đ&oacute; mỗi người đều c&oacute; một th&aacute;ch thức ri&ecirc;ng nhưng điều quan trọng l&agrave; họ đ&atilde; vượt qua được giới hạn của bản th&acirc;n để hy vọng về một cuộc sống tốt đẹp hơn v&agrave; để chia sẻ với cộng đồng, với những người xung quanh m&igrave;nh. Sống Cho Điều &Yacute; Nghĩa Hơn gồm 46 chương, mỗi chương với dung lượng từ 3-4 trang, được xem như những chiếc ch&igrave;a kh&oacute;a để độc giả c&oacute; thể mở cửa một t&acirc;m hồn v&ocirc; c&ugrave;ng cao đẹp, để từ đ&oacute; c&oacute; dịp soi chiếu lại m&igrave;nh, để tin d&ugrave; m&igrave;nh l&agrave; ai, l&agrave;m c&ocirc;ng việc g&igrave;, l&agrave;nh lặn hay tật nguyền th&igrave; m&igrave;nh vẫn c&oacute; một t&acirc;m hồn đ&aacute;ng qu&yacute; như thế.\r\n\r\nSống Cho Điều &Yacute; Nghĩa Hơn được Nick viết giản dị nhưng vẫn c&oacute; t&iacute;nh triết l&yacute;, kh&ocirc;ng kh&oacute; để bạn đọc c&oacute; thể thấy được chiều s&acirc;u của th&ocirc;ng điệp m&agrave; Nick gửi gắm trong những c&acirc;u chuyện của m&igrave;nh. Cuốn s&aacute;ch sẽ l&agrave; m&oacute;n qu&agrave; tinh thần qu&yacute; gi&aacute; cho những người đang gặp bất hạnh, đang t&igrave;m một hướng đi để sống cho điều &yacute; nghĩa hơn.\r\n\r\nĐỪNG BAO GIỜ TỪ BỎ KH&Aacute;T VỌNG Tiếp tục l&agrave; người đồng h&agrave;nh c&ugrave;ng Nick Vujicic trong tập s&aacute;ch thứ ba n&agrave;y để chuyển tới độc giả Việt Nam những c&acirc;u chuyện th&uacute; vị, dịch giả Nguyễn B&iacute;ch Lan cho biết, d&ugrave; đ&atilde; xem Nick rất nhiều tr&ecirc;n youtube hay qua s&aacute;ch b&aacute;o, nhưng được l&agrave;m việc với một người m&agrave; m&igrave;nh y&ecirc;u mến v&agrave; cảm phục về nghị lực với chị l&agrave; một điều th&iacute;ch th&uacute;. Nguyễn B&iacute;ch Lan cho biết, to&agrave;n bộ thời gian dịch ba t&aacute;c phẩm của Nick k&eacute;o d&agrave;i trong một năm bảy th&aacute;ng v&agrave; đ&acirc;y l&agrave; khoảng thời gian &yacute; nghĩa m&agrave; cuộc sống của chị lu&ocirc;n đầy ắp h&igrave;nh ảnh của Nick Vujicic. Kh&aacute;c với hai cuốn s&aacute;ch trước, Sống Cho Điều &Yacute; Nghĩa Hơn được Nguyễn B&iacute;ch Lan ho&agrave;n th&agrave;nh trong thể chất kh&ocirc;ng được khỏe. D&ugrave; c&ocirc;ng việc kh&ocirc;ng vất vả nhưng ch&iacute;nh v&agrave;o khoảng thời gian đ&oacute;, chị phải chịu một trận ốm kh&aacute; nặng. Một nửa cuốn s&aacute;ch được B&iacute;ch Lan ho&agrave;n thiện trong l&uacute;c bị ốm. B&iacute;ch Lan chia sẻ: &ldquo;V&igrave; muốn cuốn s&aacute;ch ra mắt kịp tiến độ n&ecirc;n c&oacute; những ng&agrave;y, d&ugrave; đang ốm nhưng t&ocirc;i vẫn bảo người nh&agrave; n&acirc;ng dậy khỏi giường để ngồi v&agrave;o b&agrave;n l&agrave;m việc. Chưa bao giờ t&ocirc;i dịch một t&aacute;c phẩm trong t&igrave;nh trạng ốm như vậy, đ&oacute; cũng l&agrave; một thử th&aacute;ch cho bản th&acirc;n t&ocirc;i. V&agrave; sau thử th&aacute;ch n&agrave;y, t&ocirc;i thấy m&igrave;nh lớn hơn một ch&uacute;t nữa&rdquo;.\r\n', 'FIRST NEWS', 'NXB Tổng Hợp TPHCM', '2023-04-25 01:30:02');
INSERT INTO `products` (`product_id`, `name`, `author`, `price`, `image`, `description`, `supplier`, `publiser`, `date`) VALUES
('ydU8EWxv2B', ' Độc Thân Không Phải \"Ế\"', 'John Kim', 136000, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935278607267.jpg', 'Độc th&acirc;n kh&ocirc;ng phải ế\r\n\r\nT&igrave;nh y&ecirc;u lứa đ&ocirc;i c&oacute; phải l&agrave; thứ duy nhất mang lại hạnh ph&uacute;c?\r\n\r\nRất nhiều người trong ch&uacute;ng ta c&oacute; nghĩ rằng n&ecirc;́u không có đôi có cặp thì chúng ta sẽ không th&ecirc;̉ nào cảm th&acirc;́y hạnh phúc, nhưng đ&acirc;y l&agrave; một quan niệm sai lầm m&agrave; ch&uacute;ng ta cần phải loại bỏ.\r\n\r\nTrong cuốn s&aacute;ch &ldquo;Độc Th&acirc;n Kh&ocirc;ng Phải Ế&rdquo;, t&aacute;c giả John Kim đ&atilde; kể rằng anh đã từng tham v&acirc;́n cho hàng nghìn người độc thân trong nhi&ecirc;̀u năm qua, g&acirc;̀n như t&acirc;́t cả họ đ&ecirc;̀u tin rằng họ không th&ecirc;̉ s&ocirc;́ng hạnh phúc cho đ&ecirc;́n khi tìm ra bạn đời, k&ecirc;́t hôn hoặc khi có ai đó ở nhà chờ họ trở v&ecirc;̀ sau một ngày làm việc. Với họ, độc thân đ&ocirc;̀ng nghĩa với thi&ecirc;́u sót, khi&ecirc;́m khuy&ecirc;́t và kém cỏi. Họ tin rằng chắc chắn có v&acirc;́n đ&ecirc;̀ gì đó ở mình.\r\n\r\nSong, sự thật là bạn không c&acirc;̀n phải có người yêu đ&ecirc;̉ được hạnh phúc. Chắc chắn là một m&ocirc;́i quan hệ v&acirc;̃n có th&ecirc;̉ mang lại cho bạn nhi&ecirc;̀u ni&ecirc;̀m vui, và cũng chẳng có gì sai trái n&ecirc;́u bạn mu&ocirc;́n có người yêu. Nhưng m&ocirc;́i quan hệ tình cảm không phải là đi&ecirc;̀u kiện c&acirc;̀n đ&ecirc;̉ bạn s&ocirc;́ng hạnh phúc. Đó không phải là cách duy nh&acirc;́t đ&ecirc;̉ tìm được ni&ecirc;̀m vui trong cuộc s&ocirc;́ng. Hạnh phúc của bạn không h&ecirc;̀ phụ thuộc vào việc yêu thương một người nào khác. Bạn mang trong mình quan niệm này là vì phim ảnh, quảng cáo, những chu&acirc;̉n mực xã hội, mạng xã hội và chính những lời khuyên, lời hướng d&acirc;̃n của người đi trước.\r\n\r\nNhận định lại gi&aacute; trị v&agrave; t&igrave;m lại bản th&acirc;n m&igrave;nh\r\n\r\nCuốn s&aacute;ch &ldquo;Độc Th&acirc;n Kh&ocirc;ng Phải Ế&rdquo; d&agrave;nh cho bất kỳ ai đang cảm th&acirc;́y vô vọng, cô đơn hay nản chí và không bi&ecirc;́t phải làm gì đ&ecirc;̉ cải thiện tình hình.\r\n\r\nNếu bạn đang cảm thấy giá trị của m&igrave;nh bị giảm đi hoặc bạn có v&acirc;́n đ&ecirc;̀ gì đó vì chưa tìm được &ldquo;nửa kia&rdquo; hay &ldquo;tình yêu đích thực&rdquo;.\r\n\r\nNếu bạn đã ngán tận c&ocirc;̉ việc phải &ldquo;quẹt trái quẹt phải&rdquo;, bị &ldquo;lừa tình&rdquo; hay bị &ldquo;bơ toàn tập&rdquo; trên các ứng dụng hẹn hò.\r\n\r\nNếu bạn không mu&ocirc;́n dính dáng đ&ecirc;́n những t&acirc;́m ảnh nóng, tiêu chu&acirc;̉n kép và những m&ocirc;́i tình một đêm.\r\n\r\nNếu bạn đang tự hỏi rằng mình còn cơ hội k&ecirc;́t hôn và có con không..\r\n\r\nTh&igrave; h&atilde;y đọc cuốn s&aacute;ch n&agrave;y để nhận định lại c&aacute;c gi&aacute; trị v&agrave; t&igrave;m lại bản th&acirc;n m&igrave;nh.\r\n', 'Saigon Books', 'Thế Giới', '2023-04-25 01:20:16'),
('YlJL7v4nou', 'Tu Tâm Cho Tốt Thì Đời Thong Dong', 'Hoa Dương', 76000, 'https://cdn0.fahasa.com/media/catalog/product/i/m/image_244718_1_5810.jpg', 'Tu tâm cho tốt thì đời thong dong - tu tâm cốt là để hiểu mình, hiểu đời rồi vui sống.\r\n\r\nNếu cảm thấy thế giới bên ngoài quá hỗn loạn, ấy là do bản tâm mình chưa đủ vững vàng, thấu triệt để nhìn nhận mọi thứ theo hướng rõ ràng. Muốn thay đổi thế giới, chúng ta phải bắt đầu từ việc “quay vào bên trong”, tu sửa bản thân, chuyển hóa những nhận thức về cuộc đời. \r\n\r\n“Tu tâm cho tốt thì đời thong dong” là tập hợp những bài viết ý nghĩa về cách chuyển hóa cảm xúc, thấu hiểu nguồn cơn sự việc và cách thế giới vận hành, để từ đó “hiểu đời, hiểu ta rồi vui sống”. \r\n\r\nCuốn sách sẽ giúp ta hiểu rằng:\r\n\r\nTu tâm không phải để công việc được suôn sẻ mà là để tâm đủ bình tĩnh sáng suốt trước mọi khó khăn trắc trở.\r\n\r\nTu tâm không phải để mọi mối quan hệ được tốt đẹp mà là để thấu hiểu mọi bất đồng, mọi mâu thuẫn, xáo trộn trong đó.\r\n\r\nTu tâm không có nghĩa là muốn mọi người thân làm theo mình mà là tôn trọng nhận thức và niềm tin của mỗi người theo trình độ của họ.\r\n\r\nNgười ta không buông bỏ được là vì không tìm được thứ tốt hơn. Tìm được thứ tốt hơn, phù hợp hơn, tự khắc sẽ không còn bám chấp.\r\n\r\nBiết dùng chia sẻ thay cho tranh đoạt, sẽ buông bỏ được lòng tham.\r\n\r\nDùng trí tuệ thay cho u mê, sẽ buông bỏ được cố chấp.\r\n\r\nDùng chánh niệm thay cho tạp niệm, sẽ buông bỏ được ngông cuồng.\r\n\r\nDùng tha thứ thay cho báo thù, sẽ buông bỏ được giận dữ.\r\n\r\nTu sửa mình, không phải là buông bỏ, mà là để hiểu lẽ hoán đổi.\r\n\r\nTu tâm không phải là đọc bao nhiêu câu kinh, cúi lạy bao nhiêu vị Phật, mà là sự khám phá ra chính mình và sống thực với mình. Hãy cùng nhìn lại con đường mình đã đi, soi chiếu những gì mình đã làm và thấu hiểu bản tâm mình hơn với cuốn sách này, cuốn sách khép lại năm 2021 của Tủ Sách Chữa Lành nhé,', 'Skybooks', 'NXB Tổng Hợp TPHCM', '2023-04-25 00:53:07'),
('YUfE8dxY7c', 'Cuộc Chơi Khởi Nghiệp 3', 'William H. Draper III', 143000, 'https://cdn0.fahasa.com/media/catalog/product/8/9/8935251406313.jpg', 'Cuộc Chơi Khởi Nghiệp 3\r\n\r\n&quot;Doanh nh&acirc;n v&agrave; nh&agrave; đầu tư - sự kết đ&ocirc;i ho&agrave;n hảo của những con người c&oacute; khả năng l&agrave;m thay đổi thế giới l&agrave; điều Bill Draper muốn chia sẻ với độc giả bằng ch&iacute;nh c&acirc;u chuyện cuộc đời m&igrave;nh. Bạn đọc l&agrave; doanh nh&acirc;n đang muốn t&igrave;m v&agrave; muốn biến sự kết đ&ocirc;i n&agrave;y trở n&ecirc;n ho&agrave;n hảo, th&igrave; cuốn s&aacute;ch n&agrave;y l&agrave; gợi &yacute; tuyệt vời nhất cho bạn.&quot;\r\n\r\n- Nguyễn Hồng Trưởng,\r\n\r\nQuỹ đầu tư mạo hiểm IDG Ventures Vietnam\r\n\r\n&quot;Cuốn s&aacute;ch n&agrave;y giống như hồi k&yacute; của nh&agrave; đầu tư mạo hiểm thế hệ thứ nhất tr&ecirc;n thế giới. N&oacute; diễn giải chi tiết về đầu tư mạo hiểm cho những ai c&ograve;n xa lạ với &quot;ng&agrave;nh c&ocirc;ng nghiệp&quot; n&agrave;y bằng nhiều lời khuy&ecirc;n cũng như những c&acirc;u chuyện c&oacute; thực.&quot;', 'Alpha Books', 'NXB Công Thương', '2023-04-25 02:11:14'),
('yUgoJzU41E', 'Cuộc Chơi Khởi Nghiệp 2', 'Jeffrey Bussgang', 143000, 'https://cdn0.fahasa.com/media/catalog/product/9/3/935251406306.jpg', 'Cuộc Chơi Khởi Nghiệp 2\r\n\r\n&quot;Đ&acirc;y l&agrave; một cuốn s&aacute;ch rất tuyệt vời. N&oacute; kh&ocirc;ng chỉ th&uacute; vị m&agrave; c&ograve;n cung cấp th&ocirc;ng tin chi tiết về quy tr&igrave;nh v&agrave; c&aacute;ch thức hoạt động của cả nh&agrave; đầu tư lẫn doanh nghiệp.&quot;\r\n\r\n- William Aulet\r\n\r\nGi&aacute;m đốc Trung t&acirc;m Doanh nghiệp MIT\r\n\r\n&quot;Một c&aacute;i nh&igrave;n to&agrave;n diện về mối quan hệ giữa nh&agrave; đầu tư v&agrave; doanh nghiệp. Cuốn s&aacute;ch sẽ gi&uacute;p bạn biết cần phải l&agrave;m g&igrave; để c&oacute; được một thương vụ hợp t&aacute;c tốt đẹp.&quot;\r\n\r\n- Tim Draper\r\n\r\nGi&aacute;m đốc Quỹ đầu tư Draper Fisher Jurvetson', 'Alpha Books', 'NXB Công Thương', '2023-04-25 02:10:45'),
('zeg7F8xfGV', 'Bước Chậm Lại Giữa Thế Gian Vội Vã', 'Hae Min', 63000, 'https://cdn0.fahasa.com/media/catalog/product/b/u/buoc_cham_lai_giua_the_gian_voi_va.u335.d20160817.t102115.612356.jpg', 'Bước Chậm Lại Giữa Thế Gian Vội V&atilde; (T&aacute;i Bản 2018)\r\n\r\nChen vai th&iacute;ch c&aacute;nh để c&oacute; một chỗ b&aacute;m tr&ecirc;n xe bu&yacute;t giờ đi l&agrave;m, nh&iacute;ch từng xentim&eacute;t b&aacute;nh xe tr&ecirc;n đường l&uacute;c tan sở, quay cuồng với thi cử v&agrave; tiến độ c&ocirc;ng việc, lu b&ugrave; vướng mắc trong những mối quan hệ cả th&acirc;n lẫn sơ&hellip; bạn c&oacute; lu&ocirc;n cảm thấy thế gian xung quanh m&igrave;nh đang xoay chuyển qu&aacute; vội v&agrave;ng?\r\n\r\nNếu c&oacute; thể, h&atilde;y tạm dừng một bước.\r\n\r\nĐể tự hỏi, l&agrave; do thế gian n&agrave;y vội v&agrave;ng hay do ch&iacute;nh t&acirc;m tr&iacute; bạn đang qu&aacute; bận rộn? Để cầm cuốn s&aacute;ch nhỏ dung dị m&agrave; lắng đọng n&agrave;y l&ecirc;n, chậm r&atilde;i lật giở từng trang, thong thả kh&aacute;m ph&aacute; những điều m&agrave; chỉ khi bước chậm lại mới c&oacute; thể thấu r&otilde;: về c&aacute;c mối quan hệ, về ch&iacute;nh bản th&acirc;n m&igrave;nh, về những trăn trở trước cuộc đời v&agrave; nh&acirc;n thế, về bao điều l&yacute; tr&iacute; rất hiểu nhưng tr&aacute;i tim chưa c&aacute;ch n&agrave;o nghe theo&hellip;\r\n\r\nRa mắt lần đầu năm 2012, Bước chậm lại giữa thế gian vội v&atilde; của Đại đức Hae Min đ&atilde; li&ecirc;n tục đứng đầu danh s&aacute;ch best-seller của nhiều trang s&aacute;ch trực tuyến uy t&iacute;n của H&agrave;n Quốc, trở th&agrave;nh cuốn s&aacute;ch chữa l&agrave;nh cho h&agrave;ng triệu người trẻ lu&ocirc;n tất bật với nhịp sống hiện đại hối hả.\r\n\r\n', 'Nhã Nam', 'NXB Hội Nhà Văn', '2023-04-25 01:09:38');

