--
-- Table structure for table `reviews`
--

CREATE TABLE `reviews` (
  `id` int(100) NOT NULL,
  `product_id` varchar(10) DEFAULT NULL,
  `combo_id` varchar(255) DEFAULT NULL,
  `user_id` varchar(20) NOT NULL,
  `rating` varchar(1) NOT NULL,
  `title` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `description` varchar(2500) NOT NULL,
  `date` date NOT NULL DEFAULT current_timestamp()
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

--
-- Dumping data for table `reviews`
--

INSERT INTO `reviews` (`id`, `product_id`, `combo_id`, `user_id`, `rating`, `title`, `description`, `date`) VALUES
(21, '85glwWSTrg', NULL, 'jBSM7nyWJV', '5', 'Làm tất cả mọi thứ vì gia đình', 'Xuyên suốt hành trình của Bố Già, Mario Puzo khiến nó nổi tiếng không phải bởi vì có những mưu sâu kế hiểm trong một thế giới mafia đầy rẫy chết chóc, càng không phải tiền bạc & quyền lực. Bởi những thứ đó, Holywood và những dòng phim điện ảnh thời bấy giờ thừa sức thể hiện xuất sắc.\r\n\r\nVà cũng không thiếu những tiểu thuyết kinh điển đã làm mưa làm gió trước khi Bố già ra đời.\r\n\r\nBởi vậy cái làm Bố già trở nên tiểu thuyết kinh điển & huyền thoại, cái đã thực sự làm nên thương hiệu của loạt series Bố già, chính là xuất phát, mục tiêu từ đáy của mỗi hành động: tất cả là vì gia đình.\r\n\r\nDù phải làm tất cả, phải “đưa ra một lời đề nghị hắn không thể chối từ”, không phải để thể hiện quyền lực, mà là nâng tầm sự quan trọng của những người thân trong gia đình.\r\n\r\nRõ ràng ông trùm không rảnh để quan tâm tới những điều nhỏ nhặt, nhưng một khi ai đó là người trong gia đình Corleone khẩn cầu, thì dù nhỏ bé hay lớn lao tới đâu, tất cả đều phải được giải quyết.', '2023-04-20'),
(22, 'Qg9kfupYAu', NULL, 'zAYghE9NNZ', '5', 'Người khen, kẻ chê', 'Trước giờ bản thân tôi vẫn tin rằng mỗi người đều có một vận mệnh riêng, một số phận với những hướng đi và sự lựa chọn không hề giống nhau. Dù rằng cả gia đình không hề theo bất cứ tôn giáo nào nhưng việc tiếp xúc với phật giáo và thiên chúa giáo từ nhỏ đến lớn do môi trường học tập đã khiến tôi ít nhiều bị ảnh hưởng tới nhận thức cùng suy nghĩ. Loanh quanh với những câu hỏi “ mười vạn vì sao” và tôi đã tiêu tốn một cách hào phóng thời gian cũng như tuổi trẻ của mình trong vòng luẩn quẩn không lối ra đó. Vì sao tôi tồn tại? Vì sao tôi được sinh ra? Vì sao tôi luôn cảm thấy mình khác với những người xung quanh? Mục đích hay ý nghĩa của sự tồn tại của tôi là gì? …Cho tới khi tôi gia nhập “hội những người thích sách” và được bạn bè chia sẻ cho những cuốn sách hay và đặc biệt trong số đó có hai cuốn sách đã trở thành một phần “ cứu rỗi” trong tôi. Ba người thầy và nhà giả kim thuật.', '2023-04-20'),
(23, '85glwWSTrg', NULL, 'zAYghE9NNZ', '3', 'Biết cách kiềm chế bản thân', 'Ông trùm Vito luôn biết cách kiềm chế cảm xúc của bản thân, bình tĩnh trước mọi sóng gió ập đến gia đình ông. Vì vậy, việc để lộ ra cảm xúc của mình không khác nào việc mình đưa cổ cho kẻ thù kết liễu.', '2023-04-20'),
(27, '6eYZStMJul', NULL, 'jBSM7nyWJV', '5', 'Học làm người !!!!!!!!', '\r\nNhắc tới người giỏi hùng biện, giỏi ăn nói ta thường liên tưởng đến hình ảnh mồm miệng linh hoạt, ngôn tử sắc sảo, lời lẽ đanh thép. Còn im lặng, ít nói thường được gán với nhút nhát, kĩ năng giao tiếp thuyết trình kém. Tuy nhiên có đôi khi Thinh lặng cũng chính là hùng biện. Nhờ biết thinh lặng đúng lúc đúng chỗ mà lời nói tăng thêm nhiều phần hiệu quả. Có 1 sự khác biệt rất lớn giữa “im lặng vì không biết nói gì” với “khéo léo vận dụng khoảng lặng làm điểm nhấn”. Một bài hát hay cần có vài đoạn lắng đọng để tăng cảm xúc. Một bộ phim hay cần những phút chậm lại giúp người xem cảm nhận sâu hơn. Mối mối quan hệ cũng cần có lúc lắng xuống chứ không thể liên tục cao trào. Hiểu được giá trị của thinh lặng, mới thấy học nói khó bao nhiêu thì học “im lặng đúng lúc đúng chỗ đúng cách” khó bấy nhiêu. Nhưng cần lắm phải học, và bước đầu tiên mình sẽ đọc hết cuốn này.', '2023-04-25'),
(28, 'x3u0HqygtD', NULL, 'jBSM7nyWJV', '5', 'Tuổi thơ của Clarice Starling', 'Bản dịch, phải nói là rất tệ, cứ như đang đọc Google dịch vậy, nhiều chỗ không chú thích đọc rất khó hiểu, có mỗi một đoạn đọc đi đọc lại hai đến ba lần mới hiểu, mệt mỏi, làm giảm hứng thú đọc, thật sự không lôi cuốn được mình, điểm trừ lớn nhất của truyện. Bỏ qua bản dịch thì nội dung khá ổn, tuy nhiên chả hề kinh dị, thật chứ nhiều người nói đọc ám ảnh chứ cũng không thấy ám ảnh tẹo nào, chỗ ăn thịt người này nọ thấy cũng bình thường, nhiều chỗ lan man dài dòng đọc khá mệt. Nhân vật thích nhất, cũng như nhiều người, bác sĩ Hannibal Lecter, nhân vật ngầu lòi và nổi tiếng nhất của Thomas Harris. Nhưng thật sự là nó không lôi cuốn và hấp dẫn như mình nghĩ. Tuy vậy, Sự im lặng của bầy cừu vẫn là một tác phẩm đáng đọc cho bạn nào mê trinh thám chứa yếu tố tâm lý. Nhiều bạn như mình đọc nhiều thể loại kinh dị rồi sẽ thấy nó bình thường, nếu không muốn nói là chán.', '2023-04-25'),
(29, 'a6cqDBoUNh', NULL, 'jBSM7nyWJV', '5', 'Cô gái trên tàu', 'Đây là một cuốn tiểu thuyết kinh dị khác của Paula Hawkins, kế tiếp cuốn \'Cô gái trên tàu\'. Bà mẹ đơn thân Nel Abbott chết ở bên một dòng sông với tình trạng nội tạng bên trong bị tổn thất nặng nề. Nel là mẹ của Lena. Trước đó vài tuần, bạn thân của Lena- cô gái tên là Katie Whittaker cũng chết đuối ở đó. Còn lại chỉ mình Lena, một cô gái 15 tuổi cô đơn. Không có cha mẹ và không có bạn bè, giờ đây cô phải chăm sóc cô em gái..... Phần đầu cuốn sách tôi đọc thấy có đôi chút khó hiểu, có lẽ là chưa quen nhân vật, cùng với cách viết của tác giả, kể truyện qua lăng kính của nhiều nhân vật khác nhau. Đối với những trang đầu tiên, đọc \"Vào trong dòng nước\" rất giống với việc nhìn lên bề mặt từ đáy hồ: mọi thứ đều tối tăm và chậm chạp, bạn thực sự không thể biết ai và những gì bạn đang nhìn thấy. Tuy nhiên, nếu bạn kiên nhẫn đọc , lại thấy cuốn sách thú vị. Giống như \'Cô gái trên tàu\', \'Vào trong dòng nước\' chứa đựng đầy rẫy những cảm xúc không an toàn, mất phương hướng, hiện tại đan xen lẫn quá khứ. Tác giả đã sắp xếp chi tiết một cách hợp lý để người đọc cảm thấy tò mò, ly kì. Thu hút nhất ở đây là lịch sử của con sông, nó thực sự lôi cuốn người đọc. Với cách viết liên tục thay đổi ngôi kể, giữa ngôi thứ nhất, ngôi thứ ba, dựng lên các mảnh ghép rời rạc của một bức tranh u ám, để rồi sau đó kết nối chúng với nhau. Tác giả Paula Hawkins cứ chầm chậm lôi cuốn người đọc thông qua các chiều sâu, nhân vật bằng các đầu mối, cho đến khi chúng ta thực sự chìm đắm trong sự hồi hộp, kích thích. Điểm trừ cuốn tiểu thuyết là viết khá dài dòng.', '2023-04-25'),
(30, 'tbu427TZ89', NULL, 'jBSM7nyWJV', '5', ' Cuốn \"Khi hơi thở hóa thinh không\"', 'Paul là một bác sĩ phẫu thuật thần kinh đang ở năm cuối của kỳ thực tập nội trú y khoa của Mỹ. Anh có bằng cử nhân và thạc sĩ về Văn học Anh, Triết học, Khoa học, Y khoa và từng đạt được giải thưởng cao quý nhất cho sự nghiệp phẫu thuật viên thần kinh của mình. Vài tháng trước khi kỳ thực tập kết thúc, sau nhiều lần cố tình bỏ qua mọi dấu hiệu và đổ tội cho tần suất làm việc cường độ cao, Paul bàng hoàng phát hiện ra lý do khiến cho sức khỏe của mình suy sụp. Liệu có bất kỳ khả năng nào nó là một thứ gì khác không? Không.” Paul mắc ung thư phổi. Những tháng ngày tiếp theo của anh trở thành cuộc chiến chống lại căn bệnh quái ác - nhưng anh không còn là người đồng hành, mà là nhân vật chính. Giống như mở đầu kinh điển của một cuốn tiểu thuyết giả tưởng, nhưng đây lại là câu chuyện có thực về Paul Kalanithi. Những năm tháng nghiên cứu về văn học của Paul đã giúp anh có khả năng viết nên cuốn tự truyện thực sự cảm động và xuất sắc, đến nỗi, mình gần như nghĩ nó là một tác phẩm fiction - dù nó được viết trong quãng thời gian anh chống chọi với ung thư, với những cơn đau và sự kiệt quệ đến mức làm con người ta không còn tỉnh táo. Sau lời mở đầu, cuốn sách gồm hai phần chính : “Khởi đầu từ một sức khỏe hoàn hảo”, và “Không dừng cho tới chết”, được chia cắt bởi thời điểm anh phát hiện mình mang trong mình căn bệnh ung thư.Nỗi đau thất bại đã khiến tôi hiểu được rằng sự xuất sắc về mặt kỹ thuật là một dòi hỏi về mặt đạo đức.” Phần I là hành trình dẫn Paul tới phẫu thuật thần kinh và trở thành một trong những phẫu thuật viên thần kinh giỏi nhất của nước Mỹ. Paul đã giãi bày những trăn trở của anh về sự sống qua cái nhìn triết học, và một cách chân thực, khắc họa những góc khuất sau chiếc áo choàng trắng của nhân viên y tế: áp lực gây ra bởi những giờ làm việc triền miên, trách nhiệm nặng nề với tính mạng và chất lượng cuộc sống của người bệnh, trong khi ngược đãi cơ thể mình để chăm sóc sức khỏe cho người khác và vẫn cố gắng duy trì cuộc sống riêng .Lời kể của anh tái hiện trước mắt mình những tháng ngày học tập tại trường Y: những buổi thực hành giải phẫu, sự rung động trước vẻ đẹp của cơ thể con người...; cũng như sự khắc nghiệt khôn cùng của kỳ thực tập nội trú y khoa trứ danh của Mỹ: Bước ra khỏi chương trình nội trú chỉ có thể là một bác sĩ cừ khôi hoặc một người ngoại đạo, hoặc một xác chết.“Chết là sự kiện chỉ xảy ra trong chốc lát, nhưng sống cùng bệnh hiểm nghèo lại là cả một quá trình”. Kể cả khi chưa đọc cuốn sách này, thì đúng như cậu dự đoán, kết thúc p', '2023-04-25'),
(31, 'tbu427TZ89', NULL, 'zAYghE9NNZ', '3', 'Ấn tượng đầu tiên', 'Ấn tượng đầu tiên của mình đối với cuốn sách này chính là hình bìa đậm chất bệnh viện, là một bác sĩ trong có vẻ như trong một ca phẫu thuật. Cuốn sách \"Khi hơi thở hóa thinh không\" là tự truyện của một bác sĩ bị mắc bệnh ung thư phổi, kể về cuộc chiến đấu với căn bệnh đó. Có lẽ chúng ta nhận định bác sĩ là một nghề nhàn hạ, \"hái ra tiền\" vì dễ dàng đút túi những phong bì dày cộp hay mức lương lý tưởng, cao hơn mọi ngành nghề khác. Các bác sĩ có thể luôn nhan hạ, rỗi rãi đi dạo trong bệnh viện như trong phim truyền hình. Nhưng có mấy người biết được cực khổ trong nghề. Họ phải vô cùng can đảm để mổ xẻ bệnh nhân, thực hiện những ca phẫu thuật căng như dây chão kéo dài suốt nhiều giờ đồng hồ, thậm chí là vài ngày liền. Họ mang một sứ mệnh vô cùng to lớn là giành giật sự sống khỏi tay Thần Chết cho những bệnh nhân. Trong cuốn sách này, tác giả đã chia sẻ những trải nghiệm từ khi mới bắt đầu học ngành y, tiếp xúc với bệnh nhân. Anh đã vô cùng thành công, chiến thắng hàng trăm cuộc chiến như thế, đem lại cuộc sống mạnh khỏe, hạnh phúc cho nhiều người nhưng trớ trêu thay, anh lại thất bại trong chính cuộc chiến của chính bản thân mình. Anh phát hiện ra mình bị ung thư và phải điều trị lâu dài. Anh đã kiên cường như một chiến binh thực thụ, luôn lạc quan về ngày mai tươi sáng. Thế nhưng không phải trên đời cái kết nào cũng có hậu, Paul đã qua đời trong sự đưa tiễn của toàn thể gia đình, họ hàng, khép lại cuộc đời của một vị bác sĩ đáng kính khi tuổi đời còn rất trẻ. Kalanithi rất yêu thích văn chương nên câu chuyện của anh đã được thuật lại theo một phong cách mượt mà, dung dị và đầy cảm xúc. Độc giả cũng được hiểu thêm về triết lý sống, triết lý nghề y của Kalanithi, thông qua ký ức về những ngày anh còn là sinh viên, rồi thực tập, cho đến khi chính thức hành nghề phẫu thuật thần kinh. “Đối với bệnh nhân và gia đình, phẫu thuật não là sự kiện bi thảm nhất mà họ từng phải đối mặt và nó có tác động như bất kỳ một biến cố lớn lao trong đời. Trong những thời điểm nguy cấp đó, câu hỏi không chỉ đơn thuần là sống hay chết mà còn là cuộc sống nào đáng sống.” – Kalanithi luôn biết cách đưa vào câu chuyện những suy nghĩ sâu sắc và đầy sự đồng cảm như thế. Dù không thể vượt qua cơn bệnh nan y, nhưng thông điệp của tác giả sẽ còn khiến người đọc nhớ mãi. Tác giả đã truyền cảm hứng tới tất cả chúng ta, những người đang chiến đấu với căn bệnh ung thư quái ác và những người khỏe mạnh. Hãy trân trọng những gì chúng ta đang có, hãy lạc quan lên, hãy luôn kiên cường để không hối', '2023-04-25'),
(32, 'BhECZ94yCh', NULL, 'zAYghE9NNZ', '5', 'Thế nào mới là sống', 'hời khắc tươi đẹp là cuốn sách được viết bởi một người phụ nữ mắc bệnh ung thư đang trong những năm tháng cuối cuộc đời. Mình đã tưởng tượng cuốn sách này với đầy những triết lý sống bi luỵ và sướt mướt của một người đang cận kề cái chết, nhưng thay vì thế Nina Riggs lại kể về cuộc sống bình dị của cô với những thời khắc hạnh phúc và tươi đẹp nhất, từ những bữa tiệc tùng, tham gia câu lạc bộ sách, nhận nuôi chú chó mới, cắm trại, các ngày lễ, trò chuyện cùng các con,... Trong suốt cuốn sách, Nina chưa một lần kêu ca hay tỏ ra yếu đuối, cô chẳng hề căm thù căn bệnh ung thư và lo sợ về cái chết của chính mình. Điều cô lo lắng nhất, đó là gia đình nhỏ của cô. Nina không đồng ý để chồng thắt ống dẫn tinh vì cô nói, chồng cô cần kết hôn và sinh con với người phụ nữ khác sau khi cô mất đi. Nina cùng người bạn thân của mình lên ý tưởng ghi âm lại những lời nhắc nhở và gửi vào tài khoản email, để sau này các con có thể mở ra nghe khi chúng cần. Đúng như John- chồng của Nina- nói trong lời bạt cuốn sách: “tác phẩm đểđời này của cô ấy thiên về việc gieo mầm hạnh phúc trong tương lai cho chúng tôi hơn là viết về bản thân”. Gấp lại cuốn sách, mình thực sự khâm phục Nina – một người phụ nữ hài hước, lạc quan và tài giỏi – một người Mẹ hết mực thương yêu và chăm lo cho các con – một người Vợ chu đáo, thấu hiểu và yêu chồng – dù phải tạm biệt cuộc sống khi mới bốn mươi tuổi nhưng đã chỉ cho chúng ta hay “thế nào mới là sống”.', '2023-04-25'),
(33, 'mLGiYbf6Ar', NULL, 'zAYghE9NNZ', '5', 'Vòng đu quay đêm quá đỉnh', '\r\nMột quyển sách hay. Vòng đu quay đêm là câu chuyện về 3 gia đình hàng xóm trong khu dân cư cao cấp, với gần 10 nhân vật mà mỗi người sẽ được tác giả khắc họa chân dung và cá tính đầy đủ, quá khứ của họ ra sao, mong mỏi cả đời của họ như thế nào, áp lực mà họ phải chịu và thứ khiến họ không hài lòng với thực tại là gì. ▫️ Bà mẹ nhà Endo dù được sống trong ngôi nhà mơ ước nhưng khổ tâm vì chồng hời hợt, đứa con ngỗ ngược vì thi trượt trường nữ sinh danh giá. Nhà Takahashi - nơi án mạng xảy ra - cha thành đạt, con cái đứa học giỏi, đứa cừ thể thao, nhưng như vậy là hạnh phúc? Nhà còn lại, Kojima, chỉ có một mình bà Satoko vì 2 con ở xa, lại băn khoăn về những người hàng xóm của mình. Yếu tố trinh thám thật ra bằng 0, án mạng chỉ là cái gốc để từ đó tác giả bàn những chuyện khác, mà một trong số đó là chuyện muôn thuở: mỗi gia đình đều có 2 phiên bản - 1 là thứ người ngoài nhìn vào trong thấy, và 2 là những sóng gió, bất đồng mà chỉ trong nhà biết và không bao giờ muốn lọt ra khỏi cửa, đến tai hàng xóm hay con mắt của người đời. ▫️ Thay vì kể quá trình điều tra phá án, tác phẩm đi sâu vào phân tích tâm lý xã hội, những phức tạp, nghịch lý mà ta không hề thấy xa lạ: không được như kỳ vọng thì đúng là buồn thật đấy, nhưng có được thứ mình từng mơ ước chưa chắc đã hay. Sinh ra trong gia đình danh giá, toàn người tài giỏi, là phúc hay hoạ? Và chúng ta có bao giờ có ý nghĩ giết chết người trong gia đình cho đỡ phiền phức? Con người chúng ta sinh ra không phải là để đối mặt với biến cố. Đùng một cái, bố chết, mẹ và em trai đều là nghi phạm, dù sự thật có là thế nào thì cũng quá tàn nhẫn, gia đình, mà trong mắt người ngoài là vô cùng hoàn hảo, bỗng chốc tan tác. Vòng đu quay đêm là một quyển nặng về tâm lý. Tác giả Minato Kanae dù không còn dùng lối trần thuật ngôi thứ nhất như các tác phẩm trước đó, song vẫn giữ phong cách kể đi kể lại một câu chuyện với nhiều góc nhìn khác nhau. Người đọc được cung cấp toàn bộ dữ kiện, gồm cả toàn bộ suy nghĩ, độc thoại nội tâm của các nhân vật, để chính họ có thể thử suy xét vấn đề.', '2023-04-25'),
(34, 'LMneqdUPdG', NULL, 'zAYghE9NNZ', '5', 'Chuộc tội hay thú tội ????', 'Sau \"Thú tội\", tác giả tiếp tục lối viết đi sau vào tâm lí từng nhân vật ở \"Chuộc tội\". Khác với \"Thú tội\", chúng ta dường như biết hung thủ là ai ngay từ những trang đầu, \"Chuộc tội\" giữ cú plot twist ở những trang cuối cùng. Câu chuyện có bối cảnh tại một ngôi làng \"có khí hậu trong lành nhất Nhật Bản\", nơi dần dần thay đổi vì có nhà máy sản xuất máy móc chuyển tới. Những thay đổi tích cực và tiêu cực do cuộc sống hiện đại mang lại miêu tả trong truyện, khiến mình nhớ lại cuộc sống của mình 9 năm về trước, có chút bồi hồi. Xoay quanh 4 cô gái là nhân chứng duy nhất trong một vụ án đau đớn hồi tiểu học, tác giả đi sâu vào tâm lí của từng người sau khi vụ án xảy ra, với lời hứa \"chuộc tội\" với mẹ cô bạn bị sát hại. Mình ấn tượng với tác giả về ở cách miêu tả tâm lí nhân vật và những ám ảnh vô hình vụ án đó đem lại cho 4 cô bé. Từ góc nhìn của những đứa trẻ, cộng với là tác động ngẫu nhiên của gia đình, mỗi người chọn cho mình một lối sống khác nhau, có tốt, có xấu, ... Nhưng không hiểu do số phận sắp đặt, hay xuất phát từ quan điểm sống mỗi người, mà tất cat đều gặp phải những tình huống éo le... Người mẹ của cô bé nạn nhân là cú twist cuối, cũng là sự chuộc tội của chính cô ấy. Đây là một câu chuyện tâm lí, theo mình thì khá xuất sắc, vì khi đọc thì mình căng thẳng dã man :( Sai lầm nối tiếp sai lầm, để lại hậu quả thật sự rất nghiêm trọng. Liệu trong cuộc sống thực tại, có ai tránh được điều đó không?', '2023-04-25'),
(35, 'LMneqdUPdG', NULL, 'jBSM7nyWJV', '4', 'Sau \"Thú tội\"', '\r\nChuộc Tội – Một lời nói dối có thể dẫn đến những kết cục nào ? Ai ai chắc hẳn cũng đã được dạy về những tác hại khôn lường của một lời nói dối, có lẽ sẽ có một số người nghĩ rằng chúng có thể không ảnh hưởng quá nặng nề đến cuộc sống của người khác. Nhưng có lẽ Chuộc Tội sẽ giúp chúng ta nhìn nhận lại vấn đề này một sách sâu sắc hơn Cuốn sách Chuộc Tội lấy bối cảnh vào nước Anh năm 1935, trong một ngôi biệt thự to lớn sang trọng, cô bé Briony cùng với người chị của mình Cecilia đang sống cùng với bố mẹ. Đây là một mùa hè lí tưởng mà ai ai cũng mong muốn khi được sống trong một môi trường mát mẻ, đẹp đẽ. Nhưng ngoài gia đình, nơi hai chị em sống còn có sự xuất hiện của Robbie, anh là con trai của người giúp việc. Anh học cùng trường Cambridge với Cecilia, khi cô trở về nhà nghỉ hè thì Robbie cũng đến nhà cô làm việc. Dần dần giữa cả hai hình thành tình cảm nam nữ rất đối chân thực và sâu đậm. Briony khi biết thì không những không vui mà còn một phần nào đó ghen tị với chị mình, bên trong cô bé 13 tuổi này cũng dành một chút tình cảm cho Robbie Một ngày, Robbie được mời tới nhà của Briony và Cecilia để ăn tối. Do một số hiểu lầm trước đó cũng như bắt gặp chị mình cùng với Robbie, dù không quá chắc chắn về những gì mình đã thấy cũng như liệu nó có đúng hay không Briony đã tố cáo Robbie có hành động không tốt với Lola, người vừa là chị họ vừa là bạn của cô cho dù anh không gây ra sự việc đó. Điều này đã trực tiếp đẩy Robbie đến với hai lựa chọn: một là ở trong tù, hai là tham gia vào chiến tranh chiến đấu cho đất nước. Để không phải sống trong nhục nhã, anh đã chọn tham gia chiến đấu. Robbie quyết định như vậy vì anh muốn ngẩng cao đầu để yêu và sống với Cecilia mà không xấu hổ Trong thời kì chiến tranh, Cecilia đi làm y tá để cứu giúp những người lính bị thương. Briony vì giữ trong lòng sự hối hận, nuối tiếc về việc mà mình đã gây ra đã khiến cho Robbey và Cecilia phải lìa xa nên cô cũng tình nguyện làm y tá. Liệu giữa bom đạn tàn phá, cả hai có thể ở bên nhau và hạnh phúc mãi mãi về sau ? Cuốn sách này thật sự quá đau buồn, bi thương, có thể gây ám ảnh cho những độc giả sống nội tâm. Dù có biết trước kết thúc, ta cũng vẫn sẽ tiếc thương cho các nhân vật cũng như có những cái nhìn, suy nghĩ, cảm nhận khác nhau về hoàn cảnh và hành động của họ', '2023-04-25'),
(36, 'o2hSQrxrrh', NULL, 'jBSM7nyWJV', '5', 'Án mạng mười một chữ có nét giống “Mười người da đen nhỏ” của Dì Tha', '\r\nMột quyển sách rất hay. Nhân vật chính là một nữ nhà văn trinh thám đi điều tra cái chết của bạn trai mình. Quá trình điều tra tiếp tục dẫn đến những án mạng khó hiểu và tất cả nạn nhân và nghi phạm đều liên quan đên một tai nạn du lịch bất thường trước đây khiến một người tử vong... Truyện này viết vào giai đoạn đầu của Keigo. Dung lượng mỏng dễ đọc, thuần tuý điều tra suy luận, không khó nhận ra có ảnh hưởng của Agatha Christie. Nhưng Keigo vẫn thể hiện được tài năng trong việc xếp đặt các tình tiết khéo léo và đầy bất ngờ cho đến những trang cuối. Tuy chưa đề cập đến những vấn đề xã hội sâu sắc như những tác phẩm sau này nhưng truyện cũng cho thấy sự ích kỉ, thâm hiểm tồn tại ở con người. Đời luôn có những kẻ vì lợi ích bản thân mà sẵn sàng hy sinh người khác làm chốt thí, còn mình thì ung dung phủi tay. Kết cục của truyện đúng chất Keigo, tuỳ độc giả sẽ có cảm nhận của riêng mình. Đây không phải tác phẩm tiêu biểu của ông nhưng mình thấy thú vị, đọc đổi vị khá ok.', '2023-04-25'),
(37, 'mp2Rjm8h6s', NULL, 'jBSM7nyWJV', '4', 'Quá nhiều cảm xúc hòa lẫn trong tâm trí tôi ', '\r\nQuá nhiều cảm xúc hòa lẫn trong tâm trí tôi sau khi đọc xong quyển sách này. Quyển sách là tiếng nói, là nỗi lòng của một đứa trẻ mà theo tôi bất cứ ai khi đọc xong cũng nhận ra rằng tình yêu thương, sự quan tâm có vai trò lớn lao thế nào đối với trẻ. Cậu bé Zezé năm tuổi trong câu chuyện cũng hồn nhiên, trong sáng như Anne tóc đỏ, như cô bé Heidi, cũng có phần nghịch ngợm như thằng nhóc Emil. Cậu bé đã khiến tôi phì cười không ít lần. Dù hay gây ra rắc rối nhưng Zezé là một em bé theo tôi là rất đáng yêu. Truyện đưa ta đến với những trò quậy phá, những vụn vặt ngày thường của cậu bé nhưng không hề nhàm chán chút nào cả. Tôi rất hứng thú khi được đặt bước chân vào thế giới của cậu bé con ấy để hiểu hơn về trẻ, để có thể nuôi dạy con một cách tốt nhất. Tôi lắng nghe từng suy nghĩ, từng câu thoại của cậu bé và càng thấm thía hơn bao giờ hết: Trong trái tim đứa trẻ đôi khi có những con quỷ dữ, hãy đánh đuổi con quỷ ấy đi bằng sự thấu hiểu, bằng tình yêu, sự bao dung chứ không phải là dùng đòn roi. Đòn roi, bạo lực chưa bao giờ là cách giải quyết tốt nhất cho mọi vấn đề. Cuốn sách không phải là dòng suối êm ả của ngôn từ, chẳng bóng bẩy, óng ả câu chữ, nó vốn dĩ mang hơi thở của đời thường. Nhưng nó lại đặc biệt bởi diễn biến cốt truyện hấp dẫn, tác giả biết cách dẫn dắt để đưa ta lên đỉnh đồi của cao trào, có lúc đặt ta vào những cảm xúc dịu êm như dải lụa mềm mại. Nhà văn biết cách để bóp nghẹt tim ta, làm nước mắt ta tuôn trào.', '2023-04-25'),
(38, 'zeg7F8xfGV', NULL, 'jBSM7nyWJV', '5', '“Bước chậm lại giữa thế gian vội vã” ', '\r\n“Bước chậm lại giữa thế gian vội vã” của Đại Đức Hae Min là một tập tản văn nhẹ nhàng sâu lắng khiến cho những người có nhịp sống hiện đại luôn tất bật hối hã với công việc sẽ có thời gian dừng lại một chút giữa những căng thẳng lo toan trong cuộc sống. Đây là một quyển sách đầy tính nhân văn và tình cảm của Đại đức Hae Min- một vị tu sĩ có tầm ảnh hưởng rất lớn đến giới trẻ Hàn Quốc. Là cuốn sách liên tục đứng đầu bảng best seller từ năm 2012 trên các trang sách trực tuyến của Hàn Quốc, “Bước chậm lại giữa thế gian vội vã” là cuốn sách được yêu thích của những bạn trẻ tìm lại sự bình yên giữa cuộc đời bận rộn. Ngay từ trang đầu tiên, cuốn sách đã mang lại cho người đọc cảm giác muốn được nghỉ ngơi, tìm một điểm tựa an yên trong mình. Đây là cuốn sách thực sự thích hợp với những người luôn tất bật, vội vả trên dòng đời, chạy đua với công việc, với thời gian mà quên mất đi bản thân, những mỗi quan hệ xung quanh và những điều tốt đẹp trong cuộc sống. Với câu từ giản đơn, nhẹ nhàng sâu lắng nhưng lại đầy nhiệt huyết tâm tình làm cho đọc giả có cảm giác muốn sống chậm lại để nghỉ ngơi một tí để tận hưởng cuộc sống an nhiên. Hơn thế nữa, không chỉ đem lại một cái nhìn sâu sắc về cuộc sống, Đại đức Hae Min còn đưa đến cho bạn đọc cái nhìn đúng đắn về tôn giáo, khoảng cách về sự khác biệt tín ngưỡng. Dù có đức tin không giống nhau nhưng tôn giáo nào cũng mang lại cho con người tình yêu và sự yên bình trong tâm hồn. Đại đức Hae Min đã chân thành học hỏi và tìm hiểu những tôn giáo khác, là nhà sư nhưng đôi khi cũng chọn cho mình một vài đoạn trích trong Kinh Thánh để làm kim chỉ nam cho cuộc đời mình. Theo bản thân tôi, dù mỗi tôn giáo có sự khác biệt đến mấy đi chăng nữa đều hướng con người ta về cái thiện về những đều tốt đẹp, đều hướng con người đến sự tích cực, lạc quan trong cuộc sống. Ta biết sẵn sàng trân trọng sự khác biệt, chân thành lĩnh ngộ, tiếp thu những điều tốt đẹp là bài học đáng quý mà Đại đức Hae Min truyền đến cho đọc giả thông qua cuốn sách của mình. Bước chậm lại giữa thế gian vôi vã được chia thành 8 chương với 8 chủ đề rất gần gũi và quen thuộc đối với mỗi người chúng ta trong cuộc sống, đó là: Chương 1: Nghỉ ngơi Chương 2: Những mối quan hệ Chương 3: Tương lai Chương 4: Cuộc sống Chương 5: Tình yêu Chương 6: Tu hành Chương 7: Nhiệt huyết Chương 8: Tôn giáo Mỗi chương đều mang đến cho người đọc những cảm xúc sâu lắng, học được một bài học gì đó cho bản thân. Mỗi trang sách của tác giả như những lời tâm sự chân tình, những lời nhắn nhủ gửi đ', '2023-04-25'),
(39, 'QelgC4YnOe', NULL, 'jBSM7nyWJV', '5', 'Hãy yêu đi!!!!!', '\r\nQuyển sách “Những quy tắc trong tình yêu” giúp chúng ta hiểu rằng sự quan trọng của một người đặc biệt trong đời sống tình cảm của mỗi người. Để trân trọng một người nào đó không phải là một việc quá khó, tất cả đều là lựa chọn của chúng ta. Thực ra, khi yêu, bất kỳ ai cũng ao ước sẽ mang lại hạnh phúc cho người mình thương. Khi thấy người ấy vui vẻ, bản thân cũng vui lạ thường. Nhưng rồi tháng ngày trôi qua quá vội vã, những thói quen lầm lỗi trong chúng ta trỗi dậy khiến ta tư duy và hành động những việc sai trái, vô tình làm tổn thương người mình yêu thương. Sau đó, ngộ ra được mọi việc thì tất cả đã muộn màng. Tại sao ư? Vì có thể người ấy đã không còn nữa hoặc nếu họ còn tồn tại thì tình cảm mà cả hai dành cho nhau đã không thể vẹn nguyên như thuở ban đầu. Hãy trân trọng tình cảm của chính mình và tôn trọng đối phương của bạn. Những đoạn đường quanh co của cuộc đời rồi cũng sẽ qua, nếu trân trọng nhau và cùng nhau bước tiếp thì cái kết sẽ đều như ý ta mong chờ. Lưới tình là một thuật ngữ khá quen thuộc dùng để chỉ ai đó đang hoang mang và rung động bởi cảm xúc đang xáo trộn hoàn toàn. Quyển sách này gọi đó là mê hồn trận của tình yêu và quả đúng như vậy khi tình yêu thậm chí có thể khiến chúng ta mù quáng. Và những quy tắc từ quyển sách này cũng sẽ là những tips nho nhỏ thú vị giúp bạn đồng hành theo cách tốt nhất và đúng đắn nhất khi yêu vì những trải nghiệm mà', '2023-04-25'),
(40, 'kyfLna4MRt', NULL, 'jBSM7nyWJV', '5', 'Mọi nỗ lực và chờ đợi của bạn đều có ý nghĩa.\" ', 'Quyển sách đầu tiên tiếp cho mình năng lượng tích cực cho những ngày dài; mỗi khi cuộc sống khó khăn quá, hãy chờ đợi, hãy nỗ lực, vì những điều ấy, tất cả những điều ấy, đều có ý nghĩa. Mình luôn tin như thế. Nên mình sẽ ôm chính mình thật chặt, và bảo rằng chính mình sẽ không bao giờ từ bỏ. Nói đến quyển sách này, mỗi một trang là một nguồn sáng nhỏ nhỏ, chờ người lật đến để đặt vào lòng ánh sáng hy vọng, đặt vào những suy nghĩ tích cực, ấm áp và đầy sức sống. Những câu chuyện có ngắn có dài, với góc nhìn khác nhau, Uyển Tử Văn - Uyển Tử Hào đem đến người đọc đoạn tình cảm khác, những suy nghĩ và bài học khác trong cùng một sự việc. Có thể là vụn vặt trên mạng xã hội, lông gà vỏ tỏi về việc nhà, cũng có thể là bước ngoặt lớn của cả hai anh em. Chính những bước ngoặt đó, hai anh em mới kiên cường đứng lên, vững vàng và mạnh mẽ, truyền cho mọi người nhiệt huyết từ những con chữ của mình.', '2023-04-25'),
(41, '146PEDfGuL', NULL, 'jBSM7nyWJV', '4', 'Why We Sleep ???', 'Nếu bạn xét thấy mình có tất cả các đặc điểm như “khó chìm vào giấc ngủ, thức dậy vào giữa đêm, thức dậy quá sớm vào buổi sáng, khó ngủ lại sau khi thức dậy và cảm thấy không được khỏe mạnh suốt cả ngày khi đang thức” thì bạn đã – đang bị chứng mất ngủ, và dù đang ở độ tuổi nào, tình trạng sức khỏe như thế nào… bạn vẫn có thể tích cực học cách chữa khỏi chứng mất ngủ, không phải bằng thuốc ngủ mà bằng liệu pháp CBT-I. Để nhớ rõ cụm ký tự viết tắt này, mình “chế” cho nó tên gọi là “Con Báo Trắng I”. Liệu pháp này như sau: Các gạch đầu dòng trên có vẻ nhiều, nhưng nếu thật sự muốn cải thiện giấc ngủ thì bạn sẽ tìm cách nhớ được chúng. Vì những lý do khách quan và chủ quan mà mình đã phải uống thuốc ngủ thảo mộc vài tháng nay, nhưng sau khi đọc chương sách về thuốc ngủ trong cuốn này, nhất là câu “rốt cuộc, hiệu ứng giả dược là tác dụng đáng tin cậy nhất trong tất cả các tác dụng của thuốc lên cơ thể” thì Biển đã ngay lập tức thực hiện các liệu pháp Con Báo Trắng I, dù muốn lắm cũng kiên quyết không uống viên thuốc ngủ nào nữa, kết quả là khoảng 10 ngày nay mình ngủ ngon không cần thuốc ngủ. Cuốn sách này đã có những tác động thành công đối với Biển. Đoạn trích sau đây là một trong những lý do khiến mình không muốn uống thuốc ngủ nữa: “…hàm lượng melatonin không cần kê toa thường không được các cơ quan quản lý trên toàn thế giới quy định… trong khi những đánh giá khoa học về các loại thuốc không cần kê toa đã tìm ra nồng độ melatonin dao động từ dưới 83% tới trên 478% so với những gì được ghi nhận trên nhãn sản phẩm”. Trước giờ mọi người thường biết đến melatonin như hormone chính chi phối giấc ngủ, nhưng thật ra melatonin chỉ là “người bắn phát súng bắt đầu cuộc đua giấc ngủ”, điều thật sự khiến con người buồn ngủ là một hóa chất trong não tên là adenosine. Hóa chất này sẽ bám vào các thụ thể trong não, tích tụ dần trong suốt thời gian từ khi bạn thức dậy đến lúc đi ngủ, càng thức lâu thì adenosine càng tích tụ nhiều gây ra “món nợ ngủ”, món nợ này nên chỉ có thể trả mỗi ngày chứ không thể gom lại trả vào cuối tuần, tức là không thể cắt xén giấc ngủ trong cả tuần để rồi ngủ suốt hai ngày cuối tuần. Thế nhưng, giấc ngủ đã bị xem nhẹ quá lâu (kể từ khi bóng đèn được phát minh), sức khỏe lại là một điều khó nhìn thấy bằng mắt thường, nên sẽ rất khó thuyết phục người khác coi trọng giấc ngủ và sức khỏe của chính họ. Cuốn “Sao chúng ta lại ngủ” cũng đem đến cho mình hiểu biết mới mẻ về “cú đêm” và “chim dậy sớm”. Trước đây, mình tin rằng thời điểm thích hợp để th', '2023-04-25'),
(42, 'VpaNNNcKEQ', NULL, 'zAYghE9NNZ', '5', 'L&yacute; Tiểu Long - Tuổi thơ của t&ocirc;i', 'Cuốn s&aacute;ch d&agrave;nh rất nhiều trang m&ocirc; tả sự vươn l&ecirc;n của L&yacute; trong ng&agrave;nh điện ảnh, trước ti&ecirc;n l&agrave; ở Mỹ (thất bại, đương nhi&ecirc;n, v&igrave; l&yacute; do lớn nhất: L&yacute; Tiểu Long l&agrave; người gốc &Aacute;!), sau đ&oacute; ở Hong Kong v&agrave; trong năm cuối đời l&agrave; sự vinh quang tại Mỹ, trước khi ng&ocirc;i sao n&agrave;y vụt tắt. Độc giả hiểu được ng&agrave;nh điện ảnh, ng&agrave;nh truyền h&igrave;nh, ng&agrave;nh giải tr&iacute; ở Mỹ v&agrave; HK vận h&agrave;nh ra sao, quan hệ giữa những &ocirc;ng chủ, nh&agrave; sản xuất, nh&agrave; bi&ecirc;n kịch, đạo diễn, diễn vi&ecirc;n ng&ocirc;i sao, diễn vi&ecirc;n phụ, điễn vi&ecirc;n đ&oacute;ng thế.... diễn ra chồng ch&eacute;o như thế n&agrave;o. Như những thước phim t&agrave;i liệu, ch&uacute;ng ta thấy được kinh đ&ocirc; Hollywood giai đoạn 60-70, v&agrave; điện ảnh Hong Kong trong thời sơ khai, đ&acirc;y l&agrave; phần nội dung s&aacute;ch m&agrave; độc giả quan t&acirc;m tới ng&agrave;nh giải tr&iacute; hẳn sẽ thấy v&ocirc; c&ugrave;ng th&uacute; vị, với nhiều li&ecirc;n tưởng tự nhi&ecirc;n tới showbiz Việt Nam ng&agrave;y nay. Về v&otilde; thuật, t&aacute;c giả viết kh&ocirc;ng kỹ bằng điện ảnh, song cũng cho t&ocirc;i những cảm nhận th&uacute; vị. Lần đầu t&ocirc;i biết v&otilde; thuật c&oacute; mấy chức năng hay kh&iacute;a cạnh ch&iacute;nh: chiến đấu (sure!), thể thao (trong c&aacute;c m&ocirc;n v&otilde; tại c&aacute;c cuộc thi thể thao), giải tr&iacute; v&agrave; cuối c&ugrave;ng l&agrave; t&acirc;m linh - tinh thần (kh&iacute;a cạnh &#039;đạo&#039; của v&otilde; thuật, coi đ&oacute; như l&agrave; 1 triết l&yacute; sống). Những đoạn đấu v&otilde; cả ngo&agrave;i đời v&agrave; trong phim của L&yacute; được viết rất sinh động, linh hoạt (phải cảm ơn dịch giả khi đọc những đoạn n&agrave;y - kh&ocirc;ng m&ecirc; L&yacute; Tiểu Long v&agrave; kh&ocirc;ng m&ecirc; v&otilde; th&igrave; e l&agrave; kh&oacute; dịch được như thế ). Phần h&igrave;nh ảnh đ&iacute;nh k&egrave;m trong s&aacute;ch, được in ở đầu c&aacute;c chương, cũng rất đẹp v&agrave; sinh động. Ch&uacute;ng khiến cuốn s&aacute;ch d&agrave;y cộp n&agrave;y trở n&ecirc;n &#039;mềm mại&#039; đi &iacute;t nhiều! T&oacute;m lại đ&acirc;y l&agrave; 1 cuốn s&aacute;ch kh&aacute; hay, d&ugrave; như đ&atilde; n&oacute;i, t&aacute;c giả hơi &#039;tham&#039; khi viết qu&aacute; chi tiết, khiến s&aacute;ch hơi d&agrave;y v&agrave; đ&ocirc;i chỗ lan man. Đọc để hiểu th&ecirc;m, y&ecirc;u t', '2023-04-25'),
(43, 'VI4tt7URCb', NULL, 'zAYghE9NNZ', '5', 'Khởi nghiệp!!!', '\r\nĐã có rất nhiều sách viết về chủ đề đầu từ nhưng hầu như đều là đầu tư chứng khoán trên sàn hay bất động, còn về nội dung đầu tư cho doanh nghiệp khởi nghiệp thì chưa thấy , và quyển sách này đã giải quyết được vấn đề đó. Nhìn chung, theo tôi đây thật sự là một quyển sách rất đáng để chúng ta mua về đọc. Tuy nhiên, mọi chi tiết đều có giá trị vì khi cần bạn sẽ có thể nhớ nơi tìm kiếm thông tin trước khi đưa ra quyết định quan trọng có thể ảnh hưởng đến tương lai của công ty bạn.“Một cuốn sách cần thiết cho các chủ doanh nghiệp. Brad và Jason đã làm sáng tỏ mớ lộn xộn những hợp đồng, điều khoản của một thương vụ mua bán và sáp nhập, đồng thời cắt bỏ các vấn đề pháp lý rườm rà và tập trung vào những vấn đề mấu chốt nhất. Hãy đọc cuốn sách này nếu bạn muốn hiểu bản chất và các chiến lược cho một thương vụ đầu tư mạo hiểm', '2023-04-25');

